INSERT INTO `qb_zdasjqbxx` VALUES (52, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', NULL, '17日60余名莲花池畔小区业主区政府门前讨要说法', '7月17日上午09时45分许，陆续有30余名莲花池畔小区业主到区政府上访，主要反映的是3年过渡费，小区水、电、气都存在问题，至今房产证未办理的问题，要求政府给予解决。10时45分上访人员增加至60余人，并打出1条白底黑字布标，内容为“两年半未发过渡费，我们要生存！”。分局现场处置警力开展劝说后，上访人员将布标收回。10时50分，经现场反复劝解，聚集人员不愿意正常接访。五华分局为切实保障国家机关正常工作秩序，五华分局靳宏涛政委、政工室主任顾农带领法制大队、国保大队、巡特警大队共计120名警力，在法制宣传无效的情况下，将现场聚集人员强行带离，分别送7个派出所审查处理。目前现场已无人员聚集，带离过程中无冲突发生，无人员财产损失，带离人员具体情况进一步统计核实中。（翠湖所）\r\n', '2017-07-17 09:45:00', '2017-07-17 10:50:00', 1.08, 60, '拆迁补偿', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-24 11:43:34', '2017-07-24 15:27:44', NULL, '村民', NULL, '0', '1', '并打出1条白底黑字布标', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2507, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'wsyr', '51-100人', '1', 'qkfy', 60, 0.00, 0, 0, '0', NULL, '情况反映', '0d4d5593-c3bb-488d-9ff0-f02f526b1502', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
