INSERT INTO `qb_zdasjqbxx` VALUES (58, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', NULL, '7月17日100余名昆明牙膏有限责任公司职工到市政府等部门上访', '据工作掌握：2017年7月17日9时许，青年路节孝巷昆明牙膏有限责任公司原昆明牙膏厂100余名（在职工人70余人、退休职工30余人）职工到市政府上访。\r\n\r\n经了解，昆明牙膏有限责任公司前法人沈洋、刘洁在与昆明牙膏有限责任公司“增资扩股案”二审判决未果之时，一是在外传播谣言称“牙膏厂必然败诉，他们肯定胜诉，牙膏厂现在的生产经营是不合法的”。二是称昆明牙膏有限责任公司反映有人在外省制作销售冒牌三七牌牙膏。对法院二审未判决之前，沈洋、刘洁的言行在牙膏厂引起职工极大恐慌，严重影响了职工的正常生产生活秩序。为此，事关职工切身利益，职工相互邀约100余到市政府向市纪委反映沈洋、刘洁的言论（问题）。上午11时许，职工离开市政府，随后于下午14时30分许到昆明市中级人民法院聚集，向法院领导反映问题。\r\n\r\n研判分析：长期以来，昆明牙膏有限责任公司大部职工的维权活动，主要是由企业改制遗留下的问题，围绕公司的前任厂领导沈洋、刘杰等人开展，双方的对立情绪较严重，维权职工及家属认为，诉求得不到解决，他们将面临企业倒闭，职工已经投入牙膏厂的股权权益就会得不到保障，所以将继续到省委、省政府、市委、市政府上访。建议上级主管部门及市工信委为维护社会稳定和当前的“创文”活动，继续做好疏化工作。密切关注中。（国保大队）\r\n\r\n\r\n', '2017-07-17 09:00:00', '2017-07-17 12:30:00', 3.50, 100, '企业改制/破产 ', NULL, '市政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-24 11:43:34', '2017-07-24 15:28:17', NULL, '退休职工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局,报区政府', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2602, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '104fbc80-ec2e-4952-ae16-ef838ed0acb0', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (62, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', NULL, '中国水电十四局8名退休教师不满身份待遇到省政府上访', '18日10时30分，8名中国水利水电第十四工程局（驻云南省部分地州）退休中小学校教师到省政府上访，反映退休时十四局按照企业职工待遇为该局1000余名教师办理了退休手续，要求十四局认可他们的教师身份，按照公办教师的级别享受退休待遇。接待中。\r\n', '2017-07-18 10:30:00', '2017-07-18 11:30:00', 1.00, 8, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-24 11:43:34', '2017-07-24 15:54:17', NULL, '退休职工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'jsxs', '教师学生', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '2', 'qkfy', 8, 0.00, 0, 0, '0', NULL, '情况反映', '221b6598-cdd8-4a79-b20a-0e244a4d3c10', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (64, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', NULL, '昭通市泼机镇村民7人因不满死亡赔偿一事到省政府上访', '18日10时35分许，昭通市镇雄县泼机镇坪天村7名村民继17日后再次到省政府上访，反映亲属王昌金5月25日途经平天村花鱼洞11号桥墩时，被高压电电击身亡，要求追究高压线所属单位镇雄县龙镇公司的安全事故责任并进行赔偿。接待中。\r\n\r\n\r\n', '2017-07-18 10:35:00', '2017-07-18 11:30:00', 0.92, 7, '民间纠纷', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-24 11:43:34', '2017-07-24 15:28:36', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'glsg', '各类事故', 'sryx', '10人以下', '2', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '2df89be1-4618-4fdd-adb7-186ab0a59212', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (66, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', NULL, '7月18日昆明牙膏有限责任公司职工到省委和中级人民法院上访情况', '7月18日10时许，50余名昆明牙膏有限责任公司职工到昆明市中级人民法院聚集上访，上访过程中，昆明牙膏有限责任公司的50余名职工在昆明市中级人民法院门口聚集，未发生打标语、喊口号等过激行为。昆明市中级人民法院工作人员称，对于7月17日职工提出的诉求，将于近日给予答复，截止目前，50余名昆明牙膏有限责任公司职工仍然在昆明市中级人民法院门口聚集不愿离开。\r\n\r\n另，7月18日10时许，6名昆明牙膏有限责任公司职工到省委信访办递交诉求后便离开现场。（国保大队）\r\n\r\n\r\n', '2017-07-18 10:00:00', '2017-07-18 10:30:00', 0.50, 50, '企业改制/破产 ', NULL, '市中院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-24 11:43:34', '2017-07-24 15:27:07', NULL, '退休职工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '市中院', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '352a09f0-3411-4553-b7e7-41b7d81c38d8', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (69, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', NULL, '7月14日20名“12.13”人员到青年路工商银行云南省分行集访情况 ', '7月14日16时许，20名在昆部分“12.13”人员到青年路工商银行云南省分行聚集，要求银行公布涉泛亚资金的流向。期间上访人员在青年路工商银行云南省分行大门口靠青年路方向手持高举A3纸打印的标语，内容为：“还我看病钱”；“还我养老钱”；“习主席李总理请为我做主还我救命钱！”等内容的打印件并且呼喊标语上的内容。17时30分，上述人员陆续离开现场。相关情况进一步掌握中。\r\n', '2017-07-14 16:00:00', '2017-07-14 17:30:00', 1.50, 20, '特殊群体', '小南所', '青年路工商银行云南省分行', NULL, NULL, '5', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-24 11:47:24', '2017-07-24 14:38:52', NULL, '12.13', NULL, '0', '2', '高举A3纸打印的标语', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '青年路工商银行云南省分行', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2607, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '42148f96-3245-4465-8e66-e973d91b6948', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (74, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', NULL, '17日昭通市泼机镇村民9人因不满死亡赔偿一事到省政府上访', '17日09:35分许，昭通市镇雄县泼机镇坪天村村民9人到省政府上访，反映，他们均是王昌金的家属，今年5月25日，王昌金在路过平天村花鱼洞11号桥墩下时，被架设在该路段的高压电电击身亡，据家属了解，该高压线属于镇雄县龙镇公司架设，家属遂向该公司讨要说法和赔偿，镇雄县政府亦从中协调。由于一直无法达成赔偿协议，所以家属到省政府上访，要求追究龙镇公司的安全事故责任并且对家属进行赔偿。省信访局接待后告知其回昭通市反映诉求，由于不满接待结果，9人滞留信访大厅，声称不解决就不走。现民警正在开展相关稳控工作并密切关注9人动态。\r\n', '2017-07-17 09:35:00', '2017-07-17 11:30:00', 1.92, 9, '民间纠纷', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-24 11:47:24', '2017-07-24 15:08:22', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'sryx', '10人以下', '2', 'qkfy', 9, 0.00, 0, 0, '0', NULL, '情况反映', 'e77fc8b3-3f7c-4dea-857e-b7c5753d4ec0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (77, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', NULL, '7月13日金座集资户周四例行聚集情况', '据工作掌握，7月13日9时30分许，以罗益民、纪昌凤为首的金座集资户80余人到市体育馆集访，昆明市五华区政府、盘龙区政府及市金融办相关领导进行了接访。金座集资户代表主要诉求是向五华区、盘龙区政府递交生活困难补助的申请。 \r\n\r\n11时20分许，由于该维权群体成员李媛芬于7月13日接到五华区人民法院西站法庭的三张传票金座其它群体成员的陶炳芳、白秀珍、李云祥等6人以不当得利纠纷分别对李媛芬进行起诉），开庭时间为7月19日9时、10时、11时。该传票在维权群体中引起很大反应，以罗益民、纪昌凤为首的30名金座集资户又到西站法庭过问此事，并通知全体成员于7月14日9时到西站法庭聚集。\r\n\r\n同日9时50分许，以卢福全为首的金座集资户30余人到南屏步行街中心花园附近开展例行的聚集活动，活动过程中未发生喊口号、打标语等过激行为。11时许，聚集的人员自行离开现场。\r\n\r\n相关情况密切掌握中。\r\n\r\n\r\n', '2017-07-13 09:30:00', '2017-07-13 11:30:00', 2.00, 110, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-24 11:48:05', '2017-07-24 15:55:02', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'yj', 0, 0.00, 0, 0, '0', NULL, '预警', 'f3f24325-85a3-4db8-921a-4991c2674001', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (78, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', NULL, '7月14日80余名金座集资户到西站法庭聚集情况', '？？据工作掌握，继7月13日中午12时30分许，以罗益民、纪昌凤为首的金座集资户30余人到五华区人民法院西站法庭咨询李媛芬不当得利纠纷的开庭事宜后，该30人于中午13时许自行离开。由于已经通知该群体全体成员于7月14日9时到西站法庭聚集，7月14日上午9时，该群体已经安排部分成员在现场对陆续到达的金座集资户其它成员进行劝离并告知下个星期二（7月18日）继续到昆明市体育馆聚集上访。？（国保大队）\r\n\r\n\r\n', '2017-07-14 09:00:00', '2017-07-14 11:30:00', 2.50, 80, '特殊群体', '文林所', '西站法庭', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-24 11:48:05', '2017-07-24 15:22:23', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '西站法庭', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2645, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '694d257c-921a-465e-aea2-aa38c4573b87', 'JS', NULL, 'Y', 110, NULL, NULL, '530102440000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (80, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', NULL, '7月24日购买大理市银水帝都旅游投资有限公司蜜湾二期楼盘的8名购房者到省政府上访', '7月24日14时30分许，购买大理市银水帝都旅游投资有限公司蜜湾二期楼盘的8名购房者到省政府上访，反映2013年570余户购房者购买了大理蜜湾二期楼盘，规划2014年交房，现该楼盘已烂尾，请省政府督促开发商退还所交房款并赔偿违约金。接访中。', '2017-07-24 14:30:00', '2017-07-24 23:19:00', 8.82, 8, '逾期未交房', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-28 15:34:25', NULL, NULL, '购房人员', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (81, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', NULL, '7月24日8人到顺城理想装饰公司聚集讨要说法', '7月24日13时30分，新村所报警：顺城东塔11楼理想装饰公司被一家老小8人堵门，情绪激动，无法正常办公。\r\n2013年理想公司将大理洱海龙汪项目承包给项目经理四川省资中县男子刘智斌（40岁），施工过程中刘智斌向昆明西部石材城东区“鼎易”石材城购买了28万元的装修材料，但一直未付款，虽然昆明西部石材城东区鼎易石材城负责人云南省镇雄县妇女钱云（32岁）多次，但仍无结果。今日，钱云意图通过理想装饰公司催促刘智斌结清28万元，遂带上家里老人小孩共8人到理想装饰公司讨要说法。', '2017-07-24 13:30:00', '2017-07-24 17:00:00', 3.50, 8, '讨薪索款', '新村所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-28 15:38:06', NULL, NULL, '个体户', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (82, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', NULL, '7月25日金座集资户周二聚集', '7月25日9时30分许，以纪昌凤、罗益民为首的金座集资户130余人陆续到市体育馆上访。由于昆明市农业局局长未到接访现场，引发金座集资户维权群体的不满。经解释安抚，上述人员于12时30分陆续离开市体育馆。', '2017-07-25 09:30:00', '2017-07-25 12:30:00', 3.00, 130, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-28 15:41:36', NULL, NULL, '金座', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
