INSERT INTO `qb_zdasjqbxx` VALUES (85, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', NULL, '7月25日40余名“儒商”等我市涉金融受损群体成员到省金融办上访', '为响应7月22日联合维权会议决定， 7月25日9时30分许我市涉金融受损群体“儒商”和“保利贷”、“光辉”等群体的40余名成员到云南省金融办聚集上访，要求省金融办对6月30日和7月3日上访的诉求给予书面回复，省金融办的相关领导进行了接访，12时30分许接访结束，上述人员陆续离开现场。', '2017-07-25 09:30:00', '2017-07-25 12:30:00', 3.00, 0, '涉众型经济案件', '盘龙分局', '省金融办', NULL, NULL, '0', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-28 15:52:10', '2017-09-05 16:24:48', NULL, '小微儒商', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区外', '省金融办', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2608, 2503, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530103000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (91, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-07-27 15:45:49', '7月27日130余名金座集资户周四例行聚集情况', '据工作掌握， 7月27日上午9时30分许，以罗益民、纪昌凤为首的金座集资户130余人到市体育馆集访，昆明市工商局及市金融办相关领导进行了接访。由于市工商局副局长对金座集资户代表的诉求答复不满，引发金座集资户的抗议。集资户代表要求请市工商局局长出面接访。经过对金座集资户做劝解工作后，直到中午12时10分许，金座集资户自行离开市体育馆。同日上午10时30分许，以卢福全为首的金座集资户40余人到南屏步行街中心花园附近开展例行的聚集活动，活动过程中未发生喊口号、打标语等过激行为。11时30分许，聚集的人员自行离开现场。相关情况在进一步掌握中。INSERT INTO `qb_zdasjqbxx` VALUES (99, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-07-27 09:20:21', '“7.27”投融资群体省政府非访情况', '据情报显示，“12.13”等涉众型经济犯罪利益受损群体拟于7月27日到省政府聚集上访。按照党委、政府和上级公安机关部署要求，为全力阻止和妥善处置可能发生的规模性群体性事件，分局成立“7.27”维稳应急处置工作领导小组，由副区长、分局局长涂力军任组长，党委委员、副局长高勇任副组长，各部门主要领导为成员。27日早8时30分，市局杨建军副局长、五华分局涂力军局长到五华山现场指挥部指挥应急处置工作。分局共组织警力230人，分为指挥部、现场管控、现场处置、分流劝返及审查处理等4个部分，按照 “防止大规模聚集，全力疏导，及时劝返，发能快处”的工作目标有序开展工作。为迅速妥善处理可能出现的人员聚集非访等情况，分局现场准备了5辆转运车辆；在五华体育场设分流劝返点，配合政府职能部门做好教育劝返工作；在小南、文林、龙翔、大观、新村5家派出所设审查处理点，做好对现场带至派出所的重点人员甄别盘问，统计汇总人员情况，及时报告指挥部，现场开展法制宣教及审查处理工作。自8时40分，陆续有涉众型经济犯罪利益受损群体人员到省政府信访大厅聚集，截止9时10分，信访大厅已聚集30余人，涉及“互信”、“儒商”等两个群体，等待接访中，暂无过激情况。\r\n\r\n\r\n', '2017-07-27 08:30:00', '2017-07-17 17:30:00', 231.00, 200, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '230', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-28 17:35:14', '2017-07-31 15:43:00', NULL, '涉众型经济案件受害人', NULL, '1', '2', '喊口号、打标语', NULL, NULL, NULL, NULL, '报市局,报区政府', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2506, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '1', NULL, '情况反映', 'a818f013-96ae-456a-9d6c-1d8c5d4157c6', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (105, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-07-28 17:47:45', '28日23名“12.13”人员聚集云南省工行大厅', '7月28日16时许，23名“12.13”人员（其中女性19名，男性4名）聚集到青年路395号中国工商银行云南省分行讨要投资资金，多名“12.13”人员聚集在大厅，民警立即对现场进行疏导，并告知聚集人员要理性协商，不得有过激行为。目前，聚集人员已经离开。\r\n\r\n\r\n', '2017-07-28 16:00:00', '2017-07-28 17:30:00', 1.50, 23, '特殊群体', '小南所', '省工行', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-31 13:43:50', '2017-07-31 15:40:25', NULL, '12.13', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '省工行', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '5036965a-d65b-41f4-ac92-4264114f6253', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
