INSERT INTO `qb_zdasjqbxx` VALUES (109, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-07-28 12:43:05', '7月28日我市涉军退役人员到南屏街中心广场开展活动情况', '7月28日10时20分许，涉军退役军人到南屏街中心广场靠南屏街东口一侧开展军民联欢活动，拉有2块红色布标，布标大小：长6米左右，宽70厘米左右；2块布标俄内容分别为：昆明退役战友军民联欢活动；庆祝中国人民解放军建军90周年。目前现场退役军人人数为20人，身穿65式军装。预计13时30分左右开始活动，16时结束后到金马坊聚餐。护国所已持续关注中。（护国所）\r\n', '2017-07-28 10:20:00', '2017-07-28 16:35:00', 6.25, 80, '涉军活动', '护国所', '南屏街中心广场', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-31 13:44:13', '2017-07-31 15:45:31', NULL, '对越参战老兵', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局,报区政府', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '南屏街中心广场', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2724, 2524, 'dxhdcs', '大型活动场所', 'hdqk', '活动情况', NULL, NULL, 'zzhd', '组织活动', 'jtry', '军退人员', NULL, NULL, 'sldws', '16-50人', '1', 'qkfy', 20, 0.00, 0, 0, '0', NULL, '情况反映', '30932e0f-60d5-4e7b-86be-74a8c46d2f76', 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (110, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-07-28 12:45:42', '7月28日20余名工人再次到顺城东塔15A云南南磷集团讨要货运费', '7月28日上午，曲靖交通集团物流有限公司负责人孙洪春（男、汉族、35岁、身份证号码：532201197202060357、联系电话：13577413447）再次要约20余人到云南南磷集团分公司东磷经贸有限公司讨要货运费（2014年至今600万运费、驾驶员运输费1400万），因云南南磷集团和东磷贸易有限公司亏损，无法支付。截至上报情况，讨要货运费20余名工人暂未离开，相关情况进一步掌握中。（新村所）\r\n\r\n\r\n', '2017-07-28 10:30:00', '2017-07-28 15:15:00', 4.75, 20, '讨薪索款', '新村所', '顺城东塔', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-07-31 13:44:13', '2017-07-31 15:48:14', NULL, '个体户', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '顺城东塔', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2667, 2547, NULL, NULL, 'fyqk', '反映情况', NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'sldws', '16-50人', '2', 'qkfy', 20, 2000.00, 0, 0, '0', NULL, '情况反映', '994d8b62-9dfd-4119-bb14-6ed4fd5bcb57', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (111, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-07-31 15:58:01', '31日呈贡大方居小区业主18人到省信访局上访', '31日14时30分许，呈贡大方居小区业主18人到省信访局反映呈贡教育局违反就近入学原则，致使小区子女无法就近在“中华小学滇池星城校区”上学。现信访局正接待中。\r\n', '2017-07-31 14:30:00', '2017-07-31 15:45:00', 1.25, 18, '合同纠纷 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-01 10:04:56', '2017-08-01 10:27:30', NULL, '个体户', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', NULL, NULL, 'dzzbm', '对政策不满', 'sldws', '16-50人', '1', 'qkfy', 18, 0.00, 0, 0, '0', NULL, '情况反映', '21ef733c-67a0-45ca-8918-0440e0651136', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (112, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-07-31 22:38:42', '7月31日“尚源郦城”售楼部交房现场业主动态情况', '近期“尚信地产公司”通告“尚源郦城”住宅项目第一、二栋于7月31日交房，为此，当日上午陆续有近百名该项目购房者到售楼部了解项目进展及交房细节。据观察，截止当日中午活动结束，该两栋共计407户中仅有23户同意接房。整个过程中未发生过激维权情况。通过工作掌握，目前因考虑到贸然接受未经验收的住宅将会给自身带来诸多不利及隐患，大多数购房者基本上坚持项目验收通过后才会接房的原则。目前，“尚源郦城”住宅项目在水电等基础设施不到位且未通过验收情况下，地产商就于近期实施的“交房”，业主接访将很难推进。相关情况进一步收集掌握中。（国保大队）\r\n', '2017-07-31 09:30:00', '2017-07-31 12:00:00', 2.50, 100, '逾期未交房', NULL, '尚源郦城售楼部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-01 10:04:56', '2017-08-01 10:34:28', NULL, '尚源郦城业主', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '尚源郦城售楼部', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2533, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '272e7c6d-7747-4ffa-b144-1fd011b7256d', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (113, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-08-10 13:55:14', '8月10日我市部分参战老兵及“新丰幼儿园”教职工到云南冶金集团公司聚集', '8月10日10时许，赵勇刚、毛文春、胡跃伟等我市对越参战老兵（10余人，着便装）及原“新丰幼儿园”教职工30余人，聚集小康大道云南冶金集团，反映“新丰幼儿园”被拆除后的赔偿问题及在幼儿园务工的老兵生活就业问题。冶金集团表示，近期公司董事长将出面解决幼儿园拆除补偿问题，并会考虑老兵王占非、张茂明再就业的问题。12时许，上述人员自行离开。\r\n\r\n相关情况进一步掌握中。\r\n', '2017-08-10 10:00:00', '2017-08-10 12:00:00', 2.00, 30, '拆迁补偿', '红云所', '云南冶金集团公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-14 16:55:33', '2017-08-15 17:10:54', NULL, '对越参战老兵', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '云南冶金集团公司', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2697, 2524, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '52ea4c5e-ac15-4079-84e5-42146696422b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102490000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
