INSERT INTO `qb_zdasjqbxx` VALUES (118, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-14 09:22:06', '“12.13”人员11日到云南省工行聚集情况', '8月11日15时许，15名“12.13”人员（其中女性12名，男性3名）聚集到小南所辖区青年路395号中国工商银行云南省分行讨要投资资金。聚集中，银行方工作人员称，让聚集人员先留下身份信息，等待核实后，再做处理。17时许，聚集人员全部离开，过程平稳。\r\n\r\n\r\n', '2017-08-11 15:00:00', '2017-08-11 16:30:00', 1.50, 15, '特殊群体', '小南所', '省工行', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-14 16:55:34', '2017-08-15 17:35:00', NULL, '12.13', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '省工行', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'a4d4f0e4-e7f3-4614-8b1b-100d62666c41', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
