INSERT INTO `qb_zdasjqbxx` VALUES (147, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-01 14:21:44', '8月1日周二金座集资户例行聚集情况', '据工作掌握，8月1日9时30分许，以纪昌凤、罗益民为首的100余名金座集资户到市体育馆集访。昆明市中级人民法院及市金融办的相关领导接访。接访的主要诉求是要求市中级人民法院公布返还金座集资户部分款项的构成以及后期追赃挽损情况，包括说明为什么只追究了金座公司的刑事责任而不管民事部分的责任。12时10分上述人员陆续离开市体育馆。\r\n\r\n相关情况在进一步掌握中。INSERT INTO `qb_zdasjqbxx` VALUES (149, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-08-15 16:04:39', '30名黑林铺片区国有工矿棚户区改造的农民工到省政府信访局上访', '8月15日14时30分，30名黑林铺片区国有工矿棚户区改造的农民工到省政府信访局反映，他们2015年至今未领到工资，涉及600余万元，请省政府帮助协调解决，15时30分离开省政府信访局。', '2017-08-15 14:30:00', '2017-08-15 15:30:00', 1.00, 30, '讨薪索款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-17 15:20:45', '2017-08-17 15:35:28', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '9fe4120b-649b-438b-89dc-b277887f2b8b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (150, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-08-15 17:10:32', '8月15日部分我市涉金融受损群体成员到省金融办上访情况', '8月15日9时30分许，我市涉金融受损群体“儒商”和“保利贷”及“光辉”等群体的60余名成员到省金融办上访，上访过程中打出三条白底黑字的标语，如：“儒商金融大骗子还我血汗钱”、“拥护共产党，谁审批谁监管谁负责”等，要求省金融办对7月3日上访提出的8条诉求给予书面答复，省金融办相关领导对上访人员进行了接访，11时20分，上述人员陆续自行离开现场。\r\n\r\n相关情况在进一步掌握中。\r\n', '2017-08-15 09:30:00', '2017-08-15 11:20:00', 1.83, 60, '涉众型经济案件', '盘龙分局拓东所', '省金融办', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-17 15:20:45', '2017-08-17 15:39:43', NULL, '小微儒商', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '省金融办', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2608, 2503, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '8d234a6d-4478-4e04-adb2-e7e7c0fad36b', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (151, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-08-15 17:32:04', '昭通市镇雄县母享镇穿洞村民委员会村民11人再次到省信访局反映', '8月15日16时许，昭通市镇雄县母享镇穿洞村民委员会村民11人到省信访局反映该村村支书近几年有：村支书邓润乾本人超生问题；利用职务之便敲诈勒索他人等严重违纪问题，请政府严肃查处。现信访局正接待中。\r\n\r\n\r\n', '2017-08-15 16:00:00', '2017-08-15 17:00:00', 1.00, 11, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-17 15:20:45', '2017-08-17 15:45:52', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', NULL, NULL, 'syr', '11-15人', '1', 'qkfy', 11, 0.00, 0, 0, '0', NULL, '情况反映', 'bfa8a7da-1dc1-470d-abdf-d98fe3bacad4', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
