INSERT INTO `qb_zdasjqbxx` VALUES (154, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-17 08:53:33', '30名工人到橡胶厂经济适用房项目部讨要工钱的情况反映', '8月16日10时余，30余名工人到昆明市五华区黑林铺三家巷内的“云南橡胶厂经济适用房工程项目部”讨要工钱。据工程项目经理向仕年（男）称，“云南橡胶厂经济适用房工程”为政企合作项目，为五华区政府与沐荣欣成房地产公司合作建设，该项目于2014年7月2日开工建设，至2017年3月左右，沐荣欣成房地产公司垫资7000多万在该项目上，但因政府承诺的资金未到位，沐荣欣成房地产公司不再垫资，该项目停工。今日负责建盖该项目的“福建玖鼎建设集团有限公司”工人30余人来到该公司讨要工钱400余万，沐荣欣成房地产公司负责人认可该工程款，但声称是因为政府未按时拨款，他们公司也没有钱支付工人工资。\r\n黑林铺办事处、黑林铺社区居委会、五华区劳动监察大队、五华区住建局相关相关人员到现场协调，要求沐荣欣成房地产公司在本月25日前将400万工程款交付工人。至16时，30余名工人离开。现场未发生堵路，堵门，打砸等影响社会治安的情况。（黑林铺所）\r\n\r\n\r\n\r\n\r\n', '2017-08-16 16:10:00', '2017-08-16 11:00:00', 5.17, 30, '讨薪索款', '华山所', '云南橡胶厂经济适用房工程项目部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-17 15:20:45', '2017-08-17 16:08:52', NULL, '农民工', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '云南橡胶厂经济适用房工程项目部', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2656, 2519, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'ff836a95-f760-4c9d-8f3b-a59917caadab', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (155, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-17 13:58:34', '8月17日金座集资户例行周四聚集情况', '据工作掌握，8月17日上午9时30分许，金座集资户进行了周四例行聚集。其中，以罗益民、纪昌凤为首的金座集资户140余人到市体育馆集访。昆明市五华区政府及市金融办相关领导进行了接访，主要是表达对一次性困难补助申请的诉求。中午11时许，金座集资户自行离开市体育馆；以卢福全为首的金座集资户近10人到南屏步行街中心花园附近开展例行的聚集活动，活动过程中未发生喊口号、打标语等过激行为。11时许，聚集的人员自行离开现场。\r\n\r\n\r\n', '2017-08-17 09:30:00', '2017-08-17 11:00:00', 1.50, 150, '特殊群体', '茭菱所、护国所', '市体育馆、南屏步行街', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-17 15:20:45', '2017-08-17 16:01:26', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆、南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '31ef318a-42f2-4ada-a404-53a860b96651', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
