INSERT INTO `qb_zdasjqbxx` VALUES (161, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-26 13:27:03', '8月26日盘龙区6名对越参战退伍老兵聚集南屏街中心广场“乞讨”', '8月26日10时30分左右，郭滨（男，55岁）、周珩（男，55岁）等6名对参战老兵聚焦南屏街中心广场昆百大前“乞讨”，郭身着65式军装、戴军帽、系武装带（另5人着便服），手持“参战老兵红色乞讨”长50厘米布标，在地面铺开题为“要生存  要医保  要社保”的简介，称盘龙区未给郭等12名对越参战老兵购买医保、社保，望盘龙区政府、东华办事处按照《军人优恤条例》解决其医、保等问题。五华分局发现后，及时将6人带至护国派出所劝导教育。\r\n\r\n相关情况密切掌握中。\r\n\r\n\r\n\r\n人员名单：\r\n\r\n郭滨，男，530111196110180838，联系电话：13888102064，现住址：盘龙区龙华路昆风小区5-3-201；\r\n\r\n周珩，男，53011119620201081X，联系电话：13708481438，现住址：盘龙区穿金路721号；\r\n\r\n邹云川，男，53011119620612083X，联系电话：13529227965，现住址：盘龙区小坝昆明煤机总厂5幢512号；\r\n\r\n曹惠宾，男，530111196310210835，联系电话：13577160285，现住址：官渡区北京路333号4单元5室；\r\n\r\n王平，男，530124196008162010，联系电话：13629403257，现住址：富民罗免镇者北村委会大者北村155号；\r\n\r\n张正华，男，530111196108070816，联系电话：13708846343，现住址：盘龙区穿金路345号7栋2单元302号。\r\n\r\n\r\n', '2017-08-26 10:30:00', '2017-08-26 11:30:00', 1.00, 6, '对政策不满', '护国所', '南屏街中心广场', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-28 10:18:46', '2017-08-28 15:22:51', NULL, '对越参战老兵', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '南屏街中心广场', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2724, 2524, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'f85e2230-f19e-48da-9df5-15bdb4f25c6e', 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
