INSERT INTO `qb_zdasjqbxx` VALUES (165, NULL, NULL, NULL, NULL, 'sf', NULL, 'qt', '2017-08-24 12:55:29', '23日部分我市复员军官到省民政厅上访', '据工作掌握，受河南等外省城市复员军官群体到当地政府集访的影响，在相互转发相关视频的同时，我市部分复员军官经过连日的串联商议，于8月23日9时许，我市复员军官群体选派了孔德民、郭万军等五名（五华、西山、官渡、盘龙各1至2名）代表，到我市茨坝镇的省民政厅安置处上访，要求解决生活困难、住房、医疗费偏低等问题。23日12时许，经安置处工作人员做工作，上述上访人员离开。\r\n', '2017-08-23 09:00:00', '2017-08-23 12:00:00', 3.00, 5, '对政策不满', '盘龙区茨坝所', '省民政厅', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-28 10:48:41', '2017-08-28 15:26:23', NULL, '对越参战老兵', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '省民政厅', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2616, 2524, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'f615ef82-519d-4f54-97ef-716803033f07', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (166, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-24 14:17:01', '8月24日100余名金座集资户到市体育馆上访', '8月24日9时30分许，以罗益民、纪昌凤为首的金座集资户100余人到市体育馆集访，昆明市西山区政府及市金融办的相关领导接访，主要是围绕金座维权群体中的生活困难户一次性困难补助申请的探讨，经过对金座集资户做工作。11时许，金座集资户自行离开市体育馆。    8月24日9时30分许，以卢福全为首的金座集资户30余人到南屏步行街中心花园附近开展例行的聚集活动，活动过程中未发生喊口号、打标语等过激行为。11时20分许，聚集的人员自行离开现场。\r\n\r\n\r\n', '2017-08-24 09:30:00', '2017-08-24 11:30:00', 2.00, 130, '特殊群体', '茭菱所、护国所', '市体育馆、南屏步行街', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-28 10:48:41', '2017-08-28 15:06:55', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆、南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '7816eb8c-2947-457b-9eeb-17146b3db8f4', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (168, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-08-23 09:45:00', '四川省麻省科技有限公司（云南分公司）融资人10人到省信访局上访', '8月23日9时10分许，四川省麻省科技有限公司（云南分公司）融资人10人到省信访局上访反映：2005年该公司向投资人非法集资5000余万元，涉及1140人左右，后因资金链断裂，导致本金利息都未能收回，现信访局正接待中。\r\n', '2017-08-23 10:00:00', '2017-08-23 11:30:00', 1.50, 10, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-28 10:51:02', '2017-08-28 14:50:56', NULL, '个体户', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'dzjg', '党政机关', NULL, NULL, NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'jzwt', '集资问题', 'sryx', '10人以下', '1', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', '3349bbf9-2aca-4903-bb7e-40931ac71efb', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
