INSERT INTO `qb_zdasjqbxx` VALUES (175, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-21 23:53:10', ' 普惠园小区30余名商铺业主不满地产商违约聚集', '8月21日16时许，30余名普惠园小区商铺业主聚集小区，要求收回商铺。据了解，2016年12月，普惠园小区商铺业主浙江丽水市男子邱华（45岁）等人与昆明居美房地产有限公司签订商铺招租合同，由该公司负责普惠园14幢一楼部分商铺的招租工作，并约定招租商户不得从事生鲜超市业务经营。8月21日，邱等人获悉该公司违反合同约定经营生鲜超市，欲收回商铺引发纠纷。经分局工作，聚集业主于18时许离开，但声称要公司领导出面解决此纠纷。\r\n\r\n相关情况密切掌握中。\r\n', '2017-08-21 16:00:00', '2017-08-21 18:00:00', 2.00, 30, '合同纠纷 ', '海源所', '筇王路普惠园小区', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-28 10:53:53', '2017-08-28 14:17:14', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局,报区政府', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '筇王路普惠园小区', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2792, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '685781da-8302-4a93-8cb8-e1d3569d4a63', 'JS', NULL, 'Y', 110, NULL, NULL, '530102610000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
