INSERT INTO `qb_zdasjqbxx` VALUES (177, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-22 16:16:38', '8月22日金座集资户周二例行聚集活动情况', '据工作掌握，8月22日上午9时30分许，金座集资户进行了例行活动，其中以罗益民、纪昌凤为首的金座集资户140余人到市体育馆集访，昆明市盘龙区政府及市金融办相关领导进行了接访。集资户主要是围绕金座维权群体中的生活困难户一次性困难补助申请的探讨，直到下午15时许，聚集人员才自行离开市体育馆。（国保大队）\r\n\r\n\r\n', '2017-08-22 09:30:00', '2017-08-22 15:00:00', 5.50, 140, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-28 10:53:53', '2017-08-28 14:32:47', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '56bb0d96-8499-4c0e-b583-5b68067fb646', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
