INSERT INTO `qb_zdasjqbxx` VALUES (180, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-19 00:13:14', '20余名“12.13”人员聚集工商云南省分行门口', '8月18日15时许，20余名“12.13”人员到青年路395号工商云南省分行门口聚集，并拉着横幅，喊着口号。16时40分，五华分局出动10余名警力赶到现场进行处置。涉事人员中有人举着10余米长，写有“银行违规违法、伙同诈骗、全国22万人430亿元、YUN NAN GOVESTABLISHED,RAN AND SUPERVISED FANYA,云南政府成立运作监管泛亚”字样的白色横幅。现场处置民警告知聚集人员要理性协商，不得有过激行为，经劝导，聚集人员已将横幅收起，并离开现场，未造成道路堵塞，无人员伤亡，现场无新闻媒体报道。\r\n\r\n\r\n\r\n\r\n\r\n五华分局\r\n\r\n2017年8月18日\r\n\r\n\r\n', '2017-08-18 15:20:00', '2017-08-18 17:30:00', 2.17, 20, '涉众型经济案件', '小南所', '省工行', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-28 10:55:45', '2017-08-28 11:26:42', NULL, '12.13', NULL, '0', '1', '横幅，喊着口号', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '省工行', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'ff62793f-e5e4-4713-a1fb-1d19e66d36d7', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
