INSERT INTO `qb_zdasjqbxx` VALUES (186, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-29 14:18:09', '8月29日金座集资户例行活动情况', '8月29日金座集资户例行周二聚集，9时30分许，以纪昌凤、罗益民为首的金座集资户到市体育馆集访。昆明市官渡区政府及市金融办的相关领导接访。主要是官渡区政府针对金座维权群体提出的一次性生活困难补助进行答复。11时许，上述人员陆续自行离开市体育馆。今天到市体育馆参加集访的人员有100人左右。（国保大队）\r\n\r\n\r\n', '2017-08-29 09:30:00', '2017-08-29 11:30:00', 2.00, 100, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-31 10:05:12', '2017-08-31 10:55:31', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '78ad2d22-e823-438d-9c20-aa02249c9366', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (187, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-08-28 17:03:20', '昆明市盘龙区外来务工人员入学子女家长9人到省信访局上访', '8月28日16时20分许，昆明市盘龙区外来务工人员入学子女家长9人从盘龙一小折返，再次到省信访局上访反映自己子女无法按时上学，省信访局按规定不予接待，现9名学生家长带6名小孩滞留信访接待大厅。', '2017-08-28 16:20:00', '2017-08-28 16:50:00', 0.50, 9, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-31 10:05:12', '2017-08-31 15:17:47', NULL, '外来务工人员', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2522, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'sryx', '10人以下', '1', 'qkfy', 9, 0.00, 0, 0, '0', NULL, '情况反映', '777a6e5f-6ca3-4ad8-80bb-ba029ce3aa38', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (189, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-08-29 09:17:35', '8月26日外来务工人员学生家长因子女就学问题聚集省教育厅讨要说法', '8月26日中午12时20分许，有近40名外来务工人员聚集在小菜园立交桥下云南省教育厅门前，前来了解和反映外来务工人员孩子无法入小学的问题。  据现场民警了解，来反映问题人员涉及昆明四个城区均为外来务工人员。现场聚集人员均为适龄准备入小学就读务工人员家长，因就读名额及入学方式（民办、非民办选择）曾到各区县级教育局了解和反馈，但因各区县级教育局处理问题方式粗暴简单，引起不满未能解决所提出的诉求，所以自发相约到省教育厅反映问题。来访群众书写了小纸条两份，一份上述：盘龙区茨坝阳光小学招收164人，但非民办小学，报读该小学的家长来了20人，希望孩子和在昆户口孩子一样可以就读民办小学，并称其余的家长随即就到。此前他们已经到盘龙区教育局反映情况，教育局解释现在对你们已经够好了，若不读你们要去哪闹去哪闹。另一份为官渡小学招收学生名额清单：天泽学校30人、红番学校15人、荣昆学校2人、雄兴学校2人、福兴学校3人。\r\n', '2017-08-26 12:20:00', '2017-08-27 01:20:00', 13.00, 40, '对政策不满', '莲华所', '云南省教育厅', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-31 10:05:12', '2017-08-31 15:40:17', NULL, '外来务工人员', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '云南省教育厅', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2634, 2522, NULL, NULL, NULL, NULL, NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'sldws', '16-50人', '2', 'qkfy', 40, 0.00, 0, 0, '0', NULL, '情况反映', '2a8ba940-8acf-4b44-83fe-555d2f89a61e', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (190, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-08-29 10:12:24', ' 8月29日原曲靖寻甸支前民兵13人到省信访局上访', '8月29日8时30分许，原曲靖寻甸支前民兵13人到省信访局上访反映：1988年3月，103名寻甸县民兵在县委、县政府、县武装部的组织领导下成立支前民兵，后于3月25日抵达麻栗坡老山前线并配属作战部队（35142部队、56031部队），6月29日完成支前任务后，寻甸支前民兵连宣布解散。现因生活困难，请求政府解决困难。省信访局正接待中。（华山所）\r\n', '2017-08-29 08:30:00', '2017-08-29 11:30:00', 3.00, 13, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-31 10:05:12', '2017-08-31 10:38:51', NULL, '支前民兵', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2525, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm,tsqt', '农民,特殊群体', NULL, NULL, 'syr', '11-15人', '1', 'qkfy', 13, 0.00, 0, 0, '0', NULL, '情况反映', 'e61ea764-9e15-4493-8ae9-60ea132e7cd7', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (192, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-28 17:01:23', '寻甸县13名支前民兵在三市街“乞讨”', '8月28日14时30分许，寻甸县13名支前民兵在三市街益珑大厦前拉起2块长2米、宽1.5米印有“落实政策不要让自卫还击参战人员流泪乞讨”的宣传贴（内容主要为对越自卫还击战照片及寻甸县成立支前民兵边临时党支部、任命连干部文件图片），并摆放音响及一红色塑料桶“乞讨”，其中12名支前民兵着65式军装、戴军帽及军功章、系武装带，一人持印有“自卫还击战参战支前民兵连”的红旗。吴开柱（男，55岁）等支前民兵的申诉材料称，寻甸县支前民兵连成立于1988年3年，103名民兵来自全县各乡镇，配属老山前线作战部队，负责抢修工事、运输弹药，期间荣立集体三等功一次，多人次立功嘉奖，1988年6月该连解散。上述人员对寻甸县民政部门以该连未出国参战，不能享受参战补助不满，要求发放参战补助。\r\n\r\n经分局工作，上述人员于15时45分自行离开。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-08-28 14:30:00', '2017-08-28 17:30:00', 3.00, 13, '对政策不满', '护国所', '南屏街中心广场', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-31 10:05:12', '2017-08-31 10:20:04', NULL, '支前民兵', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '南屏街中心广场', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2724, 2525, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'syr', '11-15人', '2', 'qkfy', 13, 0.00, 0, 0, '0', NULL, '情况反映', '3705a3c7-f103-4e98-9b00-361fd277ba4d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
