INSERT INTO `qb_zdasjqbxx` VALUES (198, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-30 16:15:05', '云南园林绿化股份有限公司20余名民工聚集教兴路实力壹方城讨要工程款', '8月30日8时30分许，云南园林绿化股份有限公司20余名民工持一条“付我们血汗钱，我们要吃饭，孩子要上学，还我们一个公道”长约10米的白底黑字布标，围堵在教兴路实力壹方城5幢前，向云南金实力房地产开发有限公司讨要568元绿化工程款。经工作，民工收回布标进入物管办公室协商。相关情况密切关注中。\r\n\r\n\r\n', '2017-08-30 08:30:00', '2017-08-30 14:00:00', 5.50, 20, '讨薪索款', '马村所', '教兴路实力壹方城', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-31 10:09:36', '2017-08-31 15:47:20', NULL, '农民工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '教兴路实力壹方城', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2519, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'ffc95e88-9c07-43cd-89f4-9268d69af042', 'JS', NULL, 'Y', 110, NULL, NULL, '530102540000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (199, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-08-31 09:41:58', '8月30日30余名农民工到西城时代小区云南建筑装饰有限公司聚集讨要工程款', '8月30日17时43分，海源所接110指令：有30余人围坐在西城时代10栋907室云南建筑装饰有限公司吉惠园项目部办公室讨要工钱。接警后，民警赶到事发地点，经了解：2015年4月26日，当事人谢付国与云南建筑装饰有限公司吉惠园项目部签订了承包合同，承保吉惠园项目内部贴砖工程。此项工程由谢付国、及重庆男子张永忠（46岁）共同承包，并且雇佣了43名工人。工程于2015年底完工，按合同规定工程完工后到验收前，云南建筑装饰有限公司需支付80%的工程款。谢付国称：全部工程款为365万元，目前项目方只支付了303万，但云南建筑装饰有限公司方称：经过核实，全部工程款总共为353万元，已支付了311万元，剩余的款项要等验收合格才能支付，双方为此引发分歧，导致谢付国等人到公司聚集。\r\n聚集现场未发生过激行为，民警积极联系了区劳动监察到场，组织双方协商，但双方分歧较大，一直未能协商一致，直至31日凌晨2时34分，聚集人员才自行离去。相关情况密切关注中。（海源所）\r\n', '2017-08-30 17:00:00', '2017-08-31 02:35:00', 9.58, 30, '讨薪索款', '海源所', '西城时代10栋', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-31 10:09:36', '2017-08-31 16:03:51', NULL, '农民工', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '西城时代10栋', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2793, 2519, NULL, NULL, 'clbs', '材料报送', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'e9a8b5bc-f3d8-4b7b-9252-466fe97fdd4f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102610000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (200, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-08-31 09:54:26', '昆明长久工贸有限公司（原水泥厂）职工12人到省信访局上访', '8月31日9时30分许，春雨路1356号昆明长久工贸有限公司职工12人到省信访局上访反映：2013年昆明市政府修地铁3号线，致使该公司断水、断电、断路至今，现公司员工受影响无法正常工作生活。请政府帮助解决，现信访局正接待中。\r\n', '2017-08-31 09:30:00', '2017-08-31 10:25:00', 0.92, 12, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-08-31 10:09:36', '2017-08-31 14:48:12', NULL, '厂矿职工', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2539, 'dzjg', '党政机关', 'qtxsj', '群体性事件', NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'syr', '11-15人', '1', 'qkfy', 12, 0.00, 0, 0, '0', NULL, '情况反映', '2ddebfca-29ac-4f20-bc32-5487964aac4c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
