INSERT INTO `qb_zdasjqbxx` VALUES (204, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-01 10:56:55', '9月1日光辉劳务公司16名劳务人员再次到“碧桂园都荟”销售中心讨要劳务费', '9月1日9时许，光辉劳务公司等刘成兵、晏徐清带16人左右再次来到“碧桂园都荟”售楼部门前，并扬言如果今天不给解决就跳楼。民警到现场后，双方人员在售楼部门前商谈，目前情况稳定，暂无过过激行为，民警已在现场维护秩序。目前刘成兵称项目部拖欠钢筋组工资款项31万余元。\r\n\r\n\r\n', '2017-09-01 09:00:00', '2017-09-01 19:15:00', 10.25, 16, '讨薪索款', '红云所', '“碧桂园都荟”售楼部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-04 13:54:28', '2017-09-04 14:06:59', NULL, '农民工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '“碧桂园都荟”售楼部', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2690, 2519, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '0a2b5d8c-f4c9-4be6-b5bb-a74e5a669450', 'JS', NULL, 'Y', 110, NULL, NULL, '530102490000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (205, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-04 10:18:43', '中铁十五局集团昆明至楚雄高速公路施工方14人到省信访局上访', '9月4日8时50分许，中铁十五局集团昆明至楚雄高速公路施工方14人到省信访局上访反映，2016年与中铁十五局签订合同修建昆楚高速，并向中铁十五局缴约保证金一千三百万元，但至今工程未开工，保证金也不退。接访中。\r\n', '2017-09-04 08:50:00', '2017-09-04 09:35:00', 0.75, 14, '合同纠纷 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-04 13:55:03', '2017-09-04 14:24:36', NULL, '农民工', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'dzjg', '党政机关', 'fyqk,qtxsj', '反映情况,群体性事件', NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'syr', '11-15人', '1', 'qkfy', 14, 0.00, 0, 0, '0', NULL, '情况反映', '1e87b585-f0df-489a-a279-b6d41d222685', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (206, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-05 10:12:13', '9月5日绿春县大兴镇村民10人因不满土地被征到省政府上访', '   5日08:45分许，红河州绿春县大兴镇牛洪村委会村民陈阿康一家10人到省政府上访，反映今年4月份，当地政府征用村民的土地，其中包括他们家的土地9亩用于建盖学校，但政府一直没有给其赔偿，当地政府给出的理由是陈阿康擅自改变了这9亩地的使用性质，不在赔偿范围内。陈阿康对此不满，多次向有关部门反映要求拿到征地补偿未果所以到省政府上访，要求政府对其进行赔偿。该群体4人已于昨日（9月4日）已就相同诉求来访过，因不满接待结果今日10人再次来访。省信访局再次对其进行接待。09:25分许，10人全部离开省信访局，上访过程平稳。（华山所）\r\n', '2017-09-05 08:45:00', '2017-09-05 09:25:00', 0.67, 10, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-06 10:58:45', '2017-09-06 11:15:44', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '1', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', '712b0856-ed7b-4897-80bd-16f421886052', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (207, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-05 13:19:43', '9月5日金座集资户维权群体到市体育馆聚集', '9月5日上午9时30分许，以纪昌凤、罗益民等约120名金座集资户到市体育馆集访。昆明市中级人民法院及市金融办的相关领导接访。主要是金座维权代表诉求，中级人民法院在审理金座案件时只追究了金座公司的刑事责任而没有追究民事责任，建议中级人民法院启动司法救助，切实帮助解决金座老年人的生活困难问题。10时40分许，上述人员陆续自行离开市体育馆。\r\n\r\n相关情况进一步掌握中。INSERT INTO `qb_zdasjqbxx` VALUES (208, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-05 14:56:41', '9月5日 30余名“儒商”群体成员到省金融办上访的情况', '9月5日9时许，陆续有30余名“儒商”群体成员到省金融办上访，12时许，经省金融办工作人员做工作离开。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-05 09:00:00', '2017-09-05 12:00:00', 3.00, 30, '涉众型经济案件', '盘龙分局拓东所', '省金融办', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-06 10:58:45', '2017-09-06 11:37:00', NULL, '小微儒商', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '省金融办', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2608, 2503, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '5ce59a98-6564-4106-a7bb-ec3913b91c7d', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (209, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-05 16:24:14', '30余名美团外卖送餐员因不满公司下调送餐费在人民中路傲城大厦门口拉横幅聚集', '9月 5日13时30分许，小南所接110指令，报称“人民中路象眼街口傲城大厦门口，有30余名美团外卖人员拉横幅”。接警后，小南所第一时间安排警力前往现场，维持秩序。民警到达现场后，发现多名美团人员手举两条约3米长，写有“美团外卖、霸王条约、压榨送餐员”、“美团外卖，不顾送餐员死活”的横幅，聚集在傲城大厦门口。聚集人员称“他们是五华区东风西路123号三合商利大厦17楼美团公司的外卖员工，之前公司每单外卖给他们6元的报酬，现在每单只给4元，价格降低幅度太大，引发不满，故聚集讨要说法”。民警告知聚集人员要理性协商，不得有过激行为，并劝告其先将横幅收起。情况上报时，聚集人员已收起横幅自行离开，处置过程中现场有新闻媒体（街头巷尾）进行采访。\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2017-09-05 13:30:00', '2017-09-05 15:10:00', 1.67, 30, '劳动争议 ', '小南所', '傲城大厦门口', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-06 10:58:45', '2017-09-06 11:42:15', NULL, '个体户', NULL, '0', '1', '举两条约3米长横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '傲城大厦门口', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2547, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '6b5035ae-4619-464d-8764-9e42bd89c56a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (210, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-05 17:34:39', '9月5日40余名美团外卖送餐员因不满公司下调送餐费到东风西路三合大厦美团公司昆明总部聚集讨要说法', '9月5日15时37分许，新村所接到报警称：东风西路三合大厦17楼有100多人聚集。新村所接警后，现场聚集人数仅有40人余人，经民警了解，聚集的人员均是美团公司昆明总部招募的兼职骑手，因近日公司调低了每单提成，导致送餐人员收入降低，从而引发聚集公司，要求提高每个订单的提成（现在工资是配送的订单中获取提成，无底工资）。现场，民警开展了秩序维护工作，并协调公司人人员答复聚集人员，目前公司人员已将情况上报，但答复称：每个订单的提成是由总公司定价，全国都是一个标准，昆明分公司无法自行调价。16时15分许，聚集的 40余名人员已经离开现场，后续情况核实中。（新村所）\r\n', '2017-09-05 15:35:00', '2017-09-05 16:15:00', 0.67, 40, '劳动争议 ', '新村所', '东风西路三合大厦17楼美团昆明总部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-06 10:58:45', '2017-09-06 11:45:53', NULL, '个体户', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '东风西路三合大厦17楼美团昆明总部', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2677, 2547, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'sldws', '16-50人', '1', 'qkfy', 40, 0.00, 0, 0, '0', NULL, '情况反映', '42bdfd63-2f8d-4bc4-8e50-dcd63ba50257', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (211, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-06 15:17:18', '9月6日曲靖市麒麟区各煤矿矿主代表42人因不满煤矿被关停到省政府上访', '6日14:30分许，42名曲靖市麒麟区独木水库二级保护区保留煤矿矿主代表到省政府上访，反映，2017年1月，矿主到相关部门办理转型升级、矿权变更、证照登记等手续时，相关单位以独木水库已被省环保厅列为水源保护区、煤矿产业跟进转型为由拒绝办理，涉及15家煤矿，矿主认为他们均是合法合规的煤矿，而且他们认为独木水库根本就不适合列为水源保护区，原因是水库内铬含量超标，铬是极难消除的。相关部门不予办理是在找借口，没有合法依据，引发矿主不满，因向当地政府部门反映未果所以到省政府上访，要求当地政府拿出文件给他们一个合理的解释，如果没有就给他们办理手续。该起群众因相同诉求已多次到省政府上访，因一直没有解决，所以今日再次上访。现已有省信访局工作人员接待他们，秩序平稳。', '2017-09-06 14:30:00', '2017-09-06 17:00:00', 2.50, 42, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:09:01', '2017-09-18 10:52:04', NULL, '个体户', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'dzzbm', '对政策不满', 'sldws', '16-50人', '2', 'qkfy', 42, 0.00, 0, 0, '0', NULL, '情况反映', '511467f5-4227-4cf5-97ba-54b497981c1a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (215, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-08 16:55:30', '9月8人部分在昆 “12.13” 人员到工商银行云南省分行聚集情况', '9月8日15时许，20余名在昆“12.13”人员陆续来到青年路工商银行云南省分行聚集，聚集过程中打出两条标语，内容为：“还我救命钱、政府还我养老钱、泛亚还我看病钱”；“伙同泛亚诈骗、银行违规违法”。16时40许，上述人员喊了几句口号后就地解散 ，各自离开。相关情况进一步掌握中。\r\n', '2017-09-08 15:00:00', '2017-09-08 17:00:00', 2.00, 20, '涉众型经济案件', '小南', '青年路工商银行云南省分行', NULL, NULL, '5', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:10:19', '2017-09-18 11:26:21', NULL, '12.13', NULL, '0', '2', '两条标语“还我救命钱、政府还我养老钱、泛亚还我看病钱”；“伙同泛亚诈骗、银行违规违法”', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '青年路工商银行云南省分行', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2607, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '04ece0b6-7096-4d9b-9b12-e6c54e95efdc', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (216, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-08 10:12:03', '8日禄丰县广电局职工16人因身份问题到省政府上访', '     8日9时45分许，16名原禄丰县广电局职工到省政府上访，反映，他们原是禄丰县广电局的职工，身份是事业编制，2006年企业改制，单位名称变更为云南广电网络集团公司禄丰支公司，原单位23名职工的身份变为企业编制，引发职工不满，他们认为单位在没有政策支持、没有征询职工同意的情况下就将职工的身份随意转变的行为是违规的，要求公司恢复其事业编制身份。由于多年来一直没有得到解决所以到省政府上访，要求上级政府支持他们的诉求。现来访群众已有信访局工作人员接待，秩序平稳。\r\n', '2017-09-08 09:45:00', '2017-09-08 11:30:00', 1.75, 16, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:10:19', '2017-09-18 11:18:19', NULL, '退休职工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'jgqsyry', '机关企事业人员', 'gzfldy', '工资福利待遇', 'sldws', '16-50人', '2', 'qkfy', 16, 0.00, 0, 0, '0', NULL, '情况反映', '1d319ae6-bf0f-431c-91d8-14d3d39ecbd0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (217, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-07 14:32:11', '7日曲靖市麒麟区各煤矿矿主代表25人因不满煤矿被关停到省政府上访', '7日09:05分许，9名曲靖市麒麟区独木水库二级保护区保留煤矿矿主代表因不满煤矿被关停一事到省政府上访，该起群众42人于昨日（9月6日）已就相同诉求到省政府上访过，因来访群众要求与省水利厅负责人见面，所以今日再次来到省信访局，截止10:20分许，来访人数有27人，10:40分许，3名来访群众代表与麒麟区煤炭局工作人员到水利厅座谈，其余人员在信访大厅等待，秩序平稳。12:15分许座谈结束，25人陆续离开省政府。\r\n', '2017-09-07 09:05:00', '2017-09-07 12:15:00', 3.17, 25, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:10:19', '2017-09-18 11:13:08', NULL, '个体户', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'dzzbm', '对政策不满', 'sldws', '16-50人', '2', 'qkfy', 25, 0.00, 0, 0, '0', NULL, '情况反映', '31aaf9c1-afe3-4334-90d3-9903938117f2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (218, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-07 12:53:38', '9月7日金座集资户维权群体到市体育馆聚集', '9月7日9时30分许，以纪昌凤、罗益民等120名金座集资户到市体育馆集访。昆明市检察院及市金融办的相关领导接访。由于市检察院的主要领导今天因其他工作未到场，市检察院的派来工作人员解释后，经双方协商接访下次进行。11时40分许，上述人员陆续自行离开市体育馆。相关情况进一步掌握中。\r\n', '2017-09-07 09:30:00', '2017-09-07 11:40:00', 2.17, 120, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:10:19', '2017-09-18 11:02:59', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'ae87f2ed-fa80-4e4f-85ed-bb3ecdf6f00d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (226, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-11 15:13:17', '11名侯刚家属聚集昆医附二院讨要说法', '8月26日5时30分许，暂住于昆明湖小区的寻甸县男子侯刚（33岁，汉族）因腹疼、腹泻到昆医附二院急诊科输液治疗，期间因身体不适换针水，8月27日10时许输液中途自行离开医院返家。27日15时许，侯心跳加快、呼吸困难，家人再次将其送至昆医附二院急诊科救治，19时10分许院方发现侯体征异常，经抢救无效死亡。9月11日上午，11名家属再次聚集医院领导办公室讨要说法，要求医院给予死者老人及孩子抚养费（未说明具有体数额），五华分局法制、治安、特警大队及虹山所30余名警力到场疏解劝导。14时许经分局工作，3名家属代表与院方进行协商，滞留现场不听劝解4名家属已带到虹山所接受调查。\r\n相关情况进一步掌握中。\r\n', '2017-09-11 09:00:00', '2017-09-11 15:35:00', 6.58, 11, '医患纠纷 ', '虹山所', '昆医附二院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:12:25', '2017-09-18 13:44:26', NULL, '患者家属', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局,报区政府', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '昆医附二院', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2625, 2550, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd,qy', '组织活动,请愿', NULL, NULL, 'yhjf', '医患纠纷', 'syr', '11-15人', '2', 'qkfy', 11, 0.00, 1, 0, '0', NULL, '情况反映', '99e3879d-d58c-47db-9894-a226c2b7a9f7', 'JS', NULL, 'Y', 110, NULL, NULL, '530102410000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (227, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-11 15:02:12', '11日呈贡大渔村村民13人因不满征地补偿到省政府上访', '   11日14时30分许，13名呈贡新区大渔村村民到省政府上访，反映：今年7月份呈贡新区政府张贴公告，征收大渔村1800余亩土地，涉及900余户村民，政府给予村民的补偿是每亩8万元，村民称，他们查阅相关国家规定，耕地的征地偿最低都是每亩10余万元，呈贡政府的补偿标准太低，村民不同意，要求按照国家相关标准赔付。由于向呈贡新区政府反映要求提高补偿标准未果，所以到省政府上访，要求按照国家相关标准给予村民补偿。现来访群众正在信访大厅等待接待，秩序平稳。\r\n', '2017-09-11 14:30:00', '2017-09-11 15:35:00', 1.08, 13, '拆迁补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:12:25', '2017-09-18 13:39:45', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'syr', '11-15人', '2', 'qkfy', 13, 0.00, 0, 0, '0', NULL, '情况反映', '747ee6cb-589d-484c-af7b-514693aad282', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (228, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-11 14:36:48', '9月11日省外到昆人员与部分在昆“12.13”人员到昆明市检察院聚集上访情况', '9月11日9时许，50名“12.13”人员陆续来到昆明市检察院聚集上访，其中省外到昆的“12.13”人员有25名，昆明市检察院相关领导对聚集上访的“12.13”人员代表进行了接访，接访过程中，“12.13”人员向昆明市检察院递交了致《昆明市人民检察院、王亚峰检察长、泛亚案各位检察官》的诉求书，并对“12.13”人员提出的问题进行了解答。11时50分许，接访结束，参加聚集上访的“12.13”人员在检察院门口抬着用A4纸打印，写有省外到昆人员所属省份和城市的标牌“快闪”后自行离开现场。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-11 09:00:00', '2017-09-11 11:50:00', 2.83, 50, '涉众型经济案件', '盘龙分局金星所', '市检察院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:12:25', '2017-09-18 12:01:16', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '市检察院', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2617, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '2e153e93-82b4-4bf0-9980-fbd1ad5094d0', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (229, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-09-11 12:50:11', '11日昆明牙膏厂职工29人到省政府反映商标被侵权问题', '     11日11时5分许，昆明牙膏责任有限公司（厂址在五华区节孝巷28号）29名职工到省政府上访，反映他们工厂一直生产“云南三七”牌、“三七”牌牙膏，今年3月份，该厂原厂长私自将这两个商标卖给一个叫“诺特金参口腔护理用品有限公司”生产，来访人员称，这个公司在昆明没有固定办公地点，其生产的牙膏是委托外省工厂生产的，外包装上印有“昆明牙膏责任有限公司”字样，冲击市场份额，导致昆明牙膏厂生产的牙膏滞销，严重影响了200余名职工的收入。由于多次向相关部门反映，要求查处商标侵权未果，所以到省政府上访，要求政府出面维护职工利益。省信访局接待后告知其回昆明市反映。11时35分许，29名来访群众全部离开省政府，上访过程平稳。 \r\n', '2017-09-11 11:05:00', '2017-09-11 11:35:00', 0.50, 29, '企业改制/破产 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:12:25', '2017-09-18 11:50:54', NULL, '企业 改制职工', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'jgqsyry', '机关企事业人员', 'mjjf', '民间纠纷', 'sldws', '16-50人', '1', 'qkfy', 29, 0.00, 0, 0, '0', NULL, '情况反映', '6950aa39-d3de-46f7-91d4-1fdeaea5100d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (232, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-12 15:56:27', '12日30余名学生家长聚集学府路冶金专科学校讨要说法', '9月12日 14时20分许，30余名学生家长聚集学府路冶金专科学校讨要说法。据了解：冶金专科学校招2017年9月成教学院招收五年制大专班，四个专业共招收100名初中毕业生，招生时与学生家长签订了服从专业调配协议，现学校教学安排，对部分学生进行专业调配，现有30余名学生及家长不愿意调配。学校称如学生及家长不愿调配可退学，学生家长则要求按学生填报的志愿就读，现双方仍在协商中。相关情况进一步掌握中。\r\n\r\n\r\n\r\n\r\n', '2017-09-12 14:20:00', '2017-09-12 17:55:00', 3.58, 30, '对政策不满', '莲华所', '冶金专科学校', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:14:26', '2017-09-18 14:06:05', NULL, '学生家长', NULL, '1', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '冶金专科学校', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2633, 2544, 'xx', '学校', NULL, NULL, NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'sldws', '16-50人', '2', 'qkfy', 30, 0.00, 0, 0, '1', NULL, '情况反映', '572a42e3-4792-46f4-a24d-ea216a605ab0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (233, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-12 14:33:44', '9月12日  50余名“12.13”群体成员到市中院上访的情况', '9月12日9时许，陆续有50余名省内外“12.13”人员和少数我市其他涉金融领域受损群体成员到市中院上访，9时50分许，市中院工作人员接访了张磊等7名省内外“12.13”群体代表。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-12 09:00:00', '2017-09-12 09:50:00', 0.83, 50, '涉众型经济案件', '西山区分局', '市中级人民法院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:14:26', '2017-09-18 13:59:52', NULL, '12.13', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局,报区政府', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '市中级人民法院', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2601, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '477e9124-3743-43c0-9e1a-1144d59c3c47', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (234, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-12 14:36:06', '12日玉溪市客运司机15人因不满线路营运安排到省政府上访', '   12日11时15分许，15名玉溪市客运司机及家属到省政府上访，反映，他们一共有4辆营运客车，挂靠在华泰公司，主要跑玉溪市至易门绿汁镇，以及玉溪市至通海县这两条线路，来访人员称，4个月前，华泰公司告知他们只能买通票，途中经过安宁及昆阳这两个点均不允许上下人，也不允许卖这两个站点的票，导致收入减少，引发这4辆客车司机及家属不满，他们认为，华泰公司里其他跑这两条线路的36辆车都可以中途停靠、卖票，为什么他们不行？为此，他们多次向公司及玉溪市交管局等政府部门反映，要求一视同仁，由于一直未得到解决所以到省政府上访，要求华泰公司一视同仁，允许他们中途卖票。省信访局接待后告知其回玉溪市反映诉求，由于不满接待结果，11时55分许15人离开信访大厅聚集在省政府大门右侧，声称不解决就不走。\r\n\r\n    相关情况进一步掌握中。\r\n', '2017-09-12 11:15:00', '2017-09-12 11:55:00', 0.67, 15, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:14:26', '2017-09-18 14:01:22', NULL, '个体户', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'yysj', '营运司机', 'mjjf', '民间纠纷', 'syr', '11-15人', '2', 'qkfy', 15, 0.00, 0, 0, '0', NULL, '情况反映', '6927486a-ff13-450a-a19b-0351c6801554', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (235, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-12 14:24:51', '9月12日金座集资户到市体育馆聚集上访情况', '据工作掌握，9月12日9时30分许，以罗益民、纪昌凤为首的金座集资户120余人到市体育馆集访。昆明市检察院及市金融办相关领导进行了接访。主要是金座集资户向市检察院陈述了政府在金座案件中未及时采取制止取缔、查封、失控漏管等不作为的问题。政府各职能部门在金座问题中应各自承担相应的责任，不能全部由集资户单方承担损失。要求市检察院继续对金座案件中的其它隐匿资产进行追赃挽损，公布查封的金座资产情况并对云南法制报记者梁咏祥（音）给金座公司的大力宣传、诱骗集资户误入金座公司承担相应的法律责任。中午11时50分许，金座集资户自行离开市体育馆。 ？相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-12 09:30:00', '2017-09-12 11:50:00', 2.33, 120, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:14:26', '2017-09-18 13:54:36', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '2a5ec7b3-0809-46f7-8e72-aee53d996a66', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (236, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-11 16:34:26', '9月11日省外到昆人员与部分在昆“12.13”人员到昆明市经侦支队聚集上访情况', '9月11日14时许，30余名“12.13”人员陆续来到昆明市经侦支队聚集上访，其中，省外“12.13”人员有25名，曲靖“12.13”人员有4名，昆明市经侦支队相关领导对聚集上访的“12.13”人员代表进行了接访，接访过程中，省外“12.13”人员向昆明市经侦支队递交了致《昆明市公安局、刀勇局长、承办泛亚案各位警官的诉求书》的诉求书，并对“12.13”人员代表提出的彻查泛交所资金流向彻底追赃、对泛亚案件追加罪名、追加王清民为非法集资同案主犯、追加政府相关部门和银行责任等问题进行了解答。16时10分许，接访结束，参加聚集上访的“12.13”人员在经侦支队门前集体照相后自行离开现场。\r\n\r\n相关情况进一步掌握中。\r\n', '2017-09-11 14:00:00', '2017-09-11 16:00:00', 2.00, 30, '涉众型经济案件', '西山分局', '市局经侦支队', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:14:26', '2017-09-18 13:48:29', NULL, '12.13', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '市局经侦支队', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2808, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'a82afcf0-1dc1-4108-a6cf-d6d33f67ccdb', 'JS', NULL, 'Y', 110, NULL, NULL, '530105000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (237, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-12 14:05:03', '9月12日“12.13群体到省政府非访应急处置工作情况', '据掌握，“12.13”等涉众型经济犯罪利益受损群体拟于9月12日14时许到省政府聚集上访。按照党委政府及上级公安机关部署要求，为妥善防范、处置可能发生的规模性群体性事件，五华分局成立了由副区长、分局局长涂力军任组长，党委副书记、纪委书记杨卫平，党委委员、副局长高勇任副组长，各部门主要领导为成员的“9.12”维稳应急处置工作领导小组。12日12时30分，高勇副局长到五华山现场指挥部指挥应急处置工作，按照 “止于未发，发能快处”的工作目标，以及“可散不可聚、可解不可结、可顺不可激”的处置原则，调集165名警力组成现场管控、应急处置、分流劝返及审查处理等4个工作组，配备4辆转运车辆，在市体育场设分流劝返点，配合政府职能部门做好教育劝返工作；在华山、护国、翠湖、文林、龙翔、茭菱6家派出所设审查处理点，负责重点人员的甄别盘问、法制宣教及审查处理工作。 13时45分许，“12.13”等涉众型经济犯罪利益受损群体人员陆续到省政府信访大厅聚集，截止14时，信访大厅已聚集26人等待接访，暂无过激情况。相关情况密切掌握中。\r\n\r\n\r\n\r\n\r\n', '2017-09-12 13:45:00', '2017-09-12 16:00:00', 2.25, 26, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:14:26', '2017-09-18 13:52:48', NULL, '12.13', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2501, 'dzjg', '党政机关', 'hdqk', '活动情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'sldws', '16-50人', '2', 'qkfy', 26, 0.00, 0, 0, '0', NULL, '情况反映', '3328e561-0a36-4340-80f8-d4de527cfd9f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (243, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-13 11:37:47', '9月12日 40余名“儒商”群体成员到省金融办上访的情况', ' 9月12日上午9时30分许，陆续有40余名“儒商”群体成员到省金融办聚集上访，12时许经省金融办工作人员做工作离开。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-12 09:30:00', '2017-09-12 12:00:00', 2.50, 40, '涉众型经济案件', '盘龙分局拓东所', '省金融办', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:15:37', '2017-09-18 14:08:08', NULL, '小微儒商', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '省金融办', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2608, 2503, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '1782e8c5-026d-4c61-8e6a-a51226d5e98e', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (245, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-14 13:35:17', '9月14日金座集资户到市体育馆聚集上访情况', '据工作掌握， 9月14日9时30分许，以罗益民、纪昌凤为首的金座集资户120余人到市体育馆集访，昆明市金融办相关领导进行了接访。主要是探讨金座维权群体维权人的免责协议书，内容大致为：“金座维权群体是自愿参加维权的特殊群体，多数是年老体弱，疾病缠身，常年参加市体育馆聚集上访的老年人，如果在维权活动过程中发生突发疾病、交通事故、行走安全等意外事故，一切风险责任自行承担，与维权组的组织者及通行人无关。具体签署协议的日期下次聚集开会再行通知，希望大家认真考虑各自的身体情况再行决定是否签署，最后该协议将由市金融办保存。中午11时许，金座集资户自行离开市体育馆。另据工作掌握，该维权群体将从9月15日至9月28日暂时放假，所有人员回家休整，暂不到市体育馆聚集上访并且不准参加金座其他维权群体的任何维权活动。      \r\n\r\n同日上午10时许，以卢福全为首的金座集资户近20人到南屏步行街中心花园附近开展例行的聚集活动，活动过程中未发生喊口号、打标语等过激行为。11时许，聚集的人员自行离开现场。\r\n\r\n    相关情况进一步掌握中。INSERT INTO `qb_zdasjqbxx` VALUES (246, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-09-13 17:11:40', '13日25名桃园小村村民对拆迁赔偿标准不一不满聚集西翥办事处', '9月13日11时许，25名桃园小村村民对拆迁赔偿标准不一不满聚集西翥办事处，上述人员质疑为何有的建筑被强拆，有的可赔偿，且有的无合法手续的建筑物也可赔偿，并声称若得到合理的解释将逐级上访。据了解，4月西翥办事处根据区政府规划，发布征地公告，对桃园小村1、2号地块进行称征迁，1号地块（娇子雪山专线由昆向禄劝方向左侧桃园小村洗衣粉厂至三多大村接界）、2号地块（娇子雪山专线由昆向禄劝方向右侧桃园小村洗衣粉厂至三多大村接界），1号地块统一由五华区国投公司接管，项目为军区转换地医院，高原农产品市场，地铁4号线修建土地堆放场；2号地块由西投公司接管，项目为中石油天然气公司，涉及70余户村民的土地、房屋及顺发砖厂、黑泥凹土场，可口可乐公司及部分私建的大棚厂房。现30余户已签订了拆迁协议。\r\n\r\n据掌握，上述人员已于14时许到市政府反映该诉求。相关情况密切掌握中。\r\n\r\n\r\n', '2017-09-13 11:00:00', '2017-09-13 15:20:00', 4.33, 25, '拆迁补偿', '沙朗所', '西翥办事处', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:19:54', '2017-09-18 14:24:59', NULL, '村民', NULL, '1', '1', NULL, NULL, NULL, NULL, NULL, '报市局,报区政府', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '西翥办事处', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2787, 2507, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '1', NULL, '情况反映', 'f8dcb33f-9378-428d-b6e0-c618de7afd7c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102590000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (248, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-13 13:41:53', '9月13日20余名省内外“12.13”人员到昆明市检察院聚集上访情况', '9月13日9时许，20余名省内外“12.13”人员再次来到昆明市检察院聚集上访，再次向昆明市检察院递交了部分证据和诉求书，并要求昆明市检察院将新提交的证据和诉求材料移交昆明市中级人民法院。11时20分许，接访结束，参加上访的人员在昆明市检察院门口聚集照相后自行离开现场。相关情况进一步掌握中。\r\n', '2017-09-13 09:00:00', '2017-09-13 11:20:00', 2.33, 20, '涉众型经济案件', '盘龙分局', '市检察院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:19:54', '2017-09-18 14:20:24', NULL, '12.13', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '市检察院', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2617, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '21292507-18c6-4914-a12d-02140136592b', 'JS', NULL, 'Y', 110, NULL, NULL, '530103000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (250, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-14 15:38:36', '14日莲花池村回迁安置项目工头聚集安置办讨要工程款', '    9月14日9时许，市区男子段远福（49岁）、四川资阳市男子陈付东（54岁）等25名包工头及民工聚集莲花池莲景苑回迁安置办公室讨要工程款，公安五华分局、区住建局、区人力资源社会保障局局等部门领导到场做解释安抚工作。11时30分许，上述人员陆续离开回迁安置办公室。相关情况密切掌握中。\r\n', '2017-09-14 09:00:00', '2017-09-14 11:30:00', 2.50, 25, '索薪讨款', '莲华所', '莲花池莲景苑回迁安置办', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-14 16:19:58', '2017-09-18 14:48:25', NULL, '个体户', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '莲花池莲景苑回迁安置办', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2629, 2547, 'jmxq', '居民小区', NULL, NULL, NULL, NULL, 'zzhd', '组织活动', 'nm', '农民', 'lzjf', '劳资纠纷', 'sldws', '16-50人', '2', 'qkfy', 25, 0.00, 0, 0, '0', NULL, '情况反映', '71172b86-43e4-49ab-83ed-61dd394daaad', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (256, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-09-15 09:21:40', '9月15日西翥办事处沙朗乡桃园村20余名村民到五华区区政府上访', '9月15日上午08时30分许，西翥办事处沙朗乡桃园村20余名村民到五华区政府上访，主要反映：拆迁安置赔偿问题，希望政府给予解决。现上访人员在区政府正门拉三条白底黑字的布标,布标内容为：“五华区西翥拆迁不公平”、“坚决反对五华区人为选择性不公平执法”、“我们要吃饭 要求公平拆迁 平等对待”，翠湖所民警到现场后，经劝解，上访村民已将布标收起。经区政府信访工作人员向上访村民解释，村民同意由信访部门通知西翥办事处相关领导到场，之后由区政府信访部门、西翥办事处共同接待上访村民。现上访村民情绪稳定，无过激行为，正有五华区政府信访局工作人员接待。（翠湖所）\r\n\r\n\r\n', '2017-09-15 08:30:00', '2017-09-15 12:00:00', 3.50, 20, '拆迁补偿', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-18 14:57:30', '2017-09-18 15:32:11', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2507, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'cscq', '城市拆迁', 'sldws', '16-50人', '1', 'qkfy', 20, 0.00, 0, 0, '0', NULL, '情况反映', '977fea54-44c1-4322-aa2d-bf8a82f62e88', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (258, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-14 17:32:48', '9月14日西翥办事处沙朗小村20余人因拆迁问题到区政府上访', '9月14日上午10时，西翥办事处沙朗小村20余人到五华区区政府上访，主要反映：拆迁安置赔偿问题，希望政府给予解决。上访人员在区政府正门拉三条白底黑字的布标,布标内容为：“五华区西翥拆迁不公平”、“坚决反对五华区人为选择性不公平执法”、“我们要吃饭 要求公平拆迁 平等对待”。 10时40分，经民警劝说，上访村民已将布标收起，无过激行为。后由信访办工作人员接待。整个接访过程平稳。\r\n\r\n\r\n', '2017-09-14 10:00:00', '2017-09-14 12:00:00', 2.00, 20, '拆迁补偿', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-18 14:57:30', '2017-09-18 15:30:30', NULL, '村民', NULL, '0', '2', '拉三条白底黑字的布标', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2507, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', NULL, NULL, 'sldws', '16-50人', '2', 'qkfy', 20, 0.00, 0, 0, '0', NULL, '情况反映', '1c6ee14c-8e7a-4b54-9541-e8b43f50f978', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (259, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-09-14 16:22:02', '9月14日 近50名昆明牙膏有限责任公司职工到市工信委上访的情况', '9月14日9时，近10名昆明牙膏公司职工市工信委，就要求市工信委出具“2013年市工信委派驻昆明牙膏公司工作组开展工作的情况”一事，听取市工信委的答复。10时30分，因认为市工信委出具的“2013年市工信委派驻昆明牙膏公司工作组开展工作的情况”未能客观反映情况，近50名不满情绪严重的昆明牙膏公司职工，陆续聚集到市工信委，要求市工信委全面、如实出具“2013年市工信委派驻昆明牙膏公司工作组开展工作的情况”。截止上报时，上述人员仍滞留在市工信委。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-14 09:00:00', '2017-09-14 13:00:00', 4.00, 10, '对政策不满', '呈贡分局', '市政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-18 14:57:30', '2017-09-18 15:21:49', NULL, '企业改制职工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2602, 2513, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'd4c9824a-6921-4086-82da-44f935eae5de', 'JS', NULL, 'Y', 110, NULL, NULL, '530109000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (260, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-18 14:52:30', '9月18日20名我省玉溪、宜良民办、代课教师到省教育厅上访', '9月18日10时许，20名我省玉溪（2名）、昆明宜良的民办代课教师到省教育厅上访，主要诉求为：询问云南在解决教师待遇问题上是否有新政策出台。\r\n据参与的教师表示，今天是全国民、代、幼教师统一到政府部门上访的日子，为配合此次活动他们来教育厅反映诉求。他们要求云南省教育厅认真执行国务院1988年出台的（100号）文件和1977年出台的（32号）文件，解决落实好民办教师身份待遇问题；15年教龄的离岗退养等等。后省教育厅、昆明市教育局的相关人员接待了教师，表示目前云南省尚未有新的政策出台，解决民办代课教师的问题只能依照当前文件执行。针对答复，在场的教师提出了异议，宣称云南应按照省外及曲靖陆良的办法解决老师的问题。12时许，接访结束，教师陆续离开省教育厅。（国保大队）\r\n\r\n\r\n', '2017-09-18 10:00:00', '2017-09-18 12:20:00', 2.33, 20, '对政策不满', '莲华所', '省教育厅', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-18 15:00:02', '2017-09-18 15:44:40', NULL, '民办教师', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '省教育厅', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2627, NULL, NULL, NULL, 'fyqk', '反映情况', NULL, NULL, 'zzhd,sf', '组织活动,上访', 'jsxs', '教师学生', NULL, NULL, 'sldws', '16-50人', '1', 'qkfy', 20, 0.00, 0, 0, '0', NULL, '情况反映', '00cd394d-b131-412d-a76f-ce29f9b7c7d3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (266, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-18 10:36:23', '18日12名曲靖市居民到省政府反映执法不公一事', '18日09:35分许，12名曲靖市居民到省政府上访，反映2011年，当地一个叫王雄兵的男子以认识政府高官，能拿到土地批文为由，骗取20余名居民700余万元，后被群众发现向曲靖市公安局报案。来访群众称，由于曲靖市公安局个别领导插手该案，办案民警未将报警人的全部证据递交检察院，导致检察院不予批捕诈骗人王雄兵，引发被骗人不满，多次向曲靖市公安局反映要求立即抓捕骗子王雄兵，由于一直没有得到回复所以到省政府上访，要求上级政府督促曲靖市公安局，公正执法。12名来访人于09:45分许在省政府大门人行道处拉出一条红底黄字的布标，上有“曲靖市公安局有案不查  个别领导勾结包庇坏人”，我所维稳民警立即将布标收缴，引导12人到信访大厅反映诉求，现12人正在信访大厅等待接待，秩序相对平稳。\r\n', '2017-09-18 09:35:00', '2017-09-18 11:20:00', 1.75, 12, '涉法涉诉', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-18 15:01:32', '2017-09-18 15:42:47', NULL, '居民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'mjjf', '民间纠纷', 'syr', '11-15人', '2', 'qkfy', 12, 0.00, 0, 0, '0', NULL, '情况反映', '2f0968cd-c129-4dd9-90b6-04b815ef102b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (267, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-18 10:35:23', '18日怒江大华电站职工18人因不满被下岗一事到省政府上访', '   18日09:20分许，18名怒江州兰坪县大华水电站职工到省政府上访，反映，他们原本是怒江电力集团有限责任公司的职工，属于国有企业，有职工200余人，2004年怒江州进行电力体制改革，期间由于个别领导非法操控，导致他们23名职工被划为大华水电站管理，其他人则划归南方电网公司，来访人员称，南方电网属大型国企，职工待遇一年比一年好，而他们23人至今不知道大华水电站是国企还是私企，导致自己的身份也不明确，工资待遇和五险一金的缴纳比南方电网的职工相差很多，多年来他们一直向有关部门反映要求重新安排他们的岗位未果。今年初由于合并电网的需要，大华水电站被上游电站泄洪淹没，至23名职工上不了班，大华电站要求23人买断工龄，给予每人6万元的一次性补偿，职工不满，认为补偿太低，所以到省政府上访，要求政府给予23人安排事业单位的工勤编制。现来访群众已有信访局工作人员接待，秩序平稳。\r\n', '2017-09-18 09:20:00', '2017-09-18 10:35:00', 1.25, 18, '企业改制/破产 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-18 15:01:32', '2017-09-18 15:41:03', NULL, '改制职工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'xgsyry', '下岗失业人员', 'gzfldy', '工资福利待遇', 'sldws', '16-50人', '2', 'qkfy', 18, 0.00, 0, 0, '0', NULL, '情况反映', 'f0374e33-6fe9-42ca-998b-61cb6d8c463b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (268, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-09-17 14:24:24', '17日西城时代约40名业主聚集售楼部要求尽快办理房产证', '9月17日10时40分许，西城时代约40名业主,持“我要房产证”、“一纸承诺书，忽悠多久算多久”、“三月能办房产证，拖了三年”3条白底黑字的横幅，聚集在售楼部前。经分局、海源街道办事处工作，业主收回横幅，并选派4名代表与开发商协商。经协商，开发商承诺年内为符合办理条件的1300户业主办理房产证。12时30分许聚集业主自行离开。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-17 10:40:00', '2017-09-17 12:30:00', 1.83, 40, '交房问题 ', '黑林铺所', '西城时代售楼部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-18 15:01:32', '2017-09-18 15:38:19', NULL, '房屋业主', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '西城时代售楼部', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2757, NULL, NULL, NULL, 'clbs', '材料报送', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '0c2c10fd-95de-4580-aee7-f173066a006d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (270, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-20 16:55:30', '9月20日莲花池村回迁安置项目16名包工头及民工到五华区政府上访', '20日14时许，四川资阳市男子陈付东（54岁）、大理州男子赵永康（50岁）等16名包工头及民工持法院判决书到五华区政府上访，要求协助解决拖欠工程款问题。经接待，上述人员于16时许离开，并声称9月25日仍将到五华区政府反映诉求。相关情况密切掌握中。\r\n\r\n\r\n', '2017-09-20 14:00:00', '2017-09-20 16:00:00', 2.00, 16, '索薪讨款', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-21 11:00:55', '2017-09-21 11:27:38', NULL, '个体户', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2547, NULL, NULL, 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sldws', '16-50人', '1', 'qkfy', 16, 0.00, 0, 0, '0', NULL, '情况反映', 'b54e8b00-ed12-4c43-af36-2d30c0e91b63', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (271, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-20 13:40:44', '赵勇刚等9名对越参战老兵到省政府、省国资委上访', '9月20日9时许，赵勇刚、毛文春等8名对越参战老兵（着便服）会同9新丰幼儿园教职员工到省政府上访。据参与上访的老兵表示：西山区政府相关部门对位于马街春雨路913号的新丰幼儿园进行拆除，其中幼儿园有两名保安是参战老兵，因此失去了工作岗位。为让这两名老兵再获得工作，所以他们今日到省政府上访，目的是想通过政府部门向幼儿园上级部门“云南冶金集团公司”施压，以解决两人的问题，新丰幼儿园教职员工与他们反映的问题不同，虽然是一起来省政府，但是是各反映各呢诉求。省信访局工作人员接待了老兵及教职员工，表示他们的问题只能由主管的省国资反映。后老兵和教职员工前往了省国资委，目前正在国资委正在接访中。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-20 09:00:00', '2017-09-20 09:55:00', 0.92, 9, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-21 11:00:55', '2017-09-21 11:22:28', NULL, '对越参战老兵', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2524, NULL, NULL, 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'jtry', '军退人员', NULL, NULL, 'syr', '11-15人', '1', 'qkfy', 17, 0.00, 0, 0, '0', NULL, '情况反映', '6a8139e3-a5e6-4fc8-8f19-d25bfa39b99b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (272, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-20 13:45:27', '20日学府路农贸市场租户17人因不满管理行为到省政府上访', '20日11时许，17名学府路483号综合农贸市场租户到省政府上访，代表200余名租户反映，该农贸市场于2016年11月开放，有200余名租户向市场管理方缴纳了一年的租金后入场经营。2017年5月9日他们接到莲华街道办事处的通告，告知租户该农贸市场属于违法建筑，政府要予以拆迁，要求租户搬走，引发租户强烈不满，他们认为既然是违法建筑，在去年该市场开发经营的时候为什么没有政府人员予以阻止并告知租户？现在租户除了缴纳租金外还在铺面里进行了摊位的投资，如果要拆迁，损失由谁承担？由于多次向莲华办事处反映要求退钱、赔偿损失未果所以到省政府上访，要求退租金以及投入铺面里的损失。该起群众于今年5月26日曾就相同诉求到省政府上访过。经协调，市场管理方给每个租户退了3000元钱，其他款项一直未退，引发租户不满，今日再次到省政府上访，要求市场方退租金及铺面投入的损失。省信访局接待后告知其向五华区政府反映。11时15分许，17人全部离开省政府，上访过程平稳。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-20 11:00:00', '2017-09-20 11:15:00', 0.25, 17, '拆迁补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-21 11:00:55', '2017-09-21 11:24:38', NULL, '个体户', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'mjjf', '民间纠纷', 'sldws', '16-50人', '1', 'qkfy', 17, 0.00, 0, 0, '0', NULL, '情况反映', '8ec9b8d5-6620-4807-88f9-381051643afc', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (273, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-20 11:29:35', '33名亲属再次聚集昆医附一院讨要丁丽英不治身亡的说法及赔偿', '9月14日，晋宁县妇女丁丽英（53岁）在昆医附一院行右肺下叶切除术，17日19时45分许丁晕倒后经抢救无效死亡。经工作，遗体于当日移入太平间。20日10时50分许，33名家属再次聚集医院，要求查明死因并赔偿。现分局正组织医患双方进行协商。相关情况密切掌握中。\r\n\r\n\r\n', '2017-09-20 10:50:00', '2017-09-20 19:10:00', 8.33, 35, '医患纠纷 ', '大观所', '昆医附一院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-21 11:00:55', '2017-09-21 11:19:34', NULL, '患者家属', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '昆医附一院', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2682, 2550, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'sldws', '16-50人', '2', 'qkfy', 35, 0.00, 1, 0, '0', NULL, '情况反映', 'bf2ec0c1-0d50-4b89-ad3e-c40a0fb7d4c3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102480000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (274, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-20 10:16:36', '20日云南冶金集团新丰幼儿园教职员工9人到省信访局上访', '20日9时10时许，西山区新丰幼儿园（原云南冶金集团幼儿园）教职员工9人到省政府，反映今年8月份，幼儿园被冶金集团以危房改造为由强拆，教职工没有工作，给幼儿园也造成了经济损失，要求政府给教职工安置工作。该起群众于今年8月8日曾就相同诉讼到省政府上访过，因一直没有解决所以今日再次上访。\r\n相关情况进一步掌握中。\r\n', '2017-09-20 09:10:00', '2017-09-20 10:00:00', 0.83, 9, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-21 11:00:55', '2017-09-21 11:16:42', NULL, '改制职工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'xgsyry', '下岗失业人员', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '2', 'qkfy', 9, 0.00, 0, 0, '0', NULL, '情况反映', '375490b6-23d2-4afc-9da8-d88c58c48631', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (275, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-20 09:44:57', '20日曲靖原修路民兵17人因生活困难到省政府上访', '20日09:00分许，17名来自富源、马龙、沾益、寻甸的原修路民兵到省政府上访，反映，1964年，他们响应原云南省公路局的号召，组建民兵团（约1000余人），参与修建平远机场，1967年因为粮食短缺，民兵团解散，所有民兵回乡务农。来访人员称，解散时，公路局承诺按照解放军的退伍待遇给他们补偿，但一直没有兑现。现在他们年纪大了，生活困难，要求政府按照当时的解放军退伍标准给予其补偿。该起群众于昨日（9月19日）已就相同诉求到省政府上访过，因不满接待结果，今日再次上访。省信访局告知其不予重复接待，09:20分许，17人全部离开省政府。\r\n\r\n\r\n', '2017-09-20 09:00:00', '2017-09-20 17:00:00', 8.00, 17, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-21 11:00:55', '2017-09-21 11:11:49', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sldws', '16-50人', '1', 'qkfy', 17, 0.00, 0, 0, '0', NULL, '情况反映', '3132baca-0f18-4d37-84d8-c883d05b3913', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (276, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-19 15:10:19', '19日鲁甸县文屏镇村民15人因土地被占到省政府上访', '19日14:30分许，昭通市鲁甸县文屏镇海心种养专业合作社村民15人到省政府上访，反映，1995年根据全国组织开展编制土地利用总体规划，当地政府将来访群众位于文屏镇海心片区的700余亩良田划为基本农田，涉及20余户。2002年当地政府以修建巧-威公路为名占用了来访群众的700余亩土地，并以每平米101元的价格给予村民补偿，据来访村民称，由于村民不同意，政府就故意堵断水路，导致农田受损。后群众向镇政府反映后问题有所缓解，2014年8.03地震后，当地政府重新对土地进行规划，将来访群众的农田改为体育场、铺面等，但并没有分铺面给上访人，还是以每平米101元的价格补偿给村民，引发20余户村民不满。由于向相关部门反映未果所以到省政府上访，投诉当地政府的乱作为行为，同时要求得到公平分配。该起来访群众今早曾到省委反映过，由于不满接待结果，下午到省政府上访。省信访局按照信访条例不予重复接待，现昭通市政府工作组4人正在接待他们，秩序平稳。\r\n\r\n\r\n', '2017-09-19 14:30:00', '2017-09-19 15:35:00', 1.08, 15, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-21 11:00:55', '2017-09-21 11:08:41', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'syr', '11-15人', '2', 'qkfy', 15, 0.00, 0, 0, '0', NULL, '情况反映', '375b6f22-cee6-42bd-99be-f01aec8dd03a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
