INSERT INTO `qb_zdasjqbxx` VALUES (278, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-21 16:36:55', '21日110名高新区左卫村村民因不满土地被占到省政府上访', '21日14时30分许，90余名高新区马金浦街道办事处左卫村村民因不满土地被占、村干部乱作为等问题到省政府上访，截止15时45分许，来访人数达110人，省信访局接待后告知其向昆明市政府反映。16时5分许，110余人全部离开省政府，上访过程平稳。\r\n', '2017-09-21 14:30:00', '2017-09-21 16:05:00', 1.58, 110, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-25 09:48:50', '2017-09-25 14:34:31', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'yblyr', '101-200人', '1', 'qkfy', 110, 0.00, 0, 0, '0', NULL, '情况反映', '0a8b3545-f192-455b-9c98-a23ef99adcb9', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
