INSERT INTO `qb_zdasjqbxx` VALUES (280, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-21 16:40:55', '9月21日 我市涉金融领域受损群体10人到省金融办上访的情况', '9月21日9时30分许，“小微”、“泛民登”等我市涉金融领域受损群体成员10余人，到省金融办上访。12时许，经省金融办工作人员做工作，上述上访人员离开。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-21 09:30:00', '2017-09-21 12:00:00', 2.50, 10, '涉众型经济案件', '盘龙分局', '省金融办', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-25 09:59:21', '2017-09-25 14:43:09', NULL, '小微儒商', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '省金融办', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2608, 2503, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '0ed7af65-040f-4915-8a62-6e1e92c24ae8', 'JS', NULL, 'Y', 110, NULL, NULL, '530103000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (281, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-25 13:45:18', '9月25日省内外“12.13”人员到昆明市检察院聚集上访情况', '9月25日9时许，近30名省内外“12.13”人员到昆明市检察院聚集上访，其中省外到昆的“12.13”人员有10余名，主要来自四川、上海、河北、浙江、山西、陕西、河南，省内地州玉溪到昆的有10余名，聚集过程中，昆明市检察院相关工作人员进行了接访，上访人员再次要求检察院对泛亚案件增加罪名、加大追赃力度和公布追赃情况、追究银行责任及执行中央金融会议精神等内容进行了诉求。12时30分许，接访结束，上述人员陆续自行离开现场。相关情况进一步掌握中。\r\n', '2017-09-25 09:00:00', '2017-09-25 12:30:00', 3.50, 10, '涉众型经济案件', '盘龙分局', '市检察院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-25 14:20:59', '2017-09-25 15:14:04', NULL, '12.13', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '市检察院', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2617, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '3171be4e-c4f0-4582-96ba-aa5b988da91d', 'JS', NULL, 'Y', 110, NULL, NULL, '530103000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (282, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-25 11:15:56', '9月25日18余名志远建筑有限公司农民工到五华区政府上访', '9月25日上午10时20分许，18名志远建筑有限公司包工头到区政府上访。包工头主要反映的是在对莲花池进行拆迁后，志远建筑有限公司一直拖欠工人工资未发放，后经过法院判决要求志远建筑有限公司将拖欠工资支付给农民工，但至今志远建筑有限公司未按法院判决执行，现执行期将满，希望政府出面给予解决。区政府信访办相关负责人接待处理。现场秩序正常，无过激言行。翠湖所已安排2名民警到现场维持秩序，协助做好协调工作，相关动态密切关注中。（翠湖所）\r\n', '2017-09-25 10:20:00', '2017-09-25 12:30:00', 2.17, 18, '索薪讨款', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-25 14:20:59', '2017-09-25 15:04:02', NULL, '包工头', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2521, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', NULL, NULL, 'sldws', '16-50人', '1', 'qkfy', 18, 0.00, 0, 0, '0', NULL, '情况反映', '909f2320-a32a-4dcd-ae98-33ee7d4463a3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (283, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-25 10:53:57', '9月25日富民县花菁村村民10人因不满安置补偿不到位一事到省政府上访', '   25日10:15分许，富民县撒旦镇花菁村村民10人到省政府上访，反映，1995年，因为当地出现山体滑坡，政府要求该村40余户村民搬迁至山下，村民称，当时政府承诺给每人的安置补偿费是12000元，涉及170余人，每户的建房费是6万元，但至今建房费一直没有给村民，安置费每人给了8000元，多年来村民一直要求政府将这两项补偿全部给村民，由于没有结果，所以到省政府上访，要求上级政府帮助村民拿到应得的补偿。现来访群众正在信访大厅等待接待，秩序平稳。（华山所）\r\n', '2017-09-25 10:15:00', '2017-09-25 12:30:00', 2.25, 10, '拆迁补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-25 14:20:59', '2017-09-25 15:01:27', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'ymwt', '移民问题', 'sryx', '10人以下', '2', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', '5e583b7d-4655-4d63-866d-3f464c62d68b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (284, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-25 10:50:09', '25日6名包头再次到莲花池回迁安置办聚集讨要工程款', '9月25日上午9时许,莲花池拆迁回迁建设工程中涉拖欠工程款的陈付东等6名包工头及民工再次到莲花池回迁安置办公室现场反映诉求。文林所获悉情况后，立即派出警力赶到现场，经向6人了解，今日，包工头为两个组，一组到安置办，另一组15人到五华区政府同时反映诉求。今日前来莲花池回迁安置办公室反映的主要诉求仍是讨要前期拖欠的农民工工资及工程款。目前，现场有五华区五区住建的一领导正在接访。文林所警力在现场维护正常治安秩序，截止上报时，现场无过激维权情况发生。（文林所）\r\n', '2017-09-25 09:00:00', '2017-09-25 11:30:00', 2.50, 6, '索薪讨款', '文林所', '莲花池回迁安置办公室', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-25 14:20:59', '2017-10-16 14:38:48', NULL, '包工头', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '莲花池回迁安置办公室', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2648, 2521, 'jmxq', '居民小区', 'hdqk', '活动情况', NULL, NULL, 'zzhd', '组织活动', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '2', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', 'c3363c6e-6298-4b9e-8984-a721cb56c635', 'JS', NULL, 'Y', 110, NULL, NULL, '530102440000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (285, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-09-25 10:31:11', '25日怒江大华电站职工16人因不满被下岗一事到省政府上访', '25日09:20分许，16名怒江州兰坪县大华水电站职工到省政府上访，反映，他们原本是怒江电力集团有限责任公司的职工，属于国有企业，有职工200余人，2004年怒江州进行电力体制改革，期间由于个别领导非法操控，导致他们23名职工被划为大华水电站管理，其他人则划归南方电网公司，来访人员称，南方电网属大型国企，职工待遇一年比一年好，而他们23人至今不知道大华水电站是国企还是私企，导致自己的身份也不明确，工资待遇和五险一金的缴纳比南方电网的职工相差很多，多年来他们一直向有关部门反映要求重新安排他们的岗位未果。今年初由于合并电网的需要，大华水电站被上游电站泄洪淹没，至23名职工上不了班，大华电站要求23人买断工龄，给予每人6万元的一次性补偿，职工不满，认为补偿太低，所以到省政府上访，要求政府给予23人安排事业单位的工勤编制。该起群众于9月18日已就相同诉求到省政府上访过，因一直没有得到解决，今日再次上访。省信访局接待后告知其继续回怒江州反映。10:00许，16人全部离开省政府，上访过程平稳。', '2017-09-25 09:20:00', '2017-09-25 10:00:00', 0.67, 16, '企业改制/破产 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-25 14:20:59', '2017-09-25 14:56:03', NULL, '改制职工', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'xgsyry', '下岗失业人员', 'gzfldy', '工资福利待遇', 'sldws', '16-50人', '1', 'qkfy', 16, 0.00, 0, 0, '0', NULL, '情况反映', '017ab49d-2b0a-4542-ae1b-56e48064aef8', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (286, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-09-26 10:49:12', '26日9名商铺租户围堵茭菱路省农职学院大门讨要退还商铺押金', '26日7时，9名原农职学院茭菱路沿街铺面的承租人到农职学院反映退还承租铺面押金问题。9名人员用3辆电动车将学校大门堵住，仅留行人通过，导致接送教职工的交通车无法出行。事发后，茭菱所民警赶到现场，经了解，2015年底因农职学院茭菱路校本部进行旧校改造，已停止办学，原校址已无学生上课。农职学院本部沿街商铺共24间，现已退还3间押金，剩余21间商铺押金未退，总共涉及金额4万余元。因校方认为现押金票据持有人与原票据上持有人不符（因存在转租问题），遂未退还承租人押金，从而引发9人围堵大门。\r\n\r\n经茭菱所民警协调学校副院长到场，双方协商后同意27日9时到校协商解决此事，8时许，上述人员自行离开，相关情况密切关注中。。\r\n\r\n\r\n', '2017-09-26 07:00:00', NULL, NULL, 9, '合同纠纷 ', '虹山所', '农职院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-29 10:04:13', '2017-10-17 15:51:00', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '农职院', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2626, NULL, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'c6353ef5-058b-4ce1-92d2-6aed6fdd0c57', 'JS', NULL, 'Y', 110, NULL, NULL, '530102410000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (287, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-26 09:31:35', ' 26日在西山区春雨路做工的农民工25人因工钱被拖欠到省政府上访', '   26日09:05分许，25名农民工到省政府上访，反映，他们受雇于中国19冶集团，在西山区春雨路做道路景观提升改造工程，从今年2月份起，19冶拖欠他们200余名农民工工资及工程款2000余万元，来访群众称，他们已向西山区、昆明市等政府相关部门反映了多次，要求拿到被拖欠的各种款项均未得到解决，所以到省政府上访，要求尽快拿到工钱及工程款。现来访群众正在信访大厅等待接待，秩序平稳。\r\n\r\n\r\n\r\n\r\n\r\n华山东路派出所\r\n\r\n2017年9月26日\r\n', '2017-09-26 09:05:00', NULL, NULL, 25, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-29 10:04:13', '2017-10-20 11:07:08', NULL, '农民工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', NULL, NULL, 'mjjf', '民间纠纷', 'sldws', '16-50人', '2', 'qkfy', 25, 0.00, 0, 0, '0', NULL, '情况反映', '519d3a29-1af4-449a-8ef1-d9a5b3d4cd2a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (288, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-25 15:53:37', '9月25日在昆“12.13”人员到工商银行云南省分行聚集上访情况', '9月25日14时许，20余名在昆“12.13”人员到青年路工商银行云南省分行聚集上访，聚集过程中打出 “银行还我救命钱、政府还我养老钱、泛亚还我看病钱”、“公布资金流向430亿元，云南政府成立运作监管泛亚诈骗430亿”、“还我血汗钱”等内容的标语。14时30分许，我局民警干预后现场“12.13”人员将标语收起，滞留在银行开展签名活动，准备向银行申请无息贷款。截止上报时，20余名在昆“12.13”人员仍然滞留在现场。\r\n\r\n相关情况进一步掌握中。\r\n', '2017-09-25 14:00:00', NULL, NULL, 20, '涉众型经济案件', '小南所', '青年路工商银行云南省分行', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-29 10:04:13', '2017-11-15 16:21:18', NULL, '12.13', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '青年路工商银行云南省分行', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2607, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '1eb8d387-fef9-41b6-af7b-a0c6f7c266a7', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (289, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-25 15:43:42', '9月25日高新区左卫村村民80人因不满土地被占到省政府上访', '25日14:45分许，80名高新区马金浦街道办事处左卫村村民到省政府上访，反映，1998年起该村村干部以招商引资为由，租用村民土地给外地人建厂，之后来建厂的人越来越多，占用的土地也增多，至今已被占20余亩，但一直没有给村民任何补偿，涉及300余户村民1000余人，2009年村民打听到被占用的土地已被村干部以480万元一亩卖掉，引发村民不满，多年来一直向有关部门反映要求拿回土地未果，所以到省政府上访，要求上级政府清查村干部的违规违纪行为，把村民的土地要回来。该批群众于9月21日曾就相同诉求到省政府上访过，由于还没有解决所以今日再次上访。现来访群众聚集在武警操场处，暂未进信访大厅，秩序平稳。\r\n\r\n\r\n', '2017-09-25 14:45:00', NULL, NULL, 80, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-29 10:04:13', '2017-10-20 11:07:22', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'wsyr', '51-100人', '2', 'qkfy', 80, 0.00, 0, 0, '0', NULL, '情况反映', '7b41b16a-31ce-4518-8e5b-2f9b4bfd19be', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (290, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-26 17:44:54', '9月26日云南金诺恒益投资有限公司投资人到省政府上访的情况', '9月26日14时20分许，30名云南金诺恒益投资有限公司投资人到省政府上访，上访人员主要反映：1、云南金诺恒益投资有限公司利用万世吉短线外汇交易平台，以保本、高额回报的方式，吸引了全国十余万人参与国际外汇虚拟交易，云南涉及2万余人；2、此次参加上访的人员大多2017年3月前后参与万世吉短线外汇交易平台的国际外汇虚拟交易，多的投入了200余万元，少的也投入了20至30万元，2017年7月发现无法出金；3、如该公司涉嫌违法犯罪，请政府加大追赃挽损力度，帮助投资人追回损失；4、将会继续到省委、省人大、省政法委、省检察院、省公安厅等省级相关部门反映诉求。\r\n\r\n\r\n', '2017-09-26 14:20:00', NULL, NULL, 30, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-29 10:08:34', '2017-10-20 11:06:28', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'dd96c1c4-1d26-4fe5-9829-a0576f7e8cd9', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (291, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-26 17:38:10', '9月26日45名云南金诺恒益投资有限公司投资人到省公安厅信访处群访', '15时50分许，45名云南金诺恒益投资有限公司的投资人到五一路省公安厅信访处群访。据称，云南金诺恒益投资有限公司的高管称公司因涉嫌经济犯罪被曲靖市公安部门立案侦查，部分高层被警方控制，导致公司的外汇交易平台几十亿资金被冻结，不能提取，导致投资人受损，上访的45人系该公司投资者，据称涉及全国有10万人左右，涉及全国的投资资金有50亿左右。上访人员的诉求是：要求司法部门尽快查清案情，对公司的外汇交易平台是否违法尽快定性，如果违法、便严厉查处；如果不违法，让公司负责人出面协调，退还他们的本金；帮助他们挽回损失。\r\n\r\n经省厅信访处及护国所民警的现场做工作劝解，整个信访过程秩序良好，省厅信访处已将相关情况通报曲靖市公安局等相关部门。\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2017-09-26 15:50:00', NULL, NULL, 45, '涉众型经济案件', '新村所', '省公安厅（新村所）', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-29 10:08:34', '2017-10-20 11:04:35', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2671, NULL, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'jzwt', '集资问题', 'sldws', '16-50人', '1', 'qkfy', 45, 500000.00, 0, 0, '0', NULL, '情况反映', '246d5e82-cb69-411a-8397-011dc7f302f3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (292, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-26 17:28:22', '9月26日  “儒商”群体到省金融办上访情况', '9月26日9时30分许，50余名“儒商”群体成员及云南金诺恒益投资有限公司投资人到省金融办上访，上访过程中，打出一条白底黑字标语，内容为：人民政府为人民做主。12时许，经省金融办工作人员做工作离开。（国保大队）\r\n\r\n\r\n', '2017-09-26 09:30:00', '2017-09-26 12:00:00', 2.50, 50, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-29 10:08:34', '2017-10-20 11:06:53', NULL, '小微儒商', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2503, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'd6cbd1eb-1c5f-41bd-a8de-063768ecf422', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (293, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-26 17:24:35', '26日12名“12.13”外省人员到省政府上访', '   26日15:05分许，来自四川、陕西、山西、河南、安徽、河北等地的“12.13”人员到省政府上访，反映，之前他们到北京询问该事件的结果，被告知云南省成立了一个负面资产清算小组，该小组具体负责泛亚有色金属交易平台的事，故12人昨日到昆找该小组，今早12人到了昆明市金融办询问，未得到结果，所以下午到省政府上访，一是投诉昆明市金融办接待人员态度恶劣，二是询问清算小组的地点。省信访局接待后告知其到省金融办咨询。15:32分许，12人全部离开省政府。\r\n', '2017-09-26 15:05:00', NULL, NULL, 12, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-29 10:08:34', '2017-10-20 11:05:14', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'syr', '11-15人', '1', 'qkfy', 12, 0.00, 0, 0, '0', NULL, '情况反映', '771abb50-be12-4585-b1ae-3ee8a315c6f3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (294, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-26 14:41:15', '26日寻甸县村民9人因土地租赁问题到省政府上访', '26日14:30分许，9名寻甸县倘甸镇上甸尾村村民到省政府上访，反映2016年2月份，该村村民将2000余亩土地租给滇虹公司做生产用房，每亩租金700元，涉及村民50余户，但至今滇虹公司一直没有付租金给村民，租赁时间不等。村民多次向有关部门反映要求拿到租金未果，所以到省政府上访，要求政府帮助他们拿到租金。现来访村民已有信访局工作人员接待，秩序平稳。(华山所)\r\n', '2017-09-26 14:30:00', NULL, NULL, 9, '合同纠纷 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-29 10:08:34', '2017-10-20 11:06:02', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'sryx', '10人以下', '2', 'qkfy', 9, 0.00, 0, 0, '0', NULL, '情况反映', '18547e38-21ed-41a6-a37d-18ad78dd89fc', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (295, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-26 15:45:57', '26日“万世吉”平台交易投资人32人因资金被骗到省政府上访', '   26日14:30分许，32名投资“万世吉”平台的投资人到省政府上访，反映，他们于今年3月份开始投资云南金诺恒益投资有限公司交易平台，该公司主营代理澳洲万世吉外汇短线交易。来访群众称，他们从今年3月份开始交易，至8月份发现平台出现无法交易的情况，至今投资资金无法拿出，据称，仅云南省的投资人达2万余人，发现平台无法运行后他们多次向金融办等有关部门反映未果，所以到省政府上访，要求政府成立专案组，彻查该公司的具体情况、投资人资金的去向等问题。现来访群众正在信访大厅等待接待，秩序平稳。（华山所）\r\n', '2017-09-26 14:30:00', NULL, NULL, 32, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-09-29 10:08:34', '2017-10-20 11:05:43', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'mjjf', '民间纠纷', 'sldws', '16-50人', '2', 'qkfy', 32, 0.00, 0, 0, '0', NULL, '情况反映', '4ec9cb97-b6f3-4f73-b8aa-58c983d0435f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (298, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-28 14:54:37', '28日沾益县炎方乡村民17人因土地被占到省政府上访', '28日14:05分许，17名曲靖市沾益县炎方乡磨脚村委会磨脚西村村民到省政府上访，反映，今年1月份当地乡政府征用该村200余户村民的土地用于修建珠江源旅游开发公路（具体征用土地面积总数因各家不同村民不清楚），当时乡政府承诺按照每亩41000元补偿给村民，但至今村民没有拿到一分钱，村民称今早有村民到乡政府讨要补偿费被政府工作人员打伤3人，引发村民强烈不满，所以到省政府上访，要求严惩打人者，尽快拿到征地补偿。现来访村民已有信访局人员接待，秩序平稳。\r\n', '2017-09-28 14:05:00', NULL, NULL, 17, NULL, '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:36:38', '2017-10-17 16:11:06', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sldws', '16-50人', '2', 'qkfy', 17, 0.00, 0, 0, '0', NULL, '情况反映', '4bc05834-93ee-4d6c-ba3d-cf485d72a809', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (299, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-09-28 14:50:22', '9月28日我市“万世吉”投资人到省政府聚集上访情况', '9月28日9时30分许，60余名我市“万世吉”投资人到省政府上访，要求省政府责令省公安厅受理 “万世吉”投资人的报案，并立案查处“万世吉”案件，10时30分许，60余名“万世吉”投资人来到省政府与正义路路口聚集，准备打标语，被我局民警强制干预后将部分人员带离，部分人员撤回到了省信访办内聚集。\r\n\r\n12时40分许，部分被带离的人员陆续又回到省政府，并通知现场人员自己到当地派出所报案，13时许，上述人员陆续离开现场。聚集的人员陆续自行离开现场。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2017-09-28 09:30:00', NULL, NULL, 0, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:36:38', '2017-10-17 16:13:06', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'ca0da52c-28d2-4d80-a439-dc7d39386e12', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (300, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-28 14:48:00', '9月28日部分我市涉金融受损群体成员到省政府聚集上访情况', '9月28日9时许30分许，陆续有“12.13”、“小微”、“博融”、“互信”、“泛民登”、“儒商”等群体的100余名成员到省政府聚集上访，省政府信访办相关工作人员对涉金融受损群体的5名代表进行了接访，接访过程中，代表要求省信访办对以下3条诉求给予答复，1、副市长组织政府相关部门与各平台代表见面的时间；二是针对“《处置非法集资条例征求意见稿》提出修改意见”；三是针对“全国互联网金融风险专项核查工作方案”对云南的民间融资各平台的处置意见、方案、进展情况。13时15分，上午接访结束，部分人员对接访不满不愿离开现场，现场聚集的20余人开始轮流就餐，准备下午继续到省政府信访办上访。\r\n\r\n截止目前，仍有20余名我市涉金融受损群体成员聚集在省政府。相关情况在进一步掌握中。（国保大队）\r\n', '2017-09-28 09:30:00', NULL, NULL, 100, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:36:38', '2017-10-20 11:03:53', NULL, '12.13', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '209362ed-f622-4482-b283-2b8308cba048', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (304, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-09-29 15:39:26', '29日百余名岗头村村民到红云街道办事处上访', '\r\n\r\n2017年9月28日10时许，陆续有70余名岗头村村民到市政府上访，当日14时40分许，经五华区政府等职能部门工作人员做工作离开。9月29日9时20分许，近百名岗头村村民继续到红云街道办事处聚集，红云街道办事处等部门工作人员开展了接访工作。\r\n\r\n据参加聚集上访的村民反映，岗头村社区城中村拆迁改造项目自2011年启动以来，存在拆迁补偿标准过低、拖欠过渡费等问题，事隔7年（2017年9月）后再次启动，要求：一是拆迁补偿不能按照六年前的标准执行，需要结合实际情况，参考周边房价重新制定标准；二是如最终不能达成补偿协议，也可以同等面积置换房屋；三是要求先盖回迁房，安置完毕后再拆迁；四是对开发商（红凯地产）实力存有疑虑。\r\n\r\n为要求解决过渡费被拖欠等问题，部分岗头村村民曾数次到红云街道办事处聚集，期间出现打布标和短暂堵路的情况。岗头村社区城中村拆迁改造项目再次启动后，部分村民，特别是前期未签订拆迁协议的村民，为从中获取更多利益，开始相互串联维权，因诉求暂未得到解决，该群体到省市区等政府部门的集访隐患仍存在。\r\n\r\n相关情况在进一步掌握中。\r\n\r\n\r\n', '2017-09-29 09:20:00', NULL, NULL, 100, '拆迁安置', '红云所', '红云办事处', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:36:38', '2017-10-20 17:12:47', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '红云办事处', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2686, 2507, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '4493449f-12cc-4f7a-984b-58ca42add73a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102490000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (305, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-28 10:05:40', '9月28日我市涉金融受损群体省政府群访情况（一）（以此为准）', '近日，获悉我市涉金融受损群体拟于9月28日省政府聚集上访后，党委政府及上级公安机关高度重视，五华分局持续跟进相关情报收集工作，密切关注各群体动态，同时调集警力周密部署处置工作。9月28日早8时30，分局党委委员、副局长高勇带指挥中心、国保、法制、巡特、刑侦、治安大队及周边派出所共160名警力、5辆转运车辆赶到现场开展应急处突备勤工作。\r\n\r\n8:45分许，陆续有涉金融受损群体人员出现在省政府信访局主要涉及万世吉、互信、儒商、博融、泛民登、12.13等投资平台人员。截止10时，现场聚集近140余人，其中，万世吉群体有近60人，互信、儒商、博融、泛民登、12.13、城商行、光辉等群体有近80余人。针对现场情况，五华分局警力已在省政府门口部署警力，对来访人员进行疏导劝离工作，不允许上访人员滞留省政府门口，目前经工作上访人员全部进入了省政府信访局大院，无人员滞留于省政府大门两侧，现场秩序正常，信访局人员正准备接待上访人员，国保支队、治安支队、分局国保大队正积极开展情报摸排工作中。后续上报中。（指挥中心）\r\n\r\n\r\n\r\n\r\n', '2017-09-28 08:45:00', NULL, NULL, 140, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:37:27', '2017-10-17 15:39:34', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'sryx', '10人以下', '2', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '25024f0c-3b99-40a1-a7ff-77048e37a2a1', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (306, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-09-28 10:28:48', '9月28日岗头村约70名村民对中原地产启动二次改造不满前往市政府上访', '9月28日，月牙塘所民警工作中掌握到，岗头村五个村民组约70名村民，每人凑了约50元钱，要到市政府上访，主要诉求是中原地产启动二次改造问题，要求中原地产提高拆迁赔偿，以及过渡费赔偿。\r\n\r\n目前，村民已乘坐两辆大巴前往市政府，社区民警已随之赶往市政府收集情况。\r\n', '2017-09-28 10:00:00', NULL, NULL, 70, '征地补偿', '呈贡分局', '市政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:37:27', '2017-10-17 16:16:32', NULL, '村民', NULL, '1', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2602, 2507, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'yj', 0, 0.00, 0, 0, '1', NULL, '预警', 'c9cfcfa9-4fc4-443a-bcb5-361395296e89', 'JS', NULL, 'Y', 110, NULL, NULL, '530109000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (307, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-09-28 11:49:37', '岗头村村民市政府上访（续报）', '9月28日上午，岗头村五个村民小组约70名村民，每人凑了50元钱，包乘两辆大巴车前往市政府，反应中原集团下属的红凯地产公司启动岗头村城中村二次改造，触及村民相关利益问题。据掌握，8月中旬岗头村改造项目二次启动以来，部分村民对红凯地产赔偿能力持怀疑态度，且对拆迁赔偿标准及过渡费发放标准不满，一直不愿与红凯地产公司签订拆迁补偿协议。红凯地产公司称，截至2016年12月31日，已向签订拆迁补偿协议的村民支付延期交房过渡费及过渡费合计18153万，部分村民及沙坝营农贸市场不愿签订协议，影响拆迁进度。\r\n\r\n现分局已安排民警前往市政府开展疏导劝返工作。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-28 10:00:00', NULL, NULL, 70, '拆迁补偿', '呈贡分局', '市政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:37:27', '2017-10-17 16:16:24', NULL, '村民', NULL, '1', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区外', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2602, 2507, 'cz', '城镇', 'badys', '不安定因素', NULL, NULL, 'sf', '上访', 'nm', '农民', 'cscq', '城市拆迁', 'wsyr', '51-100人', '2', 'qkfy', 70, 0.00, 0, 0, '1', NULL, '情况反映', 'd480e5f5-d9ee-46d1-af47-d6f707b41bd0', 'JS', NULL, 'Y', 110, NULL, NULL, '530109000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (308, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-28 11:55:23', '金座上访人员到步行街中心广场聚集情况', '9月28日10时许，10余名金座上访人员到步行街中心广场聚集，未悬挂布标，未喊口号，无过激行为。10时40分许，上述人员自行。\r\n\r\n相关情况进一步掌握中。\r\n\r\n\r\n', '2017-09-28 00:00:00', NULL, NULL, 10, '涉众型经济案件', '护国所', '市体育馆、南屏步行街', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:37:27', '2017-10-17 16:16:46', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '市体育馆、南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2502, NULL, NULL, NULL, NULL, NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'sryx', '10人以下', '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '1bdbaeb3-4b3a-4845-9ec9-80ff8aad373c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (309, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-28 12:16:00', '9月28日我市涉金融受损群体省政府群访情况（二）', '\r\n\r\n10时，省信访局内聚集了近140余人，据摸排，涉及群体有：博融、泛民登、城商行、光辉、小微、互信、儒商、12.13、万世吉、快融金、鑫塔、中益信、惠众等。10:45分许，50余名万世吉投资人在省政府门口花园入口处拉出两条白底黑字的横幅“人民政府为人民做主”，造成路口交通不畅。五华分局现场处置警力迅速开展工作，一是劝解上访人员回到上访地点接访；二是要求万世吉群体立即收起横幅，停止违法行为；三是现场法制宣传车告诫现场围堵路口人员已违法，下达解散令。10时55分许，聚集的50余名万世吉投资户中部分人员自行离开，但仍有16名人员滞留路口，现场处置民警果断将16人强制带离现场，转运市体育馆进行训诫。\r\n\r\n截止12时，省政府信访局内仍有80余名博融、泛民登、城商行、光辉、小微、互信、儒商、12.13、快融金、鑫塔、中益信、惠众等群体的投资户滞留，经民警工作，已选出5名代表，省、市金融办人员正接访代表中。据市体育馆警力收集，被训诫人员有10男：宋兴海（身份证号：511321198712111952）、陶乃交（身份证号：53038119901006135X）、赵文保（身份证号：530128198910212435）、邓云辉（身份核查中）、何明显（身份证号：530381198711141315）、何建波（身份证号：51040219850720141X）、蒋文琪（身份证号：530328199610011872）、杨加文（身份证号：530129199410191114）、黄伟（身份证号：53012619870813061X）、陶乃建（身份证号：53038119211181315）；6女：颜嘉雯（身份证号：530102198412221829）、杨水花（身份证号：530328198612072420）、张正香（核查中）、李欣（身份证号：532122199401100820）、陆佳佳（身份证号：522701198403030728）、王婷（身份证号：532128199412052789）\r\n\r\n目前，省政府信访局内秩序平稳，市体育馆训诫工作仍在开展中，密切关注续报中。\r\n\r\n\r\n\r\n\r\n', '2017-09-28 10:00:00', NULL, NULL, 140, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:37:27', '2017-10-17 16:12:56', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'yblyr', '101-200人', '2', 'qkfy', 140, 0.00, 0, 0, '0', NULL, '情况反映', '3fc152cf-5803-4d06-85e4-95e8fd0c6cd2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (310, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-28 13:54:58', '9月29日金座集资户拟市金融办上访', '据工作掌握，9月29日周四9时30分许，以纪昌凤、罗益民为首的金座集资户群体拟到市金融办上访。昆明市金融办的相关领导接访。预计参加人数在100余人左右。相关情况在进一步掌握中。INSERT INTO `qb_zdasjqbxx` VALUES (313, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-30 13:58:41', '我市涉军群体“9.30公祭日”活动现场情况', '2017年9月30日上午，我市对越参战老兵群体及“两援”人员群体在南屏步行街广场、胜利堂等区域开展了“9.30公祭”活动，具体情况如下：INSERT INTO `qb_zdasjqbxx` VALUES (315, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-09-30 15:08:45', '9月30日金座集资户到市体育馆上访情况', '？9月30日上午9时30分许，以罗益民、纪昌凤为首的金座集资户120余人到市体育馆集访，昆明市金融办相关领导进行了接访。会议主要由市金融办的相关领导对金座集资户进行安抚，对金座集资户提出的申请困难补助问题等两节后再行答复。中午12时30分许，金座集资户自行离开市体育馆。？相关情况在进一步掌握中。\r\n\r\n\r\n', '2017-09-30 09:30:00', '2017-09-30 12:30:00', 3.00, 120, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:43:05', '2017-10-20 17:09:18', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '700be36b-eefc-4b95-9f16-6cff184b1931', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (316, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-09-30 15:32:14', '30日宣威市田坝镇村民5人因不满搬迁安置到省政府上访', '30日10:35分许，5名宣威市田坝镇新发村村民到省政府上访，反映，2004年当地政府在该村修建大唐万家口子水电站，要求该村100余户村民整体搬迁，来访群众称，政府指定的搬迁地太过荒凉，无饮用水源，给村民的生产生活造成严重不便，而且搬迁十多年来，安置费用一直存在争议，至今没有发给村民，安置房面积严重缩水，政府承诺每户108平米，实际有98平米等问题。由于向县、市相关部门反映要求解决这些问题未果，所以到省政府上访，要求尽快解决村民的实际问题。省信访局接待后告知其回宣威市反映。10:50分许，5人全部离开省政府。\r\n\r\n\r\n\r\n\r\n\r\n华山东路派出所\r\n\r\n2017年9月30日\r\n', '2017-09-30 10:35:00', '2017-09-30 10:50:00', 0.25, 5, '移民搬迁', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:43:05', '2017-10-20 17:07:44', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '676acd7b-49f6-4e74-9f9b-d2ee8b354d8f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (321, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-09 17:31:53', '10月9日部分“12.13”人员到省工商银行聚集的情况', '10月9日14时许，陆续有18名昆明的部分“12.13”人员先到青年路395号云南省工商银行大厅内继续开展上访维权活动，以“12.13”人员生活困难为由，向银行方面递交上访维权人员低息、无息贷款的申请。16时20分许上述人员自行离开。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-10-09 14:00:00', '2017-10-09 16:20:00', 2.33, 18, '特殊群体', '小南所', '青年路工商银行云南省分行', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:45:32', '2017-10-20 16:56:13', NULL, '12.13', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '青年路工商银行云南省分行', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2607, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '2e1e28f9-1fed-43af-8dbc-74e7c8bc0868', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (322, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-09 17:34:23', '8名昭通朝阳区乐居乡新河村村民到省政府信访局上访', '10月9日15时25分，8名昭通朝阳区乐居乡新河村村民到省政府信访局上访。主要反映：当地政府于9月30日对当地村民发布公告，要求该村23户村民在8日内搬迁，村民们认为政府发布的公告搬迁的原因、内容等都不明确。现8名村民代表到省政府信访局上访要求省政府帮助解决落实：1、请当地政府明确搬迁原因。2、明确搬迁补偿的具体方案及金额。17时许被当地工作组带离。\r\n\r\n相关情况在进一步掌握中。\r\n\r\n\r\n', '2017-10-09 15:25:00', '2017-10-09 17:00:00', 1.58, 8, '移民搬迁', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:45:32', '2017-10-20 16:53:33', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '2c8a55f8-7cbf-4a34-a1b3-3a269fa06deb', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (323, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-10-09 17:35:31', '10月9日部分岗头村村民到红云办事处、岗头社区居委会反映诉求的情况', '继2017年9月28日上午10时许，部分岗头社区村民到昆明市政府上访后。2017年10月9日上午9时，部分岗头村村民约60余人继续到红云街道办事处岗头社区居委会聚集反映拆迁的相关诉求并派代表到红云街道办事处递交材料。后村民于9时30分离开。\r\n\r\n经进一步工作得知，岗头村城中村改造项目于2009年11月20日五华区政府与昆明红凯房地产开发有限公司签订前期合作协议。涉及岗头社区居委会五个居民小组，共需拆除民房1420栋、3698人。该项目2011年7月19日发布拆迁公告，2014年6月因企业拆迁资金中断，拆迁工作停滞。至此，民房已签协议的875栋。后期经五华区政府支持，昆明红凯房地产开发有限公司与省土储合作，由省土储投入资金进行土地一级开发，2017年8月14日红凯地产对之前已签协议的村民过渡费和补偿款继续发放，9月12日张贴通知，二次启动拆迁，重点对剩余的约400栋继续签订协议。\r\n\r\n村民反映的具体诉求：\r\n\r\n1、村民反映二次启动后，村组未召开任何会议，大家在不知情的情况下拆迁公司就进村开始拆迁。\r\n\r\n2、拆迁公司将拆迁下来的建筑垃圾、材料随意堆放在村中，造成村民出行不便且影响排水通畅。拆迁时的声音较大等，影响未拆迁户的正常生活。\r\n\r\n3、补偿标准是2011年公布的，现物价上涨，要求按现周边市场价进行赔偿。\r\n\r\n4、因前期拆迁不完全，影响村民租房的收入，以及部分延期交房的村民继续出租房屋，还拿过渡费，还分回迁房。造成不公平现象，要求企业补足过渡费。\r\n\r\n5、村民对回迁房的位置、户型不清，要求说明位置及做出户型图，沙盘图。\r\n\r\n针对以上问题，红云街道办事处领导高度重视，近期将带队下到各个居民小组做好村民的安抚宣传工作。并针对红凯地产准备10月15日开进机械设备进场一事展开沟通劝说工作，请红凯地产将工作放在“十九大”后进行。确保签一块、拆一块，尽量不扰民。\r\n\r\n分析建议：部分岗头村村民反映的回迁房安置、过渡费拖欠等问题开发商短期内不可能得到解决，建议红云办事处等政府职能部门共同做好相关宣传、疏导化解工作，同时建议红云派出所做好现场的巡逻防范及取证等相关工作，对现场出现的过激行为及时上报和处置，同时月牙塘派出所做好岗头村村民相关动态的掌控工作，防止大量村民聚集后到省市区等政府部门上访以及再次堵断龙泉路的情况出现。\r\n\r\n相关情况在进一步掌握中。INSERT INTO `qb_zdasjqbxx` VALUES (329, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-13 11:53:18', '昭通籍信访人员朱正乾携带管制刀具到省政府上访', '13日9时30分许，朱正乾（男，73岁，汉族，1944年1月6日生，户籍地：昭通市镇雄县乌峰镇世贵街13号，身份证号：532128194401060019，系镇雄县疾控中心退休医生）到省政府信访局上访，反映镇雄县医保中心每月扣其188元，要求上级政府解释。省信访局接待后告知其回昭通市反映，该人在离开的过程中民警发现其外套内藏有一把匕首，刀刃长12公分，单开刃，属于管制刀具。民警立即将其带回华山所。目前正在进一步审理中。\r\n\r\n\r\n\r\n\r\n\r\n华山东路派出所\r\n\r\n2017年10月13日\r\n', '2017-10-13 09:30:00', NULL, NULL, 0, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:50:18', '2017-10-20 15:44:06', NULL, NULL, NULL, '0', '2', '携带管制刀具', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '2', 'qkfy', 1, 0.00, 0, 0, '0', NULL, '情况反映', 'ced2f318-3a27-452b-aafa-e705b21aff5b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (330, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-10-13 14:00:26', '10月13日金座集资户到市体育馆自行聚集的情况', '据工作掌握， 13日9时30分许，以纪昌凤、罗益民为首的金座集资户群体到市体育馆自行聚集。参与聚集的人员主要是筛选生活确有困难的集资户，经过动员后五华区的有10余人自愿放弃困难补助申请。上午主要是自行讨论五华区、盘龙区的困难补助申请，中午12时许继续讨论官渡区、盘龙区的困难补助申请。截至上报时，讨论仍在进行中。今天到市体育馆的金座集资户约100余人。相关情况进一步掌握中。INSERT INTO `qb_zdasjqbxx` VALUES (331, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-13 14:05:20', '10月13日部分“儒商”群体成员五华经侦大队情况聚集情况', '据掌握，13日10时许，近70名“儒商”群体成员来到五华区经侦大队聚集，聚集过程中，“儒商”群体成员情绪平稳，五华区经侦大队的领导开展了接访工作，向聚集人员通报了案件侦办的部分情况，并要求聚集的“儒商”群体成员相信党和政府及公安机关能够处理好相关问题。12时15分许，上述人员陆续自行离开现场。\r\n\r\n相关情况在进一步掌握中。\r\n\r\n（正文结束）\r\n\r\n\r\n', '2017-10-13 10:00:00', '2017-10-13 12:15:00', 2.25, 70, '特殊群体', '王家桥所', '五华经侦大队', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:50:18', '2017-10-20 15:34:06', NULL, '小微儒商', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '五华经侦大队', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2781, 2503, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'f6ac69b7-cf2e-4d19-81a5-c3e4cd3f9984', 'JS', NULL, 'Y', 110, NULL, NULL, '530102580000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (332, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-13 14:35:48', '13日9名金座上访人员到步行街中心广场聚集', '13日10时许，9名金座上访人员到步行街中心广场聚集，未悬挂布标，未喊口号，无过激行为，11时10分许上述人员自行离开中心广场。相关情况进一步掌握中。\r\n', '2017-10-13 10:00:00', '2017-10-13 11:10:00', 1.17, 9, '特殊群体', '护国所', '南屏街中心广场', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:50:18', '2017-10-20 15:21:26', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '南屏街中心广场', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2724, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'sryx', '10人以下', '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '0d985408-dc47-4f9a-aeb5-7769b5b085de', 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (335, NULL, NULL, NULL, NULL, 'jj', NULL, 'qt', '2017-10-12 11:43:49', '云南联铭装饰工程有限公司12名工人聚集商业饭店讨要工程款(续报)', '10日16时许，云南联铭装饰工程有限公司重庆男子秦宗民（44岁）等12名工人聚集东风西路商业饭店讨要90万元工程款。据商业饭店负责人王云萍（女，47岁）称，9月云南联铭装饰工程有限公司承接了该饭店装修工程，期间饭店支付了部分款项，现仅剩30余万未付清。经分局、区劳动监察局工作，双方同意11日携带合同等凭据到住建部清欠办解决此纠纷。11日经工作，双方在区劳动监察大队达成一致协议，商业饭店扣除违约金及相关费用后已经付给装修方云南联铭装饰工程有限公司装修费用10万元。合同中的质保金问题，双方约定装修期满一年后再协商解决。\r\n\r\n\r\n', '2017-10-10 16:00:00', NULL, NULL, 12, '索薪讨款', '新村所', '商业饭店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:53:38', '2017-10-20 16:05:35', NULL, '农民工', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '商业饭店', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2675, 2519, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '4eb3e281-d601-4da6-83f0-fd8448f29ac2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (336, NULL, NULL, NULL, NULL, 'jj', NULL, 'bq', '2017-10-12 16:33:55', '沙朗乡白岩子村10余名村民聚集白龙山矿业公司采石场要求修缮、赔偿因开山炸石震裂的房屋', '12日10时许，西翥办事处沙朗乡龙庆社区白岩子村张立忠（67岁）、张成万（54岁）等10余名村民聚集在白龙山矿业公司采石场，要求石场修缮、赔偿因开山炸石震裂的房屋。现沙朗所、龙庆社区正组织双方进行协商。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-10-12 10:00:00', NULL, NULL, 10, '损害赔偿 ', '沙朗所', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:53:38', '2017-10-20 16:02:17', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2507, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '84ac9729-89cf-4442-9c25-a9a278256796', 'JS', NULL, 'Y', 110, NULL, NULL, '530102590000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (337, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-12 16:40:32', '10月12日金座集资户周四例行聚集情况', '12日14时许，以纪昌凤、罗益民为首的130余名金座集资户到市体育馆上访，昆明市盘龙区政府、西山区政府及市金融办的相关领导进行了接访，金座集资户向盘龙区政府、西山区政府递交“生活困难申请”， 截止上报时，聚集上访的金座集资户还滞留在市体育馆。 \r\n\r\n12日10时许，以卢福全为首的30余名金座集资户到南屏步行街中心广场例行聚集活动，聚集过程中未发现喊口号、打标语等过激行为。11时许，上述人员自行离开现场。\r\n\r\n3、10月12日，以陶炳芳为首的金座集资户因今天休息，故未到五华区政府五一广场例行聚集活动\r\n\r\n相关情况进一步掌握中。\r\n', '2017-10-12 10:00:00', NULL, NULL, 160, '特殊群体', '茭菱所 护国所', '市体育馆、南屏步行街', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:53:38', '2017-10-20 16:00:57', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆、南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '41713e6b-a332-4c92-9e67-341485c0bb3f', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (341, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-10-11 10:58:35', '富宁县田蓬镇村民19人因不满安置补偿到省政府上访', '11日9时30分许，19名文山州富宁县田蓬镇碗厂村委会村民到省政府上访，反映，2016年7月10日，该村发生山体滑坡，严重影响村民生活，当地政府要求全村26户100余名村民整体搬迁至政府指定的安全地带，村民称，当时政府承诺给予每户村民6万元无息贷款用于自建房，分20年还清，另外每户给予7785元的危房改造费，同时承诺建好房后立即通水通电。但至今无息贷款一直没有下拨，村民均是自筹费用建房，半年前房屋建好至今没有通水电，给村民造成极大不便，村民向当地政府反映要求履行承诺，当地政府称无息贷款的政策已经不存在，只有10万元的商业贷款，村民称无法承受每月700余元的利息，由于向当地政府交涉无果所以到省政府上访，要求拿到6万元的无息贷款、尽快通水通电以及发放危房改造费。现来访村民已有信访局工作人员接待，秩序平稳。\r\n\r\n\r\n\r\n\r\n\r\n华山东路派出所\r\n\r\n2017年10月11日\r\n', '2017-10-11 09:30:00', NULL, NULL, 19, '灾后重建', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:56:03', '2017-10-20 16:28:19', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sldws', '16-50人', '2', 'qkfy', 19, 0.00, 0, 0, '0', NULL, '情况反映', '6b449e0f-b5f1-46d0-8515-d21e486af715', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (342, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-11 13:36:47', '云南信龙工贸有限公司6名工作聚集安装二公司讨要工程款纠纷', '11日10时45分许，云南信龙工贸有限公司市区男子叶树能（47岁）、王建明（42岁）等6名工人，将2辆微型车停在虹山西路安二司门前，讨要工程款。据了解，2016年5月安装二公司将广福路云南省大剧院1000万的土建工程承包给云南信龙工贸有限公司（公司法人：顾余伟，公司地址：盘龙区东风东路36号建工大厦13层1312号），2017年8月安二司按合同进行结算支付。叶、王等4名小包工头因找不到顾收款，遂带员工前往安二司讨要52.3万元工程款。经分局工作，叶、王等人已于11时50分许将车移开，并同意下午在安二司办公室协商解决。相关情况密切掌握中。\r\n\r\n\r\n\r\n\r\n', '2017-10-11 10:45:00', NULL, NULL, 6, '索薪讨款', '虹山所', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:56:04', '2017-10-20 16:25:42', NULL, '农民工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2519, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, 'lzjf', '劳资纠纷', 'sryx', '10人以下', '2', 'qkfy', 6, 52.30, 0, 0, '0', NULL, '情况反映', 'c2de11ce-2da4-4b78-9ac6-3ca9188e4349', 'JS', NULL, 'Y', 110, NULL, NULL, '530102410000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (344, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-10-11 14:46:37', '约200名岗头村村民聚集红云办事处听取迁改造项目二次启动工作宣传解释', '10月11日9时，获悉红云办事处及拆迁办召集岗头村二组村民，在岗头社区居委会商谈城中村拆迁改造项目二次启动工作事宜后，约200名村民陆续聚集红云街道办事处岗头社区居委会。红云街道办事处、五华区城改办、昆明红凯房地产开发有限公司分别就岗头村城中村拆迁改造项目二次启动工作进行了宣传解释，村民代表询问了回迁房安置、失地养老保险、过渡费安置费能否按时发放等相关问题。11时40分村民自行离开居委会。14时红云街道办事处、五华区城改办、昆明红凯房地产开发有限公司还将召集岗头社区三组村民做拆迁项目二次启动的宣传解释工作。\r\n\r\n相关情况在进一步掌握中。\r\n', '2017-10-11 09:00:00', '2017-10-11 11:40:00', 2.67, 200, '拆迁安置', '红云所', '红云办事处', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:56:04', '2017-10-20 16:21:21', NULL, '岗头村', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '红云办事处', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2686, 2508, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '260a1798-2452-4356-aec6-f890f389de99', 'JS', NULL, 'Y', 110, NULL, NULL, '530102490000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (345, NULL, NULL, NULL, NULL, 'jj', NULL, 'qt', '2017-10-11 17:12:36', '10月11日金座集资户到市体育馆聚集上访情况', '据工作掌握，2017年10月11日下午14时30分许，以罗益民、纪昌凤为首的金座集资户130余人到市体育馆集访。昆明市五华区政府、官渡区政府及市金融办相关领导进行了接访。主要是金座集资户向市、区政府金融办的工作人员反映生活困难要求补助的事宜。具体情况是五华区政府、西山区政府的工作人员向金座集资户的维权代表明确表示：“集资户生活确实有困难的可以提出申请”，集资户代表表示理解，要求两区政府将申请困难补助名单返还，他们重新筛选出生活确实有困难的集资户名单再向他们申请。截止上报时，所有人员还滞留在市体育馆继续探讨。相关情况在进一步掌握中。\r\n\r\n\r\n', '2017-10-11 14:30:00', NULL, NULL, 130, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:56:04', '2017-10-20 16:15:17', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'c188efd0-510c-4bb9-bd9f-a3d7a00b7e92', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (349, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-10 10:39:49', '在施甸县打工的民工11人因工钱被拖欠到省政府上访', '    10日10时20分许，11名在施甸县打工的农民工到省政府上访，反映，2015年5月，施甸县政府建设养老服务中心项目，该工程由施甸县天泰投资有限公司承包，由于公司一直拖欠50余名工人工钱及工程款400余万元，2015年8月工程停工，至今农民工未拿到工钱，由于多次向相关政府部门反映未果所以到省政府上访，要求尽快拿到工钱。现来访群众正在信访大厅等待接待，秩序平稳。\r\n\r\n\r\n\r\n\r\n', '2017-10-10 09:00:00', NULL, NULL, 11, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:56:52', '2017-10-20 16:36:59', NULL, '农民工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'syr', '11-15人', '2', 'qkfy', 11, 0.00, 0, 0, '0', NULL, '情况反映', '71bcde21-08ab-4fae-be8e-bae84cfe3da0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (350, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-10 13:59:26', '10月10日部分在昆“12.13”人员到昆明市检察院聚集上访情况', '10月10日9时许，6名在昆“12.13”人员到昆明市检察院聚集上访，上访过程中，向昆明市检察院递交了致《昆明市检察院要求增加泛交所合同诈骗罪的诉求信》，要求昆明市检察院在公诉书中增加合同诈骗罪和给予“12.13”人员受害人身份及彻底追赃等诉求。昆明市检察院的相关领导对6名上访人员进行了接访。\r\n\r\n11时10分接访结束，6名参加上访的“12.13”人员在检察院门口，举着用A4纸打印的“10月10日检察长接待日”标牌照相后自行离开现场。\r\n\r\n相关情况在进一步掌握中。\r\n', '2017-10-10 09:00:00', '2017-10-10 11:10:00', 2.17, 6, '特殊群体', '外区', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:56:52', '2017-10-20 16:33:35', NULL, '12.13', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '4186a8bd-6717-43da-b9af-a5e6f31a44e5', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (351, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-10-10 14:00:54', '10月10日金座集资户维权群体到市体育馆聚集上访的情况', '据工作掌握，2017年10月10日上午9时30分许，以纪昌凤、罗益民为首的金座集资户到市体育馆集访。昆明市金融办的相关领导到现场接访。主要还是围绕金座集资户的一次性困难补助申请展开讨论。今天到市体育馆参加聚集的涉金座人员有130人左右，中午12时15分自行离开市体育馆。\r\n\r\n相关情况在进一步掌握中。INSERT INTO `qb_zdasjqbxx` VALUES (354, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-16 10:31:53', '16日红河州泸西县村民6人因不满村干部作为到省政府上访', '16日08:35分许，6名红河州泸西县白水镇所白村村民到省政府上访，代表100余户村民反映，该村村支书等村干部贪污扶贫款，在不召开村民大会的情况下私自确定贫困户的指标，存在暗箱操作，引发村民强烈不满，因多次向当地政府反映村干部的乱作为行为未果，所以到省政府上访，要求公开村内账目，公平评选贫困户。现来访村民正在信访大厅等待接待，秩序平稳。\r\n', '2017-10-16 08:35:00', NULL, NULL, 6, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:58:50', '2017-10-20 16:16:58', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', '1a62c7e0-aedb-4e29-a237-2e93e9f52194', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (355, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-16 14:10:08', '16日在开远市打工的农民工6人因工钱被拖欠到省政府上访', '   16日11:00许，6名农民工到省政府上访，反映，他们于2015年8月起在开远市小新寨水利工程项目打工，由于项目工程部拖欠其工程款及200余名民工工资共计600余万元，工程至2015年年底停工，至今民工一直没有拿到工钱。由于多次反映无果所以到省政府上访，要求解决农民工工资问题。该起来访群众由红河州政府工作组人员接待，11:30分许，6人全部离开省政府。\r\n', '2017-10-16 11:00:00', '2017-10-16 11:30:00', 0.50, 6, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:58:50', '2017-10-20 14:38:26', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', '7e7d11da-2a58-46f5-a53f-1bbab8f3360c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (356, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-16 14:07:09', '16日省第五戒毒所民工6人因工钱被拖欠到省政府上访', '16日11:05分许，6名在省第五戒毒所做工的民工到省政府上访，反映，2015年1月云南省第五戒毒所将戒毒所综合培训基地进场道路施工工程发包给昆明杰仁建筑劳务有限公司，该公司雇佣了54名工人施工，2016年中，工程完工，但工人一直没有拿到工钱，经民工多次反映，劳务公司中途给予其结算过69万元，还有240余万元没有结清。工人多次向劳务公司要钱，公司称系戒毒所没有给公司钱，所以公司没有钱给民工，现工人找不到公司负责人，所以多次向戒毒所讨要，因一直拿不到工钱所以到省政府上访，要求尽快拿到工钱。省信访局接待后告知其会按照信访条例的规定帮助其协调解决。11:50分许，6人全部离开省政府，上访过程平稳。\r\n', '2017-10-16 11:05:00', '2017-10-16 11:50:00', 0.75, 6, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:58:50', '2017-10-20 14:41:52', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', '0cc75c2f-16a0-46d3-b2cd-cdab8764231d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (359, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-17 11:35:21', '文山州仁和镇村民5人因不满搬迁安置一事到省政府上访', '17日9时15分许，5名文山州马关县仁和镇老格木村委会老留冲下组村民到省政府上访，反映今年初该村附近发生山体滑坡，严重影响村民生产生活，当地政府要求该村100余户村民整体搬迁至政府指定的地点安置，政府建房并与村民签订了搬迁购房合同，上访村民认为，政府与他们签订的合同中，同步搬迁户与建档立卡搬迁户的购房费差别太大，同步搬迁户相当于直接购买商品房，每户120平米23万元，建档立卡户只要几万元，引发80余户同步搬迁户的不满，认为房价太高，无法承受，因向当地政府交涉要求降低房价未果所以到省政府上访，要求上级政府帮助协调房价问题。信访局工作人员接待中，现场秩序平稳。\r\n\r\n\r\n', '2017-10-17 09:15:00', NULL, NULL, 5, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:58:50', '2017-10-20 14:30:00', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'afabc4a4-0b87-4eaa-9929-50d1fded8570', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (360, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-17 11:41:55', '云南冶金集团新丰幼儿园教职员工6人到省信访局上访', '17日9时15时许，西山区新丰幼儿园（原云南冶金集团幼儿园，地址位于西山区马街春雨路）教职员工6人到省政府，反映今年8月份，幼儿园被冶金集团以危房改造为由强拆，教职工没有工作，给幼儿园也造成了经济损失，要求政府给教职工安置工作。该起群众今年已多次就相同诉讼到省政府上访过，因一直没有解决所以今日再次上访。经省信访局工作人员接待，于10时15分许离开，过程平稳。（华山所）\r\n\r\n\r\n', '2017-10-17 09:15:00', '2017-10-17 10:15:00', 1.00, 6, '拆迁补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:58:50', '2017-10-20 14:25:46', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'zhfx', 0, 0.00, 0, 0, '0', NULL, '综合分析', 'c34b2f63-ab78-4802-b1c2-269aecfa5a92', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (361, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-17 14:30:56', '17日在临沧市做工的农民工7人因工钱被拖欠到省政府上访', '   17日10:15分许，7名在临沧市临翔区参与修建中翔公路开发建设项目的农民工到省政府上访，反映，他们于今年3月份开始进场修路，由于项目部一直拖欠工钱，到今年六月份工程停工。涉及200余名工人，600余万元工钱，后民工一直向有关部门反映要求拿到工钱未果，所以到省政府上访，要求尽快拿到被拖欠的工钱。省信访局接待后告知其向临沧市反映诉求。10:45分许，7人全部离开省政府，上访过程平稳。（华山所）\r\n', '2017-10-17 10:15:00', '2017-10-17 14:45:00', 4.50, 7, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 14:58:50', '2017-10-20 14:17:39', NULL, '农民工', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', 'b8ae7e13-7169-4e8d-bcfe-4590e4bcc121', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (364, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-15 13:09:57', '20余人聚集西城时代购物中心负一层讨要赔偿', '10月14日13时许,玉溪男子周业东（29岁）等20余人持一条“西城COHO商场视人命如草芥不闻不问！良心何在！”白底黑字横幅，一块“老人商场消费中被致成植物人！西城COHO商场视人命如草芥！”黑底白字带照片塑料牌，聚集西城时代负一层me', '2017-10-14 13:00:00', NULL, NULL, 20, '损害赔偿 ', '海源所', '西城时代', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 15:08:21', '2017-10-20 15:16:01', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '西城时代', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2790, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '61a3b0ba-172f-4d87-9800-ad075970b805', 'JS', NULL, 'Y', 110, NULL, NULL, '530102610000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (365, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-10-15 13:33:19', '10月15日部分“中天城，天阔”购房户到小区物管聚集的情况', '    据工作掌握，部分“中天城.天阔”购房户于10月15日9时许部分“中天阔.天城”购房户约30余人到小区物业门前聚集，就经常停电、垃圾乱扔环境差、车库施工进度、修路、绿化、保安员个人素质，小孩上学等相关情况进行实地考察、咨询。INSERT INTO `qb_zdasjqbxx` VALUES (367, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-10-16 14:27:57', '云南开放大学20余名教师持横幅聚集校门前要求退还购房款', '16日10时26分许，20余名教师持“开大还我教师血汗钱——300余名教师购房被骗7年”黑底白字横幅，聚集在学府路云南省开放大学（原云南省广播电视大学）门前。经工作，上述人员于11时撤除横幅，进入校会议室与校方、开发商进行协商。\r\n\r\n据了解，2010年学校为解决教师住房问题，与云南神州天宇职业有限公司合作，以6600元每平米价格团购“羊肠小村城中村改造项目”，每套房预交20万元团购费。后因拆迁受阻、市场波动等因素该项目未能如期开发完成。经协商，开发商已退还237名教师购房预交款，现尚余343名教师预交款未退还。\r\n\r\n相关情况进一步掌握中。\r\n\r\n\r\n', '2017-10-16 10:26:00', NULL, NULL, 20, '逾期未交房', '莲华所', '云南省开放大学', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-17 15:08:50', '2017-10-20 16:16:49', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '云南省开放大学', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2632, NULL, NULL, NULL, 'abxx', '安保信息', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'adfe3b81-ba63-49bb-a446-5414327b85b8', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (369, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-18 18:12:35', '南磷集团东磷经贸有限公司拖欠货运款引发百余名货车司机滞留不安定隐患凸显', '10月17日，因南磷集团拖欠曲靖交通集团物流有限公司运输款，引发100余名货车驾驶员到位于南屏街顺城东塔15A的云南南磷集团股份有限公司旗下的东磷贸易有限公司办公室聚集。当天，虽经新村所组织协商，东磷贸易有限公司提出每人先支付3000到10000元不等的运输款，而后再筹集资金支付尾款，但仅有部分货车司机同意，剩余40余名货车司机通宵滞留在该公司。\r\n18日10时许，现场滞留大车司机增加至50余人。虽经新村所民警开展工作，15时许聚集人员减少至30余人，但其中部分人员拿出事先制作好的3块横幅式布标准备维权使用，被民警当场制止。之后，大观司法所工作人员到场对滞留人员进行了司法宣讲，但宣讲过后，货车司机仍没有离开，称要等东磷贸易有限公司的上级南磷集团做出满意的答复才会离开。截止上报时，现场仍滞留30余人，不安定隐患突显。关注续报中。（国保大队）\r\n\r\n\r\n', '2017-10-18 10:30:00', NULL, NULL, 50, '索薪讨款', '新村所', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 16:26:22', '2017-10-20 10:52:15', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'cda71b6f-d799-4a65-9ed9-e3c4e626c25f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (370, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-19 11:09:01', '18日镇雄县大水溪煤矿工人8人因工资被扣再次到省政府上访', '19日09:30分许，8名昭通市镇雄县武待镇大水溪煤矿工人到省政府上访，反映，他们均是河南籍人员，是该煤矿正式工人，上月一同辞职回乡，但煤矿扣了他们8人两个月的工资，因多次向煤矿讨要以及向各级政府反映要求拿到工资未果所以到省政府上访，要求尽快拿到被扣工资。该批群众于昨日（10月18日）已就相同诉求到省信访局来访过，由于没有解决今日再次上访。省信访局接待后边告知其向劳动监察部门反映。10:50分许，8人离开信访大厅，在武警操场处聚集，目前昭通市政府工作组正在接待他们。\r\n', '2017-10-19 09:30:00', NULL, NULL, 8, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 16:26:22', '2017-10-20 10:46:45', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'qsydw,cz', '企事业单位,城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '2', 'qkfy', 8, 0.00, 0, 0, '0', NULL, '情况反映', 'bea6d1f8-effb-40f9-8532-8a89e7c56270', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (371, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-19 14:29:08', '19日南磷集团东磷经贸有限公司拖欠货运款司机37人仍滞留公司办公室', '截至10月19日12时许，讨要运输款的37名货车司机仍滞留顺城东塔15A南磷集团办公，双方仍在协商，已通知曲靖交通集团物流有限公司相关领导下午到场开展货车司机劝解疏导工作，维稳工作正常开展中，相关情况进一步掌握中。（新村所）\r\n\r\n\r\n', '2017-10-19 12:00:00', NULL, NULL, 37, '索薪讨款', '新村所', '顺城东塔15A南磷集团', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 16:26:22', '2017-10-20 10:41:41', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '顺城东塔15A南磷集团', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2668, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'sldws', '16-50人', '2', 'qkfy', 37, 0.00, 0, 0, '0', NULL, '情况反映', '0e8a2376-a30a-4338-b6cd-7c282614e301', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (372, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-19 14:47:00', '9月19日金座集资户例行聚集活动情况', '据工作掌握，9月19日9时30分许，以纪昌凤、罗益民为首的金座集资户群体到市体育馆上访。昆明市农业局及市金融办的相关领导接访。由于金座集资户代表不满意市农业局安排的工作人员接访，市金融办临时通知西山区政府的相关领导接访。接访中，集资户代表反映昨天到西山区政府递交困难补助申请时与保安人员发生的争执，所以今天金座集资户代表要求昨天的当事保安人员到现场来道歉，随后保安公司出具两名保安人员被辞退的书面材料后，此事才了结。直到中午13时10分许，上述人员才陆续自行离开市体育馆。今天到场人数在130余人左右。另外，南屏街小广场的聚集集资户自行取消，今日无人聚集。\r\n\r\n\r\n', '2017-09-19 09:30:00', '2017-09-19 13:10:00', 3.67, 130, '特殊群体', '茭菱所', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 16:26:22', '2017-11-30 15:08:58', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '693e3f69-2b9e-4a06-a023-7ef1187ccd26', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (373, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-19 14:57:08', '昆医附一院发生一起新生婴儿死亡的医患纠纷', '10月18日14时，寻甸县男子陈继永（35岁，汉族）等15人聚集昆医附一院投诉办讨要说法。\r\n\r\n据了解，10月16日10时许，陈妻峨山县妇杨丽花（32岁）怀孕9个月腹痛入住昆医附一院妇产科，当日12时30分进入手术室生产，15时30分院方告知家属所产男婴死亡，院方一直未给出死因。陈等亲属要求医院为杨丽花做试管婴儿或通过其他治疗手段保证其怀孕，院方称无法满足家属要求，死因要待尸体解剖。经工作，双方同意先暂封存病历。家属于20时许自行离开。\r\n\r\n\r\n\r\n\r\n', '2017-10-18 14:00:00', '2017-10-18 20:00:00', 6.00, 15, '医患纠纷 ', '大观所', '昆医附一院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 16:26:22', '2017-10-20 10:32:00', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆医附一院', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2682, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '85a094af-8a3e-4742-8161-b6950d70e301', 'JS', NULL, 'Y', 110, NULL, NULL, '530102480000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (374, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-17 17:56:27', '云南交通技师学院2014级一男生实习期间猝死存在隐患（续报）', '\r\n\r\n14日10时25分许，在滇缅大道比亚迪4S店前台实习的，云南交通技师学院2014级新能源汽车维修专业一班在校学生，李涵（男，19岁，大理人）突然晕倒，120送昆医附二院抢救无效，于当日12时55分死亡，医院给出初步死因为猝死，遗体已移至太平间。近日，死者父母李灼普（男，50岁）、李金兰（女、45岁）等30余名亲属闻讯后陆续从大理赴昆，由学校及比亚迪4S店安排在滇缅大道801酒店食宿。\r\n\r\n15日至16日，学校、比亚迪4S店虽与家属四次协商，但家属认为22万元的补偿费低，声称此事件若得不到满意解决，将把遗体抬学校、公司，并在网络传播消息，扩大影响。现家属吃住在酒店，暂无过激行为\r\n\r\n相关情况密切掌握中。\r\n\r\n\r\n', '2017-10-14 10:25:00', NULL, NULL, 60, '损害赔偿 ', '虹山所', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 16:28:09', '2017-10-20 11:32:30', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'mjjf', '民间纠纷', NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'wsyr', '51-100人', '2', 'yj', 60, 0.00, 1, 0, '0', NULL, '预警', '374b6e82-031a-4af2-80ae-c80e302cc829', 'JS', NULL, 'Y', 110, NULL, NULL, '530102410000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (377, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-18 09:42:58', '10月17日60余人到顺城东塔15A讨要货款续报', '经协商，南磷集团提出先向货车司机每人支付3000到10000元不等运输款，而后在筹集资金支付尾款，部分货车司机同意此支付方式便自行离开，截至18日1时30分许，不同意此支付方式的司机仍有40余人滞留公司办公室。为确保稳妥，新村所安排6名警力在场防止事态激化升级，截止18日早9时30分，40余名货车司机仍然滞留公司办公室，密切关注中。（新村所）\r\n', '2017-10-18 09:30:00', NULL, NULL, 80, '索薪讨款', '新村所', '顺城东塔15A南磷集团', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 16:28:09', '2017-10-20 11:17:38', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '顺城东塔15A南磷集团', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2668, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'wsyr', '51-100人', '2', 'qkfy', 80, 900.00, 0, 0, '0', NULL, '情况反映', '9ad70d31-d921-493c-b30c-5847819234de', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (378, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-18 09:59:19', '18日镇雄县大水溪煤矿工人8人因工资被扣到省政府上访', '18日08:35分许，8名昭通市镇雄县武待镇大水溪煤矿工人到省政府上访，反映，他们均是河南籍人员，是该煤矿正式工人，上月一同辞职回乡，但煤矿扣了他们8人两个月的工资，因多次向煤矿讨要以及向各级政府反映要求拿到工资未果所以到省政府上访，要求尽快拿到被扣工资。现来访人员已有工作人员接待，秩序平稳。（华山所）\r\n', '2017-10-18 08:35:00', NULL, NULL, 8, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 16:28:09', '2017-10-20 10:58:02', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '2', 'qkfy', 8, 0.00, 0, 0, '0', NULL, '情况反映', '92f80ff8-a078-4b20-a2e2-a3ed9a49668f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (380, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-10-17 18:01:12', '五华分局对学府路农贸市场商户违法维权行为进行现场处置', '由于学府路农贸市场负责人陈友国未按照约定前来退款，引起现场二十余名商户不满。10月17日14时许，部分商户涌入学府路机动车道旁，并拉起长约5米布标，内容为“社会主义核心价值观，请政府主持公道”，造成学府路道路交通收到影响。\r\n\r\n后五华分局莲华派出所等部门对商户上街维权行为及时进行处置，并将现场行为过激的陈德良（男，汉族，1963年5月16日，四川自贡市人，身份证号：510322196305161932），赵思靠（男，汉族，1982年3月24日，云南宣威人，身份证号：53222419820324371X），徐丽蓉（女，汉族，1968年9月5日，云南宣威人，身份证号：532224196809050569），张志纯（男，汉族，1980年5月27日，云南大理人，身份证号：532924198005270037）四人依法进行传唤。\r\n\r\n之后莲华街道办事处等部门工作人员对其余商户进行了座谈，并表示政府方面正在寻求解决办法，会采取引进新的投资方的模式解决商户问题。随后意向性投资人也表态发言称：如果顺利协商、接收顺利，将会于本月底退赔商户剩余补偿款。\r\n\r\n在场的商户对答复满意，表示愿意等待，并于17时许全部离开农贸市场。\r\n\r\n相关情况进一步收集掌握中。\r\n', '2017-10-17 14:00:00', NULL, NULL, 20, '索薪讨款', '莲华所', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 16:56:22', '2017-10-20 11:26:39', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '04d264b2-bac2-4de2-9fac-f39efe32e042', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (381, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-18 08:40:49', '10月17日部分我市“儒商”和“保利贷”、“光辉”等涉金融受损群体成员到省金融办上访情况', '17日9时30分许近50名我市“儒商”和“保利贷”、“光辉”等涉金融受损群体成员到云南省金融办聚集上访，省金融办和官渡区经侦大队及经开区经侦大队的相关工作人员进行了接访，接访过程中，未发生喊口号、打标语等过激行为，12时许，接访结束，上述人员陆续离开现场。相关情况在进一步掌握中。\r\n', '2017-10-17 09:30:00', NULL, NULL, 0, '特殊群体', '外区', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 16:56:22', '2017-10-20 11:23:03', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'f37c1bab-1382-4a4c-9920-da8343f0db59', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (383, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-18 09:59:19', '18日镇雄县大水溪煤矿工人8人因工资被扣到省政府上访', '18日08:35分许，8名昭通市镇雄县武待镇大水溪煤矿工人到省政府上访，反映，他们均是河南籍人员，是该煤矿正式工人，上月一同辞职回乡，但煤矿扣了他们8人两个月的工资，因多次向煤矿讨要以及向各级政府反映要求拿到工资未果所以到省政府上访，要求尽快拿到被扣工资。现来访人员已有工作人员接待，秩序平稳。（华山所）\r\n', '2017-10-18 08:35:00', NULL, NULL, 8, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 16:56:22', '2017-10-20 11:15:35', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '2', 'qkfy', 8, 0.00, 0, 0, '0', NULL, '情况反映', '92f80ff8-a078-4b20-a2e2-a3ed9a49668f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (384, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-17 15:00:01', '10月17日60余人到顺城东塔15A讨要货款', '10月17日12时19分许，新村所接110指令：南屏街顺城东塔15A南磷集团，有五六十人来讨要货款。\r\n\r\n接警后，民警立即赶往现场，现场约60 余人，经了解，，因南磷集团拖欠曲靖交通集团物流有限公司运输货款（2014年至今600万运费、驾驶员运输费1400万）而引发聚集讨要货款。据掌握，今年7月27日、28日到南磷集团讨要过货款，双方经协商后达成协议，云南南磷集团及分公司东磷经贸有限公司先付曲靖交通集团物流有限公司50万元，剩余的1950万元后续支付，7月28日已支付50万元。今日该公司的60 余人再次到公司讨要货款。）（新村所）\r\n', '2017-10-17 12:19:00', NULL, NULL, 60, '索薪讨款', '新村所', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 17:02:15', '2017-10-20 14:12:05', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'wsyr', '51-100人', '2', 'qkfy', 60, 1950.00, 0, 0, '0', NULL, '情况反映', 'f11b9c3a-f25f-471f-9236-e7444b4caa77', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (385, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-17 15:01:47', '水电十四局退休教师6人因不满身份待遇问题到省政府上访', '17日11:05分，6名退休教师到省政府上访，反映，他们均是中国水利水电第十四工程局（驻云南省）在全省各地自办中小学校的退休教师，退休时十四局没有认可他们的教师身份，按照企业职工待遇办理了他们的退休手续，据称大概涉及1000余人。他们认为，企业的做法不合理，教师不是工人，应该按照国家教师身份给予其待遇，多年来他们一直向省国资委、省教育厅反映要求落实身份未果，所以到省政府上访，要求十四局认可他们的教师身份，按照退休教师的级别享受待遇。该起来访群众已多次就相同诉求到省政府来访过，因一直没有解决，所以再次来访。省信访局接待后告知其继续向相关职能部门反映诉求。11:25分许6人全部离开省政府，上访过程平稳。（华山所）\r\n\r\n\r\n', '2017-10-17 11:05:00', NULL, NULL, 6, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 17:02:15', '2017-10-20 14:10:12', NULL, '退休职工', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '1', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', '9dd30571-7eb5-4b8e-99a2-0c2ce84434a0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (386, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-17 15:04:53', '10月17日金座集资户到市体育馆聚集上访的情况', '据工作掌握，10月17日9时30分许，以纪昌凤、罗益民为首的金座集资户群体到市体育馆聚集上访。昆明市工商局及市金融办的相关领导接访。主要是责问昆明市工商局在对金座案件中应该承担什么样责任的问题。今天到市体育馆的金座集资户有130余人左右。\r\n\r\n\r\n', '2017-10-17 09:30:00', NULL, NULL, 130, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 17:02:15', '2017-10-20 11:43:32', NULL, '金座', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '5495df67-7d28-4bd4-aed8-095fda45d116', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (387, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-10-17 16:37:54', '昆明恒安电力公司投资人6人因投资资金被冻结到省政府上访', '   17日15:50分许，6名昆明恒安电力公司投资人到省政府上访，反映，他们大部分是云南南方电网集团公司的职工，昆明恒安电力公司系南方电网的下属公司，多年来该公司一直发行多种理财产品，由于利息较高，信誉好，大部分购买者均是内部职工，2015年2月，昆明恒安电力公司发行新理财产品“展瑞新富绿色能源岔河水电私募基金”，期限两年，利息百分之12,100万元起卖。来访人员称，约100余人参与，募集资金6000余万元。今年2月产品到期，投资人却未能按期拿到本金及利息，引发投资人不满，多次向总公司询问未得到结果所以到省政府上访，要求上级政府出面解决。省信访局接待后告知其向金融监管部门和云南南方电网总公司反映。16:10分许，6人全部离开省政府，上访过程平稳。（华山所）\r\n', '2017-10-17 15:50:00', '2017-10-17 16:10:00', 0.33, 6, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 17:02:15', '2017-10-20 11:38:08', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'sryx', '10人以下', '1', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', 'd2ec7faa-5581-4534-8720-ee450b093467', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (388, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-10-17 15:16:37', '学府路农贸市场租户因退还租金问题到市场聚集', '2017年10月17日10时20分许，20余名学府路482号农贸市场商户因退还租金问题到农贸市场市场管理办公室聚集。因市场负责人陈国友未出面解决退款问题，且无法联系到该人，聚集的商户于11时30分许散去，并邀约下午继续到市场聚集。\r\n\r\n14时30分，约20名商户再次聚集在农贸市场入口，拉起长约5米，白底黑字社会主义核心价值观，请政府主持公道！！！”横幅，因农贸市场已关停，该行为未影响行人及车辆通行。莲华派出所民警已将横幅收缴，并带回四名拒不配合人员到所调查。其余人员已劝至市场办公室，由办事处工作人员接待，关注中。（莲华所）\r\n', '2017-10-17 10:20:00', NULL, NULL, 20, '合同纠纷 ', '莲华所', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 17:02:15', '2017-10-20 11:40:17', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '546d3ba4-509b-4be1-a46e-5de81bedbae5', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (389, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-15 17:24:58', '比亚迪4S店实习学生李涵非正常死亡后续情况', '10月14日13时10分，虹山派出所接110指令称比亚迪4S店内有人晕倒，送到工人医院抢救无效死亡。接到指令后虹山所高度重视，立即指派18号车赶往现场进行处置。\r\n\r\n民警到达现场找到报警人赵寅（性别：男，民族：白族，身份证号：53293019861001213X，户籍地：大理洱源县，现居住址：昆明市泰源新居8栋2单元702室，联系电话：13629432932）了解到，赵寅堂弟李涵（性别：男，民族：白族，身份证号：532930199803072117，户籍地：云南省大理白族自治州洱源县凤羽镇振兴村民委员会四组 ，系位于安宁市宁湖西路6号的云南交通技师学院2014级新能源汽车维修专业一班在校学生）于2017年2月18日由学院派至滇缅大道迪坤比亚迪4S店进行实习，于2017年10月14日10时25分在比亚迪4S店前台上班时突然晕倒，后被120送到昆医附二院抢救，于12时55分在院抢救无效死亡，医院给出初步死亡原因为猝死，但报警人觉得其堂弟岁数小，并且额头有外伤，对死因有异议，于是报警。尸体已移送太平间，死者家属已接通知从大理赶往昆明，民警已告知报警人如家属对死者死亡原因有异议，可申请尸检。\r\n\r\n截止信息上报时。死者李涵家属陆续到达昆明，由云南交通技师学院和迪坤比亚迪4S店安排家属入住滇缅大道801酒店，目前已确定约21名家属入住酒店305、307、311、313、403、407、409、535房间。\r\n\r\n到昆主要家属情况：\r\n\r\n1、李灼普（男，50岁，死者父亲）\r\n\r\n2、李金兰（女、45岁，死者母亲）\r\n\r\n3、李灼果（男、43岁，死者三叔）\r\n\r\n4、李灼葵（男、39岁，死者四叔）\r\n\r\n经了解，目前云南省交通厅已介入，云南交通技师学院、迪坤公司也在积极配合善后工作中，家属方表示一是要求比亚迪给予合理赔偿（未说具体数额）；二是要求将李涵尸体运回大理洱源县。学院与迪坤公司计划于今日（15日）下午与家属进行商谈（具体时间未定）。\r\n\r\n另据李灼葵、李灼果等死者家属透露，如果达不到家属要求，将会采取把李涵尸体抬到迪坤比亚迪4S店，同时会再邀约更多家属到昆聚集，表达诉求。\r\n\r\n目前，学校、迪坤公司、家属都在酒店，暂未发生冲突，虹山派出所民警现场维持秩序，开展工作中。\r\n\r\n\r\n', '2017-10-14 13:10:00', NULL, NULL, 21, '损害赔偿 ', '虹山所', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 17:03:54', '2017-10-20 15:09:00', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'mjjf', '民间纠纷', NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'sldws', '16-50人', '2', 'qkfy', 21, 0.00, 1, 0, '0', NULL, '情况反映', '43df3ad0-77d9-42e5-87b1-8f44fb1f4579', 'JS', NULL, 'Y', 110, NULL, NULL, '530102410000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (395, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-05 13:00:07', '20余名民工聚集五华区棚户区改造工程工地讨要劳务费', '5日10时许，湖北新兄弟劳务公司民工湖北大悟县男子刘林华（50岁）等20余人，聚集在金川路与王筇路交叉口五华区棚户区改造工程工地讨要劳务费，并拉下工地电闸，堵在升降吊机前。据了解，刘等30余名民工负责该工地内外墙粉刷工程，前期劳务公司陆续支付了58万元劳务费，现尚拖欠142万元劳务费。接报后，分局及时将聚集民工及劳务公司负责人带至王家桥所，会同五华区劳动监察大队组织双方调解，相关情况密切掌握中。\r\n\r\n\r\n', '2017-10-05 10:00:00', NULL, NULL, 20, '索薪讨款', '王家桥所', '五华区棚户区改造工程工地', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 17:11:08', '2017-10-20 17:05:05', NULL, '农民工', NULL, '1', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '五华区棚户区改造工程工地', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2784, 2519, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '1', NULL, '情况反映', 'ed1cdf57-f610-4021-b574-195916d00a2c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102580000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (397, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-09-28 17:46:40', '西翥办事处沙朗乡白龙山矿业公司采石场与白岩子村村民纠纷存在不安定隐患', '白龙山矿业公司在我区西翥办事处沙朗乡开采沙石，9月25日，距离采石场6公里远的白岩子村15名村民开一辆微型车将通往采石场的唯一道路堵断，导致拉运沙石的七八辆大货车无法通行，后采石场人员报警至我局沙朗派出所。接警后，民警赶到现场，据白岩子村村民称：白龙山矿业公司采石多年放炮，致使村民自建房屋不同程度的震裂，要求该公司赔偿修缮，村民因多次找公司协商未果，故堵路讨要说法。白龙山矿业公司负责人则称：公司开山炸石8年，距离采石厂最近的菖蒲塘村村民从未因房屋震裂讨要过赔偿。经民警调解，当日白岩子村村民自行离开了现场。\r\n\r\n9月28日9时许，20余名白岩子村村民再次到采石场聚集，后采石场负责人报警至沙朗所，民警到达现场，劝解双方合法维权，建议村民对受损房屋进行评估后通过法律途径解决诉求。后西翥办事、村委会组织双方调解。目前，白岩子村村民连续两次聚集堵路，不安定隐患明显，建议办事处加强化解，防止村民与采石场矛盾升级影响我区稳定。\r\n\r\n\r\n', '2017-09-28 09:00:00', NULL, NULL, 20, '损害赔偿 ', '沙朗所', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 17:12:50', '2017-10-20 17:16:35', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2507, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '6f514a47-fd95-4275-bab7-37dbf67ec7bb', 'JS', NULL, 'Y', 110, NULL, NULL, '530102590000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (399, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-09-27 10:29:20', '五华区西翥桃园一号地块项目拆迁因补偿问题存在维稳隐患', '2017年4月，为进一步加快西翥桃园片区产业项目落地和建设，五华区政府《发布关于西翥桃园一号地块项目拆迁公告》，开始对轿子雪山旅游专线西侧桃园小村一号地块项目的征地拆迁工作。\r\n\r\n随着征地拆迁工作的进行，部分桃园小村村民和拆迁征地范围内涉及的私营产业经营户因对征地拆迁补偿不满，多次到办事处和区市政府上访。其中，部分自称系五华区招商引资及产业投资较大的经营户不满情绪较严重，扬言，如不公平公开解决，将会继续到政府部门集访或抵制拆迁工作。\r\n\r\n一、涉及拆迁人员动态情况。\r\n\r\n因多次到西翥办事处反映未果，部分桃园小村村民和拆迁征地范围内的经营户相互串联后，于9月13日到市政府上访，9月14日、15日、21日到五华区政府上访。\r\n\r\n二、上访人员主要诉求。\r\n\r\n1、部分村民签了拆迁协议至今未拿到补偿款，要求尽快发放拆迁补偿款。\r\n\r\n2、经营户主要要求对被拆迁的厂房给予经济补偿和搬迁补偿，部分经营户自称系西翥办事处2012年招商引资项目，当时就未提醒需要办理改变土地用途等相关手续。\r\n\r\n3、厂房系自己投资大量资金建盖，如果不合法，在建盖时政府相关部门为什么不来制止，建盖好那么多年也没有政府工作人员来管，经营户要求，在没有达成拆迁协议之前政府相关部门不能强制拆迁其厂房。\r\n\r\n4、不能搞双重标准，要求政府公示一号地块上所有村民的住宅和经营户是否在建造时有合法手续，只要政府公平、公证的对待村民和厂房主，厂房主愿意配合拆迁。\r\n\r\n5、2012年以前建造的住宅和厂房给予补偿，以后建造的没有合法手续的住宅和厂房不予补偿的依据是什么？\r\n\r\n目前，部分村民和经营户对拆迁补偿规定不满，尤其是投资了大量资金的经营户对政府的拆迁补偿规定不满情绪较为严重，结合摸排情况分析：下一阶段，此部分厂房主的诉求得不到合理解决，涉及业主继续到省市区等政府相关部门上访，或不配合拆迁的情况极有可能发生。\r\n\r\n\r\n', '2017-09-21 09:00:00', NULL, NULL, 0, '拆迁安置', '沙朗所', '西翥桃园一号地块', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-19 17:13:50', '2017-10-20 17:20:27', NULL, NULL, NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '西翥桃园一号地块', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2786, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '3c42e074-9972-4140-ae2f-3f795c1ecbf0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102590000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (409, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-10-20 13:50:47', '宣威市文兴乡村民7人因不满交警执法问题到省政府上访', '   20日10:25分许，7名宣威市文兴乡着期村委会梨树林村村民到省政府上访，反映，今年10月10日，其亲属5人搭乘一辆面包车回家途中，路遇交警堵卡，不知什么原因，面包车司机突然掉头开往另一方向，途中与一辆大货车相撞，面包车司机及来访者亲属2人当场死亡，另外3人受伤。当地交警部门的责任认定是大货车负主要责任，面包车负次要责任。来访人员认为，当地交警在处理事件的过程中存在不规范行为，导致死伤者的赔偿问题一直得不到解决，由于向宣威市相关部门反映投诉未果，所以到省政府上访。投诉当地交警部门执法不规范的行为。省信访局接待后告知其向公安厅反映。10:30分许，7人全部离开省信访局。上访过程平稳。\r\n', '2017-10-20 10:25:00', '2017-09-30 10:30:00', 479.92, 7, '交通事故 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-20 17:24:51', '2017-10-20 17:30:17', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'xszaajdwt', '刑事治安案件等问题', 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '432d631b-e8cf-4b83-9aff-ea5d33b9d48b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (410, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-20 14:07:46', '20日曲靖交通集团物流有限公司与南磷集团东磷贸易有限公司运输款拖欠纠纷部分化解', '10月20日上午，为进一步化解曲靖交通集团物流有限公司货车驾驶员与南磷集团东磷贸易有限公司运输款拖欠纠纷，分局局长助理孔翊平带领治安大队赶到现场，与曲靖市人民政府信访局、大观街道办事处综治办负责人等再次组织货车司机与公司协商，截止20日12时30分许，现场还有15人滞留，现场秩序正常，密切关注中。（新村所）\r\n', '2017-10-20 09:00:00', NULL, NULL, 15, '索薪讨款', '新村所', '顺城东塔15A南磷集团', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-20 17:24:51', '2017-10-20 17:28:27', NULL, NULL, NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '顺城东塔15A南磷集团', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2668, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'syr', '11-15人', '2', 'qkfy', 15, 0.00, 0, 0, '0', NULL, '情况反映', 'fb634655-68f0-4f0a-8369-8a9e91e34295', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (411, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-20 14:52:35', '10月20日因索薪讨款问题12名农民工到云南饭店工地聚集', '前期，云南饭店整体改扩建项目工程由云南世博旅游控股集团有限公司下属云南云旅房地产开发有限公司开发建设，云南省建设投资控股集团有限公司下属中国有色金属工业第十四冶金建设公司承建。承建公司中国有色金属工业第十四冶金建设公司称云南云旅房地产开发有限公司拖欠该公司4900万元的工程款，其公司又欠分包的四川吉祥建筑劳务有限公司3000万工程款，为此公司无钱支付四川吉祥建筑劳务有限公司工程款（该公司主要完成室内装潢），导致四川吉祥建筑劳务有限公司无法支付工人工资。因此引发四川吉祥建筑劳务有限公司多次聚集云南饭店门口拉横幅聚集维权讨款。\r\n\r\n因此款项纠纷一直未能得到有效化解，10月20日上午，四川吉祥建筑劳务有限公司的负责人严守金带11名工人，再次到龙井街云南饭店建筑工地门口讨要工程款，项目方也派员在现场协商，现场秩序正常。新村所民警到场开展维稳工作，采集了现场聚集工人信息，开展合法维权法制宣传，目前双方仍在协商中。\r\n\r\n\r\n\r\n\r\n\r\n附：现场四川吉祥建筑劳务有限公司的工人人员名单\r\n\r\n及欠款情况\r\n\r\n费家义（身份证号：532101196609273426、联系电话：13708703265），拖欠工资6100元；\r\n\r\n洪文芝（身份证号：532101197904053425、联系电话：15288232579），拖欠工资4500元；\r\n\r\n洪文会（身份证号：53060219891016342X、联系电话：18314175843），拖欠工资5800元；\r\n\r\n龙须才（身份证号：532122196311142214），拖欠工资6100元；\r\n\r\n安永能（身份证号：532122196907272212、联系电话：15087219803），拖欠工资5890元；\r\n\r\n卢升凤（身份证号：532122197405202243、联系电话：15969016882），拖欠工资6500元；\r\n\r\n卢正秀（身份证号：532122197405202243、联系电话：13629620585），拖欠工资9600元；\r\n\r\n卢啟芬（身份证号：532101199306093421、联系电话：13577651103，拖欠工资5300元；\r\n\r\n张太莲（身份证号：532110197510113486、联系电话：17308802291），拖欠工资8500元；\r\n\r\n卢升琼（身份证号：530621199208142243、联系电话：18314492916），拖欠工资4800元；\r\n\r\n陆远巧（身份证号：532110197203104627、联系电话：18388056685），拖欠工资8500元。\r\n\r\n共计拖欠工资：71950元\r\n\r\n                                \r\n', '2017-10-20 09:00:00', NULL, NULL, 12, '索薪讨款', '新村所', '云南饭店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-10-20 17:24:51', '2017-10-20 17:26:44', NULL, '农民工', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '云南饭店', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2674, 2519, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'syr', '11-15人', '1', 'qkfy', 12, 7.20, 0, 0, '0', NULL, '情况反映', 'c7647f13-dfc1-443f-b511-268a173fc5eb', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', N