INSERT INTO `qb_zdasjqbxx` VALUES (413, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-10-31 10:30:35', '30余名岗头村村民到区政府上访', '10月31日上午9时许，岗头村村民30余人到五华区政府上访，主要反映的是拆迁补偿的问题，村民们认为拆迁补偿款过低，要求政府提高补偿款。现上访人员已由区政府信访办工作人员接待处理中。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-10-31 09:00:00', '2017-10-31 10:30:00', 1.50, 30, '拆迁补偿', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-14 18:31:36', '2017-11-15 16:19:05', NULL, '岗头村', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2508, 'dzjg', '党政机关', 'badys', '不安定因素', NULL, NULL, 'sf', '上访', 'nm', '农民', 'cscq', '城市拆迁', 'sldws', '16-50人', '1', 'qkfy', 30, 0.00, 0, 0, '0', NULL, '情况反映', '15e4d884-512c-4e77-9ae6-03b3c919479b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (414, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-10-31 11:15:27', '80余名金座集资人员到市体育馆聚集', '10月31日9时30分许，80余名金座人员自行聚集到市体育馆上访，昆明市公安局经侦支队相关工作人员在现场进行接访工作，全部上访人员情绪平稳，秩序良好，无过激行为。相关情况进一步掌握中。\r\n\r\n\r\n', '2017-10-31 09:30:00', '2017-10-31 11:30:00', 2.00, 80, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-14 18:31:36', '2017-11-15 16:09:30', NULL, '金座', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, NULL, NULL, NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'wsyr', '51-100人', '1', 'qkfy', 80, 0.00, 0, 0, '0', NULL, '情况反映', '65c7338e-cc73-4447-b627-ff163f565540', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (417, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-10-31 15:50:41', '10月31日部分岗头村民到省政府上访情况', '10月31日上午，30余名岗头村民到五华区政府上访的后，31日14时许，60余名岗头村村民再次到省政府上访，继续向省信访局反映，2009年，因修建西北三环道路工程需要，约200余户沿线岗头村村民房屋被拆迁，五华区政府给予了相应拆迁补偿。目前据了解得知，当时盘龙区政府给予的拆迁补偿明显高于五华区政府，要求按照盘龙区政府的补偿标准，补足不足部份，要求省政府协调五华区政府解决补足不足部分。14时30分许，接访结束，上述人员自行离开现场。相关情况在进一步掌握中。\r\n', '2017-10-31 14:40:00', '2017-10-31 14:30:00', 0.17, 60, '拆迁补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-14 18:31:36', '2017-11-15 16:01:02', NULL, '岗头村', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2508, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '05b6d45b-92ee-46f6-9852-40313f1ad91e', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
