INSERT INTO `qb_zdasjqbxx` VALUES (458, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-01 16:50:27', '11月1日昆百大集团珠宝公司职工60人到省信访局上访', '11月1日15时30分许，昆百大集团珠宝公司职工60人到省信访局上访反映：昆百大改制时，政府按相关政策划拨专项资金给符合条件的职工进行了身份置换补偿，部分集团职工2014年4月也拿到了补偿金，但昆百大集团珠宝公司符合条件的105名职工至今都未能拿到身份置换补偿金。现请政府协调相关部门处理。经接待，上述人员于16时10分许陆续离开，有返回南屏步行街继续聚集的迹象。相关情况密切掌握中。\r\n', '2017-11-01 15:30:00', '2017-11-01 16:10:00', 0.67, 60, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-14 18:46:55', '2017-11-15 15:53:28', NULL, '昆百大职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2516, 'dzjg', '党政机关', 'qtxsj', '群体性事件', NULL, NULL, 'sf', '上访', 'xgsyry', '下岗失业人员', 'qygzjbbc', '企业改制兼并破产', 'wsyr', '51-100人', '1', 'qkfy', 60, 0.00, 0, 0, '0', NULL, '情况反映', 'cbdce15e-313d-47f7-9d41-449933dffe38', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (459, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-02 12:01:42', '11月2日宣威市龙潭镇磨石村村民30人银不满房屋受损到省政府上访', '     2日09:30许，宣威市龙潭镇磨石村村民30人到省政府上访，反映从1998年起，该村200余户村民的房屋、水源地因受该村一煤矿的过度开采受到严重影响，导致村民房屋开裂，饮水困难，多年来村民一直向有关部门反映，村民称煤矿同意补偿村民250万元，现村民只拿到40万元，还剩余200余万元未兑现，由于一直得不到解决所以到省政府上访。要求受煤矿开采受损的房屋全部重建，剩余的200余万元尽快兑现，解决饮水问题。该批群众于昨日曾就相同诉求到省政府上访过，由于不满接待结果今日再次上访。省信访局接待后告知其回宣威市解决其诉求。因对接待结果不满，11时许来访人员离开信访大厅，其中26人现坐在省政府大门左侧人行道上,秩序稳定，华山所正密切关注。\r\n', '2017-11-02 09:30:00', '2017-11-02 11:00:00', 1.50, 30, '环境保护 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-14 18:46:55', '2017-11-23 13:45:31', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'sldws', '16-50人', '2', 'qkfy', 30, 0.00, 0, 0, '0', NULL, '情况反映', '341ac545-d965-4be8-b738-18c3fcce9e87', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (460, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-02 14:17:07', '11月2日省属企业退休教师7人到省信访局咨询工资待遇问题', '2日11:10分许，7名省属企业中小学退休教师到省信访局上访，反映2016年云南省财政厅下发了一份关于2016年国有企业中小学退休教师调整退休金待遇新增补助资金的文件，来访人员称，根据该文件精神，退休教师的工资中应享受三项补贴，目前领到其中两项，另外一项生活性补贴一直没有领到，他们多次向财政厅等多个有关部门反映，要求落实这个问题未果，所以到省政府咨询。省信访局接待后告知其继续向省财政厅以及省教育厅反映，11:25分许七人全部离开省政府。\r\n', '2017-11-02 11:10:00', '2017-11-02 11:25:00', 0.25, 7, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-14 18:46:55', '2017-11-15 15:48:54', NULL, '退休职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'jsxs', '教师学生', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '4994ea5d-37cb-4679-8f63-f0e9590f2e54', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (461, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-02 16:41:51', '11月2日部分金座集资户周四例行活动情况', '11月2日9时许，以罗玉民、季昌凤为首的金座集资户到市体育馆上访，昆明市中级人民法院的相关领导进行了接访，参加人数80余人。以卢福全为首的金座集资户将到南屏街中心广场例行聚集活动，参加人数5人。聚集期间以上人员未出现打标语、喊口号等情况，12时许聚集人员自行离开。\r\n\r\n\r\n', '2017-11-02 09:00:00', '2017-11-02 11:30:00', 2.50, 85, '特殊群体', '茭菱', '市体育馆、南屏步行街', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-14 18:46:55', '2017-11-15 15:46:23', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆、南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '77e79c15-038b-440a-b2f1-04d3fb70777e', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (462, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-03 10:02:42', '11月3日宣威市龙潭镇村民11人因不满房屋受损到省政府上访', '   11月3日09:10分许，11名宣威市龙潭镇磨石村村民因不满当地煤矿放炮采煤导致该村采煤房屋受损、水源地被污染等问题到省政府上访。该群体曾于10月31日到省委反映过，之后连续三天到省政府信访局反映诉求，因不满接待结果，11月1日、2日连续两天滞留省政府大门旁，每晚至20时左右才离开，今日再次到访，11人不进信访大厅，现聚集在省政府大门旁，目前现场秩序平稳，当地工作组已到场开展工作，密切关注中。（华山所）\r\n\r\n\r\n', '2017-11-03 09:10:00', '2017-11-03 11:00:00', 1.83, 11, '环境保护 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-14 18:46:55', '2017-11-15 15:41:58', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', NULL, NULL, NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'sryx', '10人以下', '2', 'qkfy', 11, 0.00, 0, 0, '0', NULL, '情况反映', '5ec16e08-1cf8-4cb1-8598-483c19992a6b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (463, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-11-03 15:54:37', '11月3日30余名岗头村村民到红云街道办事处讨要说法', '11月3日，月牙塘所民警获悉岗头村村民声称因拆迁补偿、过渡安置费拖欠问题要到红云街道办事处聚集讨要说法后，密切关注村民动向。3日10时许，30余名岗头村村民来到红云街道办事处表达诉求、讨要说法，红云街道办事处相关人员按规定给予村民作了答复（按第一次拆迁的方案执行），并将回迁房图纸给予村民查看，现场秩序良好，后村民于11时30分许离开办事处。\r\n\r\n\r\n', '2017-11-03 10:00:00', '2017-11-03 11:30:00', 1.50, 30, '拆迁补偿', '红云所', '红云办事处', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-14 18:46:55', '2017-11-15 15:38:32', NULL, '岗头村', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '红云办事处', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2686, 2508, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '24694fa3-d493-4a87-a69c-eb6b23351b6a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102490000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (464, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-01 11:08:55', '宣威市龙潭镇磨石村委会七组（包谷山村）30名村民到省信访局上访', '11月1日9时许宣威市龙潭镇磨石村委会七组（包谷山村）30名村民到省信访局上访反映：该村因地理交通不便，煤矿过度开采，饮水困难，缺乏像样的小学，导致山村贫穷无力自救，请求上级政府定点扶贫，现信访局正接待中。\r\n', '2017-11-01 09:00:00', '2017-11-01 11:30:00', 2.50, 30, '环境保护 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-14 18:47:18', '2017-11-15 15:57:20', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'dzjg', '党政机关', NULL, NULL, NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'sldws', '16-50人', '1', 'qkfy', 30, 0.00, 0, 0, '0', NULL, '情况反映', '5bc453f3-fe42-4591-8015-84c2d06d2b52', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (465, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-15 10:03:35', '11月15日沾益籍支前民兵8人因生活补助问题到省政府上访', '11月15日08:55分许，8名曲靖市沾益县原支前民兵到省政府上访，反映，他们均是上世纪80年代参加对越自卫反击战的支前民兵，主要任务是向老山前线运送军用物资，战争结束后返乡务农，2007年被当地政府认定为民兵。来访人员称，认定工作结束后，师宗、罗平等地的支前民兵均拿到了民兵生活补助，但沾益籍的民兵至今未拿到，涉及200余人。他们多次向有关部门反映要求拿到生活补助未果，所以到省政府上访，要求曲靖市政府一视同仁，按照师宗、罗平等地的标准给予沾益籍民兵生活补助。省信访局接待后告知其回曲靖市反映。09:15分许，8人全部离开省政府，上访过程平稳。（华山所）\r\n', '2017-11-15 08:55:00', '2017-11-15 09:15:00', 0.33, 8, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-15 17:06:43', '2017-11-15 17:08:55', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 8, 0.00, 0, 0, '0', NULL, '情况反映', '7c3b2558-b9f3-4964-a358-26bf49ae9490', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (466, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-15 10:09:58', '11月15日红河州弥阳镇村民9人因不满征地补偿到省政府上访', '11月15日09:15分许，9名红河州弥勒市弥阳镇占马村委会占马田村村民到省政府上访，反映，今年5月份，当地政府以土地流转的名义进行投资开发建设“太平湖森林生态公园”，流转期限40年，要征用他们12户村民的50余亩土地，政府给予村民的补偿是每平米106元，但村民认为，他们均是养鸡户，在土地上建盖了鸡舍，投入了大量资金，政府给的补偿款太低，村民要求按照每平米180元来赔偿。由于与当地政府协商未能达成一致，所以到省政府上访，要求政府提高补偿。现来访人员已有信访局工作人员接待，秩序平稳。（华山所）\r\n', '2017-11-15 09:15:00', '2017-11-15 11:45:00', 2.50, 9, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-15 17:06:43', '2017-11-15 17:11:30', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '2', 'qkfy', 9, 0.00, 0, 0, '0', NULL, '情况反映', 'f306f728-e3e6-4b64-80d8-1f047b6f39e7', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (468, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-15 13:42:47', '11月15日金座集资户到市体育馆聚集上访的情况', '	据工作掌握，11月15日上午9时30分许，以纪昌凤、罗益民为首的金座集资户群体到市体育馆上访。昆明市五华区、盘龙区、西山区、官渡区金融办的工作人员在市体育馆向申请一次性生活困难补助的部分金座集资户发放400元的补贴。今天参加涉金座的人员有130余人左右，到中午11时40分许，上述人员自行离开市体育馆。另据工作掌握，11月16日上午，上述人员拟继续到市体育馆参加例行的聚集上访，由昆明市金融办的相关领导接访。预计人数在130人左右。以卢福全为首的金座集资户拟到南屏街中心广场例行聚集活动，预计人数在20人左右。相关情况在进一步掌握中。（国保大队）\r\n', '2017-11-15 09:30:00', '2017-11-15 11:40:00', 2.17, 130, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-15 17:06:43', '2017-11-15 17:17:32', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'yj', 0, 0.00, 0, 0, '0', NULL, '预警', 'f47ffb1e-2dec-46f2-b8aa-0bb1cd47ae0a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (469, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-11-15 14:57:35', '11月15日昆明牙膏厂职工5人因商标侵权问题到省政府上访', '11月15日14:20分许，5名昆明牙膏责任有限公司（厂址：五华区青年路节孝巷12号）职工到省政府上访，反映,其工厂一直生产“云南三七”牌、“三七”牌牙膏，今年3月份，该厂原厂长私自将这两个商标卖给“诺特金参口腔护理用品有限公司”生产，来访人员称，这个公司在昆明无固定办公地点，其生产的牙膏是委托外省工厂生产，外包装上印有“昆明牙膏责任有限公司”字样，冲击市场份额，导致昆明牙膏厂生产的牙膏滞销，严重影响了200余名职工的收入。由于多次向相关部门反映，要求查处商标侵权未果，所以到省政府上访，要求政府出面维护职工利益。现来访群众已有信访局工作人员接待，秩序平稳。(华山所)\r\n\r\n\r\n', '2017-11-15 14:20:00', '2017-11-15 15:15:00', 0.92, 5, '企业改制/破产 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-15 17:06:43', '2017-11-15 17:19:26', NULL, '牙膏厂职工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2518, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'xgsyry', '下岗失业人员', 'qygzjbbc', '企业改制兼并破产', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '373c1167-ed29-4883-8c91-350486fee3fd', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (470, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-11-15 16:32:58', '15日20余名人民西路“壹号广场”购房户到区政府上访', '15日15时40分许，20余名人民西路“壹号广场”购房户到区政府上访，主要反映小区交房及小区配套设施不全问题，现区政府信访部门正在接访中，过程中上访人员无过激行为。翠湖所派警力到场，密切关注中。（翠湖所）\r\n', '2017-11-15 15:40:00', '2017-11-15 17:30:00', 1.83, 20, '交房问题 ', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-15 17:06:43', '2017-11-15 17:20:57', NULL, '壹号广场购房者', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2532, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'ee6fd523-1549-44d0-aec3-7f74e0e54f9b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (471, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-11-16 13:55:46', '11月16日170余名岗头村村民到红云办事处聚集的情况（续报）', '11月16日9时许，为要求提高拆迁补偿标准，陆续有170余名岗头村村民到红云办事处聚集上访，12时许，经红云办事处工作人员做工作后离开。上访过程平稳，未发生过激行为。\r\n\r\n2017年9月，岗头村社区城中村拆迁改造项目再次启动后，部分村民，特别是前期未签订拆迁协议的村民，为从中获取更多利益，开始相互串联维权，数次到省市区政府上访。因诉求暂未得到解决，该群体到省市区等政府部门的集访隐患仍存在，建议政府职能部门关注。相关情况在进一步掌握中。（国保大队）\r\n', '2017-11-16 09:00:00', '2017-11-16 12:00:00', 3.00, 170, '拆迁补偿', '红云所', '红云办事处', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-16 14:54:28', '2017-11-24 14:56:44', NULL, '岗头村', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '红云办事处', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2686, 2508, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'b4f8f2ac-a417-4cc2-a5e8-de1267fa46a8', 'JS', NULL, 'Y', 110, NULL, NULL, '530102490000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (472, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-16 13:38:45', '11月16日西山区“老鸦营”村民5人因不满过渡费未发放到省政府上访', '11月16日11:25分许，5名西山区“老鸦营”11号片区拆迁户到省政府上访，反映，永昌街道办事处拖欠该片区100余户拆迁村民今年一年的过渡安置费未发放，要求上级政府督促。省信访局接待后告知其向昆明市反映。11:30分许，5人全部离开省政府。（华山所）\r\n\r\n\r\n', '2017-11-16 11:25:00', '2017-11-16 11:30:00', 0.08, 5, '拆迁补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-16 14:54:28', '2017-11-23 13:44:09', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'cscq', '城市拆迁', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'bc8a86bf-4638-40f0-9296-1c2ccd667293', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (473, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-16 13:36:27', '11月16日金座集资户周四例行聚集到市体育馆上访的情况', '据工作掌握，11月16日上午9时30分许，以纪昌凤、罗益民为首的金座集资户群体到市体育馆上访。昆明市金融办的相关领导接访。现场主要是解决一名属于经济开发区的金座集资户的生活困难补助一事以及今后继续向政府相关职能部门追责的问题。今天参加涉金座的人员有130余人左右，中午12时30分许，上述人员自行离开市体育馆。11月16日8时30分许，以卢福全为首的金座集资户8人到南屏街中心广场聚集，聚集过程中未发生喊口号、打标语等过激行为。上午10时40分自行离开现场。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2017-11-16 09:30:00', '2017-11-16 12:30:00', 3.00, 138, '特殊群体', '茭菱所', '市体育馆、南屏步行街', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-16 14:54:28', '2017-11-23 10:41:51', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆、南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '8c720014-6652-4e79-a14c-7f4a668c7dfb', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (474, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-16 10:33:04', '11月16日镇雄籍原消防公安兵6人因生活补助问题到省政府上访', '11月16日09:50分许，6名昭通市镇雄籍人员到省政府上访，反映，他们均是1975年至1980年参加对越自卫反击战的公安消防兵，在1979年的对越自卫反击战中的主要任务是对前线的炮弹等装备进行运输和安保，退伍后回乡务农。2007年云南省民政厅对“两参”人员发放“云南省军队退役人员生活补助”，每月520元。来访人员称他们虽然不是解放军，但是生活补助的发放对象里也包括公安消防兵，并称大理、昭通市等地与他们一起入伍的消防兵已经领到了生活补助，而他们镇雄籍的118名人员没有领到，多次向相关部门反映未果所以到省政府上访，要求拿到退役生活补助。现来访人员正在信访大厅等待接待，现场秩序平稳。（华山所）\r\n', '2017-11-16 09:50:00', '2017-11-16 11:30:00', 1.67, 6, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-16 14:54:28', '2017-11-16 14:59:30', NULL, '对越参战老兵', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2524, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'tyaz', '退役安置', 'sryx', '10人以下', '2', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', '021ea862-5179-4922-ab6b-8b9e15fc52b6', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (475, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-16 10:20:16', '11月16日黑林铺“团山欣城”项目农民工5人因工钱被拖欠到省政府上访', '11月16日09:45分许，在五华区黑林铺“团山欣城”项目务工的农民工5人到省政府上访，反映，其300余名农民工于2015年2月在“团山欣城”务工，2016年初项目完工后，施工方“云南昆钢机械设备制造建安工程有限公司”和“重庆市南岸区六发建筑劳务有限公司云南分公司”拖欠300余名工人的工资共计700余万元，由于多次向有关部门讨要未果所以到省政府上访，要求尽快拿到工钱。现来访人员已有工作人员接待，秩序平稳。（华山所）\r\n\r\n\r\n', '2017-11-16 09:45:00', '2017-11-16 11:00:00', 1.25, 5, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-16 14:54:28', '2017-11-16 14:57:44', NULL, '农民工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '00f7ffab-87d5-45ba-89d9-7057a462d1cf', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (476, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-19 21:33:45', '11月19日高新区经典双城部分商铺业主到“华信悦峰”地产售楼部开展维权聚集（四）', '位于昆明市五华区滇缅大道西山大厦旁的“华信悦峰”房地产项目（曾用推广名“经典宽TOWN”），原为经典集团旗下云南沐荣欣成房地产开发有限公司所开发的“欣成创意家园”项目，后北京华信集团对经典集团进行重组，现该项目属北京华信地产管理。\r\n11月16日，约有40余名经典双城商铺业主在滇缅大道“华信悦峰”售楼部聚集，要求解决租金返还问题。\r\n11月17日上午09时许至17时30分，40余名经典双城商铺业主继续聚集在滇缅大道“华信悦峰”售楼部门口，要求解决租金返还问题，并在售楼部门口和外墙悬挂三条红底黄字，内容为：“还我血汗钱”“希望政府为我们业主讨回公道”“华信经典地产是骗子”的布标，15时“街头巷尾”的记者到达现场，记者在现场对经典双城商铺业主及“华信悦峰”售楼部人员进行采访。（详见昨日上报情况）\r\n2017年11月18日09时40分，约20余名经典双城商铺业主陆续聚集在滇缅大道“华信悦峰”售楼部门口进行维权活动，黑林铺所民警在现场执勤，10时30分现场维权业主聚集约50余人，并在售楼部外侧悬挂白底黑字标语三条，分别写有“希望政府为我们做主讨回公道”、“华信经典地产是骗子”、“还我血汗钱”等字样，同时使用一个扩音喇叭反复播放布标内容。17时许，现场围堵售楼部的业主自行散去。（详见昨日上报情况）\r\n2017年11月19日10时10分，约20余名经典双城商铺业主陆续聚集在滇缅大道“华信悦峰”售楼部门口进行维权活动，黑林铺所民警在现场执勤，10时30分现场聚集业主约30人，并在售楼部外侧悬挂白底黑字标语三条，分别写有“希望政府为我们做主讨回公道”、“华信经典地产是骗子”、“还我血汗钱”等字样，同时使用一个扩音喇叭反复播放布标内容。20时许，现场围堵售楼部的业主自行散去，并称2017年11月20日10时集聚40人左右继续围堵售楼部维权，另外其中5、6名业主将于2017年11月20日早上前往高新区管委会找相关部门反映此事。经民警了解经典双城铺面业主成立了一个微信群，其经典双城c区涉及人员微信群内有114人，每天到“华信悦峰”现场的人员有40余人，现场签到本上签到的人员有87人。现场人员扬言2017年11月20日将对“华信悦峰”售楼部旁的工地进出道路进行封堵，相关情况黑林铺所关注中。（黑林铺所）\r\n', '2017-11-19 10:10:00', '2017-11-19 20:00:00', 9.83, 40, '合同纠纷 ', '黑林铺所', '华信悦峰售楼部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-20 14:02:32', '2018-03-12 09:15:58', NULL, '个体户', NULL, '0', '2', '拉横幅，使用扩音器播放口号。', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '华信悦峰售楼部', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2777, 2547, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'd86129a9-3978-4618-9dc1-6301c6ea6b8c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (477, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-18 18:12:01', '11月18日月牙塘所理想中心工地发生一起讨要工程款的情况反映', '2017年11月18日12时许，分局月牙塘所接报一起龙泉路理想中心工地有人讨要工程款，接报后，月牙塘所警力迅速赶到现场。经了解，施工方苏一展（男、50岁、江苏人、该人自称未落户、该施工队无名称）、赵万参（男、39岁、禄劝人、身份证号530128197810234835、该施工队无名称）带领工人约20余人到龙泉路理想中心工地讨要2014年至今在龙泉路裕丰园小区的水电工程款，其中苏一展方约1250000元，赵万参方约750000元。民警现场对经济纠纷程序与方法进行了简单的讲解，后施工方苏、赵与江西省建工集团有限责任公司副总王冬钰（男、33岁、江苏如东人、身份证号32062319841101123X）就讨要工程款进行了协商，未果。截止目前，苏一展、赵万参等人已相继离开理想中心工地，整个过程秩序正常、现场无拉扯、无人员受伤、无口号、无横幅等情况，但苏、赵等人约定2017年11月20日到五华区住建局反映情况，如果区住建局不能解决问题，将带领工人到政府上访。相关情况关注中。（月牙塘所）\r\n\r\n\r\n', '2017-11-18 12:00:00', '2017-11-18 16:30:00', 4.50, 20, '索薪讨款', '月牙塘所', '龙泉路理想中心工地', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-20 14:02:32', '2018-03-12 09:17:59', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '龙泉路理想中心工地', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2716, 2519, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'a5c2f813-e1b0-42f6-9082-f6a036ba5a0f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102500000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (479, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-11-18 15:50:59', '11月18日高新区经典双城部分商铺业主到“华信悦峰”地产售楼部开展维权聚集（三）', '11月18日上午9时40分，约40余名经典双城商铺业主陆续聚集在滇缅大道“华信悦峰”售楼部门口进行维权活动，在售楼部外侧悬挂白底黑字标语三条，分别写有“希望政府为我们做主讨回公道”、“华信经典地产是骗子”、“还我血汗钱”等字样，并用小喇叭反复播放，黑林铺所民警在现场执勤，目前暂未有过激行为，相关情况持续关注中。（黑林铺所）\r\n', '2017-11-18 09:40:00', '2017-11-18 17:30:00', 7.83, 40, '合同纠纷 ', '黑林铺所', '华信悦峰售楼部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-20 14:02:32', '2018-03-12 09:16:10', NULL, '个体户', NULL, '0', '2', '拉横幅，使用扩音器播放口号。', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '华信悦峰售楼部', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2777, 2547, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'b8cc76ed-6d68-4a9d-b37e-3cd1aedba289', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (480, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-17 18:00:26', '11月17日40余名经典双城商铺业主到滇缅大道“华信悦峰”售楼部聚集情况', '11月17日上午9时许，20余名经典双城商铺业主继续聚集在滇缅大道“华信悦峰”售楼部门口，要求解决租金返还问题，无过激行为。\r\n黑林铺所民警在现场维持秩序，和黑林铺街道办事处工作人员及分局国保大队民警一起在现场对业主进行协调。10时40分许，现场聚集业主已增加到40余人，并在售楼部门口和外墙悬挂三条红底黄字，内容为：“还我血汗钱”“希望政府为我们业主讨回公道”“华信经典地产是骗子”的布标。12时，“华信悦峰”售楼部保安人员将业主悬挂的横幅进行清理，双方发生摩擦，民警迅速处置，防止了事态升级。后经典双城商铺业主拨打“街头巷尾”电话，15时“街头巷尾”的记者到达现场，记者在现场对经典双城商铺业主及“华信悦峰”售楼部人员进行采访。17时30分，现场围堵售楼部的业主自行散去，并称18日10时继续聚集围堵售楼部维权，具体人数在摸排中。（黑林铺所）\r\n\r\n\r\n', '2017-11-09 09:40:00', '2017-11-09 17:30:00', 7.83, 40, '合同纠纷 ', '黑林铺所', '华信悦峰售楼部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-20 14:02:32', '2018-03-12 09:17:43', NULL, '经典双城业主', NULL, '0', '2', '拉横幅，使用扩音喇叭播放口号。', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '华信悦峰售楼部', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2777, 2535, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'bc4a943b-69eb-462a-b2c8-74fd1c1da79c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (484, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-16 17:24:56', '11月16日经典双城40余名商铺业主因租金问题到“华信悦峰”售楼部聚集', '11月16日15时42分，工作人员向黑林铺所电话报警，称有多名经典双城商铺业主到“华信悦峰”售楼部堵门。接警后，民警迅速赶至现场维持秩序，据工作掌握，系因这些业主在购买高新区经典双城项目商铺时，经典双城商业公司承诺返还10年租金，直至目前仅支付了5年，还剩余5年租金未及时支付，故40余名业主邀约到滇缅大道西山大厦旁的“华信悦峰”售楼部聚集要求解决。民警现场工作，沐荣欣成公司负责人宜良男子陈思恺（汉族，34岁）和与在场业主协调，陈思恺称该公司为挂靠在北京华信集团旗下，与开发经典双城项目的公司不是同一家，故无法解决此事。目前仍有40余名业主聚集，无过激行为，现场秩序平稳。经民警摸排，部分业主邀约11月16日9时许继续到“华信悦峰”售楼部聚集维权，参与人数还在进一步摸排中。相关情况进一步关注中。（黑林铺所）\r\n\r\n\r\n', '2017-11-16 15:40:00', '2017-11-16 17:30:00', 1.83, 40, '合同纠纷 ', '黑林铺所', '华信悦峰售楼部', NULL, NULL, '6', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-20 14:02:32', '2017-11-20 14:27:28', NULL, '个体户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '华信悦峰售楼部', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2777, 2547, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '6609daa8-cbab-47ea-b39c-18ea9f6cefe9', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (486, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-20 14:39:54', '11月20日宣威市东山镇村民7人因不满移民搬迁不力到省政府上访', '20日14:20分许，7名宣威市东山镇法着村委会团山八组村民到省政府上访，反映，2004年，该村由于遭受山体滑坡等自然灾害的影响，当地政府启动该村移民搬迁，截止目前已搬走50余户村民，还有10余户50余人一直没有搬迁，村民称他们不知道为什么不让他们搬迁，现在居住的地方由于灾害的影响一直没有生活用水，要到较远的地方挑，交通也不方便，不通电，国家给的建房补贴被村干部截留。由于多次向有关部门反映要求解决这些问题未果所以到省政府上访，要求政府要么尽快让村民搬迁，如果不搬迁就尽快解决上述问题。现来访村民正在信访大厅等待接待，秩序平稳。(华山所)\r\n', '2017-11-20 14:20:00', '2017-11-20 16:00:00', 1.67, 7, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-20 16:05:30', '2017-11-23 13:42:00', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', 'b538f7c5-9d79-49ed-901d-560dbecedc59', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (487, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-20 14:07:31', '11月20日寻甸县海子屯村民26人因民间纠纷到省政府上访（续报）', '20日10:15分许，26名寻甸县海子屯村民因被村小组长骗贷2000余万元一事到省政府上访。来访人员称，当地农信社已将35户村民起诉至法院，10月22日将开庭审理。省信访局接待后告知其，他们所反映的问题属于涉法涉诉案件，不在政府受理范围内，要求其向司法部门进一步反映。由于不满接待结果，11:00许，20余人离开信访大厅聚集在省政府大门侧面人行道处。\r\n', '2017-11-20 10:15:00', '2017-11-20 11:00:00', 0.75, 26, '涉法涉诉', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-20 16:05:30', '2017-11-23 13:42:21', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'sldws', '16-50人', '2', 'qkfy', 26, 0.00, 0, 0, '0', NULL, '情况反映', '6ffe10e7-be6b-48d2-8ede-b80812ca8e3e', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (491, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-20 10:31:34', '11月20日盘龙区穿金路商户21人因不满区政府行为到省政府上访', '11月20日09:50分许，21名在穿金路开商铺的经营户到省政府上访。上访人员反映，上周五盘龙区政府下发了一份《关于盘龙区开墙打洞住改商专项整治的通告》，认定穿金路沿线大约200余户商铺属于住改商，要求他们在10月25日前上交营业执照，10月30日其停止经营。商户们认为盘龙区政府的通告时间过紧，很多商户花了10多万的转让费、装修费，还没有回本就要整改，利益受到损害，所以到省政府上访，要求盘龙区政府给他们指定地方经营，同时要求去政府与商户协商资金受损的赔偿方案。省信访局接待后告知其向昆明市政府反映诉求。10:15分许，21人全部离开省政府，上访过程平稳。\r\n\r\n\r\n\r\n\r\n\r\n华山东路派出所\r\n\r\n2017年11月20日\r\n', '2017-11-20 09:50:00', '2017-11-20 10:15:00', 0.42, 21, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-20 16:05:30', '2017-11-21 15:28:48', NULL, '个体户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'mjjf', '民间纠纷', 'sldws', '16-50人', '1', 'qkfy', 21, 0.00, 0, 0, '0', NULL, '情况反映', '42a81a3e-098c-4861-89fe-bc6d826cf262', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (492, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-20 16:31:40', '11月20日马龙县川洞村村民8人因不满搬迁安置到省政府上访', '11月20日16:05分许，8名曲靖市马龙县马过河镇川洞村村民到省政府上访，反映，2015年9月，当地政府因修建车马碧水库的需要，要求该村整体搬迁至指定区域，村民称政府指定的地方生活不方便，要求就近搬迁。由于与当地政府无法达成一致所以到省政府上访，要求就近搬迁。省信访局接待后告知其向曲靖市反映诉求。16:15分许，8人全部离开省政府，上访过程平稳。（华山所）\r\n', '2017-11-20 16:05:00', '2017-11-20 16:15:00', 0.17, 8, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-21 15:12:27', '2017-11-23 13:43:41', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'ymwt', '移民问题', 'sryx', '10人以下', '1', 'qkfy', 8, 0.00, 0, 0, '0', NULL, '情况反映', '5488d7bd-4c1d-4dbf-9006-aa6271888c80', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (493, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-20 17:30:49', '11月20日经典双城部分商铺业主到“华信悦峰”地产售楼部聚集续报', '11月20日10时许，约30余名经典双城商铺业主陆续聚集在滇缅大道“华信悦峰”售楼部门口进行维权活动，并在售楼部外侧悬挂白底黑字标语三条，分别写有“希望政府为我们做主讨回公道”、“华信经典地产是骗子”、“还我血汗钱”等字样，同时使用一个扩音喇叭反复播放布标内容。据工作掌握，目前“华信悦峰”相关负责人与业主协商提出两种解决途径:一是商铺由“华信悦峰”回收，返还业主购房款60万元；二是，将商铺发还业主自行租售，不再返还之前业主同经典双城公司约定的租金。双方未协商达成一致。另经民警摸排了解,云南经典房地产开发集团有限公司于2017年11月16日出具《CS2商铺进展通报》称：截止2017年11月15日共计收到业主资料102批（其中CS2栋商铺101批，其中一次性付款客户39批，已结清贷款客户28批，贷款未结清客户34批），就诉求中提出的资金使用成本及违约赔偿计算结果显示，我司需承担本金 利息 违约金，近2亿元人民币，而我司实收约为1.1亿元人民币，退铺成本为经典集团财务实收的近两倍。原计划经典集团今日会给业主电话回复，回复下次约谈的时间，但至17时业主自行散去业主未收到任何电话回复。业主声称于11月21日会采取进入工地爬塔吊、跳楼等过激行为维权。相关情况密切关注中。（黑林铺所）\r\n', '2017-11-20 10:00:00', '2017-11-20 17:00:00', 7.00, 30, '合同纠纷 ', '黑林铺所', '华信悦峰售楼部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-21 15:12:28', '2017-11-21 15:15:01', NULL, '个体户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '华信悦峰售楼部', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2777, 2547, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'cfca50da-3e23-4f90-b589-959b4ba47b2d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (494, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-11-21 10:37:10', '11月21日23余名西尚邻居小区业主到五华区政府上访', '11月21日上午09时30分许，23余名莲花池西尚邻居小区业主到区政府上访，主要反映的是小区烂尾楼，水电不通要求退房，希望政府给予解决。现场聚集业主打出白底黑字横幅1条，内容为“无辜业主、求天求地求政府”，经民警劝告，布标已收，现区政府信访办相关负责人已接待处理中。现场秩序正常，暂无事态升级情况发生。翠湖所已安排2名民警到现场维持秩序，协助做好协调工作，密切关注动态中。（翠湖所）\r\n', '2017-11-21 09:30:00', '2017-11-21 10:45:00', 1.25, 23, '交房问题 ', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-21 15:12:28', '2017-11-27 16:25:30', NULL, '西尚邻居小区业主', NULL, '0', '1', '打出白底黑字横幅1条，内容为“无辜业主、求天求地求政府”', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2536, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'sldws', '16-50人', '1', 'qkfy', 23, 0.00, 0, 0, '0', NULL, '情况反映', 'c1610ed9-c36d-4be7-860c-b430c08c4a07', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (495, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-11-21 11:43:18', '11月21日月牙塘所辖区新东方烹饪学校门口出现村民堵门情况', '11月21日10时月牙塘所接口头报警称，五华区龙泉路岗头村新东方烹饪学校门口有人堵门。接警后，民警迅速到达现场开展工作。经现场摸排了解，岗头新二村21人因1966年该村位于岗头村478号（面积约20亩）被78107部队50分队征用，村民将新东方烹饪学校的大门堵住，希望78107部队50分队出面协调新东方烹饪学校能给予岗头新二村经济补偿（具体数额暂未提）。截至目前，现场仍有十余名群众站在新东方烹饪学校门口，影响学校正常秩序。现场暂无其他过激行为。相关情况密切关注中。（月牙塘所）\r\n\r\n\r\n\r\n\r\n', '2017-11-21 10:00:00', '2017-11-21 12:00:00', 2.00, 21, '山林土地问题 ', '月牙塘所', '岗头村新东方烹饪学校', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-21 15:12:28', '2017-11-27 09:34:15', NULL, '岗头村', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '岗头村新东方烹饪学校', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2705, 2508, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '3590a396-6eec-4b69-a85f-df09dcf7e368', 'JS', NULL, 'Y', 110, NULL, NULL, '530102500000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (496, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-11-21 12:35:45', '11月21日岗头村30余人在龙泉路中天城碧桂园工地门口进行堵门情况反映', '11月21日10时13分许月牙塘所接110指令：龙泉路中天城碧桂园门口有村民堵门闹事。接警后，民警立即赶到现场处置。经民警现场摸排了解：系因为岗头村村民拆迁时土地开发商应付四千万土地赔偿款给村民，开发商答应先支付两千万土地赔偿款给村民，但村民至今未拿到该笔赔偿款，故今日邀约30余人到龙泉路中天城碧桂园工地门口进行堵门。经民警现场工作，现场龙泉路中天城碧桂园工地门口人员可以出入，截至目前岗头村村民约30人仍滞留在中天城碧桂园工地门口。现暂无其他过激行为。相关情况密切关注中。（月牙塘所）\r\n\r\n\r\n', '2017-11-21 10:15:00', '2017-11-21 13:30:00', 3.25, 30, '拆迁补偿', '月牙塘所', '龙泉路中天城碧桂园', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-21 15:12:28', '2017-11-27 09:35:02', NULL, '岗头村', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '龙泉路中天城碧桂园', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2704, 2508, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '3966ddbc-0ff5-49d4-b653-09e839d4bac1', 'JS', NULL, 'Y', 110, NULL, NULL, '530102500000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (497, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-21 13:38:41', '11月21日金座集资户到市体育馆聚集上访的情况', '据工作掌握，11月21日9时30分许，以纪昌凤、罗益民为首的金座集资户群体到市体育馆上访。昆明市农业局及市金融办的相关领导接访。由于市农业局派一名调研员接访，引发金座集资户不满，集资户强烈要求市农业局局长来接访，否则就到省信访局反映。经解释劝解工作后，集资户自行聚集开会，主要是强调聚集上访的考勤、纪律等。今日到现场聚集上访的金座集资户有120余人。中午11时30分，上述人员自行离开现场。相关情况进一步掌握中。（国保大队）\r\n', '2017-11-21 09:30:00', '2017-11-21 11:30:00', 2.00, 120, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-21 15:12:28', '2017-11-27 16:24:23', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '46b36546-6e50-4c6d-8210-fd230bfa5d5e', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (498, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-11-21 14:30:18', '11月21日 “中天城.天阔” 楼盘购房户到五华区政府聚集上访仍存集访隐患（以此件为准）', '11月21早翠湖所上报“11月21日20余名中天花园业主因购房合同问题到五华区政府上访”，经国保大队进一步核实，聚集上访人员为“中天成.天阔”业主。据工作掌握，11月21日9时许，20余名“中天成.天阔” 楼盘购房户到五华区政府上访，反映，他们是“中天成.天阔”购房户，大多于2016年1月至3月间向云南汉都房地产有限公司购买了“中天成.天阔”楼盘的商品房，缴纳了20万元左右的房款，但至今地产公司不愿意签署正式购房合同，并故意隐瞒所售商品房为抵押未解押房源的情况，涉及人员80余人。10时42分经五华区政府做工作上述人员离开。长期以来，“中天成.天阔”楼盘购房户因对延期交房、配套设施不完善等问题，数次聚集，并到红云办事处、五华区政府等政府部门上访。此次再次出现购房户到五华区政府上访，要求解决地产公司不愿意签署正式购房合同的问题，导致多种矛盾叠加。截至目前尚未得到妥善解决，该群体到省市区集访的隐患较大，建议政府职能部门予以关注。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2017-11-21 09:30:00', '2017-11-21 11:45:00', 2.25, 20, '交房问题 ', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-21 15:12:28', '2017-11-27 16:25:45', NULL, '中天成业主', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2529, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'c964b061-2a21-4fbd-9121-04403e23137a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (499, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-22 11:17:27', '11月22日在西山区草海片区做工的农民工7人因工钱被拖欠到省政府上访', '11月22日10:55分许，7名在西山区草海片区A37号地块A5子地块做工的农民工到省政府上访，反映，他们的承包方是中铁建工集团北方公司驻昆明市西山区项目部，该公司拖欠了100余名农民工今年8月至今的工钱100余万元，由于一直讨要未果所以到省政府上访，要求尽快拿到工钱。省信访局接待后告知其向昆明市反映。11时05分许，7人全部离开省政府。上访过程平稳，无过激行为。（华山所）\r\n', '2017-11-22 10:55:00', '2017-11-22 11:05:00', 0.17, 7, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-22 13:27:44', '2017-11-22 13:34:09', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', 'e2e37918-3c89-4285-8a59-a5033b14e8be', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (500, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-22 10:37:39', '11月22日寻甸县倘甸镇村民10人因不满镇政府行为到省政府上访', '11月22日09:55分许，10名寻甸县倘甸镇马街村委会马街村村民到省政府上访，反映，2015年该村60余户村民需要自建房，倘甸镇政府建设服务中心以建房需要交纳建房开工费为由，要求村民根据建房面积的不同交纳3至6万不等的保证金，并称建好房后保证金即可退回。村民遂向镇政府缴纳了保证金。2016年底房屋建好，村民要求退回保证金时，服务中心以各种理由不退，来访村民称有40余户村民因为找了各种关系退到了钱，而他们12户的40余万元至今没退，怀疑是被镇政府贪污挪用了。由于一直没有退到钱所以到省政府上访，要求上级政府查处镇政府的违纪行为同时尽快把保证金退回给村民。省信访局接待后告知其回寻甸县反映诉求。10时15分许，10人全部离开省政府。上访过程平稳，无过激行为。（华山所）\r\n', '2017-11-22 09:55:00', '2017-11-22 10:15:00', 0.33, 10, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-22 13:27:44', '2017-11-27 09:35:11', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', '80647b36-8018-4903-a7d3-fbb48e998f44', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (501, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-21 15:48:11', '11月21日太和村村民8人因不满征地补偿到省政府上访', '11月 21日15分许，8名昆明旅游度假区前卫街道办事处太和村村民到省政府上访，反映，2009年当地政府征用了该村1000余户村民的3000余亩土地，至今没有给予村民任何补偿，土地也没有开发，一直荒芜，引发村民不满，已上访8年余，要求政府给予补偿。由于一直得不到解决所以到省政府上访，要求尽快解决征地补偿，如果不给补偿就把地还给村民。省信访局接待后告知其向昆明市反映。现8人还未离开信访局，秩序平稳。（华山所）\r\n', '2017-11-21 15:00:00', '2017-11-22 17:00:00', 26.00, 8, '山林土地问题 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-22 13:27:44', '2017-11-22 13:29:31', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '2', 'qkfy', 8, 0.00, 0, 0, '0', NULL, '情况反映', '5d72aaca-d6e5-4434-9ce5-a016ec5adbe2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (502, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-22 14:51:42', '11月22日巧家县新店乡村民7人因不满征地补偿到省政府上访', '11月22日14:30分许，7名昭通市巧家县新店乡开基村村民到省政府上访，反映，2009年巧家县政府因修建清水河电站的需要，征用了该村400余户村民500余亩土地，但至今村民没有拿到征地补偿，村民称他们了解到县政府已将征地款打到新店乡政府的账上，但是乡政府一直没有发给村民，引发村民不满，另外村民还举报该村村主任贪污扶贫款一事。现来访群众正在信访大厅等待接待，秩序平稳。（华山所）\r\n', '2017-11-22 14:30:00', '2017-11-22 17:00:00', 2.50, 7, '山林土地问题 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-23 15:38:40', '2017-11-23 15:45:58', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', 'fc8398de-be7d-4c27-afa2-10490f70e74f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (503, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-23 13:41:11', '11月23日金座集资户到市体育馆聚集上访的情况', '据工作掌握，11月23日上午9时30分许，以纪昌凤、罗益民为首的金座集资户群体到市体育馆上访。昆明市政法委及市金融办的相关领导接访。主要是金座集资户向昆明市政法委反映昆明市农业局的主要领导不到市体育馆接访金座集资户，请市政法委、市金融办协调解决市农业局的主要领导到现场接访的问题。今天参加涉金座的人员有110余人左右，中午11时20分许，上述人员自行离开市体育馆。上访过程平稳，无其他过激行为。目前尚未发现金座上访人员到南屏步行街中心广场聚集。相关情况密切关注中。（国保大队）\r\n', '2017-11-23 09:30:00', '2017-11-23 11:20:00', 1.83, 110, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-23 15:38:40', '2017-11-27 16:24:04', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'd166f6c9-ba59-4188-8e9a-3ace2178f719', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (506, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-23 15:51:34', '11月23日鲁甸县营运司机因不满燃油补贴未发放到省政府上访', '11月23日15时30分许，5名昭通市鲁甸县的城市公交车承包人到省政府上访，反映，当地政府拖欠他们62辆公交车从2015年至今的燃油补贴121万元。由于向当地政府反映未果所以到省政府上访，要求尽快发放燃油补贴。省信访局接待后告知其向昭通市政府反映。15时35分许，5人全部离开省政府。上访过程平稳，无过激行为。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n华山东路派出所\r\n\r\n2017年11月23日\r\n', '2017-11-23 15:30:00', '2017-11-23 15:35:00', 0.08, 5, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-23 16:46:08', '2017-11-23 16:47:38', NULL, '个体户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'yysj', '营运司机', 'jtysgl', '交通运输管理', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '438da28f-d3ce-4657-bc9e-9c49a62778c3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (507, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-23 17:32:03', '11月23日70余名省地矿局下属地州退休职工因不满生活补贴差异到省政府上访（续报）', ' 11月23日15时许，70名省地矿局退休职工因不满待遇问题到省政府上访，省信访局接待后告知其回地矿局反映诉求，因不满接待结果，来访人员一直在信访大厅内吵闹，要求见省长。16时20分许，省地矿局负责人到信访大厅接待来访群众，告知其地矿局将把他们提出的诉求向省级相关领导反映，要求其耐心等待。经工作，17时许，70名来访人员全部离开省政府。部分上访人员离开时声称地矿局如果不给他们满意答复，他们将组织更多的人到地矿局及省政府门口静坐。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2017-11-23 15:00:00', '2017-11-24 17:00:00', 26.00, 70, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-24 15:38:01', '2017-11-24 15:39:47', NULL, '退休职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'gzfldy', '工资福利待遇', 'wsyr', '51-100人', '1', 'qkfy', 70, 0.00, 0, 0, '0', NULL, '情况反映', 'ed3e9079-03d0-40c5-b9fe-fa294e1aee0c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (508, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-24 14:59:59', '11月24日10人到云南饭店门口讨要工程款', '11月24日9时40分许，新村所民警巡逻至五华区龙井街云南饭店建筑工地门口发现有10名建筑工地农民工在工地门口聚集准备悬挂白底黑字横幅，横幅内容为“世博集团、云旅地产还我血汗钱（云南饭店）”。民警随即开展摸排调查工作，经了解系因云旅房地产开发有限公司拖欠承建云南饭店整体改扩建项目承建方中国有色金属工业第十四冶金建设公司的工程款4900万元，导致十四冶公司无法支付四川吉祥建筑劳务有限公司3000万工程款及劳务费。云旅房地产开发有限公司承诺近期偿还所欠工程款，但至今没有音讯，故10余名工地工人到现场悬挂横幅。经民警工作，9时50分许，10余名工人撤下横幅后自行离开。13时10分许，云南饭店建筑工地门口又悬挂上“世博集团、云旅地产还我血汗钱（云南饭店）”字样的横幅，但无人员聚集情况。新村所民警、大观司法所工作人员已到工地开展普法及协调协商等工作。相关工作进一步开展中。（新村所）\r\n', '2017-11-24 09:40:00', '2017-11-24 09:50:00', 0.17, 10, '索薪讨款', '新村所', '云南饭店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-24 15:38:01', '2017-11-27 09:39:36', NULL, '农民工', NULL, '0', '2', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '云南饭店', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2674, 2519, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'sryx', '10人以下', '2', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', 'c2f5faf8-5972-4167-80a7-40f23991d8e3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (509, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-27 15:15:31', '11月27日金座公司非法集资案受害群众130余人自行到西站法庭旁听(续报)', '11月27日早9时许，翠湖所上报罗益民、纪昌凤为首的金座集资户100余人到文林街五华区人民法院西站法庭聚集旁听。经国保大队进一步核实，今日参与聚集人数在130余人。聚集过程中，双方人员曾发生争执，后经工作进行劝解，秩序基本正常。上午9时开始进行了三场庭审至中午13时20分结束，审判结果择期宣布。13时30分上述人员陆续离开现场。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2017-11-27 09:00:00', '2017-11-27 13:30:00', 4.50, 130, '特殊群体', '翠湖所', '西站法庭', NULL, NULL, '110', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-27 16:02:47', '2017-11-27 16:09:13', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '西站法庭', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2645, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '75bd78a1-4423-43ce-8415-51cbbc52bf8a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (510, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-27 15:08:22', '11月27日曲靖市马龙县村民6人到省政府上访举报镇政府人员的违纪行为', '11月27日14:10分许，曲靖市马龙县马过河镇麻衣村委会川洞村村民6人到省政府上访，代表30余户村民举报马过河镇党委书记高方、副镇长周富强二人的违纪行为。来访人员，反映，2015年，当地要建设车马碧水库工程，政府要求不允许在淹没区新建住房，但是政府并未告知村民具体淹没范围，之后有7户村民新建了住房，在建房的过程中并未有政府工作人员阻止，但是建好后，镇党委书记及副镇长两人却带人强行推倒住房，引发村民强烈不满，认为两人的行为违法，要求追究他们的违法行为。由于在当地反映未果所以到省政府上访，要求清查推倒房屋的事件同时追究两人的违法行为。现来访群众正在信访大厅等待接待，秩序平稳。（华山所）\r\n', '2017-11-27 14:10:00', '2017-11-27 15:30:00', 1.33, 6, '产权物业', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-27 16:02:47', '2017-11-27 16:07:21', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', '9fb77e1f-7cc4-40f2-994e-ebce8ef8909d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (511, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-27 15:00:31', '11月27日红河州石屏县村民24人因不满村干部行为到省政府上访', '11月27日14:00许，24名红河州石屏县哨冲镇撒妈扎村村民到省政府上访，反映，该村村干部对国家下达的各项扶贫攻坚政策落实不到位，村里建档立卡户及低保贫困户均是村干部的亲属、朋友或关系人，引发其他实际困难户的不满，他们多次向村里反映要求公开信息未果所以到省政府上访，要求上级政府为他们做主，公开公平选出实际困难户。现来访村民已有信访局工作人员接待，秩序平稳。（华山所）\r\n\r\n\r\n', '2017-11-27 09:20:00', '2017-11-27 15:04:00', 5.73, 24, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-27 16:02:47', '2018-03-12 09:17:19', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sldws', '16-50人', '2', 'qkfy', 24, 0.00, 0, 0, '0', NULL, '情况反映', 'b6ae8c3d-9c57-409e-be2e-6afb17fa81b0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (512, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-27 10:19:15', '11月27日18名农民工在云南饭店门口聚集讨要工钱', '11月27日9时20分许，民警巡逻发现云南饭店施工工地围墙上挂有白底黑字“世博集团、云旅地产还我血汗钱（云南饭店）”字样横幅，工地内的塔吊上也挂有一副相同字样的横幅。据工作掌握，云旅房地产开发有限公司拖欠承建云南饭店整体改扩建项目承建方中国有色金属工业第十四冶金建设公司的工程款4900万元，导致十四冶公司无法支付四川吉祥建筑劳务有限公司3000万工程款及劳务费，四川吉祥建筑劳务有限公司因而无法支付工人工资。11月24日，10余名农民工曾在云南饭店门口聚集悬挂横幅，讨要工程款。经民警现场摸排调查，现工地大门处于关闭状态，门口有15人静坐，横幅旁边有3名男子爬在围墙上，18人均为农民工。工地门口的保安室被前来的聚集的人从外面用铁链锁住，一名保安人员暂被困在保安室。民警正在协调打开保安室。现暂无相关公司负责人到场，现场无过激行为，相关情况密切关注中。（新村所）\r\n', '2017-11-27 09:20:00', '2017-11-27 16:04:00', 6.73, 18, '索薪讨款', '新村所', '云南饭店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-27 16:02:47', '2017-11-27 16:04:32', NULL, '农民工', NULL, '0', '1', '拉横幅，堵门', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '云南饭店', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2674, 2519, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'sldws', '16-50人', '1', 'qkfy', 18, 0.00, 0, 0, '0', NULL, '情况反映', '1c66139e-7da0-4e7e-87ed-83ac8dd4ac32', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (514, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-28 17:21:40', '11月28日120余名“一号广场”业主和赵李家堆村民到五华区政府聚集上访', '据工作掌握，11月28日9时许，30余名“一号广场”业主和赵李家堆村民陆续到五华区政府上访。主要诉求：解决“一号广场”不能正常供水供电等问题。在此过程中，不断有业主在维权微信群中邀约其他业主到区政府聚维权。从而导致现场人员不断增加，截至到10时40分，现场人数增加到120余人。10时许，5名业主和村民代表进入区信访办会议室，区信访办、龙翔街道办事处、开发商代表进行了接待并听取了业主的诉求。政府要求开发商拿出一个确实可行的实施办法解决停水、停电问题。13时30分许，上述人员离开区政府。（国保大队）\r\n\r\n\r\n', '2017-11-28 09:00:00', '2017-11-28 13:30:00', 4.50, 120, '交房问题 ', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-29 09:06:53', '2017-11-29 10:09:17', NULL, '赵李家堆', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2511, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'e89a415b-1c4c-46cd-b0f4-da72925e6fe0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (515, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-28 16:24:54', '11月28日泸西县各乡镇支前民工5人因生活待遇问题到省政府上访', '   28日15:20分许，5名红河州泸西县各乡镇村民到省政府上访，反映，他们于上世纪70年代参与修建河口至老山的国防公路，工程完工后回乡务农，现在他们年纪大了，生活困难，要求政府帮助解决健在人员（具体人数来访人不清）的生活补助。省信访局接待后告知其回红河州反映。15:35分许，5人全部离开省政府。\r\n', '2017-11-28 15:20:00', '2017-11-28 15:35:00', 0.25, 5, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-29 09:06:53', '2017-11-29 10:05:30', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'ce482fb5-5789-464c-ad0a-31a962e88db2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (516, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-28 16:23:16', '11月28日会泽县水城社区村民8人因不满征地补偿到省政府上访', '11月28日15:40分许，8名曲靖市会泽县古城街道水城社区村民到省政府上访，反映，2005年当地政府征用了60余户村民的86亩土地，给予村民的补偿是每亩46000元，由于补偿费至今还有部分没有到位，而且征的地至今没有开发，引发村民不满，要求政府按照现在的征地补偿费用重新给予村民补偿。由于在当地反映未果所以到省政府上访，要求按照现在的价格给予村民补偿。现来访村民正在等待接待。秩序平稳。(华山所)\r\n', '2017-11-28 15:40:00', '2017-11-28 16:00:00', 0.33, 8, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-29 09:06:53', '2017-11-29 10:03:27', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '2', 'qkfy', 8, 0.00, 0, 0, '0', NULL, '情况反映', 'faedf114-c61f-4c01-a873-5744001c9733', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (517, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-28 16:20:35', '11月28日昆明交通协管员15人因工钱被拖欠到省政府上访', '11月28日14:50分许，15名昆明交通协管员到省政府上访，反映他们受雇于云南弘瑞保安公司（公司地址在北市区罗丈村），从今年5月份起在昆明市创卫、创文期间做交通协管，主要在东风广场路口疏导交通。该公司拖欠大约40余人9月、10月两个月的工资未发，每人月工资大约2300元。由于讨要未果所以到省政府上访，要求尽快拿到工钱。现来访群众正在信访大厅等待接待，秩序平稳。(华山所)\r\n', '2017-11-28 14:50:00', '2017-11-28 15:40:00', 0.83, 15, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-29 09:06:53', '2017-11-29 09:59:23', NULL, '农民工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'syr', '11-15人', '2', 'qkfy', 15, 0.00, 0, 0, '0', NULL, '情况反映', '517a04a3-e895-4bdb-b0d0-1c9f9abcefcc', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (518, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-28 16:21:54', '11月28日云天化下属磷业公司下岗职工5人因不满安置方案到省政府上访', '    11月28日15：00许,5名云天化下属云南康盛磷业公司下岗职工到省政府上访，反映，他们公司地址在玉溪市华宁县，今年8月企业改制，云天化出台了一份《员工安置分流方案》，详细规定了不同工种职工的安置方法。但职工们认为这份方案没有经过职代会的讨论就下发，属于违规行为，且该方案部分损害了职工利益，职工对方案不满，要求公司召开职代会讨论具体安置办法。由于职工与公司无法达成一致所以到省政府上访，要求重新制定安置方案。省信访局接待后告知其回玉溪市反映。15:10分许，5人全部离开省政府，上访过程平稳。\r\n', '2017-11-28 15:00:00', '2017-11-28 15:10:00', 0.17, 5, '企业改制/破产 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-29 09:06:53', '2017-11-29 10:01:40', NULL, '退休职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'xgsyry', '下岗失业人员', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'cfc2f5ad-aba7-40a5-9d16-a332191ae4cf', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (519, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-28 16:19:10', '11月28日在草海片区做工的农民工5人因工钱被拖欠到省政府上访', '11月28日15:35分许，5名在西山区草海片区A37号地块A5子地块做工的农民工到省政府上访，反映，他们的承包方是中铁建工集团北方公司驻昆明市西山区项目部，该公司拖欠了100余名农民工今年8月至今的工钱100余万元，由于一直讨要未果所以到省政府上访，要求尽快拿到工钱。该起民工于11月22日曾就相同诉求来访过，由于仍然没有解决所以今日再次上访。省信访局接待后告知其向昆明市反映。15:55分许，5人全部离开省政府。（华山所)\r\n\r\n\r\n', '2017-11-28 15:35:00', '2017-11-28 15:55:00', 0.33, 5, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-29 09:06:53', '2017-11-29 09:56:57', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '05dc601f-4e8f-453a-9abf-9004256c6279', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (522, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-28 14:18:25', '11月28日金座集资户到市体育馆聚集情况', '11月28日上午9时30分许，以纪昌凤、罗益民为首的金座集资户群体到市体育馆上访。昆明市工商局及市金融办的相关领导接访。议题主要是围绕市工商局在金座公司的监管问题上应承担什么责任的问题。今天参加涉金座的人员有120余人左右，中午12时许，上述人员自行离开市体育馆。(国保大队)\r\n\r\n\r\n', '2017-11-28 09:00:00', '2017-11-28 12:00:00', 3.00, 120, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-29 09:06:53', '2017-11-29 09:52:41', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '3de3c177-04e9-450d-b007-79055dfe0f49', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (523, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-29 09:46:24', '11月29日曲靖原修路民兵5人因生活困难到省政府上访', '11月29日09:00分许，5名来自富源、马龙、东川的原修路民兵到省政府上访，反映，1964年，他们响应原云南省公路局的号召，组建民兵团（约1000余人），参与修建平远机场，1967年“文革”开始后因为派系斗争、粮食短缺，民兵团解散，所有民兵回乡务农。来访人员称，解散时，公路局承诺按照解放军的退伍待遇给他们补偿，但一直没有兑现。现在他们年纪大了，生活困难，要求政府兑现承诺，按照当时的解放军退伍标准给予其补偿。该起来访群众已就相同诉求多次到省政府上访，由于一直没有解决所以今日再次到访，现正在信访大厅等待接待，秩序平稳。（华山所）\r\n', '2017-11-29 09:00:00', '2017-11-29 09:45:00', 0.75, 5, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-29 16:11:37', '2017-11-29 16:14:10', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'dzzbm', '对政策不满', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'def8f9eb-2b80-4260-a54e-6cb1ee05c056', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (524, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-29 15:49:45', '11月29日岗头村二组30名村民再次到新东方烹饪学校门口堵门', '11月29日10时30分许，月牙塘所接新东方烹饪学校主任邓自山报警：岗头村二组村村民堵断该学校大门。接警后，民警立即赶往现场处置。民警到达时，现场聚集了岗头村二组约30名老年妇女，宣称近期她们听到传闻，1966年该村位于新东方烹饪学校所在位置的土地被78107部队50分队征用，后部队将该土地转租给新东方烹饪学校，但国家于2015年开始，明令禁止部队经商，该土地已于2015年归还村上，但村民从未收到过村上分发的租金。11时许，部队派人与该村副主任王春及7名村民代表见面，部队当场出示了上世纪80年代办理的《国防国有土地使用证》，具体区域涵盖了新东方烹饪学校目前所在位置土地，其他情况涉密。11时30分许，经民警现场劝说，聚集村民仍执意不肯离开，放学学生情急之下蜂拥而出，将两名老年妇女任玲仙（52岁）、邬秀芬（67岁）带倒，民警拨打了120将二人送至新华同仁医院治疗。据了解，二人均无生命危险，任玲仙现已出院回家。截至14时20分许，聚集的村民已全部离开，现场秩序恢复平稳。相关情况密切关注中。（月牙塘所）\r\n\r\n\r\n', '2017-11-29 10:30:00', '2017-11-29 14:20:00', 3.83, 30, '山林土地问题 ', '月牙塘所', '岗头村新东方烹饪学校', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-29 16:11:37', '2017-11-29 16:15:51', NULL, '岗头村', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '岗头村新东方烹饪学校', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2705, 2508, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'e6498549-6a1b-4fe2-bd06-ccdfd585ebe9', 'JS', NULL, 'Y', 110, NULL, NULL, '530102500000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (526, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-11-29 16:21:49', '11月29日镇雄县以勒镇村民5人到省政府举报村干部问题', '11月29日16时许，5名昭通市镇雄县以勒镇毛坝村村民到省政府上访，举报镇雄县林业局林业公安科队长、以勒镇经警队队员、以勒镇林业站站长、毛坝村村主任等人相互勾结，为官不为，滥用职权，侵吞村民集体财产，侵害村民利益一事。现信访接待中，秩序平稳。（华山所）\r\n', '2017-11-29 16:00:00', '2017-11-30 16:30:00', 24.50, 5, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-30 15:59:38', '2017-11-30 16:04:13', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'f36f2edb-35d7-4d73-8e49-e42cc13669d5', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (527, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-11-30 15:40:05', '11月30日金座集资户到市体育馆聚集情况', '11月30日9时许，陆续有100余名金座集资户到市体育馆聚集上访，要求市农业局主要领导接访，因对市农业局主要领导未到现场开展接访工作不满，在整个聚集过程中，部分参加聚集的集资户不断扬言，要集体到省政府上访。市金座联合接访小组的工作人员开展了相关疏导化解工作后，15时20分，聚集的集资户放弃了到省政府集访的想法，并陆续离开。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2017-11-30 09:00:00', '2017-11-30 15:20:00', 6.33, 100, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-11-30 16:00:46', '2017-11-30 16:03:05', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'b34584eb-40d5-4a88-b63e-856053c6012b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (529, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', NULL, '12月1日金座公司非法集资案受害群众20余人自行到西站法庭进行法律咨询', '\r\n2017年12月1日下午15时许， 20余名金座公司非法集资案受害群众到西站法庭进行法律咨询。现西站法庭接待点相关负责人接待处理中，现场秩序正常，无过激言行。翠湖所已安排2名民警到现场维持秩序，协助做好协调工作，我所将密切关注事态的发展情况，及时上报上访的相关动态。', '2017-12-01 15:00:00', '2017-12-01 17:30:00', 2.50, 20, '特殊群体', '翠湖所', '西站法庭', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-04 09:22:01', '2017-12-04 09:22:13', NULL, '金座', NULL, NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区内', '西站法庭', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2645, 2502, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (530, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', NULL, '12月2日10余名包工头及农民工到莲花池回迁安置房讨要工程款现场情况', '2017年12月2日上午10时25分，文林派出所接莲花池回迁小区物管方报称：有10余名包工头及农民工在小区门口聚集讨要工程款。我所民警及时赶到现场，了解到陈付东等9名包工头及民工到莲花池回迁安置办公室现场反映诉求，讨要工程款。民警在现场积极开展工作，截止11时13分许，现场所有农民工及包工头已全部离开，现场无过激行为，但陈付东等人声称将邀约20人于下周一（12月4日）到五华区区政府上访维权。现不排除该维权群体集体上访的可能，文林派出所将密切关注该情况并加强信息上报工作。（文林所）             ', '2017-12-02 10:00:00', '2017-12-02 11:15:00', 1.25, 9, '索薪讨款', '文林所', '莲华池回迁办公室', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-04 09:26:14', NULL, NULL, '农民工', NULL, NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区内', '莲华池回迁办公室', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2647, 2519, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102440000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (531, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', NULL, '12月1日13名资金被骗群众到省政府上访', '2017年12月1日09:10分许，13名群众到省政府上访，反映，今年7月份他们陆续收到昆明滇多利商务信息咨询有限公司的电话，声称该公司可以为其办理低息贷款，根据贷款金额的不等收取手续费。来访群众反映，他们大约有200余人先后来到位于北京路1222号国际友联大厦的公司里，缴纳了几千至上万不等的手续费后，至今没有拿到贷款，涉及被骗手续费上千万元，后要求退钱时公司称只能退20﹪，引发200余人不满，三天前他们以资金被诈骗为由向金星所报案，由于金星所以及盘龙公安分局均未受理，所以到省政府上访，要求查处该公司的诈骗行为。省信访局接待后告知其向昆明市政府反映。10:00许13人全部离开省政府，上访过程平稳。（华山所）', '2017-12-01 09:10:00', '2017-12-01 10:00:00', 0.83, 13, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-04 09:30:51', NULL, NULL, '村民', NULL, NULL, NULL, '无', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (532, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-04 14:55:36', '12月4日因借高利贷被恐吓的13名群众到省政府上访', '12月4日14时15分许，13名居住在昆明的群众到省政府上访，反映，他们大约有1000余人因为向借贷公司借了高利贷，被该公司雇佣的社会人员采取殴打、泼油漆，发恐吓短信等方式追债，严重影响到他们及家人的生活，来访人员称，在位于滇池路云纺商业区的东南亚商业区A座B座内有20余家此类型的高利贷公司，借款时公司并未说明借贷利息如何算，所签的合同也不给借贷人一份，只是告知他们什么时候还，还多少，如果借贷人不还就暴力追讨。来访群众曾就此情况向永昌派出所报案，因为一直没有得到解决所以到省政府上访，要求政府查处这些非法放贷的公司的法律责任。现来访人员正在等待接待，秩序平稳。（华山所）\r\n', '2017-12-04 14:15:00', '2017-12-04 15:55:00', 1.67, 13, '民间借贷 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-04 15:05:39', '2017-12-04 17:15:30', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'ffzzwt', '非法组织问题', 'syr', '11-15人', '2', 'qkfy', 13, 0.00, 0, 0, '0', NULL, '情况反映', 'c7fe3ae5-20ed-4671-b0c1-f814b40240c2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (533, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-04 14:45:17', '12月4日在陇川县打工的农民6人因工钱被拖欠到省政府上访', '12月4日14时5分许，5名昭通市巧家县籍农民工到省政府上访，反映，他们去年初在德宏州陇川县生物发电厂工程做工，工程完工后发电厂至今拖欠200余名农民工200余万元的工钱未发，因多次向相关部门反映未果所以到省政府上访，要求尽快拿到工钱。省信访局接待后告知其回德宏州反映。14时30分许，6人全部离开省政府。上访过程平稳，无过激行为。（华山所）\r\n', '2017-12-04 14:05:00', '2017-12-04 14:30:00', 0.42, 6, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-04 15:05:39', '2017-12-04 15:14:59', NULL, '农民工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '2', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', 'a97efa11-f88f-48a8-b1be-07c0633defdb', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (534, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-04 12:18:23', '12月4日民大科技楼投资户及华敏美食城业主共30余人到华敏美食城讨要投资款', '12月4日8时30分许，民大科技楼投资户及华敏美食城业主陆续到位于一二一大街的华敏美食城负一楼聚集，讨要其投资款。文林所两名民警及时赶至现场，开展秩序维持工作。10时15分许，现场聚集的人数约为30余人，因华敏美食城负一楼现场无相关领导接待，后30余人转移至云南民族大学正门堵门，声称要堵学校大门。此时正处上课时间，进出学校的师生较少，事态未进一步升级。后30余人站在云南民族大学正门外，由云南民族大学的施处长在现场接待，现场无拉横幅等过激行为，云南民族大学校内教学秩序正常。11时15分许，以上人员再次回到华敏美食城业主陆续到华敏美食城负一楼聚集，自行协商下一步维权计划。声称再过一个周的时间，如果没有满意答复，他们将组织更多的人员来现场聚集讨要投资款。11时30分许上述人员全部离开现场。（文林所）\r\n', '2017-12-04 08:30:00', '2017-12-04 11:30:00', 3.00, 30, '合同纠纷 ', '文林所', '民院华敏美食城', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-04 15:05:39', '2017-12-04 15:13:28', NULL, '个体户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '民院华敏美食城', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2646, 2547, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '6e3e2cf7-4c53-4c7b-8ffc-f72e64f0f29c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102440000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (535, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-04 11:27:38', '12月4日修建莲花池商贸城的农民工代表5人因工钱被拖欠到省政府上访', '12月4日10时50分许，5名在五华区莲花池建盖国际商贸城的农民工代表到省政府上访，反映，他们受雇于富民致远建筑工程公司，从2012年起建盖商贸城，至2015年工程完工。该公司拖欠600余名农民工2014年1月至2015年1月的工钱共计1000余万元，因多次讨要未果所以到省政府上访，要求拿到工钱。现信访接待中。（华山所）\r\n', '2017-12-04 10:50:00', '2017-12-04 11:30:00', 0.67, 5, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-04 15:05:39', '2017-12-04 15:09:02', NULL, '农民工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '2663ea3f-c438-49bf-bcb3-85c2d6b7b7d9', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (536, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-04 11:22:23', '12月4日会泽县马路乡村民6人到省政府举报村干部行为', '12月4日10时45分许，6名曲靖市会泽县马路乡子树村村民到省政府上访，举报该村村支书李成文任人唯亲，以权谋私，歪曲政府扶贫政策，将困难户的指标分给亲属朋友，引发50余户村民不满，村民多次要求查处该人违纪行为未果，所以到省政府上访，要求查处该人。现信访接待中。（华山所）\r\n', '2017-12-04 10:45:00', '2017-12-04 11:30:00', 0.75, 6, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-04 15:05:39', '2017-12-04 15:07:55', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', '80accb51-819b-4add-8d46-fd1d9a63f59a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (537, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-05 13:48:42', '12月5日100余名金座集资人员到市体育馆聚集', '12月5日9时 30分许，100余名金座人员到市体育馆例行聚集。市中级人民法院相关工作人员在现场进行接访，在场上访人员情绪稳定、秩序正规，无过激行为。茭菱所派出两名民警在现场开展秩序维护疏导化解等工作。11时40分许，全部上访金座人员自行离开。（茭菱所）\r\n', '2017-12-05 09:30:00', '2017-12-05 11:40:00', 2.17, 100, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-05 15:34:19', '2017-12-05 15:39:13', NULL, '金座', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, NULL, NULL, NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'yblyr', '101-200人', '1', 'qkfy', 100, 0.00, 0, 0, '0', NULL, '情况反映', 'bf7b8f07-05f7-4b6d-b471-50dedc8c0167', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (538, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-05 12:16:52', '12月5日11名残疾人员到南屏步行街世纪广场“昆百大珠宝”聚集', '12月5日10时许，11名残疾人员到南屏步行街世纪广场“昆百大珠宝”聚集堵门，且悬挂印有“马立媛是骗子！欠债还钱”字样的横幅，其中世纪广场一楼“昆百大珠宝”铺面聚集7人，40楼办公区聚集4人。聚集人员称此次到世纪广场一楼和40楼堵门聚集，系因为该11名残疾人员（身份正在核实中）将共同集资的400万元及李云昆（身份正在核实中）出资的100万元共计500万元借予“昆百大珠宝”的负责人马立媛，马立媛未约定时间归还欠款，遂11人邀约至“昆百大珠宝”进行堵门讨债。经民警现场工作，横幅已被撤下，民警已将双方当事人带回派出所作进一步调查了解，期间未发生其他过激行为。相关情况持续关注中。（护国所）\r\n\r\n\r\n\r\n\r\n', '2017-12-05 10:00:00', '2017-12-05 12:15:00', 2.25, 11, '民间借贷 ', '护国所', '昆百大', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-05 15:34:19', '2017-12-05 15:43:39', NULL, '村民', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆百大', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2719, 2507, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'f3fefd87-837c-48f9-b516-c176cd615bb3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (539, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-05 10:19:02', '12月5日西山区天成花园居民78人因不满拆迁到省政府上访', '12月5日9时35分许，68名西山区原天成花园居民到省政府上访，截至10时许，人数增加到78人，反映，该小区2010年被政府拆迁，当时签订的合同是2013年建好房后原址回迁，涉及200余户，由于至今房屋没有建好，引发居民强烈不满，多年来一直向各级政府反映，要求尽快解决居民的回迁问题，由于得不到解决所以到省政府上访，要求政府赔给他们房子，不要货币补偿。现来访群众正在大厅等待接待，秩序相对平稳。（华山所）\r\n', '2017-12-05 09:35:00', '2017-12-05 12:00:00', 2.42, 78, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-05 15:34:19', '2017-12-05 15:37:29', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'cscq', '城市拆迁', 'wsyr', '51-100人', '2', 'qkfy', 78, 0.00, 0, 0, '0', NULL, '情况反映', '9ee2913c-a9a7-47f2-b323-38d15580677d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (540, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-06 16:32:05', '12月6日在寻甸县做工的农民工9人因工钱被拖欠到省政府上访', '   6日14:50分许，9名农民工到省政府上访，反映，他们于2014年起在寻甸县建盖锦湖花园二期地产项目，2016年初项目完工，至今，承包方四川长和建筑工程公司拖欠300余名农民工390余万元工钱。他们多次向寻甸县政府相关部门反映要求拿到工钱未果，所以到省政府上访，要求尽快拿到工钱。省信访局接待后告知其向昆明市政府反映。15:25分许，9人全部离开省政府，上访过程平稳。\r\n', '2017-12-06 14:50:00', '2017-12-06 15:25:00', 0.58, 9, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-06 20:31:06', '2017-12-06 20:32:29', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 9, 0.00, 0, 0, '0', NULL, '情况反映', 'cb9d001c-6ec8-481f-ac14-6b5789fbca3d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (541, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-07 14:56:21', '12月7日文茂大厦19楼优悠游旅游集团公司56名员工聚集欲讨要离职工资', '12月7日10时08分许，新村所接报文茂大厦19楼优悠游旅游集团公司有人聚集讨要工资。接警后，民警立即赶往现场处置。经了解，聚集人员有56人，系优悠游旅游集团公司员工（该公司负责人为刘铂川）。56人因不满公司有关员工考核的相关规定，要与该公司解除劳动合同并提前支付工资。该公司正常发工资的日期是每月15日，现该公司无法拿出56人的工资（约10万元）提前支付给该56人，双方僵持。经民警工作，文茂大厦物管工作人员、大观街道办事处富春社区居委会工作人员在现场协调，因该公司在官渡区注册，官渡区劳动监察部门相关人员也已经赶到现场协调解决。经工作,聚集人员于13时30分离开，要离职的56名员工派代表和公司负责人一同到官渡区劳动监察部门进行协商解决。现场秩序平稳，无过激行为。（新村所）\r\n', '2017-12-07 10:10:00', '2017-12-07 13:30:00', 3.33, 56, '索薪讨款', '新村所', '文茂大厦', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-08 09:32:23', '2017-12-08 09:45:45', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '文茂大厦', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2673, 2519, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, 'lzjf', '劳资纠纷', 'wsyr', '51-100人', '1', 'qkfy', 56, 10.00, 0, 0, '0', NULL, '情况反映', '69e9dc43-2cf5-43c2-a87f-ff6c7684672d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (542, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-07 14:48:02', '12月7日金座集资户开展周四例行聚集活动', '据工作掌握，12月7日上午9时30分许，以纪昌凤、罗益民为首的金座集资户群体到市体育馆上访。昆明市检察院及市金融办的相关领导接访。议题主要是围绕近日最高人民法院印发《最高人民法院关于进一步加强金融审判工作若干意见》提出30项意见，其中就非法集资案件审理问题，表述中改变过去“非法集资参与人”的法律概念，而明确“被害人”这一重要法律关系。并强调指出，“切实保护被害人的合法权益，有效维护社会稳定”，这一论述展开讨论。今天参加涉金座聚集的人员有120余人左右，下午14时许，上述人员自行离开市体育馆。以卢福全为首的金座集资户近10人到南屏步行街中心广场例行聚集活动，聚集过程中未发生过激行为，上午11时许，上述人员离开现场。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2017-12-07 09:30:00', '2017-12-07 14:00:00', 4.50, 130, '特殊群体', '华山所', '市体育馆、南屏步行街', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-08 09:32:23', '2017-12-08 09:43:33', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆、南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '561f171c-fb1d-4b2d-a035-ee6b9f45082e', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (543, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-07 11:02:34', '12月7日3人因工伤赔偿驾车到中海地产工地堵门', '12月7日9时22分许，红云所接110指令：红云路中海地产工地大门被堵。接警后，民警随即赶赴现场处置。现场寻甸县男子马顺学（47岁）与其妻子寻甸县女子丁米香（43岁）及其侄儿子共3人，驾驶一辆银白色小轿车（云A 3778X）将位于龙泉路及红云路交叉口的中海地产施工楼盘的大门堵住，不让施工车辆进出。经查，丁米春在中海地产建筑工地打工，10月14日在操作搅拌机时不慎造成左手中指受伤，就医后截断左手中指。后双方多次就赔偿事宜进行协商，要求工地方赔偿丁米香损失费用人民币60000元，但工地方仅愿意赔偿人民币25000元，未能协商达成一致。故今日马顺学将工地大门封堵，讨要说法。经民警工作，10时20分许，马顺学已将堵门车辆移走。现红云所正组织双方协商。相关情况密切关注中。（红云所）\r\n\r\n\r\n', '2017-12-07 09:20:00', '2017-12-07 10:20:00', 1.00, 3, '劳动争议 ', '华山所', '中海地产工地', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-08 09:32:23', '2017-12-11 15:37:12', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '中海地产工地', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2659, 2519, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'zhfx', 0, 0.00, 0, 0, '0', NULL, '综合分析', 'a4456109-7218-4fd8-830d-915ed0cb94f6', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (544, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-07 11:02:50', '12月7日丽江市宁蒗县村民代表5人因生活环境受污染到省政府上访', '7日09：05分许，5名丽江市宁蒗县红桥乡大梨树村村民到省政府上访，反映，2008年该村附近建了一个砂石料开发有限公司，该公司建成以来，严重污染附近环境，该村50余户村民的身体健康，家畜，庄稼遭受一定程度的损害，村民多次向乡县政府相关部门反映要求迁走砂石料公司未果，所以到省政府上访，要求将该公司迁走，省信访接待后，告知其回丽江市反映。9点15分许，5人全部离开省政府，上访过程平稳。(华山所)\r\n', '2017-12-07 09:05:00', '2017-12-07 09:15:00', 0.17, 5, '环境保护 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-08 09:32:23', '2017-12-08 09:36:34', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'hjwr', '环境污染', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '81aa0118-5179-4f19-95b2-4926c3d8124e', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (545, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-11 12:12:51', '12月11日德宏州陇川县村民5人因不满征地补偿到省政府上访', '12月11日11时20分许，德宏州陇川县景罕镇芒胆村曼洪傣组村民5人到省政府上访。来访村民反映2016年3月份，因为政府修建陇川通用机场的需要，征用了该村集体300余亩土地，上级共下发1000余万元的征地补偿，景罕镇镇政府在未召开村民大会的情况下将其中的600余万元拨付给无土地承包合同的村民，引发其他有承包合同的35户村民不满，要求镇政府按照全村现有人口平均分配。因多次向镇政府反映未果所以到省政府上访，要求平均分配补偿款。省信访局接待后告知其回德宏州反映诉求。上访过程平稳，无过激言行。11时30分许，5人全部离开省政府。（华山所）\r\n', '2017-12-11 11:20:00', '2017-12-11 11:30:00', 0.17, 5, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-11 14:09:52', '2017-12-12 09:09:13', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'ff8d5c8a-2042-41a3-bd8c-4e996ee18671', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (546, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-11 10:05:09', '12月11日云龙县漕涧镇村民5人因林地被盗买到省政府上访', '12月11日9时40分许，5名大理州云龙县漕涧镇漕涧村村民到省政府上访，反映，2014年该村村支书私自将39户村民的集体林地891亩抵押给银行骗取银行贷款1000余万元，后又将这些林地再次卖给私人老板用于开发，村民称，他们是看见树木被砍，林地被圈之后才知道村支书卖地的事，同时村民认为当地县林业局、镇长等人也参与此事，是村支书的保护伞，遂向当地各级政府反映要求查处村支书等人的违纪行为，由于没有答复所以到省政府上访，要求：1、按照国家标准赔偿村民被砍掉的树木；2、没收盗卖的赃款；3、严肃查处国家公职人员及村支书的违纪行为。现来访村民正在等待接待，秩序平稳。（华山所）\r\n\r\n\r\n', '2017-12-11 09:40:00', '2017-12-11 11:00:00', 1.33, 5, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-11 14:09:52', '2017-12-12 09:06:24', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '8404ab33-5155-46f2-8577-5c61c15cd401', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (547, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-08 09:15:30', '12月8日修建阿郎隧道的农民工10人因工钱被拖欠到省政府上访', '12月8日8时30分许，10名在昆明市修建阿郎隧道的农民工到省政府上访，反映，承包方中铁八局昆建公司拖欠15名农民工2016年、2017年两年的工钱共计100余万元，要求政府帮助解决。现信访接待中。秩序平稳。（华山所）\r\n', '2017-12-08 08:30:00', '2017-12-08 10:00:00', 1.50, 10, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-11 14:09:52', '2017-12-11 14:45:04', NULL, '农民工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '2', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', '507bfaeb-94d7-4416-a349-e87182d0c278', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (548, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-08 09:26:29', '12月8日镇雄县汽配城商户22人因不满拆迁一事到省政府上访', '12月8日8时20分许，22名昭通市镇雄县镇毕路阳光汽配城商户到省政府上访，反映，他们均是在该汽配城经营汽车美容、汽修、洗车等的商户，最早入住该汽配城的商户经营时间有10年左右，到目前共有76户商户在经营。来访人员称，今年4月份，镇雄县政府突然告知商户，该汽配城要拆迁，所有商户必须搬走，引发商户强烈不满，认为政府在没有考虑到商户的损失就进行强拆的行为严重影响了他们的生活，每家商户损失都在50万元以上，由于与镇雄县政府协商沟通不了，他们曾于5月2日到省政府上访，要求当地政府停止强拆，在与商户谈好赔偿事宜之后再拆迁。12月6日，当地城管部门对该汽配城进行拆迁，再次与商户发生冲突，22人遂今日到省政府上访，要求停止强拆。省信访局接待后告知其回昭通市反映。8时50分许22人离开信访大厅来到省政府大门侧面聚集。现华山所民警正在现场开展秩序维护工作，巡特警大队已派出30名特警赶往现场维护秩序，法制大队已派出法制宣传车赶赴现场进行法律宣讲工作。（华山所）\r\n\r\n\r\n', '2017-12-08 08:20:00', '2017-12-08 11:00:00', 2.67, 22, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-11 14:09:52', '2017-12-11 14:47:58', NULL, '个体户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'cscq', '城市拆迁', 'sldws', '16-50人', '2', 'qkfy', 22, 0.00, 0, 0, '0', NULL, '情况反映', '6e4153b4-060c-4081-83fc-7be992c11965', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (549, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-08 12:14:37', '12月8日32名在昆“12.13”人员到昆明市中级人民法院集访的情况', '12月8日9时30分许，因担心泛亚案件被秘密开庭，同时了解泛亚案件的开庭时间，陆续有32名在昆“12.13”人员到昆明市检察院聚集上访，10时15分许，市中院的工作人员接访了侬丽等5名“12.13”代表。截止目前，接访工作在进一步开展中。近期，涉泛亚庭审的相关动态仍是省内外“12.13”群体关注的重点，12月5日还出现部分人员煽动各省市“12.13”于2017年12月11日进京集访的情况。建议政府职能部门密切关注我市“12.13”群体的相关动态。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2017-12-08 09:30:00', '2017-12-08 12:00:00', 2.50, 23, '涉众型经济案件', '盘龙分局', '市检察院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-11 14:09:52', '2017-12-11 14:51:34', NULL, '12.13', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '市检察院', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2617, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '15b92b2f-daba-4de7-a485-0dacf07c7706', 'JS', NULL, 'Y', 110, NULL, NULL, '530103000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (550, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-11 14:36:55', '12月11日罗平县阿总村村民5人因不满林地被占到省政府上访', '12月11日14时10分许，5名罗平县罗雄镇阿总村村民到省政府上访，反映，该村集体所有的200亩林地在2011年与一种植金银花的私营企业签订了20年的租赁合同，来访村民称，去年，罗雄镇街道办事处借殡葬改革为由，打着“建设公益性公墓”为由，强行占用了村民租赁的这些林地，引发村民不满，他们认为自己的租赁合同是合法的，而办事处无视国家针对公墓选址的相关政策，强行占用村民林地，损害了村民利益，故要求办事处停止违法行为，由于在当地反映未果，所以到省政府上访，要求办事处立即停止占地行为，将林地还给村民。现来访村民正在等待接待，秩序平稳。（华山所）\r\n', '2017-12-11 14:10:00', '2017-12-11 17:00:00', 2.83, 5, '山林土地问题 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-12 09:04:58', '2017-12-12 09:32:36', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'e243a5aa-40d3-4674-9543-67a221533458', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (551, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-12-13 09:38:10', '12月13日在丽江市做工的农民工7人因工钱被拖欠到省政府上访', '12月13日9时15分许，7名农民工到省政府上访，反映，他们从2014年起在丽江市古城区参与建盖“仟佰汇商业城”，2016年完工，承包方天宇房地产开发公司拖欠近200名农民工一年半的工钱约1000余万元，他们多次向丽江市有关部门反映要求拿到工钱未果，所以到省政府上访，要求尽快拿到工钱。省信访局接待后告知其回丽江市反映。9时30分许，7人全部离开省政府，上访过程平稳。（华山所）\r\n', '2017-12-13 09:15:00', '2017-12-13 09:30:00', 0.25, 7, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-13 09:39:02', '2018-03-12 09:15:07', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', 'bad4db1d-d272-48e1-825e-8b3b301723dc', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (552, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-12 15:00:33', '12月12日  80余名“壹号广场”楼盘业主到省政府上访', '12月12日，8时40分许，赵李家堆回迁户、“壹号广场”业主为要求解决停水、停电、楼盘配套设施不完善等问题到省政府上访，其中赵李家堆回迁户还对楼盘开发商昆都国际房地产开发有限公司拖欠过渡费不满。五华分局局长助理孔翊平率分局指挥中心、治安大队、国保大队、法制大队、巡特警大队、华山所等部门在场开展稳控工作。今日10时许上报“12月12日部分赵李家堆回迁户、“壹号广场”业主到省政府上访（三）(以此件为准)”，华山所上报聚集上访人数为近百人，后经国保大队进一步核实，截至10时许聚集上访人数达峰值近80余人。10时30分许，省政府信访局、五华区信访局、龙翔街道办事处等政府的工作人接待了张立仁、刘基等5名群体代表。因对接待结果不满，来访群众一度聚集在省政府大门侧面人行道处。经分局现场警力及接访人员法制宣传及劝离工作，13时许上访群众自行离开省政府。现场秩序恢复平稳，未发生事态升级。鉴于群体诉求暂未得到彻底解决，群体到省市区政府职能部门集访的隐患仍然存在，建议五华区住建、信访、龙翔办事处、云南昆都国际地产公司在问题未得到彻底解决前，继续主动介入，开展好解释、疏导、化解工作。\r\n\r\n\r\n', '2017-12-12 08:40:00', '2017-12-12 13:00:00', 4.33, 80, '交房问题 ', '华山所', '省政府', NULL, NULL, '74', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-13 09:39:02', '2017-12-13 09:42:27', NULL, '赵李家堆', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2511, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'd5c0ec34-f3aa-4e63-91e1-3fc7f933a30c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (553, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-12 13:28:07', '12月12日金座集资户到市体育馆开展聚集活动', '12月12日9时30分许，以罗玉民、季昌凤为首的金座集资户到市体育馆上访。昆明市检察院、市中级人民法院及市金融办的相关领导接访。接访主要围绕金座集资户在争取“受害人”的身份以及金座案件中涉及民事部分与市检、市中院发生争论。中午12时30分上述人员自行离开市体育馆。今天到市体育馆参加涉金座的人数在120余人左右。相关情况在进一步掌握中。（国保大队）\r\n', '2017-12-12 09:30:00', '2017-12-12 12:30:00', 3.00, 120, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-13 09:39:02', '2017-12-13 09:41:03', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'eb282e74-2a81-4d39-88bf-11dc92eaa5f0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (554, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-14 10:06:56', '12月14日兰坪县金顶镇村民5人因不满退役安置问题到省政府上访', '12月14日9时30分许，5名怒江州兰坪县金顶镇高坪村村民到省政府上访，反映，他们共7人1997年从武警大理支队退役后回到兰坪县，他们属于城镇户口，按照当时的安置政策当地政府应该对其进行安置，来访人员称与他们相同情况的30余人都安置了，只有他们7人至今没有安置，一直在家务农，多年来一直就此问题向各级政府反映均未得到答复所以到省政府上访，要求退役安置，同时举报兰坪县政府乱作为致使群众利益受损。省信访局接待后告知其回怒江州反映同时向省纪委举报。9时45分许，5人离开省政府。（华山所）\r\n', '2017-12-14 09:30:00', '2017-12-14 09:45:00', 0.25, 5, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-15 15:45:46', '2017-12-15 15:54:28', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'tyaz', '退役安置', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '7bac3eb1-c5f0-4ea9-8638-fc699b3fa399', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (555, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2017-12-14 14:41:35', '12月14日金座集资户周四例行聚集活动暨金座集资户到市农业局上访', '12月14日9时许，陆续有100余名以纪昌凤、罗益民为首的金座集资户到市体育馆上访，要求市农业局主要领导接访。10时许，因对市农业局主要领导未到现场开展接访工作不满，近百名金座集资户采取坐公交、乘地铁等方式，陆续聚集到呈贡市农业局上访，市农业局的相关工作人员开展了接访工作。截止到发稿时，上述仍滞留在市农业局，继续要求农业局主要领导接访。12月14日10时许，以卢福权为首的10余名金座集资户继续到南屏步行街中心广场开展了例行聚集活动，11时许自行离开。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2017-12-14 09:00:00', '2017-12-14 15:30:00', 6.50, 100, '特殊群体', '华山所', '市体育馆、南屏步行街', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-15 15:45:46', '2017-12-15 15:56:39', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆、南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'c48353af-b161-401b-8bbc-deb550cd0c3b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (556, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-14 16:28:02', '12月14日永善县原溪洛渡电站移民9人因不满移民安置到省政府上访', '14日16时15分许，9名昭通市永善县原溪洛渡镇三坪村花椒湾社的村民到省政府上访，反映，2003年因为修建溪洛渡电站的需要，该村整体移民安置至永善县各地，有9户村民至今一直在永善县城内租房居住，生活压力大，他们多次向政府相关部门反映要求解决安置住房问题未果所以到省政府上访，要求尽快解决他们9户村民的安置房问题。现信访接待中，秩序平稳。（华山所）\r\n', '2017-12-14 15:00:00', '2017-12-14 16:00:00', 1.00, 9, '移民搬迁', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-15 15:45:46', '2017-12-15 15:58:09', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'ymwt', '移民问题', 'sryx', '10人以下', '2', 'qkfy', 9, 0.00, 0, 0, '0', NULL, '情况反映', 'ad7d0778-1cc5-4f62-9285-767fcf7319e1', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (557, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-14 18:15:49', '12月14日麒麟区独木水库煤矿业主及矿工110余人上访（续报）', '12月14日14时40分许，曲靖市麒麟区独木水库二级保护区保留煤矿矿主因不满去产能政策和煤矿整合重组等问题的引发煤矿被关停一事到省政府上访。该群体已于今年已多次到省政府上访，由于诉求一直没有得到解决所以今日再次到访，要求上级政府尽快拿出解决方案。情况发生后，五华分局副局长杨勇带领分局指挥中心、国保大队、巡特警大队、华山所等部门到场开展稳控工作，同时针对可能发生的过激行为已安排100名警力备勤。截止15时10分许，上访人数达110余人，15时40分许，省信访局接待其代表5人，接待完毕后，110余人仍在信访局附近滞留，云南省煤炭局及曲靖市政府工作组人员现正在信访局内协商处理事项。上访人员目前秩序平稳，暂无过激行为。现分局现场警力正在现在展开劝离工作，上访群众正陆续离开。（华山所）\r\n\r\n\r\n', '2017-12-14 14:40:00', '2017-12-14 18:30:00', 3.83, 110, '对政策不满', '华山所', '省政府', NULL, NULL, '100', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-15 15:45:46', '2017-12-15 16:00:29', NULL, '个体户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz,tsqt', '个体工商业者,特殊群体', 'dzzbm', '对政策不满', 'yblyr', '101-200人', '2', 'qkfy', 110, 0.00, 0, 0, '0', NULL, '情况反映', 'cd870fc0-ba11-4278-acc5-71b84a892991', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (558, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-15 14:27:43', '12月15日南屏街中心广场云南越战老兵聚集情况(三)【人数以此件为准】', '15日，9时52分许，参战老兵200（着65式军装130人，便装70人）余人，到南屏街中心广场聚集，并在现场打出一面印有“云南越战老兵、八一”字样的红色旗帜，另在新昆明影城门口拉出印有“祖国没有忘记我”字样的红底横幅。10时许200余名老兵前往新昆明影城，其中130名老兵进入新昆明影城3楼1号厅观影，并在观影前现场合唱“没有共产党就没有新中国”，另外70余人进入5楼3号厅观影。12时40分观影结束，13时30分许200余名参战老兵全部离开南屏步行街。观影期间秩序平稳，未发生过激行为。\r\n\r\n\r\n', '2017-12-15 09:50:00', '2017-12-15 13:30:00', 3.67, 200, '特殊群体', '护国所', '南屏步行街', NULL, NULL, '200', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-15 15:45:46', '2017-12-15 16:02:32', NULL, '对越参战老兵', NULL, '0', '2', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2723, 2524, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'eblyr', '201-500人', '2', 'qkfy', 250, 0.00, 0, 0, '0', NULL, '情况反映', '4568c638-3b1b-43ef-b90a-031265ada867', 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (559, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-13 14:33:24', '12月13日镇雄县芒部镇村民6人因房屋受损到省政府上访', '12月13日14时10分许，6名昭通市镇雄县芒部镇芒部村村民到省政府上访，反映，该村附近有一个采石场，经常放炮炸山，致使该村7户村民房屋地基塌陷，墙体开裂，村民向镇政府反映要求对房屋受损情况进行鉴定，经政府鉴定系采石场放炮所致，村民遂向采石场进行索赔，遭到拒绝，后村民向镇雄县政府反映要求采石场赔偿未果所以到省政府上访，要求上级政府出面协调采石场对村民进行赔偿。现信访接待中，秩序平稳。（华山所）\r\n', '2017-12-13 14:10:00', '2017-12-13 15:30:00', 1.33, 6, '损害赔偿 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-15 15:47:15', '2017-12-15 15:52:11', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', 'e58af39c-0302-4e8c-8e8e-da5a25e9bad9', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (560, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-13 11:16:55', '12月13日莲花池拆迁改造施工民工20余人到五华区政府上访讨要工资', '12月13日上午9时许，莲花池拆桥改造施工民工20余人到区政府上访讨要被拖欠的工资，经民警了解上访民工共计被拖欠工资9400余万元，系被莲花池拆迁改造开发商拖欠。在上访过程中，起初部分民工情绪激动出现在区政府大门楼梯处静坐的行为，经现场民警及政府工作人员劝说现静坐人员已停止静坐行为，区政府秩序恢复正常。现上访民工已由区信访、城建等部门工作人员接待。上访过程平稳，无其他过激行为。相关情况密切关注中。（翠湖所）\r\n\r\n\r\n', '2017-12-13 09:00:00', '2017-12-13 12:00:00', 3.00, 20, '索薪讨款', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-15 15:47:15', '2017-12-15 15:49:48', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2519, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'cscq', '城市拆迁', 'sldws', '16-50人', '1', 'qkfy', 0, 9400.00, 0, 0, '0', NULL, '情况反映', 'd4d7892e-87b7-4bc4-b172-6c1640d96426', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (561, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-13 13:34:03', '12月13日省地矿局退休职工95人因不满待遇问题到省政府上访（续报）', '12月13日11时30分许，95名省地矿局退休职工到省政府上访，反映，他们是省地矿局下属思茅、曲靖等各地州地矿局的退休职工。今年7月份省上下发文件给退休职工发放生活补贴，来访人员称，他们了解到在昆明的退休职工每月发1200元生活补贴，而地州的退休职工每月只发600元。引发地州职工不满，他们多次向省地矿局反映要求一视同仁未果所以到省政府上访，要求享受同等待遇。该起群众于11月23日曾就相同诉求到省政府上访过，由于不满接待结果，今日再次到访，他们进入信访大厅后准备拉出两条横幅，被民警及时制止。11时45分许，省信访局工作人员接待代表3人，告知其继续回省地矿局反映，再次引发来访人员不满，在大厅内吵闹，经民警现场工作，12时35分许，95名上访群众全部离开省政府。无其他过激行为，事态未进一步升级。（华山所）\r\n', '2017-12-13 11:30:00', '2017-12-13 12:35:00', 1.08, 95, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-15 15:47:15', '2017-12-15 15:51:01', NULL, '退休职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'gzfldy', '工资福利待遇', 'wsyr', '51-100人', '1', 'qkfy', 95, 0.00, 0, 0, '0', NULL, '情况反映', '3e349fa8-cd2b-49ba-b1f1-ae27f924cb0e', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (562, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-18 11:24:07', '12月18日蒙自物资有限公司5人因股权被没收到省政府上访', '12月18日10时35分许，云南蒙自物资有限公司股东5人（其中一人身穿80年代旧军装）到省政府上访，反映，他们一共8人均是该公司的合法股东，有股权和股份，2003年5月该公司企业改制时，公司董事长陈朴言在没有召开股东大会的情况下将8人的合法股权没收，股东身份被注销，引发8人不满，多次向公司及各级政府讨要说法未果，所以到省政府上访，要求一是严惩陈朴言贪官的违法行为，二是，将8人的合法股份归还给他们。该5人10时20分许到达省政府门口后，即在人行道处拉出2条横幅，民警当即制止，随后将5人引导至信访接待大厅反映问题，现信访接待中，秩序平稳。（华山所）\r\n', '2017-12-18 10:35:00', '2017-12-18 11:00:00', 0.42, 5, '企业改制/破产 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-18 17:28:50', '2017-12-18 17:30:39', NULL, '个体户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'jgqsyry', '机关企事业人员', 'qygzjbbc', '企业改制兼并破产', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '9655c20f-ff20-4c42-95df-0996762bfd97', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (563, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', NULL, '12月19日江苏籍居民7人因亲属在云南死亡一事到省政府上访', '19日16:20分许，7名江苏省泗洪县陈圩乡居民到省政府上访，反映，其父亲陈加升到云南旅游，12月13日参加昆明腾玺国际旅行社组织的昆明-版纳3日游，12月14日下午到达宁洱市墨黑镇吃晚餐时，陈加升突然倒地死亡，次日陈加升家属抵达墨黑镇，在与旅行社交涉的过程中，家属对旅行社的处理方式极度不满，随后向宁洱市政府相关部门反映，向旅行社讨要说法和赔偿未果，所以今日到省政府上访，要求昆明腾玺国际旅行社向家属说明陈加升的死亡原因同时要求旅行社给予家属赔偿。省信访局接待后告知其向云南省旅游执法总队反映。16:50分许7人全部离开省政府。上访过程平稳。（华山所）', '2017-12-19 16:20:00', '2017-12-19 16:50:00', 0.50, 7, '特殊群体', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-19 17:12:47', '2017-12-19 17:19:26', NULL, '村民', NULL, NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (564, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', NULL, '12月19日6名昭通永善溪洛渡水电站搬迁移民因搬迁问题到省政府上访', '19日16:35分许，6名昭通市永善县原溪洛渡镇三坪村花椒湾社的村民到省政府上访，反映2003年因为修建溪洛渡电站的需要，该村整体移民安置至永善县各地，该6户村民至今一直在永善县城内租房居住，生活压力大，他们多次向政府相关部门反映要求解决安置住房问题未果所以到省政府上访，要求尽快解决他们6户村民的安置房、宅基地、移民安置房等问题。省信访局接待后告知其回昭通市反映。16时55分许，6人全部离开省政府。（华山所）', '2017-12-19 16:40:00', '2017-12-19 16:55:00', 0.25, 6, '移民搬迁', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-19 17:14:29', '2017-12-19 17:27:41', NULL, '村民', NULL, NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (565, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-20 16:41:40', '12月20日宣威市来宾镇光明煤电公司的职工41人因不满企业改制到省政府上访（续报） ', '12月20日8时40分许，宣威市来宾镇光明煤电公司的41余名职工到省政府上访，主要反映，职工对该公司的企业改制工作不满问题。据了解，宣威市来宾镇光明煤电公司，自2001年进行企业改制，但至今都未能完成。主要原因是职工对企业出台的改制方案中，关于对职工的安置及国有资产的处置有较大异议。职工认为，企业对国有资产的处置不透明，对职工的安置不合理，在问题未解决前，企业领导强行要求职工签字认可，因而激起了职工的强烈不满情绪，于是到省政府上访。上访职工提出：1.领导因对企业的国有资产的处置应该详细透明的公示给职工；2.在职工安置问题上，应该对“新、老”职工区别对待；3.领导应与职工加强沟通交流，有想法、有意见应摆到桌面上来讲，不要“藏着、掖着”玩“猫腻”。省信访局及当地相关部门领导接待了上访职工。期间由于不满接待结果，11时30分许41人离开接访大厅到省政府大门旗杆下聚集。经民警工作，11时35分许，将41人劝说至省信访局侧面人行道处。经进一步工作，16时许，上访人员离开省信访局。相关情况进一步收集掌握中。（华山所）\r\n\r\n\r\n', '2017-12-20 08:40:00', '2017-12-20 16:00:00', 7.33, 41, '企业改制/破产 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-20 17:01:25', '2017-12-21 11:17:28', NULL, '退休职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '47ecabdd-e197-40b4-8423-257f80da2504', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (566, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-20 16:58:29', '12月20日地铁保通员到教场北路260号领取合同解除补偿款（二）', '据前期工作掌握，450余名解除劳动合同地铁保通员将于12月20日陆续到教场北路260号“昆明大容人力资源管理有限公司”领取解除合同补偿款，存不安定隐患。鉴于此情况，五华分局提前做好应急准备工作。20日分局副局长高勇带领指挥中心、国保大队、巡特警大队、法制大队、莲华所等部门62名警力到现场开展稳控工作，同时安排50名警力部门备勤。20日9时许“昆明大容人力资源管理有限公司”开始向解除劳动合同的地铁保通员发放补偿款。今日补偿款发放过程中秩序平稳，未发生过激行为及群体性事件。20日上午，310名保通员领取补偿款后自行离开；20日下午14时至17时间，又有136名保通员到“大容公司”领取补偿款后平稳离开。目前尚有4名保通员还未来领取补偿款，“大容公司”已通知该4人于12月21日前往公司领取补偿款。相关情况进一步掌握中。（国保大队）\r\n\r\n\r\n', '2017-12-20 09:30:00', '2017-12-20 11:30:00', 2.00, 101, '劳动争议 ', '莲华所', '昆明大容人力资源管理有限公司', NULL, NULL, '112', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-20 17:01:25', '2017-12-21 14:53:33', NULL, '农民工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆明大容人力资源管理有限公司', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2638, 2519, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '9432190f-7369-4acd-babb-816cae6ae41a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (567, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-21 10:37:52', '12月21日昭通市盐津县仙女洞煤矿工人6人因不满工伤赔偿到省政府上访', '12月21日10时5分许，6名昭通市盐津县仙女洞煤矿工人到省政府上访，反映，2011年至2014年他们在该煤矿从事井下挖煤工作，其中有32人因身体出现疾病于2014年初到昆明检查，后被省疾控中心确诊为尘肺病并认定为工伤，32人遂向煤矿提出工伤赔偿，遭到拒绝。2014年底，仙女洞煤矿被盐津县政府强制关停，煤矿以此为理由多次拒绝给工人工伤赔偿，来访工人认为，煤矿虽然关停了，但是政府赔偿给煤矿近1000余万元的补偿，足以支付他们的工伤赔偿。故再次向煤矿负责人讨要未果，所以到省政府上访，要求政府帮助其要到工伤赔偿。省信访局接待后告知其去省煤炭工业管理局反映。10时20分许，6人全部离开省政府。上访过程平稳，无过激行为。（华山所）\r\n', '2017-12-21 10:05:00', '2017-12-21 10:20:00', 0.25, 6, '损害赔偿 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-21 17:04:19', '2017-12-21 17:06:05', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'qsydw', '企事业单位', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'xgsyry', '下岗失业人员', 'mjjf', '民间纠纷', 'sryx', '10人以下', '1', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', '2d629adc-68f7-4a00-87f1-7efbdedc939e', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (568, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-21 11:32:50', '12月21日20余名农民工到黄土坡金泰国际二期追讨拖欠工资', '12月21日8时43分许，虹山所接群众电话报警报：金泰国际2期项目部，因老板两三个月不发工资，现场有二三十人，暂时没过激行为。 接警后，民警立即赶赴现场。经向报警人楚雄男子段正勇（28）了解，段正勇等20余人系在滇缅大道黄土坡中村金泰国际2期工地务工的工人，自2016年起一直在该工地上班，每人每月约6000-7000元工资不等，自2017年以来均未全额拿到过工资，开发商每月向工人支付1000-2000元不等的生活费。因接近年关，今日段正勇带20多人到项目部追讨拖欠工资。经与项目部负责人协商，其称所欠工资已经全部到账，预计在12月28日前发放。现工人代表正在与项目部负责人进一步协商，其余工人已经离开。28日如拖欠工资未能按时发放，可能引发农民工采取其它过激方式维权。相关情况密切关注中。（虹山所）\r\n', '2017-12-21 08:45:00', '2017-12-21 12:00:00', 3.25, 20, '索薪讨款', '虹山所', '金泰国际2期项目部', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-21 17:04:19', '2017-12-21 17:07:23', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '金泰国际2期项目部', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2619, 2519, NULL, NULL, NULL, NULL, NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'sldws', '16-50人', '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '40ecd4e6-8ab4-49c5-af81-60988227466a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102410000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (569, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-21 12:19:45', '12月21日建盖“爱尚苑”写字楼的施工方8人因工人工钱被拖欠到省政府上访', '12月21日11时20分许，8名建盖“爱尚苑”写字楼的施工方代表到省政府上访，反映，从2016年5月起，他们在位于盘龙区二环东路昙华片区景泰街与二环快速A线交叉路口的“爱尚苑”写字楼施工，至今已完成工程的百分之九十，但是承包方中金公司只支付了200万元的农民工工资，还有4200余万元的农民工工资未支付（涉及农民工人数来访人未说明），来访人称，他们不知道开发商是谁，找不到承包方的负责人，所以到省政府上访，要求政府帮助他们拿到工钱。省信访局接待后告知其向昆明市政府及相关劳动监察部门反映。11时30分许，8人全部离开省政府。（华山所）\r\n', '2017-12-21 11:20:00', '2017-12-21 11:30:00', 0.17, 8, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-21 17:04:19', '2017-12-21 17:08:14', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 8, 0.00, 0, 0, '0', NULL, '情况反映', '81d7defc-f185-40bc-9de1-31652cb3a997', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (570, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-21 15:51:18', '12月21日金座集资户到禄丰县了解金座公司资产处置情况暨开展周四聚集活动的情况', '12月21日9时许，以纪昌凤、罗益民为首的金座集资户群体成员70余人到昆明市火车南站乘火车前往楚雄州禄丰县，11时许到达禄丰县后到县政府，要求了解金座公司（基地）资产被处置的情况，禄丰县政府的工作人员开展了接访工作，经接访人员做工作，15时30分上述金座集资户返回陆丰火车站准备乘火车返昆。同日9时30分许，以卢福全为首的金座集资户近10人继续到南屏步行街中心广场开展例行聚集活动，12时30分许自行离开。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2017-12-21 09:00:00', '2017-12-21 12:00:00', 3.00, 80, '特殊群体', '护国所', '禄丰县政府、南屏步行街', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-21 17:04:19', '2017-12-21 17:09:50', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '禄丰县政府、南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2721, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '079f90e5-257c-4f37-a35d-b3b63ffd6842', 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (571, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-21 16:31:06', '12月21日盈江县永胜社区村民56人因不满房屋被强拆到省政府上访', '12月21日15时55分许，56名德宏州盈江县永胜社区小辛寨老寨村村民到省政府上访，反映，2017年2月27日，盈江县政府在没有事先与村民沟通的情况下突然张贴公告，告知村民其房屋属“违法建筑物”，要求全村260余户村民7日内自行拆除，恢复占地原貌，政府要在该区域进行城市建设。村民认为这些建筑是30年来逐渐形成的，是村民自己拿出资金建盖的，政府职能部门是认可的，现突然说是违法建筑要自行拆除村民无法接受。由于无人自行拆除，盈江县政府在11月份强行拆除了部分村民的房屋，引发村民不满，多次向县政府反映，要求赔偿未得到答复，该起村民于今年6月30日曾就相同诉求到省政府上访过，因问题没有解决，所以今日再次到省政府上访，要求：一是县政府要提供拆迁后的安置点；二是对村民被强拆的房屋以及安置进行补偿。现来访群众已有信访局工作人员接待，秩序平稳。（华山所）\r\n', '2017-12-21 15:55:00', '2017-12-21 17:00:00', 1.08, 56, '拆迁补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-21 17:04:19', '2017-12-21 17:11:13', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'wsyr', '51-100人', '2', 'qkfy', 56, 0.00, 0, 0, '0', NULL, '情况反映', '6a7b2ebc-7aea-480f-9fe7-cfcc635030dd', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (572, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-22 10:05:25', '12月22日昆明国防科工局退休教师7人因不满工资延期发放到省政府上访', '12月22日9时35分许，7名昆明国防科工局（地址在官渡区大石坝，单位对外名称是：云南铸造厂）下属自办学校（现学校已停办）的退休教师到省政府上访，反映他们一共有32名退休教师，多年来单位经常延期发放工资，最长时拖半年才发，来访人员称，他们至今不知道是哪个部门给他们发工资，也不知道是否移交给社保？引发退休教师不满，他们多次向单位领导反映，要求按期发放工资未果所以到省政府上访，要求明确告知他们，他们的工资到底是由科工局发还是由社保发，另外要求将他们移交给社保，按期发放工资。现信访接待中。（华山所）\r\n', '2017-12-22 09:35:00', '2017-12-22 09:40:00', 0.08, 7, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-25 15:24:45', '2017-12-25 15:28:12', NULL, '退休职工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '2', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '620ffb1c-40dc-46c1-818e-0446f725bd88', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (574, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-25 15:21:24', '12月25日曲靖市麒麟区潇湘村村民6人因自建房问题到省政府上访', '   12月25日14时45分许，6名曲靖市麒麟区潇湘社区潇湘村村民到省政府上访，反映，他们有18户村民因为没有宅基地一直没有自建房，今年初村委会将村内唯一的一块土地卖给了开发商搞建设，导致18户村民无地建房，因多次向政府部门反映要求划给他们宅基地未果所以到省政府上访，要求村委会给他们18户村民宅基地用于自建房。现信访接待中，秩序平稳。（华山所）\r\n', '2017-12-25 14:45:00', '2017-12-25 17:00:00', 2.25, 6, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-25 15:25:16', '2017-12-27 10:59:16', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', 'bc90b86d-a571-4286-9600-9415ed8a0a41', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (575, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-25 14:27:32', '12月25罗平县九龙街道村民6人到省政府举报村干部的违纪行为', '12月25日14时10分许，6名曲靖市罗平县九龙街道以土块村委会大备车村村民到省政府上访，举报该村村长和村支书一手遮天，从不召开村民大会就将自己的亲属、朋友报成贫困户，从而享受国家优惠政策，村民多次向上级部门反映要求公开公平选举困难户未果，所以到省政府上访，要求查处村干部的乱作为行为。现来访村民正在等待接待，秩序平稳。（华山所）\r\n\r\n\r\n', '2017-12-25 14:10:00', '2017-12-25 14:50:00', 0.67, 6, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-25 15:25:16', '2017-12-25 15:52:48', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', 'd334487f-7aa3-4786-840a-bfec528962be', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (576, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-25 11:28:40', '12月25日德宏州盈江县弄璋镇村民7人因土地被毁到省政府上访', '12月25日11时5分许，7名德宏州盈江县弄璋镇边府村村民到省政府上访，反映，12月7日，当地镇政府组织大批人员强行毁坏了400余户村民的560余亩土地，导致村民农作物全部被毁，村民称，政府强毁土地时并未告知村民理由，之前也没有发通知，引发村民强烈不满，多次向镇政府反映要求停止毁地行为未果所以到省政府上访，要求政府停止这种破坏行为，对毁坏的农作物进行赔偿，同时要求上级政府调查此事并撤换掉盈江县县长和书记。现信访接待中，秩序平稳。（华山所）\r\n', '2017-12-25 11:05:00', '2017-12-25 11:30:00', 0.42, 7, '损害赔偿 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-25 15:25:16', '2017-12-25 15:31:35', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '2', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '8ff1fb0e-9dba-484d-bb8c-4e20678f3535', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (577, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-25 10:27:50', '12月25日德宏州盈江县村民40人因不满房屋被强拆到省政府上访', '   12月25日8时45分许，40名德宏州盈江县永胜社区小辛寨老寨村村民到省政府上访，反映，今年2月27日，盈江县政府在没有事先与村民沟通的情况下突然张贴公告，告知村民其房屋属“违法建筑物”，要求全村260余户村民7内自行拆除，恢复占地原貌，政府要在该区域进行城市建设。村民认为这些建筑是30年来逐渐形成的，是村民自己拿出资金建盖的，政府职能部门是认可的，现突然说是违法建筑要自行拆除村民无法接受。由于无人自行拆除，盈江县政府在11月份强行拆除了部分村民的房屋，引发村民不满，多次向县政府反映，要求赔偿未得到答复，该起村民于今年6月30日、12月21日曾就相同诉求来访过，因问题没有解决，所以今日再次到省政府上访，要求：一是县政府要提供拆迁后的安置点；二是对村民被强拆的房屋以及安置进行补偿。现来访群众正在等待接待，秩序平稳。（华山所）\r\n\r\n\r\n\r\n\r\n', '2017-12-25 08:45:00', '2017-12-25 09:30:00', 0.75, 40, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-25 15:25:16', '2017-12-25 15:30:10', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sldws', '16-50人', '2', 'qkfy', 40, 0.00, 0, 0, '0', NULL, '情况反映', '98657f3d-ffc0-41e7-9047-843b3d4bb70f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (578, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', NULL, '12月23日五华区钱王街发生一起因经济纠纷引发的讨薪事件', '2017年12月23日17时50分，有10余名手持写有“我要生活，我要血汗钱”布标的人员在钱王街15号“雄柒印象火锅店”门口聚集。经了解该10余人（身份正在核实中）是“昆明煌杰装饰公司”装修工人，据10余名工人陈述，今年2月“昆明煌杰装饰公司”与“熊柒印象火锅店”签订装修合同，在装修进行过程中，双方曾对装修方案进行过多次改动，就装修过程中的增改项目产生的费用未达成一致意见，发生多次纠纷。今年10月“昆明煌杰装修公司”因装修费用问题再次与火锅店发生经济纠纷，在未完成装修扫尾工程的情况下撤离“熊柒印象火锅店”的装修场地。熊柒印象火锅店在装修公司撤离后找了另一家装修公司完成了扫尾工程。目前，“熊柒印象火锅店”还欠“昆明煌杰装饰公司”工程款30万余元，因此该公司无法按时发放工人的工资。据“熊柒印象火锅店”负责人席俊朗（男，30岁，四川省武侯区人，身份证号码：510107198701252179）陈述，火锅店仅欠装修公司1万元，对方所说欠款30余万元并不属实。12月23日17时许，“昆明煌杰装饰公司”10余名装修工人在“熊柒印象火锅店”聚集期间，曾冲进火锅店，与店内部分员工发生肢体冲突，护国派出所民警及时赶到，后将双方带回派出所。目前事件正在进一步调查中。', '2017-12-22 17:50:00', '2017-12-22 18:30:00', 0.67, 10, '索薪讨款', '护国所', '雄柒印象火锅店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-25 15:33:40', NULL, NULL, '农民工', NULL, NULL, NULL, '肢体冲突', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区内', '雄柒印象火锅店', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2722, 2519, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (579, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-26 13:53:41', '12月26日金座集资户例行活动中对接访不满后到市政府上访', '据工作掌握，12月26日上午9时30分许，以纪昌凤、罗益民为首的金座集资户群体到市体育馆上访。由于昆明市农业局的主要领导未到现场，委派一般工作人员来接访引发金座维权群体强烈不满。群体现场决定即刻乘地铁到昆明市农业局讨要说法。经五华国保大队及市金融办的相关领导做工作，未能阻止该群体到市农业局的意图。到上午10时许，上述人员约120余人排队离开市体育馆乘地铁前往（呈贡）昆明市农业局。后续情况进一步掌握中。（国保大队）\r\n', '2017-12-26 09:30:00', '2017-12-26 10:00:00', 0.50, 120, '特殊群体', '华山所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-27 10:42:43', '2017-12-27 10:49:38', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'yj', 0, 0.00, 0, 0, '0', NULL, '预警', 'ae7a70d7-d548-463b-8ad8-b5d2271f54ad', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (580, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-26 15:26:26', '12月26日厂口乡精顺养殖户14人因不满政府行为到省政府上访', '26日14:10分许，16名精顺公司养殖户到省政府上访，反映，2008年，他们响应昆明市政府“一湖两江”流域环境治理的号召，从盘龙区、官渡区、五华区、经开区等地搬到厂口乡秧草塘建盖猪场，由11户养殖户组成成立了精顺养殖有限公司，发展养殖业至今。来访人员称，12月20日，五华区城管突然发布通告，认定精顺养殖场为违章建筑，要求其在今年12月27日前自行拆除猪舍并搬走，引发养殖户不满，他们认为养殖场的手续是合法的，当时搬迁至厂口乡的时候，政府还给予每家搬迁的养殖户经济补偿，不是违章建筑，不愿搬迁，他们向五华区及昆明市政府反映，要求继续养殖未果，所以到省政府上访，要求政府收回拆迁通告，准许他们继续经营。现来访群众正在等待接待，秩序平稳。(华山所)\r\n', '2017-12-26 14:10:00', '2017-12-26 15:00:00', 0.83, 14, '拆迁补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-27 10:42:43', '2017-12-27 10:52:49', NULL, '个体户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'syr', '11-15人', '2', 'qkfy', 14, 0.00, 0, 0, '0', NULL, '情况反映', '6b8af73b-7e53-41d6-bd17-c6b0c6569317', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (581, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-26 15:28:10', '12月26日马龙县烟草公司合同工5人因养老问题到省政府上访', '26日14:10分许，5名曲靖市马龙县烟草公司合同工到省政府上访，反映，他们均是上世纪80年代参加工作的，一直在马龙县烟草公司工作，2001年起，公司陆续辞退了他们，涉及300余人，来访人员称，在工作期间公司没有给他们办理任何保险，只是单纯发放工资，辞退后也没有任何补助，他们认为，他们在公司工作了三四十年，根据国家劳动法的相关规定，他们应该享受各种保障，但是公司一直没有给他们办理，由于向各级政府反映要求给予养老补助未果，所以到省政府上访，要求公司按照劳动法的规定给他们办理应该享受的各种养老补助。现来访群众正在等待接待，秩序平稳。(华山所)\r\n', '2017-12-26 14:10:00', '2017-12-26 16:10:00', 2.00, 5, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-27 10:42:43', '2017-12-27 10:54:19', NULL, '退休职工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'ed497c1d-1617-4988-8a5b-082bbcd3180c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (582, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-26 16:18:08', '12月26日自称“光照集团昆明瑞泽课堂”人员持布标横幅在南屏步行街中心广场游行(续报)', '经护国所核实，到南屏步行街游行的人员共有44人。护国所将44人全部带回所了解情况。据了解，此次游行的组织者为付伟（男，40岁，昭通市鲁甸县龙树镇人，身份证号码：53212219770106181X），该人曾于2007年12月因在玉溪红塔区涉嫌寻衅滋事被判处有期徒刑3年，在玉溪黑村监狱服刑，于2009年底释放。付伟称：2017年7月其通过微信朋友圈加入“传统文化学习群”等微信群，开始接触传统文化。2017年11月，付伟利用其租用的住所盘龙区吴井路183号“广和苑”小区601室开办传统文化课堂，并组建“中国昆明梦瑞泽课堂学习群”和“官渡瑞泽课堂讲师和主持群”。截止目前，共在其住所组织学习20次，参与人员近200余人。12月13日至12月15日，付伟自费到山东省菏泽区一名为“山东光照文化传播发展有限公司”的企业学习传统文化和毛泽东思想，期间加入“华南区总部承办人”“云南课堂承办人工作群”2个微信群。12月24日，付伟通过“华南区总部承办人”微信群收到纪念毛泽东诞辰的通知后，决定于12月26日9时，在环城南路雷锋广场组织纪念活动，随后通过“中国梦昆明瑞泽工作群”发布了通知，之后12月25日下午在菊花村附近的一家广告制作室制作和购买了布标、毛泽东像、国旗等物件。12月26日9时，付伟组织其微信群内邀约的12人手持布标、国旗、毛泽东像，从官渡区吴井路“百富琪大厦”步行出发，途径吴井路、拓东路、金碧路、三市街进行游行，纪念毛泽东诞辰，在到达宝善街时才被民警截停，游行过程中沿途有30余名群众自发参与到游行队伍中。目前，44游行人员及暂时扣留的物件已移交官渡分局吴井路派出所进一步处理。\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2017-12-26 09:00:00', '2017-12-26 11:10:00', 2.17, 44, '特殊群体', '护国所', '南屏步行街', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-27 10:42:43', '2017-12-27 10:58:06', NULL, '群众', NULL, '0', '1', '游行、拉布标', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2723, 2555, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'a32060ea-16c2-4f31-8761-f541eadeb03d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (583, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-26 11:36:13', '12月26日罗平籍“三线建设”人员因生活补助问题到省政府上访', '26日10:05分许，13名曲靖市罗平县阿岗镇海马村委会海马村村民到省政府上访，反映他们于上世纪七十年代响应国家号召，配合昆明军区后勤部参与4820工地的“三线建设”，主要工作是修建备战仓库和兵工厂，当时一共招了罗平籍人员800余人，部队给其算工分，工作时间最长三年，短的一年，工程完工后即返乡务农。来访人员称，现在他们年纪大了，国家政策也好了，要求上级政府帮助他们解决目前还健在的388名罗平籍“三线”人员的养老问题。他们多次向罗平县及曲靖市政府反映一直未得到解决，所以到省政府上访，要求政府给予他们养老生活补助。省信访局接待后告知其向省民政厅反映。现13人在武警操场处聚集，罗平县公安局国保民警正在与之沟通，13人目前秩序平稳。\r\n\r\n\r\n\r\n\r\n\r\n华山东路派出所\r\n\r\n2017年12月26日\r\n', '2017-12-26 10:05:00', '2017-12-26 10:50:00', 0.75, 13, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-27 10:42:43', '2017-12-27 10:47:52', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'syr', '11-15人', '2', 'qkfy', 13, 0.00, 0, 0, '0', NULL, '情况反映', 'c09caa02-ada7-41d4-bf0a-0cd9319d641d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (584, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2017-12-28 13:53:46', '12月28日金座集资户例行周四聚集活动情况', '据工作掌握，12月28日上午9时30分许，以纪昌凤、罗益民为首的金座集资户群体到市体育馆上访。禄丰县政府畜牧兽医局、五华经侦大队及市金融办的相关领导接访。议题主要是围绕金座资产处置的一些情况进行交流，金座集资户对资产的处置情况持异议，认为存在数量不符以及贱卖的情况。今天参加旁听的人数在120余人左右。截至上报时，交流还在进行中。以卢福权为首的金座集资户群体近10人9时30分到南屏街中心广场开展例行聚集活动，聚集过程中无过激行为。10时许上述人员自行离开现场。（国保大队）\r\n\r\n\r\n', '2017-12-28 09:30:00', '2017-12-28 12:30:00', 3.00, 120, '特殊群体', '茭菱所', '市体育馆、南屏步行街', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-28 15:41:44', '2018-03-12 09:14:31', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆、南屏步行街', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '74ad1760-fa66-41af-8cf7-74823bb805f4', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (585, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-28 10:20:54', '12月28日富源县民政局合同工14人因不满被辞退到省政府上访', '12月28日9时50分许，14名曲靖市富源县民政局合同工到省政府上访，反映，2010年6月，富源县民政局发布通告，招聘合同工到各乡镇民政所工作，来访人员称共有24人被招聘后参加工作，当时县民政局的合同上明确试用期6个月，每月工资1300元，试用期结束后每月按当地平均工资标准发放，但至今民政局依然按每月1300发工资，这个月县民政局强制解除了他们24人的合同关系，每人补偿2万元，引发24人不满，他们认为民政局违反了国家劳动法的相关规定，因多次向曲靖市有关部门反映要求继续在民政局工作未果，所以到省政府上访，要求：1、无正当理由不能解除劳动关系；2、按照合同的约定补发工资；3、按照国家法律规定为他们购买相应的保险。现信访接待中，秩序平稳。（华山所）\r\n', '2017-12-28 09:50:00', '2017-12-28 10:30:00', 0.67, 14, '合同纠纷 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-28 15:41:44', '2018-01-02 10:46:34', NULL, '农民工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'xgsyry', '下岗失业人员', 'mjjf', '民间纠纷', 'syr', '11-15人', '2', 'qkfy', 14, 0.00, 0, 0, '0', NULL, '情况反映', 'ab0ebb33-0130-4a3c-9a33-d6e75c0d0279', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (586, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2017-12-27 12:41:35', '12月27日省地矿局退休职工5人因不满待遇问题到省政府上访', '12月27日11时15许，5名省地矿局（地址在白塔路金格购物中心旁）退休职工代表到省政府上访，反映，他们是省地矿局下属思茅、曲靖等各地州地矿局的退休职工。今年7月份省上下发文件给退休职工发放生活补贴，来访人员称，他们了解到在昆明的退休职工每月发1200元生活补贴，而地州的退休职工每月只发600元。引发地州职工不满，他们多次向省地矿局反映要求一视同仁未果所以到省政府上访，要求享受同等待遇。该起群众于11月23日（70人）、12月13日（95人）曾就相同诉求到省政府上访过，由于不满接待结果，今日再次到访，现来访群众5人已有省人社厅人员在小会议室接待，目前情绪平稳。（华山所）\r\n\r\n\r\n\r\n\r\n', '2017-12-27 11:15:00', '2017-12-27 12:00:00', 0.75, 5, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-28 15:41:44', '2017-12-28 15:46:36', NULL, '退休职工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '74971d35-ae23-4c08-9bc3-a61ecb8233de', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (587, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2017-12-27 11:04:33', '12月27日沧源县出租车司机6人因不满政府行为到省政府上访', '12月27日10时30分许，6名临沧市沧源县出租车司机到省政府上访，反映，他们一共有75辆车，由司机自己出资购买出租车，挂靠沧源县出租车分公司并取得了经营权，每轮经营权是8年，现已经营了两轮，2017年初沧源县政府出台文件，要求将出租车的挂靠模式转为公司经营模式，由出租车公司收回个人经营权并统一购买出租车租给司机经营，来访司机认为，国家有文件，经营模式的转换有一定的过渡期，而沧源县在今年底就要全部收回75辆车的经营权是违反国家相关规定的，他们向临沧市相关部门反映要求延长经营权未果，所以到省政府上访，要求沧源县政府再给他们一轮8年的经营权。省信访局接待后告知其回临沧市反映。10时50分许，6人全部离开省政府。（华山所）\r\n', '2017-12-27 10:30:00', '2017-12-27 10:50:00', 0.33, 6, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2017-12-28 15:41:44', '2017-12-28 15:43:32', NULL, '个体户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'yysj', '营运司机', 'dzzbm', '对政策不满', 'sryx', '10人以下', '1', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', '05b89a37-33c2-4cef-af55-184ea5694fdc', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
