INSERT INTO `qb_zdasjqbxx` VALUES (901, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-03-21 12:54:48', '3月21日宣威市居民7人因对分配政策不满到省政府上访', '   21日11时许，曲靖宣威市宛水街道居7名民到省信访局反映，1993年-1997年他们在曲靖市农机化学校学习，当时政府的政策是毕业后统一分配安置，但毕业后政府一直没有给他们分配工作，涉及15人，为此他们一直向各级政府反映要求调查处理未果，所以到省政府上访，要求按照当年的分配政策对他们15人进行分配工作以及缴纳五险一金等。经接待，上述人员于11时20分许自行离开。\r\n', '2018-03-21 11:00:00', '2018-03-21 11:20:00', 0.33, 7, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-22 14:42:15', '2018-03-26 10:35:28', NULL, '学生', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2543, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'dzzbm', '对政策不满', 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', 'b5b7ef72-ca51-417f-a281-b9b28235ffc4', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (902, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-03-21 14:50:25', '3月21日镇雄县芒部镇村民5人因房屋受损到省政府上访', '21日14时许，昭通市镇雄县芒部镇芒部村5名村民到省信访局反映，该村附近有一个采石场，经常放炮炸山，致使该村7户村民房屋地基塌陷，墙体开裂，村民向镇政府反映要求对房屋受损情况进行鉴定，经政府鉴定系采石场放炮所致，村民遂向采石场进行索赔，遭到拒绝，后村民向镇雄县政府反映要求采石场赔偿，经当地政府协调，采石场同意支付赔偿，由于具体金额存在异议未协调好，所以到省信访局上访，要求上级政府出面再次协调采石场对村民的赔偿。经工作，上述人员于14时25分许自行离开。\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n华山东路派出所\r\n\r\n2018年3月21日\r\n\r\n\r\n', '2018-03-21 14:00:00', '2018-03-21 14:25:00', 0.42, 5, '环境保护 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-22 14:42:15', '2018-03-27 14:07:49', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '963207ce-e7a8-4d95-bbb8-e3c5adf825dd', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (903, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-03-21 14:52:16', '3月21日东川区红土地镇村民14人因不满被脱贫到省政府上访', '21日14时许，东川区红土地镇花沟村委会花沟小组5名村民到省信访局反映，红土地镇被当地政府划为旅游开发区，2016年村委会要求该村村民旧房翻新，口头承诺给予贫困户补助51800元，普通村民补助4万元，另外当地政府要求村民建好房后办理营业执照，可在自家经营农家乐等。来访村民称房子建好后有多户村民一直未拿到补助，且办理了营业执照的村民政府就取消了其贫困户指标，宣布其脱贫，引发村民不满，他们认为是被脱贫的，即使办理营业执照实际家庭仍然很困难。村民称村委会成员任人唯亲，将贫困户指标给了其亲戚朋友，真正困难的村民未享受到国家扶贫政策，故多次向当地政府部门反映要求公开贫困户评选标准未果，所以到省政府上访，要求一是尽快将建房补助发给村民；二是公开公正的评选困难户。该起村民于3月14日曾就相同诉求到访过。经工作，上述人员于14时35分许自行离开。\r\n\r\n\r\n', '2018-03-21 14:00:00', '2018-03-21 14:35:00', 0.58, 15, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-22 14:42:15', '2018-03-26 10:45:29', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '6dd08f80-7256-4369-a498-a081ff09ff89', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (904, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-03-21 18:08:22', '3月21日黄土坡中村回迁村民聚集丰宁办事处要求开发商履行承诺', '3月21日15时许，原黄土坡中村10余名拆迁户聚集丰宁办事处，反映黄土坡中村拆迁时，开发商云南文洁房地产开发有限公司曾承诺村民回迁选房超出的面积可按照建筑成本享受30—40平方米的优惠。近期，金泰国际二期开始安排村民回迁，选房超出的面积却要求村民按市场价购买，引发不满。期间，村民还拔打市长热线反映。经工作，村民于16时许自行离开。相关情况进一步掌握中。\r\n', '2018-03-21 15:00:00', '2018-03-21 16:00:00', 1.00, 10, '拆迁补偿', '虹山所', '丰宁办事处', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-22 14:42:15', '2018-03-26 10:48:49', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '丰宁办事处', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2621, 2507, NULL, NULL, 'fyqk', '反映情况', NULL, NULL, 'qy', '请愿', 'nm', '农民', 'cscq', '城市拆迁', 'syr', '11-15人', '1', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', 'c560976b-a2d6-401d-aad9-5c756e08371d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102410000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (905, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-03-16 11:32:43', '3月16日10余名东风云南汽车厂退休职工聚集厂区门口', '3月16日10时许，10余名退休职工在厂门口聚集，厂区大门是打开的，两个铁凳摆在厂大门口,2名退休职工继续坐在铁凳上，不让车辆进出，人员和电动车可正常出入。下厂区仍挂着布标。现场聚集职工无过激行为，相关情况密切关注中。（黑林铺所）\r\n\r\n\r\n', '2018-03-16 10:00:00', '2018-03-16 17:00:00', 7.00, 10, '对政策不满', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-26 09:14:34', '2018-03-26 09:51:27', NULL, '改制工人', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '814129af-3349-45e2-a44f-46f7789e9b8c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (906, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-03-16 10:47:19', '3月16日武定县插甸镇村民7人到省政府举报村支书违纪行为', '3月16日10时25分许，7名楚雄州武定县插甸镇古普村委会下谷都艾村村民到省政府上访，反映，2016年该村村支书在未召开村民大会的情况下私自将村内10余亩集体林地转卖私人老板，获利20余万元，村民发现后多次向当地政府相关部门反映要求查处村支书的违纪行为未果，所以今日到省政府上访，要求将林地还给村民同时查处村支书的违纪行为以及获利资金的去向。现信访接待中。（华山所）\r\n', '2018-03-16 10:25:00', '2018-03-16 11:30:00', 1.08, 7, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-26 09:14:34', '2018-03-26 09:25:59', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', 'a6fcf3a6-e276-424c-ba3a-6fb77a5382c7', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (911, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-03-19 15:59:58', '3月19日20余名业主聚集如意大厦4楼佳达利房地产公司要求尽快交房', '3月19日9时40分许，小南所接110指令“12345市长热线曹女士（18987058713）反映，别样幸福城小区因一直没有交房，现在有40多名业主到如意大厦4楼与开发商协商，但是开发商不接待业主，现场有业主情绪激动”。接警后，民警立即赶至现场。20余名别样·幸福城业主聚集人民中路36号如意大厦4楼佳达利房地产公司，咨询交房问题，因未找到公司负责人于是拨打市长热线求助。经初步走访调查得知，别样·幸福城业2012由佳达利房地产公司开发建设，共有5块地，目前1、2、3号地已建成交房，4号地已封顶，5号地在建，涉及未交房业主100余户。经民警现场工作，双方协商后聚集人员于12时许自行离开。相关情况密切关注中。（小南所）\r\n\r\n\r\n', '2018-03-19 09:40:00', '2018-03-19 12:00:00', 2.33, 20, '交房问题 ', '小南所', '如意大厦', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-26 09:15:42', '2018-03-26 10:16:34', NULL, '别样幸福城小区业主', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '如意大厦', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2736, 2530, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '22c65879-062c-4368-a341-50ae03b04ef0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (912, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-03-19 13:44:19', '3月19日30余名篆新市场原始租户到大观街道办事处反应诉求', '3月19日11时30分许，篆新市场30余名原始租户到大观街道办事处反应诉求，反映虽原租赁合同期限到期，现市场由省建三公司收回经营权，但他们作为市场原始租赁者，曾参与市场的投资，对市场发展和繁荣做出了贡献，要求省建三公司继续将商铺继续租赁给他们，且租金不能涨太多。大观所配合大观街道办事处开展疏导劝解工作，相关情况密切关注中。\r\n', '2018-03-19 11:30:00', '2018-03-19 12:30:00', 1.00, 30, '合同纠纷 ', '大观所', '大观街道办事处', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-26 09:15:42', '2018-03-26 10:17:37', NULL, '农贸市场租户', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局,报区政府', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '大观街道办事处', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2676, 2548, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '4ae21d6c-f91f-4d44-976f-4c01dfa63777', 'JS', NULL, 'Y', 110, NULL, NULL, '530102480000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (914, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-03-19 11:47:15', '东风云南汽车厂退休职工因搬迁待遇问题聚集在云南汽车厂门口', '\r\n\r\n3月19日9时许，东风云南汽车厂20余名退休职工在上厂门口两边人行道上聚集，厂区大门是打开的，车辆及人员可正常出入厂区。下厂区仍挂着布标。目前现场聚集职工无过激行为。相关情况密切掌握中。\r\n\r\n\r\n', '2018-03-19 09:00:00', '2018-03-19 17:00:00', 8.00, 20, '对政策不满', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-26 09:15:42', '2018-03-26 09:51:16', NULL, '企业改制工人', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '96d720e5-1540-41c3-a86e-1bfa5563b416', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (915, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-03-22 12:51:07', '3月22日30名金座集资资户到省信访局投诉市金融办不作为', '22日11时许，30名金座投资人到省信访局反映，按照惯例今日在市体育馆接访的应为市金融办的主管领导，但接待他们仅是普通工作人员，投诉市金融办敷衍办公、不作为，不关心该群体的诉求，要求上级政府督促市金融办重视金座受害群体的问题。经接待于11时20分许自行离开。相关情况进一步掌握中。\r\n', '2018-03-22 11:00:00', '2018-03-22 11:20:00', 0.33, 30, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-26 09:16:49', '2018-03-26 10:51:04', NULL, '金座', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2502, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'mjjf', '民间纠纷', 'sldws', '16-50人', '1', 'qkfy', 30, 0.00, 0, 0, '0', NULL, '情况反映', '628ed642-e9b9-40e2-9713-1ec2e96bfebc', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
