INSERT INTO `qb_zdasjqbxx` VALUES (919, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-03-26 16:01:56', '3月27日云南建投退休职工6人因不满拆迁安置到省政府上访', '26日14时20分许，云南建投6名退休职工到省信访局反映，其一直居住于官渡区民航路19号单位福利房，2014年11月该片区城中村改造搬迁。2015年12月，回迁房“拓东大城”建好，其中10余户建投退休职工因各种原因一直未办到房产证，公司不允许他们回迁，多次向公司及政府部门反映无果，请求上级政府出面协调，尽快安排回迁。现6人正在等待接待，秩序平稳。\r\n\r\n\r\n', '2018-03-26 14:20:00', '2018-03-26 15:00:00', 0.67, 6, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-27 13:50:14', '2018-03-27 14:07:15', NULL, '退休职工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'cscq', '城市拆迁', 'sryx', '10人以下', '2', 'qkfy', 6, 0.00, 0, 0, '0', NULL, '情况反映', 'f55f1582-1124-4894-8866-c8727a9861fa', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (920, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-03-26 13:37:12', '3月26部分岗头村村民到红云办事处聚集情况', '3月26日10时许，陆续约80余名岗头村村民聚集红云办事处，反映村委会低价出让集体土地出问题。经工作，村民于12时许自行离开，并声称27日还将到办事处聚集。相关进一步掌握中。\r\n\r\n\r\n', '2018-03-26 10:00:00', '2018-03-26 12:00:00', 2.00, 80, '征地补偿', '红云所', '红云街道办事处', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-27 13:50:14', '2018-03-27 14:05:09', NULL, '岗头村', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '红云街道办事处', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2508, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'b787a505-28f7-4717-8ec1-43086d2f4187', 'JS', NULL, 'Y', 110, NULL, NULL, '530102490000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (921, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-03-26 11:20:12', '3月26部分在昆“12.13”人员到昆明中院聚集上访情况', '3月26日9时30分许陆续有20余名在昆“12.13”人员到昆明市中级人民法院聚集上访，聚集过程中未发生喊口号、打标语等过激行为，昆明市中级人民法院的相关工作人员分两次对10名代表进行了接访，10时50分许接访结束，上述人员陆续离开现场。相关情况进一步掌握中。\r\n', '2018-03-26 09:30:00', '2018-03-26 10:50:00', 1.33, 20, '涉众型经济案件', '西山分局', NULL, NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-27 13:50:14', '2018-04-20 11:39:22', NULL, '12.13', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '市中级人民法院', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '9b8afff7-b2a6-4305-a280-1214c89c2c32', 'JS', NULL, 'Y', 110, NULL, NULL, '530105000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (922, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-03-26 11:20:07', '3月26日宜良县匡远镇村民7人到省政府举报村干部违纪行为', '26日10:30分许，7名昆明市宜良县匡远镇启春社区居民到省政府上访，代表3个社区的200余户村民举报村干部长期以来不问群众疾苦，利用手中权力擅自剥夺村民权利，尤其是女性村民在离异、外嫁、嫁入等情况时，没有给予她们相应的村民待遇，以及私自动用集体资金为自己的亲属提供便利等，来访村民称她们多次向当地政府等部门反映要求查处村干部，还村民权利未果所以到省政府上访，要求上级政府调查村干部的违纪行为。现信访接待中。(华山所)\r\n', '2018-03-26 10:30:00', '2018-03-26 11:30:00', 1.00, 7, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-27 13:50:14', '2018-03-27 13:56:23', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '57f4f98b-c6eb-47c5-9fb3-92e4248ca178', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (923, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-03-26 11:13:04', '3月26日漾濞县苍山西镇村民20人因不满征地补偿到省政府上访', '26日10时5分许，大理州漾濞县苍山西镇马厂村委会马厂村20名村民人到省信访局反映，去年6月份，当地政府要建盖漾濞核桃工业园区，征用多个村的土地，补偿标准是，在园区内的土地每亩补偿19.8万元，园区外的每亩补偿7.9万元，引发园区外涉及的500余户村民不满，绝大部分是马厂村的土地，大约1000余亩，来访村民称，除了不满补偿标准不一致外，村民对政府征地的行政行为存在异议，认为该村被征的土地是基本良田，国家是有严格控制的，当地政府在征用土地的时候事先没有告知村民，有违法嫌疑，所以多次向大理州各级政府反映要求调查征地行为未得到满意答复，所以到省政府上访，要求：一是当地政府拿出征地的相关文件告知村民；二是在征地的过程中公开公正，一视同仁。省信访局接待后告知其回大理州反映诉求。10时35分许，村民被当地工作组带离省政府。\r\n', '2018-03-26 10:05:00', '2018-03-26 10:35:00', 0.50, 20, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-27 13:50:14', '2018-03-27 13:54:12', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sldws', '16-50人', '1', 'qkfy', 20, 0.00, 0, 0, '0', NULL, '情况反映', '57c64710-5201-4a0a-a955-e9abcfecf52d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
