INSERT INTO `qb_zdasjqbxx` VALUES (935, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-03-27 10:30:29', '3月27日师宗县竹基乡村民7人到省政府举报村支书违纪行为', '3月27日9时50分许，7名曲靖市师宗县竹基乡永安村委会永安村村民到省政府上访，举报该村村支书以权谋私、贪污挪用村内集体收支资金，在烤烟收购及指标分配、危房改造等工作中损害300余户村民利益。因该县副县长系村支书亲属导致村民多次向有关部门举报未果，故今日到省政府上访，要求上级政府给予调查。信访接待中。（华山所）\r\n', '2018-03-27 09:50:00', '2018-03-27 11:00:00', 1.17, 7, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-29 14:50:01', '2018-03-29 14:54:05', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '2', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '0debaec6-1209-4396-932a-c1a3d1cbdcb1', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
