INSERT INTO `qb_zdasjqbxx` VALUES (939, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-03-28 20:17:09', '40余人到正义路清债中心提货商场拉横幅讨要投资款', '3月28日17时许，分局小南所巡逻中发现，正义路清债中心提货商场门口聚集了5名女子，并拉出两条白底黑子横幅，内容分别为“坚决拥护党和政府领导，请为我们做主”“大骗子清债中国打清债名义骗我们”。民警立即开展工作，经了解除门口5名女子外，清债中心提货商场店铺内还聚集了40余人。初步调查，该40余人投入资金到清债中国进行投资，过程中双方发生利益纠纷。小南所已将清债中国负责人及聚集群众带回派出所进行调解，相关情况进一步掌握。（小南所）\r\n', '2018-03-28 16:30:00', '2018-03-28 17:30:00', 1.00, 40, '合同纠纷 ', '小南所', '清债中心提货商场门口', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-29 14:59:54', '2018-03-29 15:03:52', NULL, '个体户', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '清债中心提货商场门口', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2728, 2547, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '0757d4c9-4b40-478d-892f-51ea949597d2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (940, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-03-28 14:41:44', '3月28日部分金座集资户到市体育馆聚集情况', '3月28日9时，以罗益民、纪昌凤为首的100余名金座集资户到市体育馆上访，昆明市农业局的相关领导进行了接访，接访过程中，金座集资户情绪稳定，未发生喊口号、打标语等过激行为。11时30分许接访结束，上述人员自行离开现场。另据工作掌握，以罗益民、纪昌凤为首的金座集资户拟于2018年3月29日9时许到市体育馆上访，要求昆明市工商局的领导接访，？预计参加人数约 80人。 相关情况在进一步掌握中。\r\n\r\n\r\n', '2018-03-28 09:00:00', '2018-03-28 11:30:00', 2.50, 100, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-29 14:59:54', '2018-03-29 15:01:54', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'yj', 0, 0.00, 0, 0, '0', NULL, '预警', '46d1978c-837d-44f9-bd6c-a7d19f6b27b2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (941, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-03-29 16:46:25', '3月29部分我市“小微”、“泛亚资金”等涉金融受损群体成员到省金融办上访', '3月29日9时30分许，30余名我市“小微”、“泛亚资金”等涉金融受损群体成员到省金融办集访，省金融办相关工作人员对5名代表进行了接访。11时40分许接访结束，上述人员陆续离开现场。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2018-03-29 09:30:00', '2018-03-29 11:40:00', 2.17, 30, '涉众型经济案件', '盘龙分局', '省金融办', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-30 10:42:20', '2018-03-30 10:53:00', NULL, '小微儒商', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '省金融办', '2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2608, 2503, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'effd98cf-b7f1-43d9-85d5-fe3bbad4e78c', 'JS', NULL, 'Y', 110, NULL, NULL, '530103000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (942, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-03-29 14:11:01', '3月29日篆新农贸市场50余名商户聚集省建三公司反映租金问题', '3月29日10时许，篆新农贸市场约50余名商户聚集新闻路省建三公司后勤部反映租金问题，商户认为三公司曾承诺租金不会涨太多，现租金上涨1－倍，比转租的租金还高无法承受，且一季度的租金只能转帐，且不签订租赁合同，涉嫌欺诈。省建三公司现场答复，商铺租金是经专项调研及参考周边市场确定的，商户可双向选择。据了解，省建三公司告知商户须在3月31日前缴清2018年一季度的租金，对不交纳租金的商户将进行清理，不排除矛盾升级的可能。经工作，商户于11时许离开。相关情况进一步掌握中。\r\n\r\n\r\n', '2018-03-29 10:00:00', '2018-03-29 11:00:00', 1.00, 50, '合同纠纷 ', '大观所', '省建投三公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-30 10:42:20', '2018-03-30 10:50:44', NULL, '个体户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '省建投三公司', '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2684, 2547, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'c1bfde47-b846-4734-9fc8-ef8253aa9567', 'JS', NULL, 'Y', 110, NULL, NULL, '530102480000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (943, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-03-29 13:49:13', '3月29日白马小区25名居民到省信访局反映回迁安置问题', '   29日9时许，西山区白马小区25名居民到省信访局反映，2012年白马庙村城中村改造，由云南广电开发建盖“大观首府”，施工方在作业过程中地基塌陷，导致该小区位于工地一线的10栋房子开裂，涉及424户居民。来访人员称，2013年西山区政府强制要求所有居民搬迁，并承诺尽快建好新居民楼，2017年底即可原址回迁，期间要求居民自行找房住，政府给予过渡费，但至今原址仍然没有动工拆迁，回迁无期，引发400余户居民不满，多次向各级政府反映要求尽快解决未果，所以到省政府上访，要求上级政府督促，尽快动工新建居民楼，尽快让居民原址回迁。省信访局接待后告知其回昆明市政府反映。11时许，25人被西山区政府工作组带离省政府，上访过程平稳。\r\n', '2018-03-29 09:00:00', '2018-03-29 11:00:00', 2.00, 25, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-03-30 10:42:20', '2018-03-30 10:48:28', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, '1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'cscq', '城市拆迁', 'sldws', '16-50人', '1', 'qkfy', 25, 0.00, 0, 0, '0', NULL, '情况反映', '2253be39-4a6f-42a4-9175-fd90eebb5192', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
