INSERT INTO `qb_zdasjqbxx` VALUES (1102, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-08 16:43:06', '5月8日我市涉金融受损群体“儒商”等群体成员到省金融办聚集上访情况', '5月8日9时30分许，40余名我市涉金融受损群体的“儒商”、“光辉”等群体成员到省金融办聚集上访，聚集过程中打出4条标语，内容为：“谁审批、谁监管、谁负责”、“金融创新失败敢于承担”、“本息不回维权不止”、“人民政府为人民”。省金融办相关工作人员对“儒商”、“光辉”等群体的代表进行了接访。13时许接访结束，上述人员陆续离开现场。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2018-05-08 09:30:00', '2018-05-08 13:00:00', 3.50, 40, '涉众型经济案件', '盘龙分局', '省金融办', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:49:22', '2018-05-16 14:53:54', NULL, '小微儒商', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '省金融办', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2608, 2503, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'b7c3ae65-8dea-45f3-a8a5-01286b311b46', 'JS', NULL, 'Y', 110, NULL, NULL, '530103000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1103, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-08 11:16:41', '5月8日昭通市巧家县居民5人因不满延期交房到省政府上访', '     5月8日10时30分许，5名昭通市巧家县居民到省政府上访，反映，2014年上半年，他们购买了位于巧家县城内的商品房“玉屏尚城”，开发商是宇城房地产开发有限公司，当时合同上签订的交房日期是2015年12月31日，购买者大约700余户。来访群众称，从2014年底起该项目就一直处于停工状态，目前已成烂尾楼，从2015年底开始业主多次向当地政府部门反映要求尽快解决烂尾楼的问题未果，所以到省政府上访，要求上级政府协调尽快解决烂尾楼的问题。省信访局接待后告知其回昭通市反映诉求。10时55分许，5人全部离开省政府。\r\n\r\n\r\n', '2018-05-08 10:30:00', '2018-05-08 10:55:00', 0.42, 5, '逾期未交房', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:49:22', '2018-05-16 14:52:41', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf', '民间纠纷', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'fb4f8044-0870-4ae1-8e84-df19d6e7b442', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1104, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-08 11:00:03', '5月8日在宾川县做工的农民工5人因工钱被拖欠到省政府上访', '5月8日10时25分许，5名在宾川县做工的农民工到省政府上访，反映，他们是山东省胶州市洋河镇魏家庄村民，是在大理州宾川县参与修建仙鹤水库至县城人畜饮水供水管网工程的农民工，工程完工后承包商一直未与他们结算工钱，所以到省政府上访，要求尽快拿到工钱。省信访局接待后告知其宾川县政府相关部门已有答复意见，要求其回当地解决。10时35分许，5人全部离开省政府。（华山所）\r\n\r\n\r\n', '2018-05-08 10:25:00', '2018-05-08 10:35:00', 0.17, 5, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:49:22', '2018-05-16 14:51:29', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'aa0e9e89-215c-4992-80ba-469d46834a84', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1105, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-08 10:57:56', '5月8日“面对面”装饰公司员工及供货商22人因劳资纠纷到省政府上访', '5月8日9时50分许，22名“面对面”装饰公司员工及供货商到省政府上访，反映，该公司位于西山区环城南路675号汕头大厦3楼，网上平台注册名称是“壹号家具网”，从2017年10月份起，该公司员工就未领到工资，供货商也没有拿到货款，涉及公司员工200余人，供货商人数不清。由于讨要未果，今年初员工及供货商遂向西山公安分局报警。来访群众称他们了解到装饰公司负责人于两天前已离开了昆明，引发该群体不满故今日到省政府上访，要求上级政府出面协调解决。省信访局接待后告知其西山区政府相关部门已受理了其诉求，要求其回西山区反映。10时30分许，22人全部离开省政府。（华山所）\r\n\r\n\r\n', '2018-05-08 09:50:00', '2018-05-08 10:30:00', 0.67, 22, '劳资纠纷', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:49:22', '2018-05-16 14:50:21', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'lzjf', '劳资纠纷', 'sldws', '16-50人', '1', 'qkfy', 22, 0.00, 0, 0, '0', NULL, '情况反映', '90a8b30f-1ae1-427c-8cbe-3be93b889de2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1106, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-09 12:19:45', '5月9日玉溪籍原修路民工12人因生活补助问题到省政府上访', '5月9日11时10许，12名玉溪籍农民到省政府上访，反映，1964年，云南省交通运输局下发文件，从农村劳动力中招收修路人员，当时玉溪籍共招了500人，从1964年起参与修建玉-峨国防公路，到1969年结束后全部人员返乡务农。来访人员称，现在他们年纪大了，生活困难，要求政府帮助解决他们健在的原修路人员的生活补助问题。该群体于今年1月9日曾就相同诉求到访过，由于诉求没有得到解决，所以今日再次上访，要求解决生活补助问题。省信访局接待后告知其向云南省交通厅及玉溪市政府反映。11时45分许，12人全部离开省政府，上访过程平稳。（华山所）\r\n\r\n\r\n', '2018-05-09 11:10:00', '2018-05-09 11:45:00', 0.58, 12, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:54:46', '2018-05-16 14:56:53', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'syr', '11-15人', '1', 'qkfy', 12, 0.00, 0, 0, '0', NULL, '情况反映', '42e111e0-39f6-4aa4-abfd-af3298d7a701', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1107, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-09 09:50:23', '5月9日嵩明县嵩阳镇村民7人因不满棚户区改造到省政府上访', '   5月9日8时50分许，7名昆明市嵩明县嵩阳镇东北街村村民到省政府上访，反映县政府今年初在棚户区改造的过程中存在以下损害群众利益的情况：1、当地政府将该村列为棚户区改造搬迁范围的情况事前未向村民进行告知；2、村民认为他们的房屋质量好，不属于棚户区的标准，政府强制要求村民搬迁的行为违反了国家相关政策；3、补偿标准太低；4、政府强制要求村民搬迁的住房质量差、设计不合理等情况，诉求是：村民不愿意搬迁，此诉求村民已多次向当地政府反映，由于一直得不到解决所以今日到省政府上访，要求上级政府支持村民诉求。省信访局接待后告知其回昆明市反映。9时40分许，7人全部离开省政府。（华山所）\r\n\r\n\r\n\r\n\r\n', '2018-05-09 08:50:00', '2018-05-09 09:40:00', 0.83, 7, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:54:46', '2018-05-16 14:55:48', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '5e44d7a6-d21d-4b25-8517-81c6e640e075', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1108, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-10 15:52:43', '5月10日盘龙区绿化工程服务中心工人5人因技师申报问题到省政府上访', '5月10日15时15分许，5名盘龙区绿化工程服务中心工人到省政府上访，反映，今年4月3日盘龙区人力资源和社会保障局下发了一份文件《关于2018年度机关事业单位工人技术等级评定工作的通知》，由于盘龙区园林局没有及时将文件转发至盘龙区绿化工程服务中心，导致该中心8名符合申报技师条件的工人没有报上名，来访群众称，4月25日符合条件的申报人已经开始参加培训，他们8人错过了报名，由于该8人明年即将退休，错过职称评定退休时工资每月将会少500元，故到省政府上访，要求上级政府出面协调让他们参与今年的职称评定，同时投诉盘龙区园林局的不作为行为。省信访局接待后告知其向昆明市反映。15时40分许，5人离开省政府。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-05-10 15:15:00', '2018-05-10 15:40:00', 0.42, 5, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:57:43', '2018-05-16 15:07:45', NULL, '企事业单位职工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2537, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'jgqsyry', '机关企事业人员', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '7370a42c-0700-4baf-8c4a-977adebb72b7', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1109, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-10 15:10:05', '5月10日9名小微投资人到省政府上访', '   5月10日14时15分许，9名小微投资人到省政府上访，来访群众称他们来自昆明、红河、楚雄、玉溪等地，均是从2012年起投资小微企业金融交易的投资人，由于资金被骗，至今政府相关部门没有给予投资人答复，所以今日到省政府上访，诉求是投诉办理小微集资诈骗的政府相关部门不作为，推诿的行为，要求上级政府尽快重视他们的问题，现信访接待中。（华山所）\r\n\r\n\r\n', '2018-05-10 14:15:00', '2018-05-10 15:00:00', 0.75, 9, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:57:43', '2018-05-16 15:02:37', NULL, '小微儒商', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2503, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'sryx', '10人以下', '2', 'qkfy', 9, 0.00, 0, 0, '0', NULL, '情况反映', '644b9345-75e2-4047-8a58-c9e85b587cb6', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1110, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-10 15:08:53', '5月10日在四川做工的农民工5人因工钱被拖欠到省政府上访', '     5月10日14时10分许，5名农民工到省政府上访，反映，他们是四川雷波县杉树堡乡的村民，在攀枝花市水电十四工程局项目工地做工，工程完工后施工方一直拖欠50余名农民工2017年9月至今的工钱大约80余万元，来访群众称他们了解到水电十四局的局机关在昆明，故到云南省政府上访，要求上级政府出面协调十四局尽快解决拖欠工钱一事。省信访局接待后告知其向水电十四局反映。14时25分许，5人全部离开省政府。（华山所）\r\n\r\n\r\n', '2018-05-03 14:10:00', '2018-05-03 14:25:00', 0.25, 5, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:57:43', '2018-05-21 09:39:36', NULL, '华山所', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'e9e8fc39-9eda-4db4-af33-f4eba5d50b38', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1111, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-05-10 13:45:09', '金座集资户开展周四例行聚集活动', '5月10日9时30分许，110余名以纪昌凤、罗益民为首的金座集资户到市体育馆例行聚集，市检察院的工作人员进行了接访。接访过程中，集资户对要求市检察院就如何通过民事诉讼途径追讨投资款的接访不满，情绪激动，声称要到省政府集访。分局现场警力配合金座联合接访小组开展疏导劝解工作，金座集资户情绪逐渐平稳。13时许，接访结束，金座集资户自行离开。相关情况在进一步掌握中。\r\n\r\n\r\n', '2018-05-10 09:30:00', '2018-05-10 13:00:00', 3.50, 0, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:57:43', '2018-05-16 15:01:01', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '67719770-ace8-4f3b-ae45-8fa9794c52c4', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1112, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-10 11:25:37', '5月10日鹤庆县龙开口村村民5 人因土地质量问题到省政府上访', '5月10日10时许，5名大理州鹤庆县龙开口镇龙开口村村民到省政府上访，反映，2008年由于当地修建水电站的需要，政府将该村整体移民搬迁至异地，搬迁后一直没有分配土地，至2016年当地县、镇政府开始分配土地给村民，来访村民称政府分配的土地全是砂石，无法耕种，如果要恢复耕种土层会耗费大量人力，村民无力承担，故要求政府重新分配土地。由于在当地反映没有得到答复，所以到省政府上访，要求重新分配给村民能够耕种的土地。省信访局接待后告知其向移民局及大理州反映。10时15分许，5人全部离开省政府。（华山所）\r\n\r\n\r\n', '2018-05-10 10:00:00', '2018-05-10 10:15:00', 0.25, 5, '移民搬迁', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:57:43', '2018-05-16 15:00:09', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'd7015d33-3948-4cbe-ac80-5ab52a1471ab', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1113, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-10 10:05:07', '5月10日昭通公路局下岗职工8人因身份待遇问题到省政府上访', '5月10日9时许，8名昭通公路局下岗职工到省政府上访，反映，他们是1996年-1999年昭通公路管理总段（现更名为昭通公路局）下属各学校定向委培的职工子女，当时学校与他们签订了委培合同，学制三年，承诺如果完成指定学校指定专业的学习，毕业后会分配到公路局隶属单位工作且身份是公路局的正式职工，来访群众称他们毕业后公路局给他们分配了工作，但身份却是编外人员，工资低，待遇差，他们多次向公路局反映要求提高待遇，解决身份问题未果，今年4月份，公路局强制解除了他们的劳动合同关系，引发该群体不满，涉及30余人，因在昭通反映未得到解决所以到省政府上访，要求：一是公路局补偿给他们工资十多年来应得的报酬；二是解决身份问题，同工同酬；三是原单位只给他们交了五险，没有交住房公积金，要求单位补交。现信访接待中。（华山所）\r\n\r\n\r\n\r\n\r\n', '2018-05-10 09:00:00', '2018-05-10 09:30:00', 0.50, 8, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 14:57:43', '2018-05-16 14:58:56', NULL, '退休职工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'xgsyry', '下岗失业人员', 'gzfldy', '工资福利待遇', 'sryx', '10人以下', '2', 'qkfy', 8, 0.00, 0, 0, '0', NULL, '情况反映', 'e98a538b-c9a0-4bd0-8a28-20725f757eca', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1114, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-05-11 13:40:53', '“先锋小学”部分学生家长5月11日到五华区教育局聚集反映诉求相关情况', '5月11日上午10时左右开始，陆续有100余名“先锋小学”学生家长到五华区教育局院内聚集，就不满学校搬迁的诉求向区教育局进行反映。区教育局相关领导对十余名家长代表进行了接访及答复。就家长提出的反对学校搬迁的问题，教育局答复称此次搬迁系政府城市统一规划，校方将会提供校车接送等便利措施来帮助家长克服路途遥远的困难。对此答复，在场家长均表示不满，个别人员扬言如执意搬迁，将采取让学生罢课的方式予以抵制。至12时左右，家长陆续离开五华区教育局，整个聚集反映诉求过程中家长未出现过激行为。\r\n\r\n据掌握，部分家长相约将于11日下午14时在先锋小学参加校方举行的“学校搬迁通报会”。\r\n\r\n相关情况进一步收集掌握中。\r\n', '2018-05-11 10:00:00', '2018-05-11 12:00:00', 2.00, 100, '特殊群体', '西山分局', '五华区教育局', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 15:08:23', '2018-05-16 15:13:18', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '五华区教育局', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2612, 2507, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '8a3d3a9e-3e16-4a98-bb28-d6b8cd5f8907', 'JS', NULL, 'Y', 110, NULL, NULL, '530105000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1115, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-11 13:36:46', '5月11日在官渡区做工的农民工10人因工钱被拖欠到省政府上访', '11日11:20分许，10名农民工到省政府上访，反映，他们是在昆明市官渡区东三环“凤凰雅苑”小区做外墙涂料的四川籍农民工，工程承包方是四川华海建设集团，2015年底至今该公司拖欠45名农民工70余万元的工钱，由于讨要未果所以到省政府上访，要求尽快拿到工钱。省信访局接待后告知其官渡区已有处理意见要求其回官渡区反映。11:30分许，10人全部离开省政府。\r\n', '2018-05-11 11:20:00', '2018-05-11 11:30:00', 0.17, 10, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 15:08:23', '2018-05-16 15:14:18', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', '51f9d2c0-51ff-4b0d-b173-b7ead590ebed', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1120, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-14 16:18:51', '文山州广南县马场村7名村民因不满土地被征到省政府上访', '  \r\n\r\n14日15时50分许，7名文山州广南县那洒镇马厂村村民到省政府上访，反映，今年4月中旬当地县政府强行征用了该村10余户村民的土地用于开发建设，由于事前未向村民告知征地用途及补偿的方案，导致在征地过程中村民与政府人员发生冲突，村民遂向当地政府反映要求合理解释征地用途，由于未得到答复所以到省政府上访，要求；1、上级政府依法查处广南县那洒镇政府滥用职权，玩忽职守，严重腐败的问题；2、要求那洒镇政府赔偿因强拆导致上访人的损失；3要求政府协调解决村民自建房所需宅基地的问题，现信访接待中。\r\n', '2018-05-14 15:50:00', '2018-05-14 16:45:00', 0.92, 7, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 16:52:45', '2018-05-17 09:31:42', NULL, '村民', NULL, '0', '2', '村民与政府人员发生冲突', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '2', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', 'ffbbb521-d57a-4c96-8516-32594f9a71b3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1121, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-14 16:16:27', '弥勒县糖业公司职工7人因工资被拖欠到省政府上访', '      14日15:30分许，7名红河州弥勒县朋普镇朋塘小区居民到省政府上访，反映他们原是云南生物制品有限公司的职工，该公司前身为国营企业性质的弥勒糖业有限责任公司，2001年企业由国营企业改制为民营企业后，公司法人王俊平侵吞企业财产，多年来拖欠300余名员工工资，未依法缴纳五险一金，导致职工生活困难，要求上级政府调查处理此情况，省信访局接待后，告知其回红河州反映其诉求，15时45分许，7人全部离开省政府。\r\n', '2018-05-14 15:30:00', '2018-05-14 15:45:00', 0.25, 7, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 16:52:45', '2018-05-21 09:51:49', NULL, '农民工', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'mjjf', '民间纠纷', 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '709bae6f-9cce-48d2-9e2e-92b101f4de3f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1122, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-14 10:54:04', '怒江州兰坪县村民11人因不满移民补助到省政府上访', '     14日09:20分许，11名怒江州兰坪县营盘镇松柏村委会村民到省政府上访，反映，2015年由于修建水电站的需要，当地政府要求该村100余户村民整体移民搬迁至别处，来访村民称，政府承诺补偿每名村民基础设施恢复费29555元，目前每人只拿到了21250元，差额部分至今未拿到。另外村民称同样移民搬迁的另外一个村的基础设施恢复费是每人5万元，远高于该村，引发该村村民不满等问题。由于向当地政府反映要求一视同仁解决未果，所以到省政府上访，要求上级政府支持村民的诉求。省信访局接待后告知其回怒江州反映。10:15分许，11人全部离开省政府。\r\n\r\n\r\n', '2018-05-14 09:20:00', '2018-05-14 10:15:00', 0.92, 11, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 16:52:45', '2018-05-17 09:25:54', NULL, '村民', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'ymwt', '移民问题', 'syr', '11-15人', '2', 'qkfy', 11, 0.00, 0, 0, '0', NULL, '情况反映', 'cc8d89dd-b7e8-4c06-b515-ed97aba65edc', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1123, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-14 10:52:37', '红河州元阳县逢春岭乡村民5人因房屋被损坏到省政府上访', '14日09:20分许，5名红河州元阳县逢春岭乡槁吾卡村村民到省政府上访，反映，2017年，云南建投集团通过竞标，取得了元蔓高速公路元阳县境内的工程施工承包权，后将该村附近的隧道工程分包给山东路桥建设有限公司承建，同年10月，在隧道施工爆破的过程中，震坏了该村18户村民的房屋，共计3000余平方米。村民多次向路桥公司及当地政府反映要求对受损房屋进行赔偿未果，所以到省政府上访，要求尽快解决房屋受损的问题。省信访局接待后告知其回红河州反映。09:50分许，5人全部离开省政府，上访过程平稳。\r\n', '2018-05-15 09:20:00', '2018-05-14 09:50:00', 23.50, 5, '损害赔偿 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 16:52:45', '2018-05-17 09:22:02', NULL, '村民', NULL, '0', '1', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'mjjf,snwt', '民间纠纷,三农问题', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'a86f7664-aa36-4b43-b45d-52f282e2c3a3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1124, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-14 10:53:19', '在宾川县做工的农民工15人因工钱被拖欠到省政府上访', '14日08:25分许，15名在宾川县做工的农民工到省政府上访，反映，他们大部分是山东省胶州市洋河镇魏家庄村民，是在大理州宾川县参与修建仙鹤水库至县城人畜饮水供水管网工程的农民工，工程完工后承包商一直未与他们结算工钱，所以到省政府上访，要求尽快拿到工钱。该起农民工于5月8日曾就相同诉求到访过，来访人员称，由于找不到老板，所以再次到省政府上访，要求尽快解决拖欠的工资的问题。15人目前聚集在武警操场处，不愿进信访大厅反映诉求。民警正在现场开展稳控工作。\r\n\r\n\r\n', '2018-05-14 08:25:00', '2018-05-14 09:50:00', 1.42, 15, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 16:52:45', '2018-05-21 09:26:39', NULL, '农民工', NULL, '0', '2', NULL, NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'syr', '11-15人', '2', 'qkfy', 15, 0.00, 0, 0, '0', NULL, '情况反映', '42b64908-2351-40cf-8744-4d89f293deab', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1125, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-14 10:51:19', '先锋小学家长5人因不满学校搬迁到省政府上访', '14日08:45分许，5名昆明市钱局街先锋小学家长到省政府上访，他们代表300余名不同意学校搬迁方案的家长，提出以下诉求；一是要求学校将不愿意搬迁的学生分流到附近其他学校；二是，如果分流不了的可采取中华小学的搬迁方案，采取过渡搬迁的形式，逐步分流，不要强制性一次性搬迁。省信访局接待后告知其向五华区政府反映。09:10分许，5人全部离开省政府。\r\n', '2018-05-14 08:45:00', '2018-05-14 09:10:00', 0.42, 5, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 16:52:45', '2018-05-21 13:44:09', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'mjjf', '民间纠纷', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '2a3a4acb-f243-48a1-823c-06ac8b74d27d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1127, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-05-15 14:51:08', '5月15日部分金座集资户到市体育馆上访的情况', '据工作掌握： 2018年5月15日9时，以罗益民、季昌凤为首的金座集资户到市体育馆上访，昆明市西山区法院的相关领导接访，参加人数在100人左右。相关情况进一步了解掌握中。\r\n', '2018-05-15 09:00:00', '2018-05-15 09:50:00', 0.83, 100, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 16:56:58', '2018-05-21 09:30:20', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '市体育馆', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '4a4ef8aa-3b8e-4634-8fa7-aeecc075b210', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1128, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-15 09:55:49', '5月15日鲁甸县龙头山镇村民5人因不满征地补偿到省政府上访', '5月15日8:50分许，5名昭通市鲁甸县龙头山镇光明村村民到省政府上访，反映，作为鲁甸县803地震灾后重建的一部分项目，今年初当地政府在该村附近治理堰塞湖，建设电站，占用了该村50余户村民的土地，在补偿过程中，村民与政府在丈量面积、补偿标准等问题上存在异议，村民一直没有签订补偿协议，但政府的征地工作已经开始，引发村民不满，因在当地反映未得到答复所以到省政府上访，要求重新丈量土地面积以及重新协商补偿。省信访局接待后告知其回昭通市反映。\r\n', '2018-05-15 08:50:00', '2018-05-15 09:50:00', 1.00, 5, '灾后重建', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-16 16:56:58', '2018-05-17 09:35:15', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'zd', '征地', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '70c4514f-1624-46e4-8d42-d85fcaf6f5be', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
