INSERT INTO `qb_zdasjqbxx` VALUES (1157, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-22 11:30:18', '元谋县元马镇物茂乡村民5人因不满林地被占到省政府上访', '\r\n\r\n22日10:10分许，5名楚雄州元谋县元马镇物茂乡村民到省政府上访，反映，今年初，当地政府在该村附近修建光伏电站，欲占用52户村民的林地700余亩，但补偿问题一直没有与村民协商。来访村民称，上周村委会带领施工队在村民林地内开始强行施工，导致与村民发生冲突，村民遂向元谋县政府反映要求停止野蛮施工未果所以到省政府上访，要求当地政府立即停止野蛮暴力施工，与村民协商完成占地补偿费用后再开工。省信访局接待后告知其回楚雄州反映。10:35分许，5人全部离开省政府。（华山东路派）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-05-22 10:10:00', '2018-05-22 10:35:00', 0.42, 5, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-24 09:37:21', '2018-05-24 10:08:24', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '5a2cdb07-fb7b-439d-a738-afd44e408144', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
