INSERT INTO `qb_zdasjqbxx` VALUES (1160, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-05-22 13:57:10', '5月22日部分金座集资户例行聚集情况', '5月22日9时30分许，陆续有100余名以纪昌凤、罗益民为首的金座集资户到市体育馆聚集上访，昆明市信访局的相关工作人员进行了接访，接访过程中，信访局就金座集资户提出的生活困难，金座资产处置等问题进行了解释说明。11时许，接访结束，上述人员陆续离开现场。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2018-05-22 09:30:00', '2018-05-22 11:00:00', 1.50, 100, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-24 09:37:22', '2018-05-24 10:28:33', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '48e0d713-fdd6-43dd-89e7-2453d15dbb20', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1161, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-23 11:26:29', '巧家县老店镇出门11人殷不满土地被占到省政府上访', '23日10:30分许，11名昭通市巧家县老店镇阿泥卡村公所村民到省政府上访，反映，当地政府未经村民同意，将该村100余户村民的300余亩土地强行征用，分给移民搬迁至该村附近的其他村民使用，引发该村村民不满，来访村民称，上个月底，镇政府带人强行毁坏了来访村民的土地，引发双方发生冲突。由于村民对当地政府粗暴毁地的行为不满所以到省政府上访，诉求是：村民不同意把自己的土地分给他人，要求政府将被占土地还给村民，同时对毁坏的青苗等农作物进行赔偿。省信访局接待后告知其回昭通市反映。11：00许，11人全部离开省政府，上访过程平稳。（华山东路派）\r\n', '2018-05-23 10:30:00', '2018-05-23 11:00:00', 0.50, 11, '征地补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-24 09:39:29', '2018-05-24 10:30:03', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'syr', '11-15人', '1', 'qkfy', 11, 0.00, 0, 0, '0', NULL, '情况反映', '2aef1863-c2e3-4060-aff6-481f7bc61a40', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1162, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-05-23 12:30:00', '5月23日20余人到愉筷美食城讨要营业款', '5月23日11时许，20余名租户到小康大道589号3楼朗月湾美食广场愉筷美食城讨要16万元左右营业款。据了解，愉筷美食城拖欠昆明恒彬企业有限管理公司17万元房租，且未按合同于15日将营业额结算给商户，为此商户聚集讨要营业款。相关情况密切关注中。\r\n\r\n\r\n', '2018-05-23 11:00:00', '2018-05-23 12:00:00', 1.00, 20, '索薪讨款', '红云所', '愉筷美食城', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-24 09:39:29', '2018-07-18 17:07:54', NULL, '朗悦湾', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '愉筷美食城', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2699, 2534, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '11083e1a-a43e-4aed-b753-5428cec1a2a3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102490000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1166, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-05-23 16:51:13', '5月23日部分在昆“12.13”人员到是中院集访', '5月23日9时30分许，陆续有20余名在昆“12.13”人员到昆明市中级人民法院聚集上访，昆明市中级人民法院的相关工作人员对现场聚集的“12.13”人员代表进行了接访，接访过程中，“12.13”人员递交了题为“请尽快结案的请求和建议”诉求书，要求尽快开庭审理泛亚案件，昆明市中级人民法院的相关工作人员对“12.13”人员提出的相关问题进行了解释和说明。12时许接访结束，上述人员陆续离开现场。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2018-05-23 09:30:00', '2018-05-23 12:00:00', 2.50, 20, '涉众型经济案件', '西山', '昆明市中级人民法院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-24 09:39:29', '2018-05-24 10:47:10', NULL, '12.13', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '昆明市中级人民法院', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'f7f3fbfd-35e1-4997-9843-1d40ad42015d', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1167, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-05-23 16:58:22', '5月22日我市涉金融受损群体“儒商”、“博融”等群体成员到省金融办聚集上访情况', '5月22日9时30分许，我市涉金融受损群体的“儒商”、“光辉”、“博融”、 “快融金”、4个群体成员到省金融办聚集上访，10时许，部分投资人在省金融办门口的围栏上悬挂出4条布标，（“人民政府为人民”“本息不回  维权不止”“谁审批  谁监管  谁负责”“金融创新失败敢于承担”），11时许，经民警工作，投资人将布标撤回，金融办对上述群体的代表进行了接访，12时\r\n\r\n30分许，上访人员陆续离开现场。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2018-05-22 09:30:00', '2018-05-22 12:30:00', 3.00, 4, '涉众型经济案件', '盘龙分局', '省金融办', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-24 09:39:29', '2018-05-24 10:50:04', NULL, '小微儒商', NULL, '0', '2', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '省金融办', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2608, 2503, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'ce9569e2-a07f-4a37-bef8-75c0032f4ccd', 'JS', NULL, 'Y', 110, NULL, NULL, '530103000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1169, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-24 09:35:24', '5月24日在香格里拉打工的农民工5人因工钱被拖欠到省政府上访', '5月24日10时许，5名农民工到省政府上访，反映，他们5人于2015年在香格里拉三坝乡安南村修建当地农民的房屋，该村的房屋修建属于政府帮扶当地农民的自建房屋，2016年底房屋建好后，雇佣他们5人的包工头一直拖欠其12万元的工钱至今。由于讨要未果所以到省政府上访，要求尽快拿到工钱。现信访接待中。（华山所）\r\n\r\n\r\n', '2018-05-24 10:00:00', '2018-05-24 11:00:00', 1.00, 5, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-25 10:20:42', '2018-05-25 11:01:58', NULL, '农民工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '2', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '44c62102-75e1-46e9-9981-4dbadd53cbd7', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1170, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-05-24 09:54:17', '5月24日14名支前民工因身份认定问题到省政府上访', '5月24日9时15分许，14名来自大理、红河、楚雄三地的原支前民工到省政府上访，反映，他们于1979年2月至1981年4月对越自卫反击战期间参与修建绿春县二甫边防公路，工程完工后各自返乡务农，他们了解到政府对支前民兵有相关的生活补助政策，故要求民政部门认定他们的身份是“参战民兵民工”身份并给予相关补助，三地目前健在的修路民工有3000余人。由于反映多年一直没有得到解决所以到省政府上访，要求认定身份并发放相关补助。现信访接待中。（华山所）\r\n', '2018-05-24 09:15:00', '2018-05-24 10:00:00', 0.75, 14, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-05-25 10:20:42', '2018-05-25 11:03:55', NULL, '农民工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'dzzbm', '对政策不满', 'syr', '11-15人', '2', 'qkfy', 14, 0.00, 0, 0, '0', NULL, '情况反映', '2c7fdba4-0479-4893-a958-0aa4d482d222', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
