INSERT INTO `qb_zdasjqbxx` VALUES (1269, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-21 10:12:21', '13名退休职工在东风云南汽车厂门口聚集', '6月21日9时45分许，13余名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，未出现堵门堵路及影响交通的情况，也无过激行为。相关情况持续关注中。\r\n\r\n\r\n\r\n\r\n', '2018-06-21 09:45:00', '2018-06-21 10:50:00', 1.08, 13, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-22 09:17:32', '2018-07-19 17:42:36', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'ab47c127-5e4d-47b5-82d2-5e16fcee098e', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1274, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-06-22 15:31:49', '昆百大珠宝公司员工30人因贷款纠纷问题到省政府上访', '     22日14:30分许，30名昆百大珠宝公司员工因不满公司大额贷款导致职工利益受损一事到省政府上访，30人高喊“昆百大还钱”口号聚集在省政府大门侧面人行道处，由于该群体反映的诉求系涉法涉诉问题，不在信访部门受理范围内，经民警现场工作，15:00许，30人离开省政府称去五华区政府政法委反映诉求。（华山所）\r\n\r\n\r\n\r\n\r\n', '2018-06-22 14:30:00', '2018-06-22 15:00:00', 0.50, 30, '劳资纠纷', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-25 09:08:54', '2018-06-25 09:41:59', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'jgqsyry', '机关企事业人员', 'mjjf', '民间纠纷', 'sldws', '16-50人', '1', 'qkfy', 30, 0.00, 0, 0, '0', NULL, '情况反映', 'd0282580-9248-4771-95b5-c5a3228d7042', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1275, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-22 14:46:23', '6月22日 金座群体再次到市体育馆聚集维权', '6月22日9时许，陆续有约100名以纪昌凤、罗益民为首的金座集资户到市体育馆聚集，市信访局、市政法委、五华区信访局、官渡区信访局、西山区信访，及金座联合接访小组办公室的工作人员开展了接访工作。因所提出的“要求盘龙区信访局”到市体育馆接访的诉求未得到解决，截至发稿时，聚集的集资户仍滞留在市体育馆不愿离开。相关情况在进一步掌握中。\r\n\r\n\r\n', '2018-06-22 09:00:00', '2018-06-22 10:00:00', 1.00, 100, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-25 09:08:54', '2018-07-18 16:48:46', NULL, '金座', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '市体育馆', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '37c30ca1-0ff7-4325-9f86-4ba142b96a8d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1277, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-22 11:42:09', '51名昆百大珠宝员工在世纪中心广场聚集', '6月22日上午9时35分许，昆百大珠宝员工陆续到世纪中心广场前聚集，截止10时40分，已有51余名员工到世纪中心广场阶梯上聚集。现场无拉布标、喊口号等过激行为。昆百大珠宝公司派员到场与员工沟通，但员工声称不信任公司，现护国所所长已联系公司总经理，要求给出妥善答复，防止事态升级。同时已安排巡逻民警和2名PTU加强现场和周边巡逻，密切关注中。（护国所）\r\n\r\n\r\n', '2018-06-22 09:35:00', '2018-06-22 12:20:00', 2.75, 60, '劳资纠纷', '护国所', '世纪中心广场', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-25 09:08:54', '2018-07-18 16:48:07', NULL, '企事业单位职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '世纪中心广场', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2725, 2537, NULL, NULL, NULL, NULL, NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'wsyr', '51-100人', '1', 'qkfy', 60, 0.00, 0, 0, '0', NULL, '情况反映', 'f8231f51-6dde-4fd8-8c64-7a5b9e7a8b32', 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1282, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-06-25 11:46:37', '大中专毕业生300余人因不满毕业分配问题到省政府上访（以此为准）', '25日09：30分许，300余名群众到省政府上访，反映，他们均是上世纪90年代后期按照国家指令性计划招收入学的统招统配大中专毕业生，至今在长达近20年里当地政府一直没有兑现分配的承诺，导致他们待岗至今，来访群众称当时考学填报志愿时，因为看到这些学校是包分配的，所以填报了这些学校，但毕业时学校以及当地政府却没有按照招生简章的规定给予他们分配工作，只是告知其回家等待。该群体人员多年来一直向有关部门反映要求落实当年的分配政策未果，引发该群体不满，所以到省政府上访。今日到访的群众称，涉及此问题的全省各地均存在，今日来访的群众来自昆明、玉溪、红河、昭通，大理（1人）、楚雄（1人）、丽江等地。11:10分许，省信访局联合省教育厅、人社厅等部门负责人联合接待代表6人，目前接待进行中，其余人员在武警操场处聚集，秩序暂时平稳。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-06-25 09:30:00', '2018-06-25 14:30:00', 5.00, 300, '对政策不满', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-26 09:24:38', '2018-06-26 09:41:47', NULL, '农民工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'dzzbm', '对政策不满', 'eblyr', '201-500人', '2', 'qkfy', 300, 0.00, 0, 0, '0', NULL, '情况反映', '5b2756b5-e3a0-4aaf-b16b-37f548bc23eb', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1284, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-06-25 09:55:42', '6月25日20余名棕树营村村民到区政府上访', '25日上午09时许，20余棕树营村民到五华区区政府上访，主要反映的是1年多没有收到过渡费的问题，要求政府进行解决。上访人员已由区政府信访办工作人员接待处理中。民警已到现场维持秩序，开展协调工作，上访过程平稳无过激言行。分局密切关注事态的发展情况，及时上报相关的动态。（翠湖所）\r\n\r\n\r\n', '2018-06-25 09:00:00', '2018-06-25 11:00:00', 2.00, 20, '拆迁补偿', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-26 09:24:38', '2018-06-26 09:44:01', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2507, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', NULL, NULL, 'cscq', '城市拆迁', 'sldws', '16-50人', '1', 'qkfy', 20, 0.00, 0, 0, '0', NULL, '情况反映', '07aaa277-ed11-420f-bb9b-1c89b994c2f6', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1286, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-06-25 12:26:00', '厂口西翥养殖户5人到区政府上访', '6月25日上午11时左右，厂口西翥办事处老花铺养殖户5人到区政府上访，主要反映:2017年5月他们的养殖场被政府征用，场地被拆迁，养殖户搬迁到了厂口徒普鲁村张宪法新建的养猪场（该养殖场现有4户养殖户，共养殖有2763头猪），2018年6月24日西翥办事处通知张宪法养殖场于2018年6月26日自行搬离，并将生猪清理。但由于时间紧，养殖户无法在规定的时间内搬离并清理生猪，希望政府宽限一些时间，延长搬离时限。\r\n\r\n现上访人员已由区政府信访办工作人员接待处理中。现场秩序正常，无过激言行，翠湖所安排2名民警到现场维持秩序。11时50分许，上访人员5人离开区政府。(翠湖所)\r\n\r\n\r\n', '2018-06-25 11:00:00', '2018-06-25 11:50:00', 0.83, 5, '拆迁安置', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-26 09:42:37', '2018-06-26 09:45:31', NULL, '个体户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2547, 'dzjg', '党政机关', 'badys', '不安定因素', NULL, NULL, 'sf', '上访', 'nm', '农民', NULL, NULL, 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'eb2231ed-2d4c-4e04-8c77-c4d03cc1cc4a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1287, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-25 12:33:39', '黑林铺村拆迁工程未拆迁户因养老保险购买问题到黑林铺街道办事处群访', '6月25日上午9时左右，黑林铺街道办报称：有40余名老年人在黑林铺街道办聚集，并拉起横幅：强烈要求兑现养老保险。原因是之前的黑林铺村的拆迁问题，之前被拆房屋涉及746人，可享受每年15000元的商业保险，但对现在还未拆迁房屋的215人，则不能享受该项商业保险。因此引发拆迁户不满，黑林铺村居民以邹敬金为代表称每年15000元的商业保险不能只给拆了房子的居民，不能搞一村两种政策，故未拆迁户相互邀约到黑林铺街道办上访。\r\n\r\n虽然黑林铺街道办负责人接访了来访群众，但无结果，现来访群众在街道办大厅聚集，无过激行为，民警现场开展秩序维护工作，密切关注中。（海源所）\r\n\r\n\r\n', '2018-06-25 09:00:00', '2018-06-25 10:00:00', 1.00, 40, '拆迁补偿', '海源所', '黑林铺街道', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-26 09:46:21', '2018-07-18 16:47:21', NULL, '村民', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '黑林铺街道', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2795, 2507, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'ed091925-8088-4317-88e9-264a9687cdcd', 'JS', NULL, 'Y', 110, NULL, NULL, '530102610000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1288, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-26 15:27:07', '昆明远卓深港金融服务有限公司清算重组引发百人聚集 维稳风险隐患激化可能性大(以此版本为准)', '6月24日，小南所社区民警工作中发现：五华区人民中路33号巨龙大厦7楼“昆明远卓深港金融服务有限公司”门口张贴了《宜辀金服（卓金网贷平台）依规退出公告》，主要内容称公司P2P网络借贷平台“卓金网贷”经半年整改，加之市场经济形势下行，平台被迫关闭，将委托资产公司介入，采取市场化方式进行清算重组。结合前期掌握的情况，为防止可能引发的投资户规模性聚集，小南所连夜对该公司基本情况进行了核查，并报告了分局经侦大队。\r\n\r\n6月25日，远卓公司正常营业。当日10时许，小南所接110指令称有50余名投资户在公司聚集。小南所立即赶公司，同时分局指挥中心指令经侦大队派员到场开展工作。民警对现场聚集的投资户进行了法制宣传及劝导，告知要通过法律途径维权，直至17时许，介入清算重组的第三方资产公司“江西博林企业咨询管理有限公司”相关人员才在公司出现，向投资户答复了初步问题，投资户才陆续全部离开。6月26日10时许，“江西博林企业咨询管理有限公司”按25日的约定，在远卓公司对投资户进行答复，小南所出动10名警力到现场秩序维护。之后，陆续有300余名投资户到场，“江西博林企业咨询管理有限公司”人员对到场的投资户相关情况进行了登记，并要求投资户选出代表，统一向代表就清算重组工作进行解释答复。分局经侦大队、小南所对远卓公司相关工作电脑进行了证据保存，针对投资户的报案，明确答复投资户经侦大队、小南所为接报点，将会对投资户的报案进行备案登记，目前暂无证据表明需要公安机关立案侦查。截止上报时，现场已有部分投资户离开，但仍有200余人滞留，在现场警力的维护下秩序平稳。\r\n\r\n目前，小南所已对“江西博林企业咨询管理有限公司”进行了初步核查，该公司主要业务是关注全国各地资金断裂、经营不善的公司，之后适时介入开展资产评估、收购，公司正与远卓公司商谈收购事宜。鉴于上述情况，虽然昆明远卓深港金融服务有限公司声称正处于清算重组，且有江西博林企业咨询管理有限公司对恐慌的投资户进行解释安抚，但资金无法兑现、清算重组期限不定等不确定性因素多、涉及人员多，投资户维稳风险隐患激化可能性大，建议省、市、区党委政府高度关注，公安机关密切掌握动态，争取主动，降低维稳风险。\r\n\r\n                                 \r\n\r\n\r\n', '2018-06-25 10:00:00', '2018-06-25 11:00:00', 1.00, 50, '民间借贷 ', '小南所', '昆明远卓深港金融服务有限公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-27 09:39:52', '2018-08-02 10:13:05', NULL, '远卓投资户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆明远卓深港金融服务有限公司', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2729, 2504, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '5265c0ae-4ebe-46c3-ad55-56bc9e4c3d44', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1290, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-06-26 12:53:42', '宜良县汤池镇村民7人到省政府举报村支书违法', '26日11:25分许，7名昆明市宜良县汤池街道办事处汤池社区村民到省政府上访，举报汤池社区党委书记杨永祥自1994年担任书记以来贪污征地款、私自强征农田400余亩建房盗卖，利用村干部创办房地产公司，诈骗290余户村民买房款上亿元，利用集体资金公费旅游等违纪行为。多年来村民一直向各级政府反映要求查处该人未得到答复，所以到省政府上访，要求立即查处该人的违纪行为。省信访局接待后告知其向昆明市监察委反映。11:40分许，7人全部离开省政府。（华山所）\r\n', '2018-06-26 11:25:00', '2018-06-26 11:40:00', 0.25, 7, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-27 09:39:52', '2018-06-27 11:06:28', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, NULL, NULL, 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '61d35334-dd23-45cb-8326-9be46bd8cc3d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1291, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-26 09:51:32', '110余名金座人员到市体育馆例行聚集', '26日9时30分许，110余名金座人员到昆明市体育馆进行例行聚集，禄丰县政府相关领导在现场进行接访。现场秩序平稳，情况良好。民警正在现场开展相关工作，后续情况密切关注中。（茭菱所）\r\n\r\n\r\n', '2018-06-26 09:30:00', '2018-06-26 12:00:00', 2.50, 110, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-27 09:39:52', '2018-07-20 09:52:27', NULL, '金座', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '市体育馆', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, NULL, NULL, NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'yblyr', '101-200人', '1', 'qkfy', 110, 0.00, 0, 0, '0', NULL, '情况反映', '95c07346-16ee-47d6-82f9-96bdd8d7849f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1295, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-06-27 15:44:27', '东川区红土地镇村民10人因不满建房补助未发放到省政府上访', '6月27日15时5分许，10名东川区红土地镇花沟村委会村民到省政府上访，反映，红土地镇被当地政府划为旅游开发区，2016年村委会要求该村村民旧房翻新，口头承诺给予建档立卡贫困户补助51800元，非贫困户给予补助4万元，2017年村民陆续建好房后，当地政府以面积超标为由，一直没有发放补助，涉及24户。村民对此理由不满，认为，政府在村民建房的过程中只是要求村民注意楼层、用料等方面的注意事项，没有告知村民建房面积的标准，房屋建好后以此为由拒发补助是不对的，故多次向有关部门反映要求尽快发放补助未果所以到省政府上访，要求当地政府尽快发放补助。省信访局接待后告知其向昆明市扶贫办反映。15时30分许，10人全部离开省政府。（华山所）\r\n\r\n\r\n', '2018-06-27 15:05:00', '2018-06-27 15:30:00', 0.42, 10, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-28 13:38:57', '2018-06-28 13:44:24', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', 'a693574b-36bc-4f28-b6cf-6b1c651f310a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1297, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-27 11:05:55', '6月27日百余人到昆明远卓深港金融服务有限公司聚集维权', '6月27日9时许，100余名投资户陆续到人民中路33号巨龙大厦7楼昆明远卓深港金融服务有限公司聚集。分局已提前做好警力部署工作，现场警力第一时间开展秩序维护、法律宣传、疏导劝解工作，目前聚集人员秩序平稳，该公司相关负责人正常开展答疑接待工作。10时10分许，昆明广播电视台记者来到现场对部门投资户进行了采访。相关情况密切关注中。\r\n\r\n\r\n', '2018-06-27 09:00:00', '2018-06-27 16:00:00', 7.00, 100, '索薪讨款', '小南所', '昆明远卓深港金融服务有限公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-28 13:38:57', '2018-07-18 16:42:54', NULL, '远卓投资户', NULL, '1', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆明远卓深港金融服务有限公司', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2729, 2504, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '1', NULL, '情况反映', 'cffbfd68-e2e5-4df6-9bda-0cdcd3a85f23', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1298, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-27 09:59:22', '15名退休职工在东风云南汽车厂门口聚集', '6月27日9时40分许，15名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，未出现堵门堵路及影响交通的情况，也无过激行为。相关情况密切关注中。（黑林铺所）\r\n\r\n\r\n', '2018-06-27 09:40:00', '2018-06-27 10:55:00', 1.25, 15, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-28 13:38:57', '2018-07-19 17:42:25', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'f114f137-b520-4932-a07f-67b0c22a7d39', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
