INSERT INTO `qb_zdasjqbxx` VALUES (1304, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-06-28 09:59:05', '50名卓金网贷投资人因资金受损一事到省政府上访', '27日16:30分许，50名卓金网贷投资人到省政府上访，反映，从2015年起，他们陆续购买卓金网贷的理财产品（该公司地址位于人民中路巨龙大厦7楼），产品有1个月、3个月、半年、一年期限不等，年收益率在百分之12至百分之17之间，2018年6月22日之前，所有理财产品本金及收益均能正常存取，6月22日之后该公司所有产品均出现资金无法存取的情况，来访人员称，他们了解到公司董事长不知所踪，引发投资人恐慌，由于在公司找不到负责人故到省政府上访，要求上级政府出面协调尽快解决资金受损一事。省信访局接待后告知其向公安经侦部门反映。17:20分许，50人全部离开省政府。部分人明日（6月28日）将到五华经侦大队反映。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-06-27 16:30:00', '2018-06-27 17:20:00', 0.83, 50, '劳资纠纷', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-29 09:06:36', '2018-07-20 15:37:07', NULL, '远卓投资户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2504, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'sldws', '16-50人', '1', 'qkfy', 50, 0.00, 0, 0, '0', NULL, '情况反映', 'a33608cd-7651-4a11-8ae5-623d7269681c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1305, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-06-28 10:37:51', '富源县十八连山鸿顺煤矿投资人22人因经济纠纷到省政府上访', '28日09:20分许，22名曲靖市富源县十八连山乡鸿顺煤矿投资人到省政府上访，反映，2014年初该煤矿的《采矿许可证》到期后他们向曲靖市煤炭局递交了相关申请，但至今有关部门一直不给予办理签发，导致20余名投资人利益受损，遂向相关部门反映，要求曲靖市政府部门落实省煤矿整顿关闭工作联席会议30号文件中关于煤炭产业调整转型升级方案的相关内容。由于在曲靖市反映未得到答复所以到省政府上访，要求上级政府支持其诉求。现信访局正接待代表4人，其余人员在武警操场处等待，秩序平稳。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-06-28 09:20:00', '2018-06-28 11:20:00', 2.00, 22, '劳资纠纷', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-29 09:06:36', '2018-06-29 09:21:00', NULL, '个体户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'mjjf', '民间纠纷', 'sldws', '16-50人', '2', 'qkfy', 22, 0.00, 0, 0, '0', NULL, '情况反映', '726dbf48-4167-41bd-9cd8-b1f4d2b299d2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
