INSERT INTO `qb_zdasjqbxx` VALUES (1307, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-28 11:08:00', '10余名东风云南汽车厂退休职工聚集在东风云南汽车厂门口反映诉求', '28日09时30分许，10余名东风云南汽车厂的退休职工聚集在云南汽车厂下厂区大门口人行道上，要求东风云南汽车厂解决他们的问题及诉求，并在下厂区大门口侧边栏杆处、门上分别横挂3幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”未出现堵门堵路及影响交通的情况，也无过激行为。相关情况持续关注中。（黑林铺所）\r\n\r\n\r\n\r\n\r\n', '2018-06-28 09:30:00', '2018-06-28 11:20:00', 1.83, 10, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-06-29 09:06:36', '2018-07-19 17:42:18', NULL, '退休职工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '3d93bcce-9d31-4c02-848c-28f7b52986bb', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1311, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-29 12:27:20', '30余名云南光辉养殖有限公司养殖户聚集拆迁现场讨要说法', '6月29日上午，西翥街道办事处组织力量对陡普鲁社区“云南光辉养殖有限公司”附属养殖场中的两栋违章建筑进行强拆。拆除过程养殖户情绪状态平稳，未发生过激行为。拆除工作完成后，30余名养殖户因对西翥街道办近期禁止养殖户拉运泔水一事不满前来讨要说法。经分局厂口所配合西翥街道办开展疏导劝解、宣传教育工作，双方未发生过激行为，短时聚集后养殖户自行离开。相关情况密切关注中。（厂口所）\r\n\r\n\r\n', '2018-06-29 10:00:00', '2018-06-29 11:00:00', 1.00, 30, '拆迁安置', '厂口所', '云南光辉养殖有限公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-02 09:28:25', '2018-07-18 16:40:46', NULL, '个体户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '云南光辉养殖有限公司', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2789, 2547, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'f03d33a4-af44-4b7c-a10c-33176c5ceeb8', 'JS', NULL, 'Y', 110, NULL, NULL, '530102600000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1313, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-29 10:47:15', '10余名东风云南汽车厂退休职工聚集在东风云南汽车厂门口反映诉求', '29日09时10分许，10余名东风云南汽车厂的退休职工聚集在云南汽车厂下厂区大门口人行道上，要求东风云南汽车厂解决他们的问题及诉求，并在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”截止情况上时，退休职工未出现堵门堵路及影响交通的情况，也无过激行为。相关情况持续关注中。（黑林铺所）\r\n\r\n\r\n\r\n\r\n', '2018-06-29 10:00:00', '2018-06-29 10:50:00', 0.83, 10, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-02 09:28:25', '2018-07-19 17:42:09', NULL, '退休职工', NULL, '0', '2', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'e8a56ab7-a702-4796-acf7-54f536bd17c0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1315, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-06-30 11:12:05', '6月30日因商铺租赁问题南疆宾馆院门口30余人聚集挂布标维权', '   6月30日8时许，东风西路241号南疆宾馆院门口聚集30余人、悬挂条幅。\r\n\r\n龙翔所立即赶往现场，经初步了解系原租用东风西路241号沿街商铺的商户因租用商铺涉及5月军产关停，无法继续经营，要求赔偿问题，与租赁方协商未果，进而引发商户聚集。针对此情况，分局指挥中心第一时间调集周边派出所警力增援龙翔所，对现场聚集的30余人开展维稳劝解工作，将悬挂的“南疆宾馆处理不公”、“要求依法合理赔偿”等32条白底黑色横幅迅速拆下。\r\n\r\n目前，涉及主要公司德春电信、南疆SAP在分局警力的协调下，正于租赁方人员商谈，南疆宾馆周边治安秩序已恢复。后续情况密切关注中。\r\n', '2018-06-30 06:00:00', '2018-06-30 11:00:00', 5.00, 30, '拆迁安置', '龙翔所', '南疆宾馆院', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-02 09:30:53', '2018-07-18 16:40:08', NULL, '个体户', NULL, '1', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '南疆宾馆院', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2666, 2547, 'qsydw', '企事业单位', NULL, NULL, NULL, NULL, 'ffjh', '非法聚会', 'gtgsyz', '个体工商业者', NULL, NULL, 'sldws', '16-50人', '1', 'qkfy', 30, 0.00, 0, 0, '1', NULL, '情况反映', '816126d8-d4dc-4fff-bfe2-94c025270e4d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102460000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
