INSERT INTO `qb_zdasjqbxx` VALUES (1334, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-03 10:06:45', '16名投资户到昆明远卓深港金融服务有限公司聚集', '7月3日9时许，16名投资户到巨龙大厦7楼昆明远卓深港金融服务有限公司聚集。今日投资户到公司聚集一是打印投资合同等材料用于报案；二是远卓公司拟请第三方公司介入，投资户与第三方公司商议债权事宜。今日远卓公司员工正常上班，投资户由公司相关负责人有序接待中，现场秩序正常。\r\n\r\n\r\n\r\n\r\n', '2018-07-03 09:00:00', '2018-07-03 11:10:00', 2.17, 16, '民间借贷 ', '小南所', '昆明远卓深港金融服务有限公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-06 09:24:49', '2018-08-02 10:12:40', NULL, '远卓投资户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆明远卓深港金融服务有限公司', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2729, 2504, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'e5b9a70f-4f03-4f2e-b9dc-c60e7f1091bb', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1335, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-03 09:46:43', '15余名东风云南汽车厂退休职工聚集在滇缅大道东风云南汽车厂门口反映诉求', '03日9时18分许，15余名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，未出现堵门堵路及影响交通的情况，也无过激行为。相关情况黑林铺所社区民警持续关注中。（黑林铺所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-07-03 09:34:00', '2018-07-03 11:10:00', 1.60, 15, '劳资纠纷', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-06 09:24:49', '2018-07-20 09:48:51', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '63272421-cc84-4033-92a6-48f25355bce1', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
