INSERT INTO `qb_zdasjqbxx` VALUES (1342, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-04 10:11:15', '10名在昭通镇雄县兴隆煤矿打工的农民工因欠薪问题到省政府上访', '      4日09:20分许，10名农民工到省政府上访，反映，他们均是来自江苏、重庆、江西等地的农民工，从2017年2月起至2017年9月在昭通市镇雄县兴隆煤矿打工，该煤矿拖欠12名农民工7个月的工钱110余万元未付，由于一直讨要未果所以到省政府上访，要求解决拖欠工钱的问题。省信访局接待后告知其向省劳动监察局反映。09:35分许,10人全部离开省政府。\r\n', '2018-07-04 09:20:00', '2018-07-04 09:35:00', 0.25, 10, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-06 09:26:16', '2018-07-06 13:57:15', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', '2ce479aa-6f8f-4eed-9f82-e8f53f280aab', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1343, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-04 10:05:13', '30名官渡区住建局退休职工因生活补助问题到省政府上访', '4日09:20分许，30名官渡区住建局退休职工到省政府上访。反映，他们了解到从2017年12月起，事业单位退休人员每月新增生活补贴1200元，由区财政补贴，但这笔钱至今没有发放到他们手里，共涉及该局退休人员59人，他们曾多次向官渡区住建局等相关部门反映要求发放这笔钱未果所以到省政府上访，要求尽快发放生活补贴。现信访接待中。\r\n', '2018-07-04 09:20:00', '2018-07-04 10:00:00', 0.67, 30, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-06 09:26:16', '2018-07-20 15:59:42', NULL, '退休职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'gzfldy', '工资福利待遇', 'sldws', '16-50人', '1', 'qkfy', 30, 0.00, 0, 0, '0', NULL, '情况反映', 'aab7007f-8b22-4af6-a75e-0a411da0c65a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
