INSERT INTO `qb_zdasjqbxx` VALUES (1347, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-05 09:53:16', '15余名退休职工在滇缅大道东风云南汽车厂门口聚集', '7月05日9时18分许，15余名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，未出现堵门堵路及影响交通的情况，也无过激行为。相关情况持续关注中。\r\n\r\n\r\n', '2018-07-05 09:18:00', '2018-07-05 10:53:00', 1.58, 15, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-06 09:27:09', '2018-07-19 17:40:04', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'd78531eb-b018-42a6-92cc-29697f713af8', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1353, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-06 11:16:44', '在弥勒市做工的个体户代表11人因工钱被拖欠到省政府上访', '7月6日10时40分许，11名个体从业人员到省政府上访，反映，他们大部分是外省人，从2017年起陆续来到红河州弥勒市，受雇于红河爱尚科技有限公司，该公司系弥勒市招商引资来的花卉产业集团，开工建设的示范区占地约3万余亩，来访人员在产业园内从事育苗、道路排水，搭建花卉大棚等工作，共计39家施工单位及个体户，之今爱尚公司拖欠这39家单位工程款及农民工工资400余万元，他们多次向红河州相关部门反映要求尽快解决此问题未果所以到省政府上访，要求尽快解决拖欠款项的问题。省信访局接待后告知其向省劳动监察局反映。11时许，11人全部离开省政府。\r\n\r\n\r\n\r\n\r\n', '2018-07-06 10:40:00', '2018-07-06 11:00:00', 0.33, 11, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-09 09:17:26', '2018-07-09 09:38:42', NULL, '个体户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'mjjf', '民间纠纷', 'syr', '11-15人', '1', 'qkfy', 11, 0.00, 0, 0, '0', NULL, '情况反映', 'abf0873f-5125-4bd3-956c-a69dcda2c241', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
