INSERT INTO `qb_zdasjqbxx` VALUES (1372, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-10 09:58:46', '平板玻璃厂20余名职工前往昆明市国资委上访', '工作获悉，7月10日9时30分许，现平板玻璃厂有部分离岗放假人员，通过平板玻璃厂职工微信群邀约到呈贡昆明市国资委上访，现约有20余人已前往，其中部分为邀约一起前往，部分系自行前往。\r\n\r\n据掌握，这20余名职工系对平板玻璃厂改制职工安置，及解除合同相关政策不满意。目前平板玻璃厂共有职工561人，一直处于停产状态，每月补贴每位职工1000元的生活保障费。昆玻股份有限公司对职工制定了职工安置方案，内容为按照工龄，每年工龄补偿7200元人民币，双方自愿解除劳动合同，该方案已上报五华区人社局、昆明市总工会、昆明市国资委登记备案，计划从2018年7月9日起，将方案内容与561名职工进行一对一面谈，双方自愿协商解决，如不同意该方案的职工具继续维持现状。相关情况密切关注中。\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-07-10 09:30:00', '2018-07-10 10:30:00', 1.00, 20, '企业改制/破产 ', '呈贡分局', '昆明市国资委', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-11 09:24:02', '2018-07-20 09:45:00', NULL, '昆明平板玻璃厂职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区外', '昆明市国资委', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2811, 2515, 'qsydw', '企事业单位', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'qygzjbbc,gzfldy', '企业改制兼并破产,工资福利待遇', 'sldws', '16-50人', '1', 'qkfy', 20, 0.00, 0, 0, '0', NULL, '情况反映', '663a2b33-881f-43ae-bfac-171e041676a0', 'JS', NULL, 'Y', 110, NULL, NULL, '530109000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1373, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-10 09:39:26', '100余民办教师聚集云南省教育厅', '7月10日9时许, 100余名持省内各地牌子（宣威、盘龙、东川、师宗、富源、玉溪、晋宁、红河、永善、罗平寻甸）民办教师聚集云南省教育厅，并拉出“昆明市民代幼教师‘老有所养，病有所医，住有所居’诉求”、“云南省下岗民代幼教师老有所养 病有所医”2条红底白字横幅。经执勤民警劝说，聚集民办教师已将横幅收回，等待接访。相关情况密切掌握中。\r\n\r\n\r\n', '2018-07-10 09:00:00', '2018-07-10 09:31:00', 0.52, 100, '身份待遇', '莲华所', '云南省教育厅', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-11 09:24:02', '2018-07-18 16:32:41', NULL, '民办、代课教师', NULL, '1', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '云南省教育厅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2634, 2545, 'dzjg', '党政机关', NULL, NULL, NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'yblyr', '101-200人', '1', 'qkfy', 100, 0.00, 0, 0, '1', NULL, '情况反映', 'b00724e0-3532-4012-b879-24499c324fc0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1375, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-07-11 16:16:53', '厂口乡养殖户14人因不满禁养补偿到省政府上访', '11日15:40分许，14名养殖户到省政府上访，反映，他们从2008年起在五华区厂口乡西翥街道办事处辖区养殖生猪，2018年5月，五华区下发《关于配合完成禁养区划定工作的通知》，要求禁养范围内的400余家养殖户立即处理现有的生猪并拆除相关设施，来访个体户称，他们每家在猪圈设施上投入了上百万元资金，五华区政府在没有与他们商议赔偿方案，也没有按照国家相关规定给予养殖户赔偿的情况下，于7月3日起强行在养殖区设置路障，不允许拉饲料的车辆出入，导致400余养殖户的15000余头生猪的生存出问题，引发养殖户不满，多次向五华区政府相关部门反映要求拆除路障未果，所以到省政府上访，诉求是：1、立即取消路障解决生猪的生存问题；2、相关政府部门要与养殖户协商，出台拆迁、搬迁以及补偿方案后再禁养。省信访局接待后告知其向昆明市政府反映诉求。16:05分许，14人全部离开省政府。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-07-11 15:40:00', '2018-07-11 16:05:00', 0.42, 14, '损害赔偿 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-12 09:33:07', '2018-07-20 10:17:29', NULL, '个体户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'mjjf', '民间纠纷', 'syr', '11-15人', '1', 'qkfy', 14, 0.00, 0, 0, '0', NULL, '情况反映', 'f7240b6c-8079-4eea-84e1-674beefa8a83', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1376, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-11 13:45:47', '10余投资户到昆明远卓深港金融服务有限公司聚集', '7月11日12时40分许，陆续有10余名投资户到巨龙大厦7楼昆明远卓深港金融服务有限公司聚集。今日投资户维权主要动向：一是部分投资户称已在曙光小区找到昆明远卓深港金融服务有限公司主要负责人五华区男子侬开明（42岁）的家，但未见到侬开明本人。 二是由于长期拖欠物业费用，巨龙大厦物管公司将于7月12日昆明远卓深港金融服务有限公司进行停水停电处理。相关情况密切关注中。\r\n\r\n\r\n', '2018-07-11 12:40:00', '2018-07-11 13:40:00', 1.00, 10, '民间借贷 ', '小南所', '昆明远卓深港金融服务有限公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-12 09:33:07', '2018-08-02 10:11:31', NULL, '远卓投资户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆明远卓深港金融服务有限公司', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2729, 2504, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '19979bd4-a4aa-433b-8b58-35bb23704b2c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1379, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-11 10:59:18', '30余名民办、代课教师到省教育厅上访', '7月11日9时许，30余名民办、代课教师再次到省教育厅上访，并在现场拉起两条横幅，内容为红底白字“昆明市民代幼教师‘老有所养，病有所医，住有所居’诉求”、“云南省下岗民代幼教师老有所养 病有所医”。部分教师称对昨日省教育厅的答复情况不满意，认为没有达到预期上访的目的，目前仍在省教育厅门口滞留。分局莲华所警力已在现场开展秩序维护工作，现场秩序平稳、暂无过激行为。相关情况密切关注中。\r\n\r\n\r\n', '2018-07-11 09:00:00', '2018-07-11 15:30:00', 6.50, 30, '涉法涉诉', '莲华所', '省教育厅', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-12 09:33:07', '2018-07-18 16:30:38', NULL, '民办、代课教师', NULL, '0', '2', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '省教育厅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2627, 2545, 'dzjg', '党政机关', NULL, NULL, NULL, NULL, 'qy', '请愿', 'jsxs', '教师学生', 'gzfldy,xxgl', '工资福利待遇,学校管理', 'sldws', '16-50人', '2', 'qkfy', 30, 0.00, 0, 0, '0', NULL, '情况反映', '509eaa50-d8bd-4472-82ea-38b0dda05c0f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1381, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-11 09:36:20', '15余名东风云南汽车厂退休职工聚集在东风云南汽车厂门口', '11日9时20分许，15余名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，未出现堵门堵路及影响交通的情况，也无过激行为。相关情况持续关注中。（黑林铺所）\r\n\r\n\r\n\r\n \r\n\r\n\r\n', '2018-07-11 09:20:00', '2018-07-11 10:50:00', 1.50, 15, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-12 09:33:07', '2018-07-19 17:39:29', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '007a6f67-9591-40e1-8477-395925cb689a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1382, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-07-11 09:18:40', '罗平县阿岗镇村民15人因土地纠纷再次上访', '11日8时35分许，15名曲靖市罗平县阿岗镇高桥村委会挪者大寨村民因土地纠纷一事再次到省政府上访，该起村民28人昨日（7月10日）已就相同诉求到访过，因不满接待结果，今日其中15人再次到访，省信访局不再重复接待，现当地工作组正在现场与之交流，秩序暂时平稳。相关情况进一步掌握中。\r\n', '2018-07-11 08:35:00', '2018-07-11 10:00:00', 1.42, 15, '山林土地问题 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-12 09:33:07', '2018-07-12 13:40:37', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'syr', '11-15人', '2', 'qkfy', 15, 0.00, 0, 0, '0', NULL, '情况反映', '47bb3ca7-a8f1-4bae-aad2-3eef80d5543a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1383, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-11 10:33:39', '20余名西翥街道办养殖户前往市政府上访', '近期，西翥街道办事处厂口乡精顺养殖、云南光辉养殖有限公司、云南永铭养殖有限公司等公司养殖户因对西翥街道办不允许养殖户拉运泔水、饲料一事不满相互邀约拟到市政府上访。据工作掌握，截至 7月11日10时30分许已有20余名养殖户自行驾车前往市政府。相关情况密切关注中。\r\n', '2018-07-11 10:30:00', '2018-07-11 11:30:00', 1.00, 20, '损害赔偿 ', '呈贡分局', '市政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-12 13:51:51', '2018-07-12 13:55:03', NULL, '个体户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2602, 2547, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '60f5dadd-883b-47d3-9f64-f42c11aa2ff1', 'JS', NULL, 'Y', 110, NULL, NULL, '530109000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
