INSERT INTO `qb_zdasjqbxx` VALUES (1387, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-12 13:30:44', '镇雄县芒部镇村民5人因房屋受损到省政府上访', '7月12日11时许，5名昭通市镇雄县芒部镇芒部村委会湾头村村民到省政府上访，反映该村附近长期存在一个采石场，长期放炮采石将村民房屋震坏，村民多次向有关部门反映要求采石场搬迁未得到答复，所以到省政府上访，要求将采石场搬迁同时赔偿村民损失。省信访局接待后告知其向昭通市政府反映。11时15分许，5人全部离开省政府。\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-07-12 11:00:00', '2018-07-12 11:15:00', 0.25, 5, '损害赔偿 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-13 14:11:39', '2018-07-13 15:53:48', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', 'cc9c28e8-ac18-4e53-bb0e-f75c621309af', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1388, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-12 10:49:47', '蒙自市旅游大巴司机10人因不满车辆被停运到省政府上访', '     12日10:05分许，10名蒙自市旅游大巴司机到省政府上访，反映，他们一共43名车主，从2008年起自行购买大巴车，挂靠红河州新风情旅游汽车有限公司，在云南省内各城市跑旅游运营，2017年7月21日，蒙自市交通运输管理处发文要求该公司所有车辆停业整改，该43人遂按照要求进行了安全隐患整改，但交运局一直没有同意解除禁运，至今已有一年。来访车主称，他们了解到系公司管理人员出现职务侵占等违法情况导致车辆被禁运，现违法者已被公安机关采取强制措施，且车主本身并没有出现任何问题，为什么至今不让他们经营，他们当中有部分人是贷款购买的车辆，停运导致车主每天都要承担停车费、保险费流失等损失，生活陷入被动，为此他们多次向蒙自市相关部门反映要求解除停运禁令未得到答复，所以到省政府上访，要求上级政府协调尽快解除禁运。现信访接待中，秩序平稳。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-07-12 10:05:00', '2018-07-12 11:00:00', 0.92, 10, '劳资纠纷', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-13 14:11:39', '2018-07-13 15:52:56', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'mjjf', '民间纠纷', 'sryx', '10人以下', '2', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', '41344c99-871d-4c5d-94dc-890b4d7437c2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1389, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-12 09:37:03', '在玉溪市李棋街道打工的农民工36人因工钱被拖欠到省政府上访', '12日09:00许，36名农民工到省政府上访，反映，他们从2017年9月起，在玉溪市李棋街道办事处土库屯上山头村的统建房施工项目处务工，该工程由佑丞建筑有限公司承建，该公司已支付他们工钱40万元，至今仍然拖欠36名工人工钱120万元未付。由于在玉溪市各级部门反映未得到解决所以到省政府上访，要求尽快解决拖欠农民工工钱问题。现信访接待中。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-07-12 09:00:00', '2018-07-12 09:30:00', 0.50, 36, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-13 14:11:39', '2018-07-13 15:50:50', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sldws', '16-50人', '2', 'qkfy', 36, 0.00, 0, 0, '0', NULL, '情况反映', '545ec7bb-36ef-459f-8658-bf4702c4e57c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1390, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-12 09:29:01', '富源县后所镇村民14人因房屋受损到省政府上访', '12日08:45分许，14名曲靖市富源县后所镇庆云村委会秧田沟村村民到省政府上访，反映，从2008年起，该村有14家村民的房屋开始出现不同程度的地基塌陷、墙体开裂等问题，她们认为是附近的5家煤矿采煤所致，多年来他们多次向乡镇各级政府反映要求调查房屋受损的原因一直未得到答复，故到省政府上访，要求调查清楚是哪家煤矿采煤导致房屋受损，然后根据国家政策给予她们补助。省信访局接待后告知其向曲靖市政府反映。09:10分许，14人离开接访大厅，目前聚集在省政府花园出口方向人行道处。我所密切关注中。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-07-12 08:45:00', '2018-07-12 09:10:00', 0.42, 14, '损害赔偿 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-13 14:11:39', '2018-07-13 15:41:40', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'syr', '11-15人', '2', 'qkfy', 14, 0.00, 0, 0, '0', NULL, '情况反映', 'd04b60e3-c0f3-4269-844e-2518fcf0685a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1391, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-13 11:12:09', '15名退休职工到东风云南汽车厂门口聚集', '7月13日9时30分许，15余名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，未出现堵门堵路及影响交通的情况，也无过激行为。相关情况持续关注中。\r\n', '2018-07-13 09:30:00', '2018-07-13 10:00:00', 0.50, 15, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-16 09:16:32', '2018-07-19 17:35:03', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'e4f2824a-774d-4174-a4de-107e49f83e85', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1392, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-13 10:40:34', '陆良县罗贡村村民11人到省政府举报村支书违纪行为', '     7月13日9时40分许，11名曲靖市陆良县小百户镇罗贡村委会大村村民到省政府上访，举报该村村委会书记焦乔良在担任此职务的多年间，存在挪用克扣百姓烤烟分配数获利、乱收烤烟漂浮育苗池子费获利、不公开村委会支出账目等贪腐违纪行为。现曲靖市信访局工作人员正在接待来访群众，目前秩序平稳。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-07-13 09:40:00', '2018-07-13 10:00:00', 0.33, 11, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-16 09:16:32', '2018-07-16 09:18:45', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'syr', '11-15人', '2', 'qkfy', 11, 0.00, 0, 0, '0', NULL, '情况反映', 'b84d2d84-f751-4f90-82f8-c67a2e301b8a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1393, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-16 17:25:02', '20余名投资户聚集在巨龙大厦7楼昆明远卓深港金融服务有限公司', '7月16日16时许，巨龙大厦7楼昆明远卓深港金融服务有限公司有20余名投资户聚集在现场，现场秩序良好，投资户情绪稳定。公司方有3名员工留守公司，员工情绪激动，存在较大抵触。\r\n\r\n经了解，一是由于长期拖欠物业费用，巨龙大厦物管公司于7月12日对7楼昆明远卓深港金融服务有限公司进行停水停电处理，截至目前，公司仍处于停水停电状态，导致员工日常生活存在不便。二是7月10日原为昆明远卓深港金融服务有限公司工资发放日，但至今，公司37名员工仍未收到工资，员工生活存在困难，需另谋生计。\r\n\r\n民警已积极与员工协商希望其能继续留守公司，稳定投资户情绪，员工虽表示愿意配合公安机关开展工作，但抵触情绪较大。可能带来不安定的隐患，后续情况将密切关注中。（小南所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-07-16 16:00:00', '2018-07-16 17:00:00', 1.00, 20, '民间借贷 ', '小南所', '昆明远卓深港金融服务有限公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-17 09:25:19', '2018-08-02 10:11:10', NULL, '远卓投资户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆明远卓深港金融服务有限公司', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2729, 2504, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'f79ad604-8f39-45ca-84d3-5f1b354559a0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1394, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-16 15:07:44', '巨龙大厦7楼昆明远卓深港金融服务有限公司50余名投资户到区政府上访', '7月16日14时许，云南省昆明市五华区人民中路“昆明远卓深港金融服务有限公司”投资户50余人到五华区政府上访。据现场上访投资户反映：该卓金金服国资投资有限公司，以利率8-11%为收益，累计吸纳投资户投资3.2亿元，现在该公司已经倒闭，办公地点无人办公，投资户投入的本金及收益无法收回，于是到五华区政府上访。区政府信访办相关负责人接待处理中。现场秩序正常，无过激言行。民警已到现场维持秩序，协助做好协调工作，后续情况密切关注中。（翠湖所）\r\n', '2018-07-16 14:00:00', '2018-07-16 14:30:00', 0.50, 50, '民间借贷 ', '翠湖所', '区政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-17 09:25:19', '2018-08-02 10:11:19', NULL, '远卓投资户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2762, 2504, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'sldws', '16-50人', '1', 'qkfy', 50, 0.00, 0, 0, '0', NULL, '情况反映', 'db1de3c8-a074-4284-9d28-8edc165c21fe', 'JS', NULL, 'Y', 110, NULL, NULL, '530102430000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1395, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-16 14:41:19', '宜良县狗街镇小马街村委会陈官营村村民12人到省信访局上访', '7月16日14时许，宜良县狗街镇小马街村委会陈官营村村民12人到省信访局上访，反映该村领导利用职务之便中饱私囊，借用承包方式霸占该村陈官营水库和三百亩山林，现请求上级领导查处。现省信访局正接待中。（华山所）\r\n', '2018-07-16 14:00:00', '2018-07-16 14:30:00', 0.50, 12, '村务管理 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-17 09:25:19', '2018-07-17 09:47:52', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', NULL, NULL, 'syr', '11-15人', '1', 'qkfy', 12, 0.00, 0, 0, '0', NULL, '情况反映', 'b3350441-8bf5-45ac-b21e-bcd08880de7b', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1396, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-16 14:22:21', '7月16日百余名昆明平板玻璃厂聚集职工已平稳离开', '7月16日上午9时30分许，陆续有70余名昆明平板玻璃厂职工到厂区聚集，并悬挂5条白底黑字标语，内容为“坚决抵制非法买断”“职工做主”“我们要生存 我们要吃饭 我们要住房”“黑心企业云内动力 滚出昆玻”“守住赖以生存的土地”等。\r\n\r\n聚集职工主要诉求为不满意职工安置方案，认为买断工龄系公司暗箱操作。10时许，现场聚集职工已达100余人。该厂相关领导接访了聚集职工。11时40分许，现场百余名职工陆续全部离开。据民警掌握，聚集中有50余名职工，每人自出100元，声称作为将来维权活动费用，部分职工离开时，称将会天天到厂区聚集。\r\n\r\n    目前，现场治安秩序已恢复正常，属地黑林铺所持续关注中。（黑林铺所）\r\n', '2018-07-16 09:30:00', '2018-07-16 11:40:00', 2.17, 100, '企业改制/破产 ', '黑林铺所', '昆明平板玻璃厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-17 09:25:19', '2018-07-20 16:05:57', NULL, '昆明平板玻璃厂职工', NULL, '0', '2', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆明平板玻璃厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2775, 2515, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '7123c425-8ef0-412d-85a5-184ea0e3c68d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1399, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-16 09:45:27', '13余名东风云南汽车厂退休职工聚集在滇缅大道东风云南汽车厂门口', '7月16日9时28分许，13余名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，未出现堵门堵路及影响交通的情况，也无过激行为。相关情况民警持续关注中。（黑林铺所）\r\n\r\n\r\n\r\n\r\n', '2018-07-16 09:28:00', '2018-07-16 10:49:00', 1.35, 13, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-17 09:25:19', '2018-07-19 17:34:54', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'be9f972d-6991-416d-bca6-a6fa6d8991ee', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1402, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-07-17 13:58:51', '7月17日50余名“尚源郦城”业主到省政府上访', '7月17日9时30分许，50余名“尚源郦城”业主到省政府上访，其主要反映“尚源郦城”小区的大部分购房业主接房后，因开发商资金问题，目前住宅小区依然处于“无正式供电、无绿化、无燃气、无消防设施、无落户条件”的状况。近期该小区物业要求业主缴纳物管费、停车费、车位管理费等费用，此举引发业主极大不满，期间因部分业主拒绝缴纳，双方还一度发生矛盾冲突。业主普遍认为该小区在缺乏各种配套设施且未经验收情况下，向业主征收上述费用极为不合理。部分业主认为只有通过继续上访才能解决项目当前存在的问题。因此，今日50余名业主聚集到省政府上访，请求领导为他们协调解决问题。省信访局的工作人员接待了上访业主，并答复业主所反映问题属市、区政府的有关职能部门解决。于是上访业主于11时40分离开省政府转向五华区政府反映问题。（国保大队）\r\n\r\n\r\n', '2018-07-17 09:30:00', '2018-07-17 11:40:00', 2.17, 50, '产权物业', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-18 09:05:27', '2018-07-23 11:30:16', NULL, '尚源郦城', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '省信访局', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '2be26558-faad-4682-a4f3-5a1905c4b157', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1403, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-17 11:23:21', '7月17日昆明平板玻璃厂职工再次到厂区聚集', '7月16日,110余名平板玻璃厂职工因对职工安置问题不满意到厂区聚集，至当日11时40分许离开。7月17日上午9时30分许，约30余名玻璃厂职工到位于五华区昭宗路的平板玻璃厂厂区聚集。\r\n\r\n情况发生后，黑林铺所民警到场，现场秩序正常，无过激行为，昨日悬挂5条白底黑字标语（内容为坚决抵制非法买断、职工做主、我们要生存 我们要吃饭 我们要住房、黑心企业云内动力 滚出昆玻、守住赖以生存的土地）仍悬挂于厂区。今日职工主要诉求仍为不满意职工安置方案，认为买断工龄系暗箱操作。厂区无厂领导接待，黑林铺所密切关注中。（黑林铺所）\r\n', '2018-07-17 09:30:00', '2018-07-17 11:30:00', 2.00, 30, '企业改制/破产 ', '黑林铺所', '昆明平板玻璃厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-18 09:05:27', '2018-07-19 17:55:45', NULL, '昆明平板玻璃厂职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆明平板玻璃厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2775, 2515, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'e1b3c305-fd57-4049-816f-a9f1cb518c2d', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
