INSERT INTO `qb_zdasjqbxx` VALUES (1408, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-17 09:17:57', '佑丞建筑有限公司13名农民工再次到省信访局上访', '17日8:30许，13名农民工再次到省政府上访，反映，他们从2017年9月起，在玉溪市李棋街道办事处土库屯上山头村的统建房施工项目处务工，该工程由佑丞建筑有限公司承建，该公司已支付他们工钱40万元，至今仍然拖欠36名工人工钱120万元未付。由于在玉溪市各级部门反映未得到解决所以到省政府上访，要求尽快解决拖欠农民工工钱问题。现信访接待中。（华山所）\r\n', '2018-07-17 08:30:00', '2018-07-17 09:00:00', 0.50, 13, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-18 09:05:27', '2018-07-20 10:22:23', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '省信访局', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'syr', '11-15人', '1', 'qkfy', 13, 0.00, 0, 0, '0', NULL, '情况反映', '6f7549a6-03c3-4195-9e7f-667f188446f4', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
