INSERT INTO `qb_zdasjqbxx` VALUES (1448, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-19 10:02:25', '平板玻璃厂10余名职工因对职工安置问题不满意到厂区聚集', '7月19日上午9时30分许，10余名玻璃厂职工到位于本市五华区昭宗路附近的平板玻璃厂厂区聚集，秩序正常，无过激行为，厂区内仍悬挂5条白底黑字标语（内容为坚决抵制非法买断、职工做主、我们要生存 我们要吃饭 我们要住房、黑心企业云内动力 滚出昆玻、守住赖以生存的土地）。主要诉求为不满意职工安置方案，认为买断工龄系暗箱操作。今日现场无厂区领导接待，黑林铺所民警在现场了解情况，维持秩序，并对职工进行劝解。相关情况密切关注中。（黑林铺所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-07-19 09:30:00', '2018-07-19 11:30:00', 2.00, 10, '企业改制/破产 ', '黑林铺所', '平板玻璃厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-20 09:19:08', '2018-07-20 15:14:25', NULL, '昆明平板玻璃厂职工', NULL, '0', '2', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '平板玻璃厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2763, 2515, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '99ed1fca-bd17-42b1-ac4e-e2fac243fc2a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1449, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-19 09:38:39', '12名汽车厂退休职工聚集在滇缅大道东风云南汽车厂门口（烦请以此为准，谢谢！）', '7月19日9时25分许，12名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，未出现堵门堵路及影响交通的情况，也无过激行为。相关情况黑林铺所社区民警持续关注中。（黑林铺所）\r\n\r\n\r\n\r\n\r\n', '2018-07-19 09:25:00', '2018-07-19 10:50:00', 1.42, 12, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-20 09:19:08', '2018-07-20 09:39:38', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '4853851f-b693-4f38-b941-403129193252', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
