INSERT INTO `qb_zdasjqbxx` VALUES (1468, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-24 09:34:31', '6名民工聚集滇缅大道 “欣成创意家园”讨要工程款', '7月23日15时许，昭通镇雄县男子陈伟（37岁）等6名民工聚集滇缅大道“欣成创意家园”工地讨要工程尾款。据了解，2012年陈等人受雇云南金堰劳务分包有限公司，负责该项目的基坑工程，开发商华信集团称已与劳务公司结清所有款项，但陈等人称尚余180余万元工程款未结清。经工作，陈等人同意会同劳务公司共同协商欠款事宜，于17时许自行离开。相关情况进一步掌握中。\r\n\r\n\r\n', '2018-07-23 15:00:00', '2018-07-23 17:00:00', 2.00, 6, '索薪讨款', '黑林铺所', '欣成创意家园', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-25 09:13:37', '2018-07-25 09:21:55', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '欣成创意家园', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2759, 2519, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'a37b3ef6-aaf2-4c95-aa1d-b41f817ce419', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1474, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-07-25 13:50:54', '5名男子到佳能达药业门口人员聚集讨要工程款', '7月25日12时许，黑林铺所接群众报警称4、5名男子在辖区昭宗平板玻璃厂一生活区旁佳能达药业门口聚集讨要工程款。\r\n\r\n接警后，民警迅速赶到现场。经了解系四川籍男子唐兵（53岁）等5人因与之前在此处经营的云南红叶药业存在工程款纠纷，唐等人意图讨要工程款故前来聚集。现唐兵联系对方，对方称目前不在昆明，经民警劝说，唐兵等人同意等对方回昆后再与对方商讨工程款事宜，目前唐兵等人已离去。相关情况密切关注中。\r\n\r\n\r\n', '2018-07-25 12:00:00', '2018-07-25 12:30:00', 0.50, 5, '索薪讨款', '黑林铺所', '佳能达药业', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-26 09:09:37', '2018-07-26 09:25:03', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '佳能达药业', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2774, 2519, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '064d8ac3-90e2-4451-a207-62acabc0fed9', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
