INSERT INTO `qb_zdasjqbxx` VALUES (1477, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-07-26 14:52:39', '7月26日黑林铺所辖区平板玻璃厂部分职工因对职工安置问题不满意可能引发纠纷', '7月26日上午9时20分许，10余名玻璃厂职工到位于本市五华区昭宗路附近的平板玻璃厂厂区聚集，秩序正常，无过激行为，在厂区内以及大门口悬挂5条白底黑字标语（内容为坚决抵制非法买断、职工做主、我们要生存我们要吃饭、我们要住房、黑心企业云内动力、滚出昆玻、守住赖以生存的土地）。主要诉求为不满意职工安置方案，认为买断工龄系暗箱操作。11时50分许，聚集职工自行离开，未出现堵路及影响交通的情况。\r\n\r\n民警在工作中了解到，玻璃厂部分职工计划于7月27日（星期五）前往玻璃厂就职工安置问题讨要说法，初步摸排约有200名职工准备前往，现不排除协商不成到省、市政府上访的可能。相关情况密切掌握中。相关情况持续关注中，有新情况将第一时间上报。（黑林铺所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-07-26 09:20:00', '2018-07-26 11:50:00', 2.50, 10, '企业改制/破产 ', '黑林铺所', '平板玻璃厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-27 09:19:51', '2018-07-31 09:53:43', NULL, '昆明平板玻璃厂职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '平板玻璃厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2763, 2515, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '7d8457b6-768b-4d68-aca3-0a786e7a7d77', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
