INSERT INTO `qb_zdasjqbxx` VALUES (1482, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-07-27 15:16:27', '8名民工聚集昭宗路佳能达药业公司前讨要工程款', '7月27日8时许，四川遂宁市男子唐兵（53岁）等8人聚集在昭宗路佳能达药业公司前讨要工程款。据了解，2013年7月，唐承接云南红叶药业有限公司依禄村荒地平整工程。2015年6月，五华区法院判决红叶药业支付75万余元工程尾款，因公司法人失联一直未执行。现该地块由云南佳能达医药有限公司租用。2018年2月，唐等人曾多次聚集佳能达医药公司前讨要。相关情况进一步掌握中。\r\n\r\n\r\n', '2018-07-27 08:00:00', '2018-07-27 10:30:00', 2.50, 8, '索薪讨款', '西山区政府', '昭宗路佳能达药业公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-30 10:16:39', '2018-07-30 10:52:52', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '昭宗路佳能达药业公司', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2600, 2519, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '9870f937-f928-4bc8-b2b0-130ee677bfcf', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
