INSERT INTO `qb_zdasjqbxx` VALUES (1484, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-27 10:41:30', '7月27日盘龙区唐家营片区B地块（汇都国际）拆迁户7人到省信访局上访', '7月27日9时30分许盘龙区唐家营片区B地块（汇都国际）拆迁户7人到省信访局上访，反映2010年唐家营片区B地块拆迁后，当时开发商承若2013年上半年完成建房回迁工作，但到现在8年后任然无法回迁，并且自2015年3月开发商驰久（金马源）房地产开发有限公司就停发了拆迁户的过渡费至今，现请政府协调解决。现信访局正接待中。（华山所）\r\n', '2018-07-27 09:30:00', '2018-07-27 10:27:00', 0.95, 7, '拆迁安置', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-30 10:16:39', '2018-07-30 10:20:34', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', NULL, NULL, 'cscq', '城市拆迁', 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '05349a2f-f041-4686-b247-eb94c44a1ddc', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1485, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-07-30 16:25:31', '“12.13”人员到省政府上访情况', '7月30日9时30分，省内、外“12.13”人员20人到省政府上访.上访人员反映：泛亚人员资金索赔事宜。昆明市人员有8人参加上访，外省籍人员12人.提出接访后，省信访局回复泛亚案已通过司法途径解决，不再予以接访。11时30分，上访人员离开省政府。在省政府上访期间，秩序良好，未出现喊口号，大标语等情况。相关情况在进一步掌握中。	\r\n\r\n\r\n', '2018-07-30 09:30:00', '2018-07-30 11:30:00', 2.00, 20, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-31 09:17:06', '2018-07-31 09:57:19', NULL, '12.13', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2501, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'b97b6a78-c8da-421b-9bda-059ddc694cd8', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
