INSERT INTO `qb_zdasjqbxx` VALUES (1488, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-30 14:20:48', '7月30日红河州泸西县金马、旧城、三塘三地1978年河口马蝗堡国防公路民工9人到省信访局上访', '7月30日14时许红河州泸西县金马、旧城、三塘三地1978年河口马蝗堡国防公路民工9人到省信访局上访，反映1979年对越自卫还击战期间从事支前民工工作，工作辛苦拖垮了身体，现希望政府按照1979年支前民兵安排待遇，给予困难补助。现信访接待中。（华山所）\r\n', '2018-07-30 14:00:00', '2018-07-30 15:00:00', 1.00, 9, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-31 09:17:06', '2018-07-31 09:46:26', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm,tsqt', '农民,特殊群体', NULL, NULL, 'sryx', '10人以下', '1', 'qkfy', 9, 0.00, 0, 0, '0', NULL, '情况反映', '8d35e4c6-55e5-4eb0-8c1a-1586e7fb6278', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1489, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-30 10:17:44', '玉溪市佑丞建筑有限公司10名农民工再次到省政府上访', '7月30日8时30分许，10名农民工再次到省政府上访，反映，他们从2017年9月起，在玉溪市李棋街道办事处土库屯上山头村的统建房施工项目处务工，该工程由佑丞建筑有限公司承建，该公司已支付他们工钱40万元，至今仍然拖欠36名工人工钱120万元未付。由于在玉溪市各级部门反映未得到解决所以到省政府上访，要求尽快解决拖欠农民工工钱问题。现信访接待中。(华山所)\r\n', '2018-07-30 08:30:00', '2018-07-30 09:26:00', 0.93, 10, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-07-31 09:17:06', '2018-07-31 09:35:31', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2519, 'dzjg', '党政机关', 'fyqk,mjjf', '反映情况,民间纠纷', NULL, NULL, 'sf', '上访', 'nm', '农民', 'lzjf', '劳资纠纷', 'sryx', '10人以下', '1', 'qkfy', 10, 0.00, 0, 0, '0', NULL, '情况反映', 'a53821be-a207-4505-b3b7-18b70c1dd5c0', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1492, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-31 14:37:28', '盛泰公园盛世小区业主130人到省信访局上访', '7月30日14时许官渡区盛泰 公园盛世小区业主130人到省信访局上访，反映开发商逾期8个月交房，交房后才发现小区没有验收，使用临水临电，后于2016年在政府的协调下解决了用电问题，但小区的其他配套设施自交房三年内一直都未完善，产权证未能办理，小区封压住房将被法院拍卖等问题，现希望政府能协调解决，现信访局正接待中。（华山所）\r\n', '2018-07-30 14:00:00', '2018-07-30 15:00:00', 1.00, 130, '逾期未交房', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-01 08:45:14', '2018-08-14 10:20:43', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '省信访局', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'yblyr', '101-200人', '1', 'qkfy', 130, 0.00, 0, 0, '0', NULL, '情况反映', '72b08693-c5a8-4600-bce1-f18185d32134', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1495, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-07-31 12:29:33', '云南省恩洪煤矿职工120余人到省信访局上访', '7月31日9时30分许，云南省恩洪煤矿职工120余人到省信访局上访，该煤矿隶属于云南煤化工集团东源煤电股份有限公司，自2014年以来先后7次停产，特别是2015年至2016年基本处于停产半停产状态，职工被迫外出打临工谋生，2018年1月26日因采矿许可证到期再次停工，现上访职工聚集在省政府大门左侧，拒接进到信访大厅上访，打标语“我们要工作、我们要吃饭”、“谁来关注煤炭人的生计”并高喊口号。相关情况密切掌握中。\r\n', '2018-07-31 09:30:00', '2018-07-31 16:10:00', 6.67, 120, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-01 08:45:14', '2018-08-14 10:20:26', NULL, '企事业单位职工', NULL, '0', '1', '拉横幅、喊口号', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '省信访局', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2537, 'dzjg', '党政机关', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'xgsyry', '下岗失业人员', 'qygzjbbc', '企业改制兼并破产', 'yblyr', '101-200人', '1', 'qkfy', 120, 0.00, 0, 0, '0', NULL, '情况反映', 'ecbecfda-2832-4121-87c0-d686b3987438', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
