INSERT INTO `qb_zdasjqbxx` VALUES (1500, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-01 13:06:57', '8月1日300余名远卓投资户到巨龙大厦聚集临时商议后到省金融办群访', '8月1日9时许，陆续有投资户到巨龙大厦7楼昆明远卓深港金融服务有限公司聚集，人数最多时达300余人。分局国保大队、小南所所警力到现场开展秩序维护及稳控工作。\r\n\r\n现场聚集的投资户相互商议维权方法，最后临时决定到省金融办群访，并选出了代表。11时许，陆续有200余投资户自行前往云南省金融办群访，在整个聚集过程中，无过过激维权情况发生，现场秩序正常。截止上报时，现场滞留10余名投资户，其余人员均已自行离开。据初步掌握，今日聚集的投资户主要以昆明地区的为主，有少量安宁、寻甸等投资户参与，据称2日、3日投资户将继续聚集维权，人数规模摸排中。（国保大队）\r\n', '2018-08-01 09:00:00', '2018-08-01 11:00:00', 2.00, 300, '民间借贷 ', '国保大队', '昆明远卓深港金融服务有限公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-02 09:22:46', '2018-08-02 10:17:16', NULL, '远卓投资户', NULL, '1', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆明远卓深港金融服务有限公司', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2729, 2504, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '1', NULL, '情况反映', 'dafc4f4f-ba64-40f5-9d08-ce696fce7d1e', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1501, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-01 12:49:42', '百余名莲花池畔小区业主到省地税局直属征收局聚集', '8月1日9时30分许，100余名莲花池畔小区业主按预警到三合商利大厦12楼省地税局直属征收局聚集。业主称购房发票、契税影响到房产证办理，故今日到地税局要求查询发票办理进度。现地税局工作人员、开发商负责人正协商处理方案。分局法制大队、巡特警大队、新村所等部门100余名警力在现场开展秩序维护、疏导劝解工作。部分业主声称将前往区住建局上访，相关情况密切关注中。\r\n\r\n\r\n', '2018-08-01 09:30:00', '2018-08-01 13:00:00', 3.50, 100, '逾期未交房', '新村所', '省地税局', NULL, NULL, '100', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-02 09:22:46', '2018-08-02 10:02:34', NULL, '小区业主', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '省地税局', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2670, NULL, 'dzjg', '党政机关', NULL, NULL, NULL, NULL, 'zzhd', '组织活动', NULL, NULL, NULL, NULL, 'wsyr', '51-100人', '1', 'qkfy', 100, 0.00, 0, 0, '0', NULL, '情况反映', '10d01056-2d40-4901-beff-0a63cb60c466', 'JS', NULL, 'Y', 110, NULL, NULL, '530102470000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1502, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-08-01 09:55:50', '8月1日参战退伍老兵到南屏街中心广场聚集“打扫卫生”', '8月1日8时30分许，陆续有参战退伍老兵出现在南屏街中心广场，截止9时40分现场共聚集了17名老兵，其中15男、2女，聚集的老兵有5人着便服，其余人员着65式绿色军服。\r\n\r\n分局护国所、国保大队警力在现场开展稳控工作，现场老兵放置了一个音箱，尚未播放，一面写有“学习雷锋好榜样，忠于人民忠于党”字样的红旗，一条写有“把有限的生命，投入到无限的为人民服务之中去！”的横幅，四张印着雷锋头像的广告版，分别是印有雷锋简介文字的一张，印有“向雷锋同志学习”字样一张，印有雷锋照片的两张。\r\n\r\n聚集老兵向民警表述，他们是来中心广场打扫卫生的，以此庆祝八一建军节。目前，老兵分为四个小队正在打扫中心广场，分局警力密切关注中，续报中。（护国所）\r\n', '2018-08-01 09:30:00', '2018-08-01 13:20:00', 3.83, 17, '特殊群体', '护国所', '南屏街中心广场', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-02 09:22:46', '2018-08-02 10:03:06', NULL, '对越参战老兵', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '南屏街中心广场', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2724, 2524, NULL, NULL, NULL, NULL, NULL, NULL, 'zzhd', '组织活动', 'jtry', '军退人员', NULL, NULL, 'syr', '11-15人', '1', 'qkfy', 11, 0.00, 0, 0, '0', NULL, '情况反映', '3595ae8c-dd4f-4ab2-9428-e7106a9806db', 'JS', NULL, 'Y', 110, NULL, NULL, '530102510000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
