INSERT INTO `qb_zdasjqbxx` VALUES (1514, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-02 16:11:57', '10余名业主到国富现代城新时代广场售楼部拉横幅维权', '8月2日上午，48名国富现代城时代广场业主为子女入学一事前往武成小区国福校区讨要说法。经校方说明后，业主离开学校并制作维权横幅于12时许到新时代广场售楼部以及国福现代城玫瑰苑售楼部拉横幅维权。横幅内容为国福大骗子  政府不作为 小孩无学上 ”“还我武成学们 履行协议  还我武成学位”等。分局月牙塘所警力已在现场开展秩序维护工作，相关情况密切关注中。\r\n\r\n\r\n\r\n\r\n', '2018-08-02 12:00:00', '2018-08-02 13:25:00', 1.42, 48, '逾期未交房', '月牙塘所', '国富现代城新时代广场', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-03 11:14:14', '2018-08-03 14:41:57', NULL, '小区业主', NULL, '0', '2', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '国富现代城新时代广场', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2708, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '88ebcc2b-026f-40f9-817c-a6c51e595db1', 'JS', NULL, 'Y', 110, NULL, NULL, '530102500000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
