INSERT INTO `qb_zdasjqbxx` VALUES (1516, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-02 15:07:34', '洪家营新村5名拆迁户聚集工地阻挠清理建筑废墟', '洪家营新村拆迁改造工程自2012年6月启动以来，仍有5户村民未签订拆迁协议。2018年4月，5户村民房屋因施工全部倒塌，村民联名起诉至五华区法院。8月2日14时20分许，获悉开发商“文洁地产公司”将对倒塌房屋废墟进行清理后，5名村民聚集在工地现场，要求停止施工，并与工地保安发生争执。相关情况密切掌握中。\r\n\r\n\r\n', '2018-08-02 14:20:00', '2018-08-02 15:00:00', 0.67, 11, '拆迁补偿', '丰宁所', '洪家营新村', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-03 11:14:14', '2018-08-03 14:48:59', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '洪家营新村', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2753, 2507, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '4175f0e8-4abe-4ea1-bcb0-ae65a3e97d53', 'JS', NULL, 'Y', 110, NULL, NULL, '530102560000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
