INSERT INTO `qb_zdasjqbxx` VALUES (1540, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-07 09:22:27', '8月6日云南新源再生产业有限公司退休职工再次到新源阁酒店聚集', '8月6日9时许，云南新源再生产业有限公司30余名退休老职工再次到新源阁酒店门口，要求诺嘉公司归还新源酒店的经营权。至7日凌晨1时，除7名新源再生产业有限公司退休工人在新源阁酒店内休息外，其它人员离开回家。\r\n\r\n为防止双方纠纷升级，黑林铺所均安排警力在场，至上报情况时，未发生堵门、堵路及其它违反治安管理的案事件。密切关注中。（黑林铺所）\r\n', '2018-08-06 09:00:00', '2018-08-07 01:00:00', 16.00, 30, '合同纠纷 ', '黑林铺所', '新源阁酒店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-08 09:58:18', '2018-08-08 14:45:07', NULL, '退休职工', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '新源阁酒店', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2758, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '04ee62c0-79fe-4089-bf3c-1e22614bd619', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1541, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-08 12:25:26', '27名新源再生产业有限公司职工聚集在新源阁酒店的情况反映20180808', '8月7日9时许，云南新源再生产业有限公司20余名退休老职工来到新源阁酒店门口，要求诺嘉公司归还新源酒店的经营权。直至晚上仍有9至10名新源再生产业有限公司退休工人滞留在新源阁酒店二楼666和888两个房间内休息，其它人员自行离开回家。\r\n\r\n8月8日11时20分许，27名新源再生产业有限公司职工又来到新源阁酒店门口，在二楼房间窗外横挂2幅白底黑字布标：一条内容为“黑、恶势力霸占产权，三年不交租金”。另一条内容为“还我新源商务楼、清政府主持正义”。要求诺嘉公司归还新源酒店的经营权。目前多数职工在酒店二楼的两个房间内商量事情，无过激行为，未出现堵路及影响交通的情况。相关情况持续关注中。\r\n\r\n\r\n\r\n \r\n\r\n黑林铺派出所\r\n\r\n2018年8月8日\r\n\r\n\r\n', '2018-08-08 11:20:00', '2018-08-08 13:00:00', 1.67, 27, '合同纠纷 ', '黑林铺所', '新源阁酒店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-09 09:25:36', '2018-08-09 10:00:00', NULL, '企事业单位职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '新源阁酒店', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2758, 2537, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '869318f0-ca07-47be-b364-fb834f1f28b2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1542, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-08 10:57:35', '20余名退休职工到东风云南汽车厂门口聚集', '8月08日9时40分许，20名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，无过激行为。至10时36分许聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。\r\n\r\n\r\n\r\n \r\n', '2018-08-08 09:40:00', '2018-08-08 10:36:00', 0.93, 20, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-09 09:25:36', '2018-08-09 09:52:46', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'a9ffefcb-9e41-4c8e-a5b7-053dde097612', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
