INSERT INTO `qb_zdasjqbxx` VALUES (1547, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-09 13:57:09', '8月9日“昆明远卓深港金融服务有限公司”投资群体到五华体育馆聚集情况', '8月9日9时许，陆续有700余名以“昆明远卓深港金融服务有限公司”为主的群体成员到五华体育馆聚集上访，其中我市涉金融受损群体“儒商”、“小微”等部分群体成员参加了此次聚集，聚集过程中，省金融办等政府职能部门的工作人员对部分代表进行了接访，由于现场聚集的人员对接访不满，大部分人员便离开现场。11时许， 80余名“昆明远卓深港金融服务有限公司”的群体成员前往省金融继续上访。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2018-08-09 09:00:00', '2018-08-09 12:00:00', 3.00, 700, '民间借贷 ', '西山分局', '五华体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-10 09:17:18', '2018-08-10 09:38:28', NULL, '远卓投资户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '五华体育馆', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2801, 2504, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '031bc8be-5e21-4ed7-9188-a81003dbec07', 'JS', NULL, 'Y', 110, NULL, NULL, '530105000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1548, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-08-09 14:01:40', '8月9日百余名退休教师到省政府上访', '8月9日9时许，昆明市下辖的盘龙区、五华区、西山区、官渡等四区，原工矿企业学校的100余名退休教师到省政府上访。反映，他们大多数都是90年代退休的企业教师，曾于8月3日到昆明市政府进行过上访。今日相互串联各区企业退休教师，到省政府信访局提交诉求，递出《国务院办公厅9号文件》复印件和《关于坚决要求国有企业中小学退休教师的归属问题的报告》2份材料给信访局，要求解决教师身份归属问题，与教育局直管学校退休教师的待遇一样。提出2016年退休教师每人每月应发的1200.00元，只发了三个月后来就停发了，与教育局直管学校退休教师的待遇不一样的问题。省信访局相关工作人员接访了上访退休教师推选五名代表 ，市教委和四区教育局相关工作人员配合省信访局开展工作后，到11点30分接访结束后，召集各区上访人员，积极开展疏导化解工作进行登记，逐人了解情况后，12时许所有上访人员自觉有序离开省信访局。相关情况在进一步掌握中。（国保大队）\r\n', '2018-08-09 09:00:00', '2018-08-09 12:00:00', 3.00, 100, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-10 09:17:18', '2018-08-29 11:09:32', NULL, '退休职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '7da75a4b-d99e-4727-87dc-fdeffc45d52f', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
