INSERT INTO `qb_zdasjqbxx` VALUES (1574, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-15 16:01:38', '8月15日莲花池畔龙池物业召开业主见面会', '8月15日14时30分许，分局接报称莲花池畔龙池物业在客户服务大厅召开了业主见面会，据掌握现场约有80余名业主。五华区政府住建、消防、莲华办事处等多部门对莲花池畔小区水、电、气的供应等问题给予相关答复。目前在答复中，现场秩序良好，无过激行为。民警在现场维持秩序，后续情况将密切关注中，如有情况及时上报。（莲华所）\r\n\r\n\r\n', '2018-08-15 14:30:00', '2018-08-15 16:00:00', 1.50, 80, '物业管理', '莲华所', '莲花池畔', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-16 09:28:46', '2018-08-16 09:45:27', NULL, '小区业主', NULL, '1', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '莲花池畔', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2630, NULL, 'jmxq', '居民小区', NULL, NULL, NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'wsyr', '51-100人', '2', 'qkfy', 80, 0.00, 0, 0, '1', NULL, '情况反映', '588b969d-4754-48d6-acc3-ba04c137250a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1575, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-08-15 15:09:32', '8月15日昭阳区母鹿社区居民13人因不满拆迁补偿到省政府上访', '15日14:30分许，13名昭通市昭阳区凤凰街道办事处母鹿社区居民到省政府上访，反映，2010年当地修建珠海大道，其社区被划入拆迁范围，2016年正式启动拆迁，来访群众称，当地政府未按照国家305号令和省里109号令认真落实土地补偿安置政策，房屋拆迁补偿价仅给群众500元每平米，远低于市场价，引发拆迁群众不满，多次向昭阳区政府反映，要求按照市场价补偿村民未果，所以到省政府上访，要求按照国家及省里的相关规定解决群众的拆迁安置补偿问题。该起群众于8月13日曾就相同诉求上访过，由于没有得到答复所以今日再次上访。省信访局接待后告知其向昭通市住建局反映。15:00分许，13人全部离开省政府。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n华山东路派出所\r\n\r\n2018年8月15日\r\n\r\n\r\n', '2018-08-15 14:30:00', '2018-08-15 15:00:00', 0.50, 13, '拆迁补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-16 09:28:46', '2018-08-17 10:05:08', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'cscq', '城市拆迁', 'syr', '11-15人', '1', 'qkfy', 13, 0.00, 0, 0, '0', NULL, '情况反映', '898e315c-6ada-4112-a94c-ecf42a9a27b1', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1577, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-08-15 12:59:56', '8月15日我市多个涉金融领域受损群体到省政府群访情况（二）', '8月15日9时许，“儒商”、“小微”、“互信”等涉金融投资受损群体人员开始陆续到省政府上访。来访人员被及时劝解进入了省政府信访局大厅。\r\n\r\n10时30分许，来访人员达130余人，人数截止13时维持在130人左右。信访局窗口接待了来访投资户，但由于不满窗口工作人员接待结果，聚集在信访大厅的投资户情绪激动，高喊“见省长”、“见父母官”等口号。11：50分许，省金融办、市金融办、省市信访局联合工作组在小会议室开始接待来访代表6人，其余人员在大厅及操场处等待。现信访接待中。\r\n\r\n据现场掌握，来访的群体有：“儒商”、“小微”、“互信”、“博融”、“泛民登”、“中益信”、“快融金”、“泛亚”、“光辉”等群体，相关动态进一步摸排中。（情报中心）\r\n', '2018-08-15 09:00:00', '2018-08-15 14:40:00', 5.67, 130, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '127', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-16 09:28:46', '2018-08-16 15:58:05', NULL, '小微儒商', NULL, '0', '2', '喊口号', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2503, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'jzwt', '集资问题', 'yblyr', '101-200人', '2', 'qkfy', 130, 0.00, 0, 0, '0', NULL, '情况反映', '9e02dd58-ce4e-4cd0-bc47-9b4a8a07192a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1578, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-08-15 11:16:04', '50余名平板玻璃厂职工因对职工安置问题不满临时邀约到省、市政府上访', '8月15日8时许，平板玻璃厂50余名职工到培训点集中，表示不愿参加返岗培训，随后返回平板玻璃厂的镀膜车间聚集。\r\n\r\n10时40分许，50余名玻璃厂职工相互邀约组织前往省政府和市政府上访。其中，10余名职工骑电动车到省政府上访；40余名职工驾驶11辆机动车前往市政府行政中心6号楼上访（车牌号：云A09082、云A6XL19、云AG8A70、云A7W7S2、云A5E5N9、云A8G91N、云A8R133、云AONX75、云A9203T）。\r\n\r\n民警初步掌握，工人主要诉求为不满意职工安置方案，认为玻璃厂买断工龄系暗箱操作，损害了职工自身利益。\r\n', '2018-08-15 10:40:00', '2018-08-15 11:40:00', 1.00, 10, '身份待遇', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-16 09:28:46', '2018-08-29 11:08:21', NULL, '昆明平板玻璃厂职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2515, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'f5eb47e6-0548-4900-a817-50919f05e3a2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1579, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-15 10:44:07', '8月15日15余名东风云南汽车厂退休职工聚集在滇缅大道东风云南汽车厂门口', '08月15日9时15分许，15名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，无过激行为。至10时31分许聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。（黑林铺所）\r\n\r\n\r\n\r\n \r\n\r\n\r\n', '2018-08-15 09:15:00', '2018-08-15 10:31:00', 1.27, 15, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-16 09:28:47', '2018-08-16 09:51:43', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'd488d471-2260-4fa3-8837-f6abe6167f66', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1581, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-08-15 10:13:40', '8月15日我市多个涉金融领域受损群体到省政府群访情况（一）', '8月15日9：00许，“儒商”、“小微”、“互信”、“博融”、“泛民登”、“中益信”、“快融金”等涉金融投资受损群体人员开始陆续到省政府上访，五华分局现场应急处突警力127人由高勇副局长带队在现场开展人员稳控疏导工作。现场警力采取疏导引导的工作方式，见到一个投资户立即引导一个投资户进入信访大厅，防止上访群体成员无故滞留省政府大门口，防止群体扎堆抱团。\r\n\r\n截至9:40分许，来访人员达90余人，均已劝解进省政府信访局，且现场秩序正常，据初步掌握暂未发现“远卓”投资户群体人员到省政府上访的情况，现场来访人数仍在缓慢增加中，续报工作开展中。（情报中心）\r\n', '2018-08-15 09:00:00', '2018-08-15 14:40:00', 5.67, 100, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '127', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-16 09:28:47', '2018-08-16 09:42:51', NULL, '小微儒商', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2503, 'dzjg', '党政机关', 'fyqk,qtxsj', '反映情况,群体性事件', NULL, NULL, 'sf', '上访', NULL, NULL, 'jzwt', '集资问题', 'wsyr', '51-100人', '2', 'qkfy', 100, 0.00, 0, 0, '0', NULL, '情况反映', 'cea347ae-3a70-4a99-bb6c-bd5051487b93', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
