INSERT INTO `qb_zdasjqbxx` VALUES (1583, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', NULL, '20余名新源再生产业有限公司职工聚集在新源阁酒店', '2018年8月14日、15日，云南新源再生产业有限公司20余名退休职工在新源阁酒店一楼大厅聚集，要求诺嘉公司归还新源酒店的经营权。我所民警已对在场人员进行政策宣讲，截止情况上报时无过激行为，未出现堵路及影响交通的情况。15日有8名退休职工在新源酒店留宿，占据新源阁酒店两个套间，目前新源阁酒店未能正常经营，相关情况黑林铺所持续关注中。\r\n\r\n \r\n', '2018-08-15 09:00:00', '2018-08-15 12:00:00', 3.00, 20, '合同纠纷 ', '黑林铺所', '新源阁酒店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-16 10:25:53', NULL, NULL, '退休职工', NULL, NULL, NULL, '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区内', '新源阁酒店', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2758, 2556, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
