INSERT INTO `qb_zdasjqbxx` VALUES (1608, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'qt', '2018-08-22 14:46:40', '8月22 日 70余名“昆明远卓深港金融服务有限公司”投资户群体成员到市政法委聚集上访', '8月22日9时许，为持续给政府相关部门施压，引起政府相关部门对“昆明远卓深港金融服务有限公司”投资人的重视，陆续有70余名“昆明远卓深港金融服务有限公司”投资户群体成员到昆明市政法委聚集上访。相关情况在进一步掌握中。（国保大队）\r\n\r\n\r\n', '2018-08-22 09:00:00', '2018-08-22 10:29:00', 1.48, 70, '民间借贷 ', '官渡分局', '昆明市政委', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-23 10:04:32', '2018-08-23 10:39:27', NULL, '远卓投资户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区外', '昆明市政委', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2810, 2504, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '612c2cc2-bfe3-4143-ac90-3fadc7470d31', 'JS', NULL, 'Y', 110, NULL, NULL, '530107000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1609, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-22 11:23:28', '8月22日10名东风云南汽车厂退休职工聚集在汽车厂门口', '08月22日9时15分许，10名东风云南汽车厂的退休职工连续三日聚集在云南汽车厂，职工分散在大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，无过激行为。至10时29分许聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。（黑林铺所）\r\n\r\n\r\n\r\n \r\n\r\n\r\n', '2018-08-22 09:15:00', '2018-08-21 10:29:00', 22.77, 10, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-23 10:04:32', '2018-08-23 10:36:48', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '8a9c9cec-9770-4b13-af3e-955af70e9540', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1610, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-08-22 10:36:17', '21日甘肃、宁夏收购商20人因与一心堂发生纠纷一事到省政府上访', '21日16:40分许，20名群众到省政府上访，反映，他们是收购甘肃、青海、西藏等地冬虫夏草的收购商，今年初通过一名中间商的介绍将价值520余万元的虫草送到云南鸿翔药业一心堂总公司，但公司至今没有按照供货协议支付他们货款，造成近百名经营户经济损失，期间他们多次向昆明市各级政府反映，有关部门也出面协调多次仍然没有结果，今年7月20日他们向呈贡分局经侦大队报案，由于等不到解决结果，故今日到省政府上访，要求尽快拿到货款。省信访局接待后告知其此事已进入司法程序要求其到昆明市政法委反映。16:50分许，来访人离开接访大厅，目前聚集在省政府侧面人行道处，我所已安排警力密切关注。\r\n', '2018-08-21 16:40:00', '2018-08-21 16:50:00', 0.17, 20, '索薪讨款', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-23 10:04:32', '2018-08-23 10:35:11', NULL, '个体户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2547, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'gtgsyz', '个体工商业者', 'mjjf', '民间纠纷', 'sldws', '16-50人', '2', 'qkfy', 20, 0.00, 0, 0, '0', NULL, '情况反映', 'a9f09b44-d79e-4597-b7f0-10a4bdb28cd6', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1611, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', NULL, '7名群众在云南师范大学一二一校区资产处门口聚集拉横幅', '8月22日10时50分许，7名群众在云南师范大学一二一校区资产处门口聚集拉横幅，横幅为红底黑字，上书写“恳请师大资产公司：娃娃要读书！”。现场群众无过激行为。\r\n经了解，7名群众（6女1男）为学生家长，孩子均就读于新建设国际小学（该小学位于莲华所辖区，具体地址我所暂未掌握）。聚集的学生家长称新建设国际小学的办学地址是向云南师范大学租赁的，但因新建设国际小学拖欠了租金，于是云南师范大学将收回该地，致使孩子无处就学。后家长到小学了解情况，小学称已将拖欠的租金补齐，并与师大签订过18年的办学合作协议，但云南师范大学仍要将场地收回，于是学生家长到师范大学资产处聚集，要求师范大学按照协议执行，继续租赁场地给新国小办学，让孩子有地方上学。11时30分许，新建设国际小学安全负责人王老师、云南师范大学公安处胡处长到现场与7名学生家长沟通，13时10分许，聚集人员全部离开，但声称明天还会来。\r\n因今日云南师范大学资产处无负责人到场，我所暂未掌握改事件具体原因，社区民警已持续跟进开展工作，后续情况待报。\r\n', '2018-08-22 10:50:00', '2018-08-22 13:10:00', 2.33, 7, '学校管理', '文林所', '云南师范大学', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-23 10:42:37', NULL, NULL, '个体户', NULL, NULL, NULL, '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, NULL, NULL, NULL, '区内', '云南师范大学', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2651, 2547, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JS', NULL, 'Y', 110, NULL, NULL, '530102440000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1612, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-08-23 16:18:53', '怒江州贡山县7名群众到省信访局举报“远卓深港”虚构惠农助农政策骗取群众资金', '23日15时30分许，7名怒江州贡山县群众到省政府上访，反映，2016年9月起，云南卓金金服网贷平台（总公司地址在昆明人民中路巨龙大厦）虚构惠农助农政策，向少数民族地区困难群众借款，诈骗怒江地区150名群众1800余万元。来访村民称他们得知今年6月25日该平台实际控制人无法找到，群众的资金无法拿到后遂向当地公安机关报案，由于至今没有得到答复所以到省政府上访，要求上级政府过问该案，督促有关部门加快侦破力度，尽早追回被骗资金，还困难群众血汗钱。省信访局接待后告知其反映的诉求属涉法涉诉问题，不属于信访受理事项，要求向省政法委反映。16:00许，7人全部离开省政府。\r\n\r\n\r\n\r\n\r\n\r\n华山东路派出所\r\n\r\n2018年8月23日\r\n', '2018-08-23 15:30:00', '2018-08-23 16:00:00', 0.50, 7, '劳资纠纷', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-24 09:55:02', '2018-08-24 11:19:27', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'jzwt', '集资问题', 'sryx', '10人以下', '1', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', 'e4f89810-3aa7-4dfd-b1f2-2acbbc198c5a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1613, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-08-23 14:56:14', '8月23日7名贵州籍男子因亲属死亡一事到省政府上访', '8月23日14时15分许，贵州籍男子李家付等7人到省政府上访，反映其弟弟李家举于2018年7月7日在官渡古镇盛世乐坛KTV门口因斗殴被十余人围殴致死，来访人称官渡派出所对此没有立案，官渡检察院没有批捕嫌疑人，所有嫌疑人都被放走，现尸体放在官渡区人民医院内，至今没有人给家属一个交代，故到省政府上访，要求追究这些人的刑事责任，追究办案人员的不作为行为，同时请求政府帮助其协调所产生的一切费用。省信访局接待后告知其反映的问题属涉法涉诉范畴，要求其向昆明市司法部门反映。现7人还滞留在武警操场处。后续情况密切关注中。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-08-23 14:15:00', '2018-08-23 15:00:00', 0.75, 7, '损害赔偿 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-24 09:55:02', '2018-08-24 11:17:26', NULL, '村民', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'xszaajdwt', '刑事治安案件等问题', 'sryx', '10人以下', '2', 'qkfy', 7, 0.00, 0, 0, '0', NULL, '情况反映', '26961057-2edb-4a04-a17f-626a350b130c', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1616, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-23 11:39:25', '15名东风云南汽车厂退休职工到厂门口聚集', '08月23日9时09分许，15名东风云南汽车厂的退休职工分散聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，无过激行为。至10时32分许聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。\r\n\r\n\r\n', '2018-08-23 09:09:00', '2018-08-23 10:32:00', 1.38, 15, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-24 09:55:02', '2018-08-24 11:16:20', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '6bf17b55-dc6a-43cc-b948-da6318f980fe', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1617, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-23 10:27:39', '8月23日7名远卓投资户聚集在巨龙大厦', '8月23日10时许，巨龙大厦7楼昆明远卓深港金融服务有限公司有7名投资户来访，人员在会议室等待，现场秩序良好，投资户情绪稳定。后续情况将密切关注中。（小南所）\r\n\r\n\r\n', '2018-08-23 10:00:00', '2018-08-23 11:00:00', 1.00, 7, '民间借贷 ', '小南所', '昆明远卓深港金融服务有限公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-24 09:55:02', '2018-08-24 11:15:18', NULL, '远卓投资户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '昆明远卓深港金融服务有限公司', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2729, 2504, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '47525709-88c6-4b3a-ae51-adfd9a8c5633', 'JS', NULL, 'Y', 110, NULL, NULL, '530102520000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1618, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-08-23 10:00:00', '8月23日蒙自市期路白乡村民18人因不满移民补偿问题到省政府上访', '     23日09时许，18名蒙自市期路白乡村民到省政府上访，反映，当地有一个五里冲水库是水源地。2017年7月，蒙自市政府为保护水源地的需要，要求规划范围内的村民搬迁至政府指定的区域居住，搬迁补偿标准是不论大小每人补助3万元，然后在搬迁地购买政府建盖的安居房。来访村民称，补偿范围不包括外嫁女、招赘女婿以及他们的子女，涉及203人，引发该群体不满，他们认为外嫁女虽然外嫁出去，但是户口没有迁走，入赘的也将户口迁入了当地，应当享受搬迁补偿，他们多次向当地政府反映要求一视同仁享受搬迁补偿未得到答复所以到省政府上访，要求按照派出所登记的户口为准，一视同仁享受搬迁补偿。省信访局接待后告知其向红河州政府反映。现来访村民在信访局附近，民警密切关注中。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-08-23 09:00:00', '2018-08-23 13:00:00', 4.00, 18, '移民补偿', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-24 09:55:02', '2018-08-24 11:17:36', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sldws', '16-50人', '1', 'qkfy', 18, 0.00, 0, 0, '0', NULL, '情况反映', '426d98ab-a946-4e2e-8a36-77a96ddbd4b9', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1619, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-23 09:57:20', '8月23日90余名金座集资人员到市体育馆例行上访', '8月23日9时许，90余名金座人员到市体育馆进行例行上访。市金融办相关工作人员在现场进行接访，民警正在现场开展秩序维护疏导化解等工作。上访人员秩序良好，无过激行为。（茭菱所）\r\n', '2018-08-23 09:00:00', '2018-08-23 13:00:00', 4.00, 90, '特殊群体', '茭菱所', '市体育馆', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-24 09:55:02', '2018-08-24 11:00:37', NULL, '金座', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '市体育馆', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2428, 2502, NULL, NULL, NULL, NULL, NULL, NULL, 'sf', '上访', NULL, NULL, NULL, NULL, 'yblyr', '101-200人', '1', 'qkfy', 110, 0.00, 0, 0, '0', NULL, '情况反映', '6e4f6aa2-92e0-47dd-acab-749c6f69b2bb', 'JS', NULL, 'Y', 110, NULL, NULL, '530102550000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
