INSERT INTO `qb_zdasjqbxx` VALUES (1624, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-08-24 11:14:22', '8月24日5名投资国际商贸公司的中老年人因资金被骗到省政府上访', '    24日10时20分许，5名昆明老人到省政府上访，反映，从2016年8月起，他们通过街边传单，朋友推荐陆续参与到沈阳老妈乐（全家福）国际商贸公司在昆明开展的购卡返利活动中，卡的级别有十几级，最高级是翡翠级，每张翡翠卡26800元，每周除按购买金额返利外，另外赠送蚕丝被、电饭锅、保健品等物品，购买卡的级别越高金额越多，返利和礼品赠送就越多，故很多老年人投入大量资金参与进来。来访人称，从2017年9月起，他们就没有再领到返利及礼品，后了解到该公司在昆明的三个负责人卷款逃跑，引发投资老年人恐慌及不满，感觉被骗，据称涉及在昆明的中老年人5000余人，另外还有昭通、曲靖、版纳、临沧、红河等地的中老年人共计1万余人，涉及被骗资金2亿5千余万元。他们称去年已向公安机关报案，由于至今没有得到答复所以到省政府上访，要求尽快拿到被骗资金。省信访局接待后告知其向省金融办反映。10时50分许，5人全部离开省政府。（华山所）\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '2018-08-24 10:20:00', '2018-08-24 10:50:00', 0.50, 5, '涉众型经济案件', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-27 09:09:20', '2018-08-27 10:10:45', NULL, '投资户', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2505, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'ltxry', '离退休人员', 'jzwt', '集资问题', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '2e4cc2e6-f951-42a7-b6bb-74bc8ee32057', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1625, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-24 10:37:11', '8月24日30余名东风云南汽车厂退休职工到厂门口聚集', '8月24日09时05分许，30余名东风云南汽车厂的退休职工分散聚集在厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”\r\n\r\n至10时28分许，聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。（黑林铺所）\r\n', '2018-08-24 09:05:00', '2018-08-24 10:28:00', 1.38, 30, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-27 09:09:20', '2018-08-27 09:15:26', NULL, '退休职工', NULL, '0', '1', '拉横幅', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '79f7a26a-38f9-49ea-a5d4-860aec1928dd', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1626, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-26 14:30:36', '10余名新源再生产业有限公司职工聚集在新源阁酒店的情况反映', '\r\n\r\n8月25日，云南新源再生产业有限公司10余名退休职工在新源阁酒店一楼大厅聚集，要求诺嘉公司归还新源酒店的经营权，当晚有9名退休职工在新源酒店留宿。相关情况持续关注中。\r\n\r\n\r\n', '2018-08-25 08:00:00', '2018-08-25 10:00:00', 2.00, 10, '合同纠纷 ', '黑林铺所', '新源阁酒店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-27 09:12:00', '2018-08-27 10:09:10', NULL, '退休职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '新源阁酒店', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2758, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'e3e1d34c-c5be-448d-b77b-4f8a8e3d0495', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1627, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-26 14:12:23', '50余名家长到区教育局新闻路小学咨询点反映子女入学问题', '8月25日、26日，五华区教育局在五华区新闻路小学设立了咨询处，回答家长关于子女在五华区上小学的各种问题。\r\n\r\n25日，住在北市区的家长50余人，反映因北市区公立学校学位名额已满，私立小学玖如堂小学学费高昂（一学年收包括餐费、午托、校服等各项费用共12800元），要求降低费用或者更换教育费用较低的学校。\r\n\r\n现场教育局10余名工作人员正在与家长沟通协商，并向昆明市教育局反映了此事。相关情况进一步掌握中。\r\n\r\n\r\n', '2018-08-25 10:25:00', '2018-08-26 10:27:00', 24.03, 50, '入学问题', '大观所', '区教育局新闻路', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-27 09:12:00', '2018-08-27 10:19:39', NULL, '学生家长', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '区教育局新闻路', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2685, 2544, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '31aa40a4-9583-48dc-9321-3acb330f3904', 'JS', NULL, 'Y', 110, NULL, NULL, '530102480000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1628, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-26 13:55:00', '15名新源再生产业有限公司职工聚集在新源阁酒店的情况反映20180824', '2018年8月24日，云南新源再生产业有限公司15名退休职工来到新源阁酒店聚集，要求诺嘉公司归还新源酒店的经营权，15名员工分布在酒店二楼房间内看电视、聊天等，我所民警已对在场人员进行政策宣讲。截止情况上报时，约有6名退休职工留宿新源阁酒店，暂无过激行为。相关情况持续关注中。\r\n\r\n\r\n', '2018-08-24 08:45:00', '2018-08-24 10:00:00', 1.25, 15, '合同纠纷 ', '黑林铺所', '新源阁酒店', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-27 09:12:00', '2018-08-27 09:48:39', NULL, '退休职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '新源阁酒店', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2758, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'cfc64da7-3eb4-413a-879f-bdf5ee1b9912', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1629, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'qt', '2018-08-26 13:48:43', '昭阳区省耕山水业主及子女60余人因就学问题到省政府上访', '     25日08:45分许，60余名昭通市昭阳区群众到省政府上访，反映，2014年9月22日昭通市教育局与省耕山水小区开发商昭通市深耕置业有限公司签署相关协议，凡该小区业主的子女小学均可上附近的昭通市实验小学，来访业主称2016年、2017年该小学已连续两年招收小区业主子女，今年6月份，业主根据学校相关规定提交了子女入学的相关材料，8月21日，家长收到学校短信告知其子女不能入学，原因未告知，后家长了解到，学校将业主子女入学的名额划给了市政府机关公职人员的子女，导致该小区业主130余名小孩无法入该校，引发家长不满，遂向昭通市政府反映要求解决入学问题，由于得不到满意答复，昨日（8月24日）曾有4名家长到省政府上访，省信访局接待后告知其回昭通市反映，今早08:45分许，60余名家长及子女来到省政府，身穿白色T恤,上写“消除特权 就近入学”字样，部分人跪在地上高喊“为民做主”，要求立即解决孩子入实验小学的问题。\r\n', '2018-08-25 08:45:00', '2018-08-25 10:00:00', 1.25, 60, '学校管理', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-27 09:12:00', '2018-08-27 09:44:11', NULL, '村民', NULL, '0', '2', '喊口号', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'cz', '城镇', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'tsqt', '特殊群体', 'dzzbm', '对政策不满', 'wsyr', '51-100人', '2', 'qkfy', 60, 0.00, 0, 0, '0', NULL, '情况反映', '08e0db37-95e9-4fd2-941a-56f2bab998f3', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1630, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-27 17:12:28', '8月27日大观所妥善化解20余名民工到云南省建三公司讨薪警情', '8月27日13时许，云南省建投第三公司报警至大观所称：有20多民工到新闻路省建三公司大院讨要工钱。\r\n\r\n民警到达现场，经了解：聚强劳务公司于2015年向省建三公司承包了阜外医院昆明规划馆的钢筋工程，负责人为杨凌云。后何勇（男，四川省广安人，云南聚强劳务公司招聘劳务用工的负责人）于2015年10月向云南聚强劳务公司承包了阜外医院昆明规划馆的钢筋工程，带领其招聘来的工人进行工程施工，并于2016年8月结束完工。何勇称现聚强劳务还有100万左右工钱未付给他，致使其未向工人支付劳务费。 \r\n\r\n经民警将三公司项目技术负责人罗怀明及聚强劳务公司负责人杨凌云及工程承包人何勇约到一起进行核对，了解到省建三公司已将所有工程款项按合同付给聚强劳务公司，聚强劳务公司负责人杨凌云已当场确认。聚强劳务公司已按合同规定将工人工钱付给何勇，何勇当场确认。聚集的原因是何勇个人未按合同支付20余名工人的工钱。经民警协调，聚强劳务公司负责人杨凌云及工程承包人何勇与现场20余名工人进行对账，并协商工钱支付的事宜，民警告知现场讨薪人员，理性协商、不要过激维权，现双方协商中。（大观所）\r\n', '2018-08-27 13:00:00', '2018-08-27 14:00:00', 1.00, 20, '索薪讨款', '大观所', '云南省建三公司', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-28 09:19:02', '2018-08-28 09:56:07', NULL, '农民工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '云南省建三公司', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2683, 2519, NULL, NULL, 'gzfy', '工作反映', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '9ae0508b-4377-4c95-a04a-23d5cbdbb1f2', 'JS', NULL, 'Y', 110, NULL, NULL, '530102480000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1631, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-08-27 14:25:37', '外来务工人员不满子女被划分到私立学校而聚集到省教育厅上访情况', '2018年08月27日上午09时许，莲华所接省教育厅报称有50多名学生家长到不满子女被划分到私立学校而到省教育厅上访。\r\n\r\n据了解，北市区96名学生因为是外来务工子女被划分到莲华所辖区玖如堂小学；莲华所辖区师大国际小学因为停办涉及130人外来务工子女分留到普吉辖区的私立博容小学；另有昭通市买小区房的多名户主（约100人左右）因户口未转，子女就不能在当地读公立学校，昭通市教育局承诺只要户口转移到当地就能安排其子女上公立学校（具体学校未说明）；另有两名高新区外来务工子因分留问题无法到公立小学上学。\r\n\r\n省教育厅已安排领导开展接待工作，截止到目前大部分家长已经离开。现场只有10名左右的家长等待省教育厅给出的答复情况。现场秩序良好，未发生过激行为。\r\n\r\n相关情况进一步掌握中。\r\n\r\n\r\n\r\n\r\n', '2018-08-27 09:00:00', '2018-08-27 10:00:00', 1.00, 50, '入学问题', '莲华所', '省教育厅', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-28 09:19:02', '2018-08-28 09:47:09', NULL, '学生家长', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', '省教育厅', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2627, 2544, NULL, NULL, NULL, NULL, NULL, NULL, 'qy', '请愿', NULL, NULL, NULL, NULL, 'wsyr', '51-100人', '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '57f851ce-d6ba-4dfc-babe-3eba5f0a2c5a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102420000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1632, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-27 13:56:17', '8月27日部分人民路“壹号广场”业主聚集情况', '据工作掌握，由于近期“壹号广场”间断性停水，2018年8月27日9时30分许，30余名“壹号广场”业主在10栋2楼昆都国际房地产开发有限公司聚集，要求回复供水。后昆都国际开发商回复27日下午16时以前全部恢复供水，12时20分许聚集业主离开现场。\r\n\r\n相关情况在进一步掌握中。\r\n\r\n\r\n', '2018-08-27 09:30:00', '2018-08-27 12:20:00', 2.83, 30, '物业管理', '国保大队', '壹号广场', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-28 09:19:02', '2018-08-28 09:41:55', NULL, '小区业主', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '壹号广场', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', 'be44e38d-a104-48db-a03b-a1f1e53704e5', 'JS', NULL, 'Y', 110, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1633, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-08-27 13:55:22', '8月27日部分“卓金”集资户到五华经侦大队上访的情况', '据工作掌握，2018年8月27日9时许，陆续有90余名“卓金”集资户到五华公安分局经侦大队聚集上访，要求五华经侦大队对“卓金”案件立案。11时许，五华经侦大队接访了5名“卓金”集资户代表。12时45分，接访结束，上述人员陆续离开五华经侦大队。\r\n\r\n相关情况在进一步掌握中。\r\n\r\n\r\n', '2018-08-27 09:00:00', '2018-08-27 12:45:00', 3.75, 90, '民间借贷 ', '西山分局', '五华公安分局经侦大队', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-28 09:19:02', '2018-08-28 09:39:43', NULL, '远卓投资户', NULL, '0', '2', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '五华公安分局经侦大队', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2806, 2504, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '6b2c456f-d752-46c8-8580-6ffeb2b68b4b', 'JS', NULL, 'Y', 110, NULL, NULL, '530105000000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1634, NULL, NULL, NULL, NULL, 'jj', '维稳信息', 'bq', '2018-08-27 10:59:22', '08月27日10名东风云南汽车厂退休职工例行聚集在汽车厂门口', '08月27日09时07分许，10名东风云南汽车厂的退休职工例行聚集在云南汽车厂大门两侧人行道上，要求东风云南汽车厂解决他们的问题及诉求，在下厂区大门口侧边栏杆处、门上分别横挂4幅内容相同的白底黑字布标，内容为“要求公开土地置换合同、严查国有土地流失和国有资产流失”。“严惩贪污腐败，我们要吃饭、我们要生存、还我血汗钱。”汽车厂职工分散聚集在厂区门口和两旁人行道上，无过激行为。至10时29分许聚集职工自行收拾标语离开，未出现堵路及影响交通的情况。相关情况持续关注中。（黑林铺所）\r\n\r\n\r\n\r\n \r\n\r\n\r\n', '2018-08-27 09:07:00', '2018-08-27 10:29:00', 1.37, 10, '身份待遇', '黑林铺所', '东风云南汽车厂', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-28 09:19:02', '2018-08-28 09:30:03', NULL, '退休职工', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PubSecurityBusy', NULL, NULL, '区内', '东风云南汽车厂', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2606, 2556, NULL, NULL, 'qt', '其他', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 'qkfy', 0, 0.00, 0, 0, '0', NULL, '情况反映', '9b420749-1721-4076-a241-c837fe3d40de', 'JS', NULL, 'Y', 110, NULL, NULL, '530102570000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
INSERT INTO `qb_zdasjqbxx` VALUES (1635, NULL, NULL, NULL, NULL, 'sf', '维稳信息', 'bq', '2018-08-27 09:46:21', '8月27日景洪市勐旺乡村民5 人因土地性质变更一事到省政府上访', '8月27日9:10分许，5名版纳州景洪市勐旺乡瑶家村委会大龙山村村民到省政府上访，反映，2008年当地进行林改，其生产队将该村50余名有土地承包证的村民的3600亩农田划为国有林地，村民不服，认为生产队侵害其合法权益，导致农民利益受损，故一直向当地各级部门反映，要求调查处理，还地于民。由于至今没有得到答复所以到省政府上访，要求政府调查。省信访局接待后告知其向西双版纳州政府反映。09:25分许，5人全部离开省政府。（华山所）\r\n', '2018-08-27 09:10:00', '2018-08-27 09:25:00', 0.25, 5, '山林土地问题 ', '华山所', '省政府', NULL, NULL, '2', NULL, NULL, NULL, NULL, NULL, NULL, 151951, 'cjgly', '2018-08-28 09:19:02', '2018-08-28 09:28:20', NULL, '村民', NULL, '0', '1', '无', NULL, NULL, NULL, NULL, '报市局', NULL, NULL, 'PoliticalStability', NULL, NULL, '区内', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 2392, 2507, 'nn', '农村', 'fyqk', '反映情况', NULL, NULL, 'sf', '上访', 'nm', '农民', 'snwt', '三农问题', 'sryx', '10人以下', '1', 'qkfy', 5, 0.00, 0, 0, '0', NULL, '情况反映', '92eb78cb-d387-4790-b703-7d821e4d799a', 'JS', NULL, 'Y', 110, NULL, NULL, '530102450000', NULL, NULL, NULL, NULL, NULL, 'ZQYSB', NULL, NULL);
